[8901] = {"Très chère Elenia,",nil, {"Apporter la Lettre écrite avec soin à Elenia Haydon, près de la banque de Fossoyeuse."}},
[8902] = {"Très chère Elenia,",nil, {"Apporter la Lettre pleine d'amour à Elenia Haydon, près de la banque de Fossoyeuse."}},
