[8922] = {"Un objet surnaturel",nil, {"Remettre le Récipient de sang scellé à Mux Brouillemana, à Gadgetzan en Tanaris."}},
[8923] = {"Un objet surnaturel",nil, {"Remettez le Récipient de venin scellé à Mux Brouillemana, à Gadgetzan."}},
[8924] = {"La chasse aux ectoplasmes",nil, {"Utilisez l'Alambic ectoplasmique à proximité de morts-vivants désincarnés pour recueillir 12 Ectoplasmes brûlés en Silithus, 12 Ectoplasmes glacés au Berceau-de-l'hiver et 12 Ectoplasmes stables dans les Maleterres de l'est. Remettez à Mux Brouillemana à Gadgetzan. Il veut aussi récupérer l'Alambic ectoplasmique."}},
[8925] = {"Une source d’énergie portable",nil, {"Trouvez le Seigneur-magma Bokk dans les Steppes ardentes, prenez son Coeur de magma et rapportez-le à Mux Brouillemana, à Gadgetzan."}},
