[9119] = {"Panne au sanctum occidental",nil, {"Parler à la telluriste Velania, au Sanctum occidental du bois des Chants éternels."}},
[9120] = {"La chute de Kel'Thuzad",nil, {"Apportez le Phylactère de Kel'Thuzad à la chapelle de l'Espoir de Lumière dans les Maleterres de l'est."}},
[9121] = {"Naxxramas, la citadelle de l'effroi",nil, {"L'archimage Angela Dosantos à la chapelle de l'Espoir de Lumière dans les Maleterres de l'est veut 5 Cristaux des arcanes, 2 Cristaux de nexus, 1 Orbe de piété et 60 pièces d'or. Vous devez aussi être <Honoré/Honorée> auprès de l'Aube d'argent."}},
[9122] = {"Naxxramas, la citadelle de l'effroi",nil, {"L'archimage Angela Dosantos à la chapelle de l'Espoir de Lumière dans les Maleterres de l'est veut 2 Cristaux des arcanes, 1 Cristal de nexus et 30 pièces d'or. Vous devez aussi être <Révéré/Révérée> auprès de l'Aube d'argent."}},
[9123] = {"Naxxramas, la citadelle de l'effroi",nil, {"L'archimage Angela Dosantos à la chapelle de l'Espoir de Lumière dans les Maleterres de l'est vous accordera gratuitement l'Occultation arcanique. Vous devez être <Exalté/Exaltée> auprès de l'Aube d'argent."}},
[9124] = {"L'armure de traqueur des cryptes ne se fabrique pas toute seule",nil, {"Le veneur Leopold, à la chapelle de l'Espoir de Lumière dans les Maleterres de l'est, veut que vous lui apportiez 30 Morceaux de démon des cryptes."}},
[9125] = {"Des morceaux de démon des cryptes",nil, {"Votre travail pour le premier lot a été exemplaire, <nom>. Si vous en avez plus, je peux les prendre maintenant. Pour chaque lot que vous rapporterez, je vous récompenserai avec un insigne."}},
[9126] = {"Des morceaux pour la faucheuse d'os",nil, {"Rohan l'assassin, à la chapelle de l'Espoir de Lumière dans les Maleterres de l'est, veut 30 Fragments d'os."}},
[9127] = {"Des fragments d'os",nil, {"Dans mon métier, un homme ne peut jamais avoir trop de fragments d'os, <nom>.Chaque fois que vous aurez un surplus de fragments, apportez-les-moi, je vous récompenserai avec un autre insigne."}},
[9128] = {"L'équation élémentaire",nil, {"L'archimage Angela Dosantos à la chapelle de l'Espoir de Lumière dans les Maleterres de l'est veut 30 Noyaux des éléments."}},
[9129] = {"Le noyau des éléments",nil, {"À vrai dire, j’utilise les noyaux pour créer des armures de givrefeu. C’est l’armure que portent les mages qui se battent dans Naxxramas. Sans vous et quelques autres, nous serions incontestablement en train de perdre cette guerre.nnCeci dit, vous pouvez m’apporter encore des noyaux et je vous octroierai d’autres insignes."}},
[9130] = {"Des marchandises de Lune-d'argent",nil, {"Remettre le Bordereau de chargement de l'intendante Lymel au maître du ciel Vol-solaire, à Tranquillien."}},
[9131] = {"Armer un cuirassier",nil, {"Korfax, de la chapelle de l'Espoir de Lumière dans les Maleterres de l'est, veut 30 Morceaux de sombrefer."}},
[9132] = {"Des morceaux de sombrefer",nil, {"Pour chaque brassée de ces morceaux que vous m'apporterez, je vous donnerai un insigne - le plus sera le mieux. Hors de ma vue maintenant, avant que je vous tranche en deux !"}},
[9133] = {"Un vol jusqu'à Lune-d'argent",nil, {"Acheter un vol pour Lune-d'argent et remettre le Bordereau de chargement de l'intendante Lymel à Sathren Aube-azur, à Lune-d'argent."}},
[9134] = {"La maîtresse du ciel Tombejour",nil, {"Apporter la Cargaison de l'intendante Lymel à la maîtresse du ciel Tombejour, à l'extérieur de la Porte du berger à Lune-d'argent."}},
[9135] = {"Retour à l'intendante Lymel",nil, {"Acheter un vol de retour vers Tranquillien dans les Terres fantômes, et remettre la Cargaison de l'intendante Lymel à l'intendante Lymel, à Tranquillien."}},
[9136] = {"La flore sauvage",nil, {"Rayne de la chapelle de l'Espoir de Lumière dans les Maleterres de l'est veut 30 Palmes sauvages."}},
[9137] = {"Des palmes sauvages",nil, {"Pour chaque fagot de palmes que vous me livrerez, je vous payerai avec un insigne de votre choix, de l’Aube ou de la Croisade.nnLes insignes peuvent être échangés auprès des intendants contre toutes sortes de récompenses."}},
[9138] = {"Le village de Solcouronne",nil, {"Tuez 10 Gardes nérubis, puis retournez voir l'arcaniste Vandril à Tranquillien dans les Terres fantômes."}},
[9139] = {"Brume-d'or",nil, {"Vaincre 6 Fantômes quel'dorei et 4 Âmes en peine quel'dorei au village de Brume-d'or, puis retourner voir l'arcaniste Vandril à Tranquillien, dans les Terres fantômes."}},
[9140] = {"Coursevent",nil, {"Collectez 6 Substances fantasmatiques et 4 Fragments de gargouille. Rapportez-les à l'arcaniste Vandril à Tranquillien dans les Terres fantômes."}},
[9141] = {"On m'appelle \"Le Coq\"",nil, {"Le commandant d'ordonnance Metz, à la chapelle de l'Espoir de Lumière dans les Maleterres de l'est, veut une Marque de valeur de l'Aube d'argent."}},
[9142] = {"Commission d'artisan",nil, {"Vous savez comment ça se passe, mon petit. Pour une marque de valeur, vous recevez une commission d'artisan."}},
[9143] = {"S'occuper de Zeb'Sora",nil, {"Collecter 6 Oreilles de trolls de Zeb'Sora et les remettre au Forestier Valanna dans les Terres fantômes."}},
[9144] = {"Disparu dans les terres Fantômes",nil, {"Suivez la route qui part du village de Brise-Clémente vers le sud. Lorsque vous approcherez les Terres Fantômes, cherchez le Courrier Aube-glorieuse."}},
[9145] = {"Il faut aider le forestier Valanna !",nil, {"Trouver le forestier Valanna et le lieutenant Tomathren sur la rive du lac Elrendar, dans les Terres fantômes, près du village de Solcouronne."}},
[9146] = {"Un rapport pour le capitaine Hélios",nil, {"Parler au capitaine Hélios à l'enclave des Pérégrins, sur la rive sud du lac Elrendar, dans les Terres fantômes."}},
[9147] = {"Le courrier blessé",nil, {"L'apothicaire Thedra du bois des Chants éternels veut que vous lui apportiez 4 Échantillons de sang pestiféré pris sur les bêtes des Terres fantômes."}},
