[9252] = {"La défense de Brise-Clémente",nil, {"Tuer 4 Maraudeurs moignons-pourris et 4 Sombres âmes en peine, puis retourner voir le forestier Sareyn au village de Brise-Clémente dans le bois des Chants éternels."}},
[9253] = {"Le garderune Deryan",nil, {"Parler au garderune Deryan, dans le bois des Chants éternels, près de la frontière des Terres Fantômes."}},
[9254] = {"L'apprentie égarée",nil, {"Parler à l'apprentie Mirveda au Sanctum oriental dans le bois des Chants éternels."}},
[9255] = {"Les notes de recherche",nil, {"Apporter les Notes de recherche à la magistrice Landra Aube-glorieuse au village de Brise-clémente, dans le bois des Chants éternels."}},
[9256] = {"Le village de Brise-Clémente",nil, {"Parler au forestier Degolien, au village de Brise-Clémente, dans le bois des Chants éternels."}},
[9257] = {"Atiesh, le grand bâton du Gardien",nil, {"Anachronos, aux Grottes du temps en Tanaris, veut que vous emportiez Atiesh, le grand bâton du Gardien, à Stratholme et que vous le placiez sur la Terre consacrée. Abattez l’entité qui sera extraite du bâton et retournez le voir."}},
[9258] = {"Le bosquet incendié",nil, {"Trouver Larianna Ruissevent près du Bosquet incendié, au sud-ouest du bois des Chants éternels."}},
[9259] = {"Un traître envers la Voile sanglante",nil, nil},
[9260] = {"Enquêter au sujet du Fléau à Stormwind",nil, {"Se procurer trois Pierres nécrotiques pâles du Fléau à l'extérieur de Hurlevent et enquêter au sujet des cercles runiques lumineux près du campement."}},
[9261] = {"Enquêter au sujet du Fléau à Forgefer",nil, {"Se procurer trois Pierres nécrotiques pâles du Fléau à l'extérieur de Forgefer et enquêter au sujet des cercles runiques lumineux près du campement."}},
[9262] = {"Enquêter au sujet du Fléau à Darnassus",nil, {"Se procurer trois Pierres nécrotiques pâles du Fléau à l'extérieur de Darnassus et enquêter au sujet des cercles runiques lumineux près du campement."}},
[9263] = {"Enquêter au sujet du Fléau à Orgrimmar",nil, {"Se procurer trois Pierres nécrotiques pâles du Fléau à l'extérieur d'Orgrimmar et enquêter au sujet des cercles runiques lumineux près du campement."}},
[9264] = {"Enquêter au sujet du Fléau aux Pitons du Tonnerre",nil, {"Se procurer trois Pierres nécrotiques pâles du Fléau à l'extérieur des Pitons du Tonnerre et enquêter au sujet des cercles runiques lumineux près du campement."}},
[9265] = {"Enquêter au sujet du Fléau à Fossoyeuse",nil, {"Se procurer trois Pierres nécrotiques pâles du Fléau à l'extérieur de Fossoyeuse et enquêter au sujet des cercles runiques lumineux près du campement."}},
