[9272] = {"L'habit fait le boucanier",nil, nil},
[9274] = {"Les esprits des noyés",nil, {"Le forestier Krenn'an, à l'enclave des Pérégrins dans les Terres fantômes, veut que vous tuiez 8 Apparitions vengeresses et 8 Apparitions voraces dans le lac voisin."}},
[9275] = {"Une petite pointe d'assaisonnement",nil, {"Empoisonnez la nourriture de troll aux camps de Zeb'Nowa, puis retournez voir l'apothicaire Venustus à l'enclave des Pérégrins, dans les Terres fantômes."}},
[9276] = {"L'attaque de Zeb'Tela",nil, {"Tuez 8 Exhalombres Ombrepins et 8 Chasseurs de têtes Ombrepins, puis retournez voir le pérégrin Solanna à l'enclave des Pérégrins des Terres fantômes."}},
[9277] = {"L'assaut de Zeb'Nowa",nil, {"Tuez 10 Seigneurs des fauves Ombrepins et 10 Maléficieurs Ombrepins, puis retournez voir le pérégrin Solanna à l'enclave des Pérégrins des Terres fantômes."}},
[9278] = {"Bienvenue !",nil, {"Apporter le Bon-cadeau du Guet d'azur à Jaeleil au Guet d'azur."}},
[9279] = {"Vous avez survécu !",nil, {"Parler à Proenitus au Point d'impact, dans le val d'Ammen."}},
[9280] = {"Recharger les cristaux de soin",nil, {"Extraire 8 Fioles de sang de phalène sur les Phalènes du val, puis les donner à Proenitus, au Point d'impact du val d'Ammen."}},
[9281] = {"Faire place nette",nil, {"L'apprenti Vor'el, au domaine d'Andilien dans les Terres fantômes, veut que vous tuiez 10 Filetoiles supérieures et 10 Ravageurs Griffes-fantômes."}},
[9282] = {"L'enclave des Pérégrins",nil, {"Le magistère Kaendris du sanctum du Soleil veut que vous parliez au forestier Vynna, à l'enclave des Pérégrins des Terres fantômes."}},
[9283] = {"Sauvez les rescapés !",nil, {"Servez-vous du Don des naaru sur l'un des Survivants draeneï éparpillés dans le val d'Ammen, puis retournez voir Zalduun au Point d'impact du val d'Ammen."}},
