[9292] = {"Cristal nécrotique fêlé",nil, {"Apportez le Cristal nécrotique fêlé au lieutenant Orrin à l'extérieur des portes de Hurlevent."}},
[9293] = {"Ce qui doit être fait...",nil, {"Rassemblez 10 Échantillons de fouette-racines et rapportez-les à la botaniste Taerix, au point d'impact du val d'Ammen."}},
[9294] = {"Guérir le lac",nil, {"Répandez le Produit neutralisant près du Cristal de puissance irradié. Ensuite, retournez voir la botaniste Taerix au point d'impact du val d'Ammen."}},
[9295] = {"Une lettre du front",nil, {"Apportez la Lettre déchirée au gardien des registres à la chapelle de l'Espoir de Lumière dans les Maleterres de l'est."}},
[9299] = {"Une note du front",nil, {"Apportez la Note détériorée au gardien des registres à la chapelle de l'Espoir de Lumière dans les Maleterres de l'est."}},
[9300] = {"Une page du front",nil, {"Apportez la Page abîmée au gardien des registres à la chapelle de l'Espoir de Lumière dans les Maleterres de l'est."}},
[9301] = {"Une enveloppe du front",nil, {"Apportez l'Enveloppe tachée de sang au gardien des registres à la chapelle de l'Espoir de Lumière dans les Maleterres de l'est."}},
[9302] = {"Une missive du front",nil, {"Apportez la Missive froissée au gardien des registres à la chapelle de l'Espoir de Lumière dans les Maleterres de l'est."}},
[9303] = {"Inoculation",nil, {"Utilisez le Cristal d'inoculation sur 6 Chouettides nidebois. Retournez ensuite auprès du redresseur de torts Aldar au Point d'impact du val d'Ammen."}},
[9304] = {"Un document du front",nil, {"Apportez le Document taché au gardien des registres à la chapelle de l'Espoir de Lumière dans les Maleterres de l'est."}},
[9305] = {"Pièces de rechange",nil, {"Récupérez 4 Pièces de rechange pour l'émetteur puis rapportez-les à la technicienne Zhanaa au Point d'impact du val d'Ammen."}},
[9309] = {"L'éclaireur disparu",nil, {"Trouvez Tolaan sur l'Ombrecrête du val d'Ammen et parlez-lui."}},
[9310] = {"Cristal nécrotique voilé",nil, {"Apportez le Cristal nécrotique voilé au lieutenant Rukag dans la cour de Fossoyeuse."}},
[9311] = {"L'espionne elfe de sang",nil, {"Tuez la Géomètre Candress, puis allez rendre compte au redresseur de torts Aldar, au Point d'impact du val d'Ammen."}},
[9312] = {"L'émetteur",nil, {"Parlez à la technicienne Zhanaa au point d'impact dans le val d'Ammen."}},
[9313] = {"En route pour le Guet d'azur",nil, {"Parlez au technicien Dyvuun au Guet d'azur, sur l'île de Brume-azur."}},
[9314] = {"Une demande du Guet d'azur",nil, {"Parlez à l'hôtesse Chellan au Guet d'azur sur l'île de Brume-azur."}},
[9315] = {"Anok'suten",nil, {"Tuez Anok'suten et retournez voir l'arcaniste Vandril, à Tranquillien dans les Terres fantômes."}},
[9317] = {"Pierre à aiguiser consacrée",nil, {"Si vous m’apportez huit runes nécrotiques des envahisseurs du Fléau, je vous fournirais une pierre à aiguiser consacrée. Elle devrait vous être d’une grande utilité pour vos combats contre les serviteurs du roi-liche."}},
[9318] = {"Huile de sorcier bénite",nil, {"En échange des runes nécrotiques des envahisseurs du Fléau, l’Aube d’argent vous donnera une fiole d’huile de sorcier bénite."}},
[9319] = {"Une lumière au sein des ombres",nil, {"Trouvez et touchez les feux de joie situés au pic Rochenoire, à Hache-tripes, dans la Scholomance et à Stratholme. Puis retournez voir le gardien des flammes de la fête."}},
[9320] = {"Potion de mana majeure",nil, nil},
[9321] = {"Potion de soins majeure",nil, nil},
[9322] = {"Feux sauvages en Kalimdor",nil, {"Visitez les campements du solstice d'été en Azshara, en Silithus, au cratère d'Un'Goro et à Berceau-de-l'Hiver, puis revenez voir le Gardien des flammes."}},
[9323] = {"Feux sauvages dans les Royaumes de l'est",nil, {"Rendez visite aux campements de la fête du Feu situés dans les Terres foudroyées, les Maleterres de l'est, les Hinterlands, et la gorge des Vents brûlants, puis allez parler au gardien des flammes de la fête."}},
[9324] = {"Voler la flamme d'Orgrimmar",nil, {"Rapportez la Flamme d'Orgrimmar au Maître des traditions."}},
[9325] = {"Voler la flamme de Thunder Bluff",nil, {"Rapportez la flamme des Pitons du Tonnerre au Maître des traditions."}},
[9326] = {"Voler la flamme de Fossoyeuse",nil, {"Rapportez la flamme de Fossoyeuse au Maître des traditions."}},
[9327] = {"Les Réprouvés",nil, {"Allez parler au Grand exécuteur Mavren à Tranquillien, dans les Terres fantômes."}},
[9328] = {"Un héros parmi les Sin'dorei",nil, {"Apportez la Tête de Dar’Khan à Lor’themar Theron, à Lune-d’Argent."}},
[9329] = {"Les Réprouvés",nil, {"Allez parler au Grand exécuteur Mavren à Tranquillien, dans les Terres Fantômes."}},
[9330] = {"Voler la flamme de Stormwind",nil, {"Rapportez la flamme de Hurlevent au Conteur de la fête."}},
[9331] = {"Voler la flamme d'Ironforge",nil, {"Rapportez la flamme de Forgefer au Conteur de la fête."}},
[9332] = {"Voler la flamme de Darnassus",nil, {"Rapportez la flamme de Darnassus au Conteur de la fête."}},
[9333] = {"Les gants de l'Aube d'argent",nil, {"Si vous m'apportez trente runes nécrotiques, je vous donnerai en échange une pièce d'armure de main au choix. Vous devriez les trouver très pratiques pour vous battre contre les morts-vivants du Fléau."}},
[9334] = {"Huile de sorcier bénite",nil, {"En échange des runes nécrotiques des envahisseurs du Fléau, l’Aube d’argent vous donnera une fiole d’huile de sorcier bénite."}},
[9335] = {"Pierre à aiguiser consacrée",nil, {"Si vous m’apportez huit runes nécrotiques des envahisseurs du Fléau, je vous fournirai une pierre à aiguiser consacrée. Elle devrait vous être d’une grande utilité pour vos combats contre les serviteurs du roi-liche."}},
[9336] = {"Potion de soins majeure",nil, nil},
[9337] = {"Potion de mana majeure",nil, {"En plus de nos autres fournitures, nous disposons de quelques potions de mana majeures qui pourraient vous être utiles. Je vous en donnerai une en échange de quinze runes nécrotiques."}},
[9338] = {"Allégeance au Cercle cénarien",nil, {"Même si vous n'avez pas besoin des récompenses matérielles que nous proposons, ces badges sont la marque de notre amitié et de notre confiance mutuelle.nnApportez-les-moi. Je ne manquerai pas de prendre note des services que vous nous rendrez s'ils allaient au-delà de l'appel du devoir."}},
[9339] = {"La récompense d'un voleur",nil, {"Rapportez la Flamme de Stormwind à (NOM)."}},
[9340] = {"La Grande fissure",nil, {"Le capitaine des forestiers Venn'ren du Guet de l'épervier veut que vous tuiez 8 Jeunes fauchepierre et 3 Alphas fauchepierre à la Grande fissure, à l'est du Guet de l'épervier."}},
[9341] = {"Tabard de l'Aube d'argent",nil, {"Vous avez grandement aidé notre cause, <nom>. Si vous le souhaitez, je peux vous procurer le tabard de l'Aube d'argent. Nous sommes fiers de vous compter parmi nos alliés."}},
[9342] = {"Les perceurs de croûte maraudeurs",nil, {"Le contremaître Razelcraz, des mines près de Thrallmar, veut que vous tuiez 15 Perceurs de croûte maraudeurs. Les vers ont tendance à attaquer brutalement à partir de leurs tunnels souterrains."}},
[9343] = {"Tabard de l'Aube d'argent",nil, {"Vous avez grandement aidé notre cause, <nom>. Si vous le souhaitez, je peux vous procurer le tabard de l'Aube d'argent. Nous sommes fiers de vous compter parmi nos alliés."}},
[9344] = {"Un départ précipité",nil, {"La prophétesse Regulkut veut que vous retrouviez son élève Grelag."}},
[9345] = {"Préparation du baume",nil, {"Cueillez 12 Atrics des flammes infernales pour un baume et retournez voir Grelag au Poste de Brise-échine."}},
[9346] = {"Quand les sangliers voleront",nil, {"Le commandant de la garde Krunk, de Thrallmar, veut 10 Carcasses dodues de sanglier infernal, prises sur les Sangliers infernaux affamés."}},
[9349] = {"Ramasser les œufs de ravageurs",nil, {"Récupérez 12 Œufs de ravageurs pour le compte de Legassi, de l'épave du zeppelin dans la péninsule des Flammes infernales."}},
[9351] = {"Les marcheurs du Vide sauvages",nil, {"Remettez 10 Essences de marcheur du Vide condensées à Laïus \"Brailleur\" Airbuse à l'épave du zeppelin."}},
[9352] = {"Les intrusions darnassiennes",nil, {"Rendez-vous au Sanctum occidental, au sud-ouest de la Place de l'Épervier, et tuez les intrus s'il y en a. Ensuite, allez faire votre rapport à la telluriste Velania."}},
[9355] = {"Un boulot pour un type intelligent",nil, {"Tuer 15 Perceurs de croûte maraudeurs, puis retourner voir le contremaître Biggums au bastion de l'Honneur."}},
[9356] = {"Les doigts dans le nez",nil, {"Apportez 12 Ailes de busard dodues à Legassi à l'épave du zeppelin."}},
[9357] = {"Se présenter à Aeldon Signesol",nil, {"Présentez-vous à Aeldon Signesol à la porte elfique intérieure, dans le bois des Chants éternels."}},
[9358] = {"Le forestier Sareyn",nil, {"Présentez-vous au forestier Sareyn, à l’extérieur du village de Brise-Clémente, dans le bois des Chants éternels."}},
[9359] = {"La Retraite des pérégrins",nil, {"Présentez-vous au lieutenant Coursaurore à la Retraite des Pérégrins, dans le bois des Chants éternels."}},
[9360] = {"L'invasion des Amani",nil, {"Remettre les Plans d'invasion amani au lieutenant Coursaurore à la Retraite des pérégrins."}},
[9361] = {"On se régale avec le sanglier infernal !",nil, {"Legassi de l'Épave du zeppelin veut que vous récupériez 8 morceaux de Viande de sanglier infernal purifiée. Pour purifier la viande, utilisez la Mixture de purification sur la Viande de sanglier infernal contaminée que vous prendrez sur des Sangliers infernaux."}},
[9362] = {"Le seigneur de guerre Krellian",nil, {"Récupérez la Coquille prismatique pour l'archimage Xylem. L'archimage réside dans une tour au sommet des falaises d'Azshara."}},
[9363] = {"Avertir le village de Brise-clémente",nil, {"Rendez-vous au village de Brise-clémente dans le bois des Chants éternels et parlez au forestier Degolien."}},
[9364] = {"Magie morcelée",nil, {"Métamorphosez les Fouette-bile d'Azshara, puis tuez les clones qui apparaissent quelques instants plus tard. Quand vous aurez tué 50 clones de métamorphose, retournez voir l'archimage Xylem en Azshara."}},
[9365] = {"La récompense d'un voleur",nil, {"Rapportez la Flamme de Stormwind à (NOM)."}},
[9366] = {"Il nous faut du gangresang",nil, {"Ryathen le Sombre, du Guet de l'épervier dans la péninsule des Flammes infernales, veut que vous lui procuriez 6 Échantillons de grangresang prélevés sur les Fiels de la terreur qui vivent aux bassins d'Aggonar."}},
[9367] = {"La fête du Feu",nil, {"Touchez les feux de joie à l'intérieur de Hurlevent, Forgefer et Darnassus, puis parlez à n'importe quel Maître des traditions de la fête dans une des capitales."}},
[9368] = {"La fête du Feu",nil, {"Touchez les feux de joie à l'intérieur d'Orgrimmar, des Pitons du Tonnerre et de Fossoyeuse, puis parlez à n'importe quel Maître des traditions de la fête dans une des capitales."}},
[9369] = {"Recharger les cristaux de soin",nil, {"Extraire 8 Fioles de sang de phalène sur les Phalènes du val, puis les rapporter à Proenitus, au Point d'impact du val d'Ammen."}},
[9370] = {"Il faut arrêter la purification",nil, {"Placez la Gemme de signalisation près de l'autel d'Aggonar, aux bassins d'Aggonar, et triomphez de tous les anachorètes draeneï qui répondront à vos invocations. Retournez voir Ryathen le Sombre au Guet de l'épervier avec la Gemme de signalisation quand vous en aurez terminé."}},
[9371] = {"La botaniste Taerix",nil, {"Parlez à la botaniste Taerix au Point d'impact dans le val d'Ammen."}},
[9372] = {"Contamination démoniaque",nil, {"Thiah Crin-rouge, du Poste cénarien dans la péninsule des Flammes infernales, vous a demandé de récupérer 6 Échantillons de sang de sanglier infernal sur des Sangliers infernaux massifs."}},
[9373] = {"La missive manquante",nil, {"Remettre l'Etui en cuir rongé à Thiah Crin-rouge, qui campe près des ruines de Sha'naar, dans l'ouest de la péninsule des Flammes infernales."}},
[9374] = {"Le journal d'Arelion",nil, {"La magistrice Carinda, au Guet de l'épervier de la péninsule des Flammes infernales, veut que vous vous rendiez aux bassins d'Aggonar pour y récupérer le Journal d'Arelion."}},
[9375] = {"La route du Guet de l'épervier",nil, {"Protégez le Pèlerin elfe de sang blessé sur la route du Guet de l'épervier, puis parlez à Taleris Mirelaube."}},
[9376] = {"Les souffrances du pèlerin",nil, {"Le Pèlerin elfe de sang blessé a été attaqué par des Ravageurs Plumecroc alors qu'il se trouvait dans le ravin de Plumepoussière. Trouvez son campement et fouillez-le à la recherche de son sac manquant."}},
[9378] = {"DND FLAG Naxxramas, la citadelle de l'effroi",nil, {"L'archimage Angela Dosantos à la chapelle de l'Espoir de Lumière dans les Maleterres de l'Est vous accordera gratuitement l'Occultation arcanique. Vous devez être <Exalté/Exaltée> auprès de l'Aube d'argent."}},
[9380] = {"La multiplication des molosses",nil, {"Récupérer 6 Cadavres de molosses dimensionnels intacts et les remettre au prospecteur Murantus au bastion de l'Honneur."}},
[9381] = {"Les flèches infaillibles",nil, {"Récupérez 8 Plumes de queue de gratte-les-os sur des vautours Gratte-les-os, et remettez-les au fauconnier Drenna Ruissevent du Guet de l'épervier."}},
[9382] = {"Le sort des sabots-fourchus",nil, {"Tuer 15 Sabots-fourchus poil-hirsute, puis retourner voir Amaan le sage au temple de Telhamat."}},
[9383] = {"Un plan ambitieux",nil, {"Utilisez le Cristal sanctifié sur un marcheur du Vide incontrôlé blessé, puis retournez voir Elsaana au temple de Telhamat."}},
[9385] = {"Les ravageurs déchaînés",nil, {"Tuez 10 Ravageurs Plumecroc et retournez voir Gremni Barbe-longue dans la péninsule des Flammes infernales."}},
[9386] = {"Une Lumière au sein des ombres",nil, {"Trouvez et touchez les feux de joie situés au pic Rochenoire, à Hache-tripes, dans la Scholomance et à Stratholme, puis parlez au gardien des flammes de la fête."}},
[9387] = {"La source de la corruption",nil, {"Allez aux ruines de Sha'naar, dans la péninsule des Flammes infernales, et obtenez 5 Essences démoniaques sur les Sous-chefs illidari. Retournez voir l'apothicaire Azethen au Guet de l'épervier une fois ce travail terminé."}},
[9388] = {"Flammes vacillantes en Kalimdor",nil, {"Rendez-vous aux feux de joie d'Orneval, des Tarides, de Sombrivage et des Serres-Rocheuses, puis parlez au Gardien des flammes de la fête."}},
[9389] = {"Flammes vacillantes dans les Royaumes de l'est",nil, {"Rendez-vous aux feux de joie des contreforts de Hautebrande, de la forêt des Pins argentés, de la Marche de l'ouest et des Paluns, puis parlez au Gardien des flammes de la fête."}},
[9390] = {"À la recherche de Sedaï",nil, {"L'anachorète Obadeï veut que vous partiez à la recherche de son frère Sedaï dans les alentours, vers l'est, dans la péninsule des Flammes infernales."}},
[9391] = {"Jalonner le chemin",nil, {"Le capitaine des forestiers Venn’ren, du Guet de l’épervier, veut que vous alliez dans la Grande fissure de la péninsule des Flammes infernales et que vous allumiez la Balise du sud, la Balise de l’ouest, et la Balise centrale. Retournez le voir avec la Torche allumée quand vous aurez accompli cette tâche."}},
[9392] = {"La formation de voleur",nil, {"Parlez à l'éclaireur Kariel, le maître des voleurs, à l'intérieur de la Flèche solaire de l'île de Haut-soleil."}},
[9393] = {"La formation de chasseur",nil, {"Parlez au forestier Sallina, le maître des chasseurs, à l'intérieur de la Flèche solaire de l'île de Haut-soleil."}},
[9394] = {"Où est Wyllithen ?",nil, {"Retrouver le paysagiste Wyllithen sur les Terres de Ternesoir dans le bois des Chants éternels."}},
[9395] = {"Le havre de Saltheril",nil, {"Parler au seigneur Saltheril au havre de Saltheril dans le bois des Chants éternels."}},
[9396] = {"La magie des arakkoa",nil, {"L'arcaniste Calesthris Aubétoile, du Guet de l'épervier de la péninsule des Flammes infernales, veut que vous lui rameniez 4 Parchemins haal'eshi."}},
[9397] = {"Fermez, fermez la cage aux oiseaux",nil, {"Rendez-vous à la tanière des Haal'esh dans la péninsule des Flammes infernales et fouillez les nids de kaliris à la recherche d'une Jeune femelle kaliri. Utilisez la Cage à oiseaux vide pour la capturer et apportez-la au fauconnier Drenna Ruissevent au Guet de l'épervier."}},
[9398] = {"Prédateurs mortels",nil, {"L'éclaireur Vanura du temple de Telhamat dans la péninsule des Flammes infernales veut que vous tuiez 8 Jeunes fauchepierre et 4 Alphas fauchepierre."}},
[9399] = {"De cruels sous-chefs",nil, {"Tuez 4 Sous-chefs illidari aux ruines de Sha'naar, puis retournez voir Ikan au temple de Telhamat, dans la péninsule des Flammes infernales."}},
[9400] = {"L'assassin",nil, {"Nazgrel veut que vous suiviez la route qui passe au nord de la citadelle des Flammes infernales, à la recherche d'un assassin nommé Krun Brise-échine."}},
[9401] = {"Une arme étrange",nil, {"Remettre la Hache lourde en pierre à Nazgrel, à Thrallmar dans la péninsule des Flammes infernales."}},
[9402] = {"Va chercher !",nil, {"Nagez jusqu'au fond de l'étang des Murmures-sereins et récupérez la Fiole azur pour l'instructeur Antheol."}},
[9403] = {"L'eau pure",nil, {"[Instructor Antheol wants you to fill the Azure Phial at Elrendar Falls.]"}},
[9404] = {"Fraîchement coupée",nil, {"Tuez les Gardes verts des Chants éternels jusqu'à ce que vous trouviez une Branche vivante pour l'instructeur Antheol."}},
[9405] = {"Le mandat du chef de guerre",nil, {"Apporter la Hache lourde en pierre à la prophétesse Regulkut, à Thrallmar dans la péninsule des Flammes infernales."}},
[9406] = {"Les Mag'har",nil, {"Remettez la lettre des Mag’har à Nazgrel, à Thrallmar dans la péninsule des Flammes infernales."}},
[9407] = {"À travers la Porte des ténèbres",nil, {"Traversez la Porte des ténèbres et allez trouver le général de division Orion. Il attend votre arrivée de l'autre côté, en Outreterre."}},
[9408] = {"Héros oubliés",nil, {"Parler au commandant Danath Trollemort au bastion de l'Honneur."}},
[9409] = {"Livraison urgente",nil, {"Livrer le Paquet de fioles à Zalduun au Point d'impact du val d'Ammen."}},
[9410] = {"Un guide spirituel",nil, {"Utilisez le Totem du loup à l'endroit où vous avez trouvé le corps de Krun Brise-échine et suivez l'Esprit ancestral du loup."}},
[9415] = {"Se présenter au maréchal Murbleu",nil, {"Parlez au maréchal Murbleu au campement de l'Alliance, près de la Ruche'Zora."}},
[9416] = {"Se présenter au général Kirika",nil, {"Parlez au général Kirika au campement de la Horde, près de la Ruche'Regal."}},
[9417] = {"La menace des arakkoa",nil, {"Gremni Barbe-longue veut que vous tuiez 4 Marche-vent haal'eshi et 6 Gardes-serres haal'eshi."}},
[9418] = {"L'orbe d'Avruu",nil, {"Apportez l'Orbe d'Avruu à l'Autel haal'eshi, au sud-est de la tanière des Haal'esh, sur la péninsule des Flammes infernales. Remettez-le à Aeranas lorsqu'il sera libre."}},
[9419] = {"Passer le désert au peigne fin",nil, {"Trouver de la Silithyste et la livrer à l' Extracteur de silithyste, puis parler au maréchal Murbleu au campement de l'Alliance près de la Ruche'Zora."}},
[9420] = {"Les plus belles plumes",nil, {"Mirren Barbe-longue, de la péninsule des Flammes infernales, veut que vous lui rapportiez 8 Plumes de kaliri, prises sur les kaliris de la Tanière des Haal'esh."}},
[9421] = {"La formation de chaman",nil, nil},
[9422] = {"Passer le désert au peigne fin",nil, {"Trouver de la Silithyste et la livrer à un Extracteur de silithyste, puis parler au général Kirika au campement de la Horde près de Ruche'Regal."}},
[9423] = {"Retourner voir Obadeï",nil, {"Retournez voir l'anachorète Obadeï au temple de Telhamat dans la péninsule des Flammes infernales, et apprenez-lui la mort de Sedaï."}},
[9424] = {"La vengeance de Makuru",nil, {"Makuru, au temple de Telhamat dans la péninsule des Flammes infernales, veut que vous lui rameniez 10 Perles ancestrales Mag'har."}},
[9425] = {"Présentez-vous au Moulin-de-Tarren",nil, {"Rendez-vous à Moulin-de-Tarren, dans les contreforts de Hautebrande, et parlez à la conseillère Brunaurore."}},
[9426] = {"Les bassins d'Aggonar",nil, {"Amaan le Sage veut que vous vous rendiez aux bassins d'Aggonar pour y tuer 6 Fiels de la terreur et 6 Pourritures caustiques."}},
[9427] = {"Purifier les eaux",nil, {"Amaan le Sage veut que vous vous rendiez aux Bassins d’Aggonar et que vous utilisiez la Fiole de purification sur le cadavre d’Aggonar. Retournez le voir lorsque l’eau aura purifié l’essence d’Aggonar."}},
[9428] = {"Présentez-vous au poste de Bois-brisé",nil, {"Rendez-vous au poste de Bois-brisé en Orneval et parlez au conseiller Ligesol."}},
[9429] = {"En route pour Sombre-comté",nil, {"Rendez-vous à Sombre-comté dans le bois de la Pénombre et parlez à l'anachorète Delan."}},
[9430] = {"Les reliques de Sha'naar",nil, {"Amaan le Sage, au Temple de Telhamat dans la Péninsule des Flammes infernales, veut que vous vous rendiez aux ruines de Sha'naar pour y récupérer 10 Reliques de Sha'naar."}},
[9431] = {"Une nouvelle approche",nil, {"Récupérez 2 poignées de Terre purificatrice sur les Eboulides fulminants, et remettez-les à Wizlo Braquecoquard au poste de Blanc-relais."}},
[9432] = {"En route pour Astranaar",nil, {"Rendez-vous à Astranaar en Orneval, et parlez au redresseur de torts Palanaar."}},
[9433] = {"A Dip in the Moonwell",nil, {"Use the Robotron Control while standing near the Concealed Command Console hidden in a small cluster of bushes on the rim of the needle closest to Thalanaar to take command of a Robotron 3000. Pilot the robot to the western edge of Thousand Needles where the Alliance outpost of Thalanaar is located. Use the robot to collect a sample of the moonwell's water and return it to Wizlo Bearingshiner at Whitereach Post."}},
[9434] = {"Tester le tonique",nil, {"Livrez la Caisse de composants à la magistrice Elosai au poste de Librevent."}},
[9435] = {"Les cristaux manquants",nil, {"Reprenez le Chargement de cristaux rares aux Réprouvés et rapportez-le à Huraan à Austrivage."}},
[9436] = {"Étudier les Scalps-rouges",nil, {"Nemeth Œil-de-faucon au campement Grom’gol veut que vous lui rameniez un Totem scalp-rouge pris sur un Chaman scalp-rouge."}},
[9437] = {"Le crépuscule du Coureur de l'aube",nil, {"Balandar Brillétoile, à Mur-de-Fougères, veut que vous récupériez la Cargaison du Coureur de l’aube et que vous sauviez son second, Ithania, des troupes de Theramore à la Tour de la halte nord."}},
[9438] = {"Messenger to Thrall",nil, {"Travel to Orgrimmar and give Thrall the Letter from Nazgrel."}},
[9439] = {"Des bagages non réclamés",nil, {"Récupérez le Sac du conseiller et la Rapière du conseiller qui se trouvent dans la forteresse d’Angor, et rendez-les au conseiller Sarophas, à Kargath."}},
[9440] = {"De petites bouchées",nil, {"Donnez des Bouchées gangrenées aux animaux capturés par les Perdus. Ramenez les restes à Cersei Chantebrune, à Pierrêche, dans le marais des Chagrins."}},
[9441] = {"Émissaire auprès des Mag'har",nil, {"Thrall veut que vous retourniez voir Gorkan Poing-de-sang au poste Mag'har de la péninsule des Flammes infernales, et que vous lui offriez vos services."}},
[9442] = {"Une maladie débilitante",nil, {"L’imploratrice de la terre Ryga, au poste de Mag'har de la péninsule des Flammes infernales, veut que vous lui rameniez 1 Champignon tête-sèche cueilli dans une caverne de la Grande fissure."}},
[9443] = {"La pseudo-marque du Porteur de Lumière",nil, {"Obtenez la Marque du Porteur de Lumière et ramenez-la à Mehlar Aubelame à la Barricade dans les Maleterres de l'ouest."}},
[9444] = {"Profaner la tombe d'Uther",nil, {"Profaner la Tombe d’Uther puis retourner voir Mehlar Aubelame à la Barricade des Maleterres de l’Ouest."}},
[9446] = {"La tombe d'Uther",nil, {"Escorter l’Anachorète Truuen à la tombe d’Uther, dans les Maleterres de l’ouest. Ensuite, parler avec la grande prêtresse MacDonnell au camp du Noroît dans les Maleterres de l’Ouest."}},
[9447] = {"Administrer le baume de guérison",nil, {"L’Imploratrice de la terre Ryga veut que vous administriez le Baume de guérison à 10 Grunts Mag'har affaiblis proches du poste Mag'har, dans la péninsule des Flammes infernales."}},
[9448] = {"Pitié pour les maudits",nil, {"Tuez 3 Perdus maudits avant de retourner voir l'anachorète Avuun au Havre boueux, dans le marais des Chagrins."}},
[9449] = {"L'appel de la terre",nil, {"Parlez à l’Esprit du val au Bosquet sacré du val d’Ammen."}},
[9450] = {"L'appel de la terre",nil, {"Tuez 4 Esprits de terre sans repos, puis retournez voir l’Esprit du val au Bosquet sacré du val d’Ammen."}},
[9451] = {"L'appel de la terre",nil, {"Donnez le Cristal de terre à Firmanvaar, au Point d’impact du val d’Ammen."}},
[9452] = {"Le lutjan rouge, c'est bon !",nil, {"Utilisez le Filet de pêche draeneï pour attraper 10 Lutjans rouges. Ensuite, retournez voir Diktynna sur l'île de Brume-azur, rendez-lui le filet et donnez-lui les poissons."}},
[9453] = {"Trouvez Actéon !",nil, {"Livrer la Caisse de lutjans rouges à Actéon au Guet d'azur."}},
[9454] = {"La grande chasse aux frôle-lune",nil, {"Apportez 6 Filets de cerfs frôle-lune à Actéon du Guet d'azur."}},
[9455] = {"Une étrange trouvaille",nil, {"Remettez le Cristal luisant faiblement à l'exarque Menelaous, au Guet d'azur."}},
[9456] = {"Nettoyage des traquenuits, île n°2",nil, {"L'exarque Meleanous du Guet d'azur vous a demandé de tuer 8 Traquenuits chétifs infectés."}},
[9457] = {"Un protecteur inhabituel",nil, {"Servez-vous du Cadeau de Naïas près de l’Autel de Naïas pour invoquer l’élémentaire d’eau Naïas. Lorsque vous l’aurez vaincu, retournez voir Nemeth Œil-de-faucon au campement Grom’gol avec le Cœur de Naïas."}},
[9460] = {"Associer ses forces",nil, {"Keltus Sombrefeuille veut que vous fassiez les poches du Chef des Sentinelles, que vous y récupériez un Mouchoir en dentelle et que vous le lui remettiez."}},
[9461] = {"L'appel du feu",nil, {"Allez voir le prophète Velen, à la Voûte des lumières, à bord de l'Exodar."}},
[9462] = {"L'appel du feu",nil, {"Allez voir Tuluun au Guet d'Azur, sur l’île de Brume-azur."}},
[9463] = {"Usage médical",nil, {"L'anachorète Fateema au Guet d'azur veut que vous lui apportiez 8 Vrilles de piègeracine."}},
[9464] = {"L'appel du feu",nil, {"Parlez à Hargne à la clairière des braises, sur l'île de Brume-d'azur."}},
[9465] = {"L'appel du feu",nil, {"Récupérez la Torche rituelle et rapportez-la à Hargne à la clairière des braises sur l'île de Brume-d'azur."}},
[9466] = {"On recherche : Griffe-noire la Sauvage",nil, {"Apportez les Griffes de Griffe-noire au capitaine des forestiers Venn'ren au Guet de l'épervier dans la péninsule des Flammes infernales."}},
[9467] = {"L'appel du feu",nil, {"Récupérez les Cendres d'Arrogance et rapportez-les avec la Torche rituelle à Hargne, à la clairière des braises sur l'île de Brume-d'azur. N'oubliez pas que vous pouvez utiliser l'Orbe de retour pour vous téléporter auprès de Hargne une fois que vous aurez les cendres."}},
[9468] = {"L'appel du feu",nil, {"Apportez les Cendres d'Arrogance à Tuluun, au Guet d'azur, sur l'île de Brume-azur."}},
[9469] = {"L'appui de Barbe-de-plumes",nil, {"Rendez-vous au Taudis de Barbe-de-plumes et allez voir le nain s'il est chez lui. S'il n'est pas là, cherchez des indices pour savoir où vous pourriez le trouver."}},
[9470] = {"Un geste de bonne volonté",nil, {"L'ambassadeur Rualeth au Nid-de-l'Aigle veut que vous tuiez le Féticheur Mai'jin et son araignée familière Tcha’kaz."}},
[9471] = {"Faire sa proie des prédateurs",nil, {"L'ambassadeur Rualeth au Nid-de-l'Aigle veut que vous tuiez 10 Crins-d'argent galeux et 5 Loups Crins-d'argent."}},
[9472] = {"La maîtresse d'Arelion",nil, {"La magistrice Carinda, au Guet de l'épervier, veut que vous attiriez Viera Murmesoleil hors de la ville, puis que vous utilisiez le Parchemin de vindicte de Carinda sur elle."}},
[9473] = {"Une tentative alternative",nil, {"Daedal au Guet d'azur veut que vous lui apportiez 5 Bulbes de gueules-de-loup azurées."}},
[9474] = {"La marque du Porteur de Lumière",nil, {"Récupérez la Marque du Porteur de Lumière et rapportez-la à l'anachorète Truuen au camp du Noroît, dans les Maleterres de l'ouest."}},
[9475] = {"La récupération des œufs",nil, {"[Gather 5 Gryphon Eggs and return them to Ambassador Rualeth at Aerie Peak.]"}},
[9476] = {"À la poursuite de Barbe-de-plumes",nil, {"Rendez-vous à Shadra'Alor et cherchez-y les traces de Barbe-de-plumes."}},
[9483] = {"Les plaisirs de la vie",nil, nil},
[9484] = {"Dompter la bête",nil, {"Utilisez le Bâtonnet de domptage pour apprivoiser un Faucon-dragon affolé, puis retournez voir le lieutenant Coursaurore."}},
[9485] = {"Dompter la bête",nil, {"Utilisez le Bâtonnet de domptage pour apprivoiser une Brumoptère, puis retournez voir le lieutenant Coursaurore."}},
[9486] = {"Dompter la bête",nil, {"Utilisez le Bâtonnet de domptage pour apprivoiser un Ancien Preste-patte, puis retournez voir le lieutenant Coursaurore."}},
[9487] = {"Les saccageurs des arcanes",nil, {"L'arcaniste Vandril veut que vous récupériez 10 Fragments arcaniques pour son compte."}},
[9488] = {"Une robe simple",nil, {"L'arcaniste Vandril a besoin de 15 Étoffes de lin et d’une Glande à soie de filetoile."}},
[9489] = {"Purifier la Malebrèche",nil, {"Lancez Prière de Robustesse sur 6 Forestiers des Chants éternels dans la Malebrèche."}},
[9490] = {"La matriarche des écorche-roc",nil, {"Prenez les Griffes de Griffe-noire la Sauvage dans la Grande fissure et remettez-les à Ikan, au temple de Telhamat la Péninsule des flammes infernales."}},
[9491] = {"Cupidité",nil, {"Erelan, à Tranquillien, veut que vous retrouviez la Bague ébréchée en or dans les catacombes des Amani."}},
[9492] = {"L'heure du tournant",nil, {"Apportez le Poing du chef de guerre Kargath au commandant Danath Trollemort, au Bastion de l'Honneur."}},
[9493] = {"L'orgueil de la Gangr'horde",nil, {"Le commandant Romus du Bastion de l'Honneur veut que vous alliez tuer 8 Légionnaires de la Main brisée, 4 Centurions de la Main brisée et 4 Champions de la Main brisée."}},
[9494] = {"Les gangrebraises",nil, {"Le magus Zabraxis du Bastion de l'Honneur veut que vous lui rapportiez une Gangrebraise."}},
[9495] = {"La volonté du Chef de guerre",nil, {"Nazgrel à Thrallmar veut que vous lui rameniez le Poing du chef de guerre Kargath."}},
[9496] = {"L'orgueil de la Gangr'horde",nil, {"Le chasseur des ombres Ty'jin, de Thrallmar, veut que vous tuiez 8 Légionnaires de la Main brisée, 4 Centurions de la Main brisée et 4 Champions de la Main brisée."}},
[9498] = {"Le Guet de l'épervier",nil, {"Présentez-vous au capitaine des forestiers Venn'ren au Guet de l'épervier de la péninsule des Flammes infernales."}},
[9499] = {"Le Guet de l'épervier",nil, {"Rendez-vous au Guet de l'épervier au sud-ouest de la péninsule des Flammes infernales et présentez-vous au capitaine des forestiers Venn'ren."}},
[9500] = {"L'appel de l'eau",nil, {"Parlez au long-voyant Nobundo, dans le Hall de Cristal, à l’intérieur de l’Exodar sur l’île de Brume-azur."}},
[9501] = {"L'appel de l'eau",nil, {"Parlez à Aqueuse, à l’intérieur du Récif caché, près de l’île de Brume-sang."}},
[9502] = {"L'appel de l'eau",nil, {"Parlez au long-voyant Nobundo, dans le Hall de Cristal, à l’intérieur de l’Exodar sur l’île de Brume-azur."}},
[9503] = {"L'appel de l'eau",nil, {"Récupérer 6 Essences infectes et les remettre à Aqueuse, au Récif caché, près de l’île de Brume-sang."}},
[9504] = {"L'appel de l'eau",nil, {"Remplir la Gourde en peau vide et retourner voir Aqueuse, au Récif caché, près de l’île de Brume-sang."}},
[9505] = {"La prophétie de Velen",nil, {"Daedal veut que vous alliez au sud, à la recherche des gens mentionnés par la créature blessée."}},
[9506] = {"Il faut bien commencer quelque part",nil, {"L'amiral Odesyus, au Point d'accostage d'Odesyus, veut que vous récupériez le Compas de marine et la Carte nautique volés par la KapitalRisk."}},
[9508] = {"L'appel de l'eau",nil, {"Apportez la Tête de Tel'athion à Aqueuse, du récif caché près de l'île de Brume-sang."}},
[9509] = {"L'appel de l'eau",nil, {"Remettez la Flasque de l'eau la plus pure au long-voyant Nobundo à l’intérieur de l’Exodar sur l’île de Brume-azur."}},
[9510] = {"Les peaux de sabots-fourchus poil-hirsute",nil, {"L’approvisionneur Braknar au poste de Mag'har dans la péninsule des Flammes infernales veut que vous lui rameniez 5 Peaux de sabots-fourchus poil-hirsute."}},
[9511] = {"Les plans de bataille de Kargath",nil, nil},
[9512] = {"Jumbo gumbo du cuistot",nil, {"« Cuistot » McFadesauce, au Point d'accostage d'Odesyus, veut que vous lui rapportiez 6 Chairs de clampant grouillant."}},
[9513] = {"Reconquérir les ruines",nil, {"La Prêtresse Kyleen Il'dinare, au Point d'accostage d'Odesyus sur l'île de Brume-azur, veut que vous tuiez 5 Myrmidons Irécaille, 5 Nagas Irécaille et 5 Sirènes Irécaille."}},
[9514] = {"Tablette couverte de runes",nil, {"Apportez la Tablette couverte de runes à la Prêtresse Kyleen Il'dinare au Point d'accostage d'Odesyus, sur l'île de Brume-azur."}},
[9515] = {"Le seigneur de guerre Sriss'tiz",nil, {"La Prêtresse Kyleen Il'dinare, au Point d'accostage d'Odesyus sur l'île de Brume-azur, veut que vous tuiez le chef de guerre Sriss'tiz."}},
[9516] = {"Détruire la Légion",nil, {"Tuez 15 démons de n’importe quel type sur la Colline Gangrefeu, dans le Canyon de la Malechute et sur la Crête de la Malechute."}},
[9517] = {"Un gaspillage honteux",nil, {"Rassemblez 5 fagots de Bois gangrené de Satyrnaar."}},
[9518] = {"Les agents de la destruction",nil, {"Tuez le Surveillant Gorthak et 12 autres membres de la Horde du Camp de bûcherons Chanteguerre."}},
[9519] = {"Le calice perdu",nil, {"Récupérez le Calice d’Élune."}},
[9520] = {"Les plans diaboliques",nil, {"Apportez les Plans diaboliques au Redresseur de torts Vedaar au Camp de bûcherons Chanteguerre, en Orneval."}},
[9521] = {"Un rapport du front nord",nil, {"Apportez le Rapport de la Sentinelle Melyria à la Sentinelle Chanteloin au Bosquet d’Aile-argent, en Orneval."}},
[9522] = {"Plus jamais !",nil, {"Récupérez l’Épée de flammes de Gorgannon et la Gangrelance du Chercheur."}},
[9523] = {"Fragile et précieux - à manipuler avec précaution",nil, {"L'archéologue Adamant Cœur-de-fer, au point d'accostage d'Odesyus sur l'île de Brume-azur, veut que vous lui rameniez 8 Reliques anciennes."}},
[9524] = {"Prisonnière de la citadelle",nil, {"Sauver le capitaine Alina, à l’intérieur de la citadelle des Flammes infernales, avant son exécution.nnCette quête doit être accomplie en mode de difficulté du donjon Héroïque."}},
[9525] = {"Imprisoned in the Citadel",nil, {"Rescue Captain Boneshatter inside Hellfire Citadel before he is executed.nnThis quest must be completed in Heroic dungeon difficulty."}},
[9526] = {"Reprendre la colline Gangrefeu",nil, {"Planter 8 Boutures d'arbre dans les Monticules de terre fertile, puis retourner voir Noueux au Chant des forêts, en Orneval."}},
[9527] = {"Tout ce qui reste",nil, {"Cowlen, de l'île de Brume-azur, veut que vous retrouviez les Restes de la famille de Cowlen."}},
[9528] = {"Un appel à l'aide",nil, {"Escorter Magwin à travers l'île, jusqu'à chez elle."}},
[9529] = {"La Pierre",nil, {"Trouvez une Pierre de Vide dans la Malebrèche."}},
[9530] = {"Un plan bourgeonne",nil, {"L'amiral Odesyus, au Point d'accostage d'Odesyus sur l'île de Brume-azur, veut que vous lui rameniez un Arbre creux et 5 Piles de feuilles."}},
[9531] = {"L'art du camouflage ne se perd pas",nil, {"L'amiral Odesyus, au Point d'accostage d'Odesyus de l'île de Brume-azur, veut que vous observiez la rencontre entre Geezle et le traître.nnRetournez le voir lorsque vous connaîtrez l'identité du traître."}},
[9532] = {"Trouver Keltus Sombrefeuille",nil, {"Trouvez Keltus Sombrefeuille."}},
[9533] = {"La main tendue",nil, {"Rendez-vous au Chant des forêts, et parlez à l'architecte Nemos."}},
[9534] = {"Destroy the Legion",nil, {"Slay 15 of any demon type at Felfire Hill, Demon Fall Canyon, or demon Fall Ridge and then return to Valusha at Splintertree Post in Ashenvale."}},
[9535] = {"Les plans diaboliques",nil, {"Apporter les Plans diaboliques à Valusha au poste de Bois-Brisé en Orneval."}},
[9536] = {"Plus jamais !",nil, {"Tuer Gorgannon et Diathorus le Chercheur, puis retourner voir Valusha au poste de Bois-brisé en Orneval."}},
[9537] = {"Le gnominé est...",nil, {"L'amiral Odesyus, du point d'accostage d'Odesyus, veut que vous récupériez la Communication du traître sur l'ingénieur Finemouture, dit « l'Étincelle »."}},
[9538] = {"Apprendre la langue",nil, {"Lisez le Manuel de langue furbolg calmepin qui vous a été remis par Aurren, puis examinez le Totem d'Akida au Guet d'azur."}},
[9539] = {"Le totem de Coo",nil, {"Grâce aux indices qui vous ont été fournis, trouvez le totem de Coo."}},
[9540] = {"Le totem de Tikti",nil, {"Grâce aux indices qui vous ont été fournis, trouvez le totem de Tikti."}},
[9541] = {"Le totem de Yor",nil, {"Grâce aux indices qui vous ont été fournis, trouvez le totem de Yor."}},
[9542] = {"Le totem de Vark",nil, {"Grâce aux indices qui vous ont été fournis, trouvez le totem de Vark."}},
[9543] = {"Expiation",nil, {"Parler à Amaan le Sage, au temple de Telhamat dans la péninsule des Flammes infernales."}},
[9544] = {"La prophétie d'Akida",nil, {"Libérez 8 Captifs Calmepins des cages du village des Bras-hirsutes. Retournez voir Arugoo des Calmepins au Guet d'azur quand vous aurez accompli cette tâche."}},
[9545] = {"La relique du prophète",nil, {"Amaan le Sage, du Temple de Telhamat dans la Péninsule des Flammes infernales, veut que vous retourniez auprès du Cadavre de Sedaï, au nord-est du Temple de Telhamat, et que vous utilisiez la Relique du prophète à cet endroit. Revenez le voir quand ce sera fait."}},
[9547] = {"L'appel de l'air",nil, {"Parlez au long-voyant Nobundo, dans le Hall de Cristal, à l’intérieur de l’Exodar sur l’île de Brume-azur."}},
[9548] = {"L'équipement chapardé",nil, {"Rapportez l'Equipement de Clopper à Clopper Crackboum, qui se cache dans une carapace de tortue échouée."}},
[9549] = {"Les artéfacts des Vase noire",nil, {"Apportez 3 Idoles murlocs grossières et 6 Couteaux murlocs grossiers à Clopper Crackboum, qui se cache dans une carapace de tortue échouée."}},
[9550] = {"Une carte pour où ?",nil, {"Trouvez le pavillon décrit par la carte, et découvrez ce que celui qui l’a dessinée essayait de représenter."}},
[9551] = {"L'appel de l'air",nil, {"Parlez au long-voyant Nobundo, dans le Hall de Cristal, à l’intérieur de l’Exodar sur l’île de Brume-azur."}},
[9552] = {"L'appel de l'air",nil, {"Parlez à Velaada au pic Vent-sauvage, sur l’île de Brume-azur."}},
[9553] = {"L'appel de l'air",nil, {"Parlez à Susurrus au pic Vent-sauvage, sur l’île de Brume-azur."}},
[9554] = {"L'appel de l'air",nil, {"[Return to Farseer Nobundo at the Exodar on Azuremyst Isle with the Whorl of Air.]"}},
[9555] = {"L'appel du feu",nil, {"Parlez au long-voyant Nobundo dans le Hall de Cristal, à bord de l'Exodar."}},
[9556] = {"Au vainqueur...",nil, {"Récupérez une bouteille d'Eau sacrée de Stratholme à l'intérieur de Stratholme, et remettez-la au Mage de Lordaeron, au quartier des mages de Hurlevent ou de Fossoyeuse, lorsque votre camp aura vaincu."}},
[9557] = {"Déchiffrer le livre",nil, {"Apportez l'Ancien journal de bord à l'Anachorète Paetheus au Guet du sang."}},
[9558] = {"Les Barbe-longue",nil, {"Cherchez Gremni Barbe-longue au nord-ouest de la tanière des Haal’esh, loin à l’ouest du bastion de l’Honneur de la péninsule des Flammes infernales."}},
[9559] = {"Le repaire des Calmepins",nil, {"Parlez au Grand chef Calmepin au repaire des Calmepins."}},
[9560] = {"Les bêtes de l'Apocalypse !",nil, {"Moordo, du repaire des Calmepins de l'île de Brume-azur, veut que vous lui rapportiez 8 Peaux de ravageurs."}},
[9561] = {"Les mots de Nolkai",nil, {"Trouvez l’endroit décrit dans le journal de Nolkai et découvrez les biens qu’il a enterrés il y a longtemps."}},
[9562] = {"Pourquoi les murlocs ?",nil, {"Gurf, du repaire des Calmepins sur l'île de Brume-azur, veut que vous retrouviez 5 sacs de Céréales calmepins."}},
[9563] = {"Gagner la confiance de Mirren",nil, {"Mirren Barbe-longue veut que vous lui procuriez 1 Bière amère de Rempart-du-Néant. Pour achever cette quête, les gens du bastion de l'Honneur doivent être Amicaux envers vous."}},
[9564] = {"La dignité de Gurf",nil, {"Rapportez la Dignité de Gurf à Gurf au repaire des Calmepins sur l'île de Brume-azur."}},
[9565] = {"Fouillez le repaire des Calmepins",nil, {"Fouillez le repaire des Calmepins à la recherche d'indices."}},
[9566] = {"Les cristaux de sang",nil, {"Parlez au Grand chef Calmepin au repaire des Calmepins."}},
[9567] = {"Connais ton ennemi",nil, {"Trouvez le Monument de Nazzivus à Nazzivian et récupérez un Glyphe du monument de Nazzivus. Lorsque vous l'aurez, retournez voir le redresseur de torts Aalesia au Guet du sang."}},
[9568] = {"À l'offensive",nil, {"Le redresseur de torts Aalesia du Guet du sang veut que vous tuiez 10 Satyres Nazzivus, 5 Voleurs Nazzivus et 5 Gangreliges Nazzivus."}},
[9569] = {"Contenir la menace",nil, {"Tuez 5 Traqueurs des ombres axxariens, 5 Implorateurs de l'enfer axxariens et Zevrax, puis récupérez 5 Cristaux corrompus et retournez auprès du Redresseur de torts Aalesia au Guet du sang."}},
[9570] = {"Le Kurken se cache dans le coin",nil, {"Kurz le Révélateur, du repaire des Calmepins de l'île de Brume-azur, veut que vous tuiez le Kurken et que vous lui rameniez la Peau du Kurken."}},
[9571] = {"La peau du Kurken",nil, {"Donnez la Peau du Kurken à Moordo, au repaire des Calmepins."}},
[9572] = {"Affaiblir les remparts",nil, {"Tuez le gardien des guetteurs Gargolmar, Omor l'Intouché et le drake Nazan. Rapportez la Main de Gargolmar, le Sabot d'Omor et la Tête de Nazan à Caza'rez, à Thrallmar dans la péninsule des Flammes infernales."}},
[9573] = {"Le chef Oomooroo",nil, {"Calmepin le Jeune, du repaire des Calmepins de l'île de Brume-azur, veut que vous tuiez le chef Oomooroo et 9 Indomptables affolés."}},
[9574] = {"Victimes de la corruption",nil, {"Apportez 6 morceaux d’Ecorce cristallisée à Morae, au Guet du sang."}},
[9575] = {"Affaiblir les remparts",nil, {"Tuez le gardien des guetteurs Gargolmar, Omor l'Intouché et le drake Nazan. Rapportez la Main de Gargolmar, le Sabot d’Omor et la Tête de Nazan à Flingot, du bastion de l'Honneur dans la péninsule des Flammes infernales."}},
[9576] = {"Le collier d'Aileron-cruel",nil, {"Rapportez le Pendentif de cristal rouge à Morae au Guet du sang."}},
[9578] = {"À la recherche de Galaen",nil, {"Retrouvez Galaen, le mari de Morae."}},
[9579] = {"Le destin de Galaen",nil, {"Remettez l’Amulette de Galaen à Morae au Guet du sang."}},
[9580] = {"Les premiers sec-ours",nil, {"Le pisteur Lyceon, du Guet du sang, veut 8 Flanchets d'anciens ours bruns."}},
[9581] = {"Ce qu'apprennent les cristaux",nil, {"Procurez-vous un Échantillon de cristal du site de l'impact en utilisant la Pioche à cristaux sur le Cristal du site de l'impact et apportez-le au messager Mikolaas au Guet du sang."}},
[9582] = {"La force de l’Un",nil, {"Ouvrez la Cage du ravageur et tuez le Ravageur de la mort qui s’y trouve, puis retournez voir Ruada au Guet d’azur, sur l’île de Brume-Azur."}},
[9584] = {"Le deuxième échantillon",nil, {"Procurez-vous un Échantillon de cristal altéré en utilisant la Pioche à cristaux sur le Cristal de Brume-Sang altéré et apportez-le au messager Mikolaas au Guet du sang."}},
[9585] = {"Le dernier échantillon",nil, {"Procurez-vous un Échantillon de cristal axxarien en utilisant la Pioche à cristaux sur le Cristal axxarien et apportez-le au messager Mikolaas au Guet du sang."}},
[9586] = {"Aider Tavara",nil, {"Guvan du Guet d’azur veut que vous trouviez Tavara et que vous guérissiez ses blessures."}},
[9587] = {"De sombres nouvelles",nil, {"Apportez la Lettre inquiétante au commandant Danath Trollemort au bastion de l'Honneur dans la péninsule des Flammes infernales."}},
[9588] = {"De sombres nouvelles",nil, {"Apportez la Lettre inquiétante à Nazgrel, à Thrallmar dans la péninsule des Flammes infernales."}},
[9589] = {"Le sang, c'est la vie",nil, {"Récupérez 10 Fioles de sang de gangr'orc et rapportez-les à Flingot au bastion de l'Honneur dans la péninsule des Flammes infernales."}},
[9590] = {"Le sang, c'est la vie",nil, {"Collectez 10 Fioles de sang de gangr'orc et rapportez-les au centurion Caza'rez à Thrallmar dans la péninsule des Flammes infernales."}},
[9591] = {"Dompter la bête",nil, {"Utilisez le Totem de domptage pour apprivoiser un Clampant barbelé et revenez voir la chasseresse Kella Arquenuit lorsque vous aurez réussi."}},
[9592] = {"Dompter la bête",nil, {"Utilisez le Totem de domptage pour apprivoiser un Trotteur des bois supérieur et retournez voir la chasseresse Kella Arquenuit."}},
[9593] = {"Dompter la bête",nil, {"Utilisez le Totem de domptage pour apprivoiser un Traquenuit et revenez voir la chasseresse Kella Arquenuit."}},
[9594] = {"Les signes de la Légion",nil, {"Tuez 8 Satyres Nazzivus et 8 Gangreliges Nazzivus, puis apportez la Plaque d'armure de Tzerak au redresseur de torts Aalesia, au Guet du sang."}},
[9595] = {"Contrôle",nil, {"Tuez des Murlocs sur le Rivage envasé pour invoquer une Âme en peine quel'dorei magicienne, puis détruisez-la. Allez ensuite parler à Bati à la Voûte des lumières de l'Exodar."}},
[9598] = {"Rédemption",nil, {"Étudiez le Tome de divinité et parlez à Jol."}},
[9599] = {"<UNUSED>",nil, {"Parlez à Jol, à l'Exodar."}},
[9600] = {"Rédemption",nil, {"Jol veut que vous vous serviez du Symbole de vie pour ressusciter un Jeune chaman furbolg avant de revenir la voir."}},
[9601] = {"En route pour la Barricade",nil, {"Parlez à Mehlar Aubelame à la Barricade."}},
[9602] = {"Délivrez-les du mal...",nil, {"Remettre la Communication du traître à l'exarque Menelaous au Guet d'azur."}},
[9603] = {"Des lits, des bandages, etc.",nil, {"Remettez la Liste de Topher à Laando, le maître des hippogriffes du Guet du sang."}},
[9604] = {"Sur les ailes d'un hippogriffe",nil, {"Acheter à Laando un vol en hippogriffe jusqu'à l'Exodar. À votre arrivée, remettez la Liste de Topher à Nurguni, au Siège du naaru dans l'Exodar."}},
[9605] = {"Le maître des hippogriffes Stephanos",nil, {"Allez trouver Stephanos à l'extérieur de l'Exodar et achetez-lui un vol à dos d'hippogriffe pour rentrer au Guet du sang."}},
[9606] = {"Retourner voir Topher Loaal",nil, {"Achetez à Stephanos un vol en hippogriffe pour rentrer au Guet du sang. Une fois <arrivé/arrivée>, livrez les Fournitures de Nurguni à l'Hôte Topher Loaal."}},
[9607] = {"Cœur de rage",nil, {"Faire une enquête exhaustive sur la Fournaise du sang, puis en faire un rapport au commandant de corps Danath Trollemort, au bastion de l’Honneur dans la péninsule des Flammes infernales."}},
[9608] = {"Cœur de rage",nil, {"Faire une enquête exhaustive sur la Fournaise du sang, puis en faire un rapport à Nazgrel, à Thrallmar dans la péninsule des Flammes infernales."}},
[9609] = {"Aidez le gardien Biggs",nil, {"Trouvez le gardien Biggs dans le marais des Chagrins."}},
[9610] = {"Le bassin des larmes",nil, {"Procurez-vous 5 Artéfacts atal'ai et remettez-les à Holaaru, au Havre boueux du marais des Chagrins."}},
[9612] = {"Merci du fond du cœur !",nil, {"Parlez à l'exarque Menelaous au Guet d'azur."}},
[9616] = {"Bandits !",nil, {"Remettez la Communication des elfes de sang à l'exarque Menelaous au Guet d'azur."}},
[9617] = {"Aller voir les pérégrins",nil, {"Parlez au lieutenant Coursaurore à la retraite des Pérégrins."}},
[9618] = {"Remettre les rapports",nil, {"Transmettez les Rapports de Keltus Sombrefeuille à Zelanis, à Lune-d'argent."}},
[9619] = {"La rune d'invocation",nil, {"Servez-vous de la Pierre de Vide pour invoquer un Marcheur du Vide au village Brume-d'or, vainquez-le puis retournez voir Talionia à Lune-d’Argent."}},
[9620] = {"Les explorateurs disparus",nil, {"Le messager Mikolaas veut que vous retrouviez l'équipe d'explorateurs qu'il a envoyé dans les ruines de Loreth'Aran, et que vous les aidiez de votre mieux."}},
[9621] = {"Emissaire auprès de la Horde",nil, {"Remettez la Lettre de Lor'themar Theron à dame Sylvanas Coursevent à Fossoyeuse, la capitale des Réprouvés dans les clairières de Tirisfal."}},
[9622] = {"Avertir votre peuple",nil, {"Parlez à l'exarque Menelaous au Guet d'azur."}},
[9623] = {"Il est temps de grandir",nil, {"L'exarque Menelaous, du Guet d'azur, veut que vous remettiez les Ordres de l'exarque à Torallius le Pâtre, qui se trouve hors de l'Exodar."}},
[9624] = {"La meilleure des gâteries",nil, {"Aonar, à la Croisée de Kessel, veut que vous lui rameniez 10 Poires des sables."}},
[9625] = {"On ne plaisante pas avec les elekk",nil, {"Parlez à Vorkhan le berger des elekk sur l'île de Brume-sang."}},
[9626] = {"Rencontre avec les Orcs",nil, {"Dame Sylvanas Coursevent veut que vous remettiez la Lettre scellée par Sylvanas au Haut seigneur Saurcroc, à Orgrimmar, la capitale de Durotar."}},
[9627] = {"L'allégeance à la Horde",nil, {"Retournez à Lune-d’Argent et allez faire votre rapport à Lor’themar Theron."}},
[9628] = {"Récupérer des données",nil, {"Récupérez le Cristal de données du relevé sur les Nagas irécailles près des cartographes morts et remettez-le au messager Mikolaas, au Guet du sang."}},
[9629] = {"Attraper... relâcher...",nil, {"Morae, du Guet du sang, veut que vous « marquiez » 6 Eclaireurs Vase noir avec le Marqueur de murlocs."}},
[9630] = {"Le journal de Medivh",nil, {"L’archimage Alturus, du défilé de Deuillevent, veut que vous entriez dans Karazhan et que vous parliez à Wravien."}},
[9631] = {"L'aide d'une collègue",nil, {"Remettez le Fragment d'os carbonisé à Kalynna Rougelatte, dans la Zone 52 de Raz-de-Néant."}},
[9632] = {"De nouveaux alliés",nil, {"Remettez la Lettre d'introduction à la chasseresse Kella Arquenuit, à l'Île de Brume-Azur."}},
[9633] = {"En route pour Auberdine",nil, {"Prenez le bateau pour Auberdine. Il part du quai qui se trouve près de la chasseresse Kella Arquenuit. Lorsque vous serez à Auberdine, présentez votre Lettre d’introduction à Thundris Tissevent."}},
[9634] = {"Des prédateurs d'outre-monde",nil, {"Tuez 10 Jeunes Brume-sang, puis retournez voir Vorkhan le berger des elekk à la Croisée de Kessel sur l'île de Brume-sang."}},
[9635] = {"L'extracteur de granules Zappeur-Gazier !",nil, {"Apportez 15 Pièces détachées de la pompe à vapeur à Mack Diver, à Zabra'jin dans le marécage de Zangar."}},
[9636] = {"L'extracteur de granules Zappeur-Gazier !",nil, {"Apportez 15 Pièces détachées de la pompe à vapeur à Manue Fretin, à Telredor dans le marécage de Zangar."}},
[9637] = {"La requête de Kalynna",nil, {"Kalynna Rougelatte veut que vous récupériez le Tome de la pénombre sur le Grand démoniste Néanathème dans la citadelle des Flammes infernales, et le Livre des noms oubliés sur le Sombre tisseur Syth dans les salles des Sethekk à Auchindoun.nnCette quête doit être accomplie en mode de difficulté Héroïque."}},
[9638] = {"En de bonnes mains",nil, {"Parler avec Gradav dans la Bibliothèque du gardien, à Karazhan."}},
[9639] = {"Kamsis",nil, {"Parler avec Kamsis dans la Bibliothèque du gardien, à Karazhan."}},
[9640] = {"L'ombre d'Aran",nil, {"Obtenez le Journal de Medivh et retournez voir Kamsis dans la Bibliothèque du Gardien de Karazhan."}},
[9641] = {"Les éclats de cristal irradié",nil, {"Le redresseur de torts Boros du Guet du sang veut que vous lui apportiez 10 Éclats de cristal irradiés."}},
[9642] = {"Encore des éclats de cristal irradié",nil, {"Si vous avez d’autres éclats de cristal irradiés, je serai ravi de les échanger contre l’un de mes cristaux."}},
[9643] = {"Les lianes étrangleuses",nil, {"Le pisteur Lyceon, du Guet du sang, veut que vous lui rameniez 6 Lianes étrangleuses épineuses."}},
[9644] = {"Plaie-de-nuit",nil, {"Allez sur la Terrasse du Maître à Karazhan et utilisez l'Urne noircie pour invoquer Plaie-de-nuit. Récupérez l'Essence arcanique voilée sur le cadavre de Plaie-de-nuit, et rapportez-la à l'archimage Alturus."}},
[9645] = {"La terrasse du Maître",nil, {"Allez sur la Terrasse du Maître à Karazhan et lisez le Journal de Medivh."}},
[9646] = {"Avis de recherche : Griffe-de-mort",nil, {"Apportez la Patte de Griffe-de-mort au Messager Mikolaas au Guet du sang."}},
[9647] = {"Réduire les bruissements",nil, {"Le pisteur Lyceon au Guet du sang veut que vous tuiez 10 Virevolteurs bleus royaux."}},
[9648] = {"Ménagerie mycologique de Mac'Aree",nil, {"Jessera de Mac'Aree au Guet du sang veut 1 Satyre puant aquatique, 1 Champignon de sang, 1 Polypore des ruines et 1 Collybie gangrenée."}},
[9649] = {"Les larmes d'Ysera",nil, {"Jessera de Mac'Aree au Guet du sang veut 2 Larmes d'Ysera."}},
[9663] = {"La tournée de Kessel",nil, {"Kessel, à la croisée de Kessel, veut que vous alliez prévenir de l'invasion imminente des elfes de sang le Grand chef Calmepin au repaire des Calmepins, l'Exarque Menelaous au Guet d'azur et l'Amiral Odesyus au Point d'accostage d'Odesyus. Revenez voir Kessel lorsque vous aurez terminé cette tâche. Vous avez 15 minutes. (Attention à ne pas aller dans l'eau.)"}},
[9664] = {"Fonder de nouveaux avant-postes",nil, {"Capturez la Tour de la garde de la couronne, la Tour du Mur d'est, la Tour du Col du nord et la Tour de Pestebois dans les Maleterres de l'est. Revenez ensuite voir l'émissaire Blanchebarbe à la Chapelle de l'Espoir de Lumière."}},
[9665] = {"Renforcer nos défenses",nil, {"Capturez la Tour de la garde de la couronne, la Tour du Mur d'est, la Tour du Col du nord et la Tour de Pestebois dans les Maleterres de l'est. Revenez ensuite voir l'émissaire Gormok à la Chapelle de l'Espoir de Lumière."}},
[9666] = {"Déclaration de puissance",nil, {"Sur ordre de Kessel, à la Croisée de Kessel sur l'Île de Brume-sang, allez tuer le Seigneur Xiz au Repaire des Irécailles et plantez la Bannière draeneï sur son cadavre."}},
[9667] = {"Il faut sauver la princesse Calmepin",nil, {"Faites sortir la Princesse Calmepin de sa cage, puis allez parler à l'ambassadeur Calmepin Frasaboo au Guet du sang.nnVous devez tuer le Grand chef Bras-hirsute pour obtenir la clé qui ouvre la cage."}},
[9668] = {"Rapport à l'exarque Admetius",nil, {"Parlez à l'Exarque Admetius au Guet du sang."}},
[9669] = {"L'expédition disparue",nil, {"Tuez 8 Tisseuses de brume, 8 Sangsues de brume et leur chef Zarakh. Revenez voir Achelus au Guet du sang quand votre mission sera achevée."}},
[9670] = {"Ils sont vivants ! Peut-être...",nil, {"Le Chercheur Cornelius vous a demandé de libérer 5 Chercheurs de l’expédition."}},
[9671] = {"Une livraison urgente",nil, nil},
[9672] = {"L'héritage du sang maudit",nil, {"L’amiral Odesyus vous a demandé de trouver le Capitaine Edward Hanes et de lui parler."}},
[9673] = {"La formation de dresseur",nil, {"Parlez à Halthenis à Lune-d’Argent."}},
[9674] = {"Les nagas au sang maudit",nil, {"Le capitaine Edward Hanes, sur l'île Balafre-du-ver, veut que vous tuiez 10 Nagas du sang maudit."}},
[9675] = {"La formation de dresseur",nil, {"Parlez à Ganaar à l'Étage des commerces de l'Exodar."}},
[9676] = {"La formation du paladin",nil, {"Parlez à Jesthenis Heurtesoleil, le maître des paladins, à l'intérieur de la Flèche solaire, sur l'île de Haut-soleil."}},
[9677] = {"Le chevalier-seigneur Vaillessang vous réclame",nil, {"Parlez au chevalier-seigneur Vaillessang, place des Pérégrins, à Lune-d'argent."}},
[9678] = {"La première épreuve",nil, {"Voyagez jusqu'à l'île que le chevalier-seigneur a décrite, trouvez la caverne et allumez le brasero. Lorsque la Première épreuve sera terminée, retournez voir le chevalier-seigneur Vaillessang à Lune-d'argent."}},
[9680] = {"Exhumer le passé",nil, {"L'archimage Alturus veut que vous vous rendiez dans les montagnes au sud de Karazhan dans le défilé de Deuillevent et que vous y récupériez un Fragment d'os carbonisé."}},
[9681] = {"Une étude du pouvoir",nil, {"Parlez au magistère Astalor Ligessang dans la chambre secrète sous les quartiers généraux des chevaliers de sang."}},
[9682] = {"Les sans-espoir…",nil, {"Le capitaine Edward Hanes de l'île Balafre-du-ver veut que vous récupériez 4 Âmes au sang maudit."}},
[9683] = {"Mettre un terme à la malédiction du sang",nil, {"Le capitaine Edward Hanes de l'île Balafre-du-ver veut que vous détruisiez la Statue de la reine Azshara et éliminiez Atoph du Sang maudit."}},
[9684] = {"Revendiquer la Lumière",nil, {"Utilisez le Calice chatoyant sur les Magistères elfes de sang pour le remplir et retournez voir le chevalier-seigneur Vaillessang à Lune-d’Argent."}},
[9685] = {"La rédemption des morts",nil, {"Apportez le Calice chatoyant rempli à l'auberge de Lune-d’Argent et utilisez ses pouvoirs magiques pour ressusciter Sangrias Mortelame."}},
[9686] = {"La deuxième épreuve",nil, {"Achevez la seconde épreuve en battant le Champion Hargnessang, le Champion Brise-Lumière, le Champion Vivelame et le Champion Heurtesoleil dans le pavillon situé à côté de maître Kelerun Deuillessang."}},
[9687] = {"Rétablir la tranquillité",nil, {"Le prince Toreth de l'île Balafre-du-ver veut que vous rapportiez 8 Os de dragon."}},
[9688] = {"Vers le Rêve",nil, {"Le prince Toreth de l'île Balafre-du-ver vous a demandé de tuer 5 Jeunes virides et 5 Rejetons virides."}},
[9689] = {"Tranchegueule",nil, {"Le prince Toreth de l'île Balafre-du-ver vous a demandé de tuer Tranchegueule."}},
[9690] = {"La deuxième épreuve",nil, {"Présentez-vous au maître Kelerun Deuillessang."}},
[9691] = {"Retour à Lune-d'argent",nil, {"Retournez voir le chevalier-seigneur Vaillessang à Lune-d’Argent."}},
[9692] = {"La voie de l'adepte",nil, {"Apportez une Gemme de Kor corrompue, une Caisse de lingots de forge-sang, une bonne mesure de Sang du Courroucé et un Insigne de chevalier de sang au Chevalier-seigneur Vaillessang, à Lune-d’Argent."}},
[9693] = {"Ce qu'Argus signifie pour moi",nil, {"L'exarque Admetius du Guet du sang vous a demandé de parler au redresseur de torts Boros."}},
[9694] = {"Le Guet du sang",nil, {"Le redresseur de torts Boros au Guet du sang veut que vous tuiez 10 Espions Epervier du soleil."}},
[9696] = {"Traductions…",nil, {"Apportez la Missive des Eperviers du soleil à l'interrogatrice Elysia au Guet du sang."}},
[9697] = {"La gardienne Leesa'oh",nil, {"Parlez à la gardienne Leesa'oh au Poste de garde cénarien dans le marécage de Zangar."}},
[9698] = {"Une audience avec le Prophète",nil, {"Apportez la Missive des Eperviers du soleil traduite au prophète Velen à l'Exodar."}},
[9699] = {"Vérité ou fiction",nil, {"Apportez les Ordres de Velen au redresseur de torts Boros, au Guet du sang."}},
[9700] = {"Des traits de magie dans l'obscurité",nil, {"Le redresseur de torts Boros du Guet du sang veut que vous trouviez l'emplacement du Portail du soleil et que vous tuiez 5 Anomalies du Vide."}},
[9701] = {"L'observation des sporelins",nil, {"Examiner le Vallon des pontes dans le marécage de Zangar, puis rapporter vos découvertes à la gardienne Leesa'oh au Poste de garde cénarien dans le marécage de Zangar."}},
[9702] = {"Un problème de gloutonnerie",nil, {"Récupérez 10 morceaux d’Aliments abandonnés puis revenez voir la gardienne Leesa'oh au Poste de garde cénarien, dans le marécage de Zangar."}},
[9703] = {"Le cryocœur",nil, {"Le redresseur de torts Kuros au Guet du sang veut que vous récupériez 12 boîtes de Fournitures médicales."}},
[9704] = {"Tuée par les Déshérités",nil, {"Allez au sud dans les ruines de Lune-d’Argent et cherchez l’Estafette morte dans l'allée du Point-du-jour."}},
[9705] = {"Récupérer un paquet",nil, {"Retournez voir l’estafette Alarion avec le Paquet d’Erona."}},
[9706] = {"Le journal de Galaen - Le destin du redresseur de torts Saruan",nil, {"Apportez le Journal de Galaen au redresseur de torts Kuros au Guet du sang."}},
[9707] = {"Forger l'arme",nil, {"Apportez la Caisse de matériaux à Bemarrin à Lune-d’Argent."}},
[9708] = {"Des champignons familiers",nil, {"Collectez 15 Échantillons de champignon et apportez-les à la gardienne Leesa'oh au Poste de garde cénarien dans le marécage de Zangar."}},
[9709] = {"Voler aux voleurs",nil, {"Récupérez 10 Boîtes de champignons et rapportez-les à la gardienne Leesa'oh au Poste de garde cénarien dans le marécage de Zangar."}},
[9710] = {"La corsèque à la trempe de sang",nil, {"Attendez pendant que Bemarrin forge votre arme."}},
[9711] = {"Matis le Cruel",nil, {"Le redresseur de torts Kuros du Guet du sang veut que vous capturiez Matis le Cruel."}},
[9712] = {"Le cheval de guerre thalassien",nil, {"Parlez au chevalier-seigneur Vaillessang pour apprendre comment invoquer votre cheval de guerre thalassien."}},
[9713] = {"Drapeau pour la récolte des chapeluisants",nil, nil},
[9714] = {"Rapportez-moi un autre jardinet !",nil, nil},
[9715] = {"Rapportez-moi un jardinet !",nil, {"Récupérez 5 Hibiscus sanguins et rapportez-les à Gzhun’tt à Sporeggar dans le marécage de Zangar."}},
[9716] = {"Des ennuis au lac Umbretourbe",nil, {"Déterminez la cause de la diminution de l'eau au lac Umbretourbe. Puis retournez voir Ysiel Chantelevent au Refuge cénarien dans le marécage de Zangar."}},
[9717] = {"Oh, alors s’ils veulent la guerre…",nil, {"Cueillez une Palme de sporielle et rapportez-la à T'shu à Sporeggar dans le marécage de Zangar."}},
[9718] = {"À vol d'oiseau",nil, {"Ysiel Chantelevent veut que vous vous serviez de l'Amulette du corbeau des tempêtes pour explorer chacun des principaux lacs du Marécage de Zangar."}},
[9719] = {"Traquer la traqueuse",nil, {"Apportez le Cerveau de la Traqueuse noire à Khn'nix à Sporeggar dans le marécage de Zangar."}},
[9720] = {"L'équilibre doit être préservé",nil, {"Ysiel Chantelevent veut que vous utilisiez les Graines de vignefer sur les Commandes de pompe à vapeur au Lac des serpents, au Lac Umbretourbe, au Lac des furoles et au Lagon. Retournez ensuite lui rendre les graines qu'il vous reste au Refuge cénarien dans le marécage de Zangar."}},
[9721] = {"Le seigneur Solanar vous réclame",nil, {"Parlez au seigneur Solanar Hargnessang à Lune-d’Argent."}},
[9722] = {"La voie du maître",nil, {"Allez revoir le seigneur Solanar Hargnessang pour accepter son offre de parrainage."}},
[9723] = {"Un geste d'engagement",nil, {"Apporter 40 Etoffes runiques, 6 Barres d’arcanite, 10 Soleillettes, 5 Runes ténébreuses et 150 pièces d'or au seigneur Solanar Hargnessang, à Lune-d’Argent."}},
[9724] = {"Prévenir le Cercle cénarien",nil, {"Parlez à Amythiel Marche-brume au Poste cénarien dans la péninsule des Flammes infernales."}},
[9725] = {"Une preuve de loyauté",nil, {"Le seigneur Solanar Hargnessang, à Lune-d’Argent, veut que vous détruisiez 3 Chariots à viande du Fléau et que vous tuiez 15 Ingénieurs de machines de siège du Fléau."}},
[9726] = {"Puisque nous sommes entre amis...",nil, {"Tuez 12 Esclavagistes écailles-sanglantes et 6 Enchanteresses écailles-sanglantes, puis retournez voir Gzhun'tt à Sporeggar dans le marécage de Zangar."}},
[9727] = {"Puisque nous sommes toujours entre amis...",nil, {"Tuez 12 Esclavagistes écailles-sanglantes et 6 Enchanteresses écailles-sanglantes, puis retournez voir Gzhun'tt à Sporeggar dans le marécage de Zangar."}},
[9728] = {"Un accueil chaleureux",nil, {"Le gardien Hamoot du Refuge cénarien veut que vous lui apportiez 30 Griffes de naga."}},
[9729] = {"Fhwoor écrabouiller !",nil, {"Escortez Fhwoor près de la pompe à vapeur des Furoles pour récupérer l’Arche de Ssslith. Ramenez Fhwoor avec l’arche jusqu’à un endroit sûr, puis allez faire votre rapport à Gzhun'tt, à Sporeggar dans le marécage de Zangar."}},
[9730] = {"Le chef des Sombrecrêtes",nil, {"Tuez Rajah Haghazed au Lagon et retournez voir le gardien Hamoot de l'Expédition cénarienne dans le marécage de Zangar pour chercher votre récompense."}},
[9731] = {"Plan des canalisations",nil, {"Explorez le Lac des Serpents à la recherche d'un drain. Retournez voir Ysiel Chantelevent au Refuge cénarien pour présenter vos découvertes."}},
[9732] = {"Retour au marécage",nil, {"Retournez voir Ysiel Chantelevent au Refuge cénarien dans le marécage de Zangar."}},
[9733] = {"Prévenir le Cercle cénarien",nil, {"Parlez à Mahuram Sabot-robuste à Thrallmar dans la péninsule des Flammes infernales."}},
[9734] = {"Retour au marécage",nil, {"Retournez voir Ysiel Chantelevent au Refuge cénarien dans le marécage de Zangar."}},
[9735] = {"Les vrais maîtres de la Lumière",nil, {"Le seigneur Solanar Hargnessang de Lune-d'argent veut que vous lui apportiez une fiole d'Eau bénite de la Main de Tyr."}},
[9736] = {"Les vrais maîtres de la Lumière",nil, {"Le seigneur Solanar Hargnessang de Lune-d’Argent veut que vous lui apportiez 1 Catalyseur arcanique, 1 Poudre crépusculaire, 1 Diamant d'Azeroth et 1 Diamant noir parfait."}},
[9737] = {"Les vrais maîtres de la Lumière",nil, {"Utilisez la Mixture d'extinction près de la flamme éternelle dans la chapelle d'Alonsus pour ôter la protection de la Lumière. Vainquez quiconque tentera de défendre la chapelle."}},
[9738] = {"Disparus au combat",nil, {"Découvrez ce qui est arrivé au lieur de terre Rayge, au naturaliste Morsure, à l'Échardonneur Vertemain et à l'implorateur céleste Griffe. Retournez ensuite voir la guetteuse Jhang au réservoir de Glissecroc dans le marécage de Zangar."}},
[9739] = {"Les malheurs des sporelins",nil, {"Fahssn au vallon des pontes veut que vous collectiez 10 Sacs de spores mûres."}},
[9740] = {"Le portail du soleil",nil, {"Le redresseur de torts Aesom du Guet du sang veut que vous détruisiez le Portail du soleil."}},
[9741] = {"Les bestioles du vide",nil, {"Tuez 12 Bestioles du Vide et faites votre rapport au redresseur de torts Aesom au Guet du sang."}},
[9742] = {"Encore des sacs de spores",nil, nil},
[9743] = {"Ennemis par nature",nil, {"Fahssn, du vallon des pontes du marécage de Zangar, veut que vous lui rameniez 6 Vrilles de seigneurs-tourbe."}},
[9744] = {"Plus de vrilles !",nil, {"Vous avez tué de nombreux géants, mais il en reste encore beaucoup. Et ils attaquent toujours nos sacs de spores !"}},
[9746] = {"Les limites de l'épuisement physique",nil, {"Le redresseur de torts Aesom au Guet du sang veut que vous tuiez 10 Pyromanciens Epervier du soleil et 10 Défenseurs Epervier du soleil."}},
[9747] = {"La tribu Umbretourbe",nil, {"Ikeyen du Refuge cénarien veut que vous vous rendiez au village Umbretourbe pour y tuer Kataru, 8 Prophètes d'Umbretourbe, 6 Sorciers-docteurs d'Umbretourbe et 6 Oracles d'Umbretourbe."}},
[9748] = {"Ne buvez pas l'eau",nil, {"Le redresseur de torts Aesom au Guet du sang veut que vous utilisiez le Flacon d'échantillon d'eau pour obtenir un Échantillon d'eau de Brume-sang."}},
[9749] = {"Ils sont vivants ! Peut-être...",nil, {"Le Chercheur Cornelius a demandé que vous libériez 5 Chercheurs de l'expédition."}},
[9750] = {"UNUSED Urgent Delivery",nil, nil},
[9751] = {"L'héritage du sang maudit",nil, {"L’amiral Odesyus vous a demandé de trouver le Capitaine Edward Hanes et de lui parler."}},
[9752] = {"L'évadée d'Umbretourbe",nil, {"Escortez Kayra Long-crin jusqu'au Refuge cénarien dans le marécage de Zangar. Présentez-vous à Ysiel Chantelevent lorsque vous aurez accompli cette tâche."}},
[9753] = {"Ce que nous savons...",nil, {"Parlez à l'exarque Admetius au Guet du sang."}},
[9756] = {"Ce que nous ne savons pas…",nil, {"Parlez à un agent Epervier du soleil capturé au Guet du sang et obtenez des informations secrètes des Eperviers du soleil. Retournez auprès de l'exarque Admetius quand vous aurez terminé cette tâche."}},
[9757] = {"Aller trouver la chasseresse Kella Arquenuit",nil, {"Parlez à la chasseresse Kella Arquenuit près de l'Amarrage de Valaar."}},
[9758] = {"Retourner voir l'arcaniste Vandril",nil, {"Retournez voir l'arcaniste Vandril à Tranquillien dans les Terres fantômes."}},
[9759] = {"La fin de leur monde",nil, {"Suivez et protégez le démolisseur Legoso pendant qu'il essaye de détruire la Bobine vectorielle et tuer Sironas. Retournez voir l'exarque Admetius au Guet du sang si vous réussissez cette mission."}},
[9760] = {"Le Repos du redresseur de torts",nil, {"Parlez au redresseur de torts Corin au Repos du redresseur de torts sur l'île de Brume-Sang."}},
[9761] = {"Ouvrir la voie",nil, {"Le redresseur de torts Corin au Repos du redresseur de torts veut que vous tuiez 8 Saboteurs Epervier du soleil et 8 Agents Epervier du soleil."}},
[9762] = {"Une prophétie à écrire",nil, nil},
[9763] = {"Le repaire du seigneur de guerre",nil, {"La guetteuse Jhang veut que vous trouviez et exécutiez le seigneur de guerre Kalithresh à l'intérieur du réservoir de Glissecroc."}},
[9764] = {"Les ordres de dame Vashj",nil, {"Apportez les Ordres de dame Vashj à Ysiel Chantelevent au Refuge cénarien dans le marécage de Zangar."}},
[9765] = {"Les préparatifs de guerre",nil, {"Procurez-vous des Armes de Glissecroc sur les nagas du réservoir de Glissecroc et apportez-les à Ysiel Chantelevent au Refuge cénarien dans le marécage de Zangar."}},
[9766] = {"Les armes de Glissecroc",nil, nil},
[9767] = {"Connais ton adversaire",nil, {"Procurez-vous un Grimoire de Glissecroc sur les nagas du réservoir de Glissecroc et apportez-le à Ashyen au Refuge cénarien dans le marécage de Zangar."}},
[9769] = {"La mode, ça ne s'explique pas",nil, {"Apportez 8 Ailes diaphanes de n'importe quelles lucioles du marécage de Zangar à Magasha au poste du Rat des marais."}},
[9770] = {"Les crocs-du-marais menaçants",nil, {"Reavij du Poste du rat des marais veut que vous alliez tuer 10 Eventreurs Croc-du-marais."}},
[9771] = {"À la recherche de l'éclaireur Jyoba",nil, {"Partez à la recherche de l'éclaireur Jyoba à la Morte-bourbe, au Marécage de Zangar."}},
[9772] = {"Le rapport de Jyoba",nil, {"Reprenez le Rapport de l'éclaireur Jyoba aux Géants flétris et rapportez-le à Zurai au poste du Rat des marais."}},
[9773] = {"Plus de champignons !",nil, {"Prélever 8 Filets d'anguilles sur des Anguilles d'Umbretourbe et les apporter à Magasha, au poste du Rat des marais."}},
[9774] = {"Les écailles d'hydre épaisses",nil, {"Zurai, au poste du Rat des marais, veut que vous lui apportiez 12 Écailles d'hydre épaisses."}},
[9775] = {"Aller voir le chasseur des ombres Denjai",nil, {"Apporter le Rapport de Zurai chasseur des ombres Denjai à Zabra'jin."}},
[9776] = {"Le Havre d'Orebor",nil, {"Parlez à Ikuti au Havre d'Orebor."}},
[9777] = {"Les spores de fulgor",nil, {"Apportez 6 Spores de fulgor à Ruam, à Telredor."}},
[9778] = {"Le gardien Hamoot",nil, {"Allez vous présenter au gardien Hamoot au Refuge cénarien dans le marécage de Zangar."}},
[9779] = {"L'interception des messages",nil, {"Tuez assez d’Elfes Eperviers du soleil pour trouver la Missive des Éperviers du soleil et rapportez-la au Redresseur de torts Boros, au Guet du sang."}},
[9780] = {"Les filets d'anguilles d'Umbretourbe",nil, {"Noraani, à Telredor, veut que vous lui apportiez 8 Filets d'anguille."}},
[9781] = {"Trop de bouches à nourrir",nil, {"Tuez 10 Hydres du bourbier et retournez voir Haalrun à Telredor."}},
[9782] = {"La Morte-bourbe",nil, {"Apportez un Échantillon de terre de Morte-bourbe au Redresseur de torts Idaar, à Telredor."}},
[9783] = {"Une sécheresse peu naturelle",nil, {"Le redresseur de torts Idaar, à Telredor, veut que vous tuiez 12 Géants flétris."}},
[9784] = {"L'identification des morceaux de plantes",nil, {"Il me faudrait encore d'autres morceaux de plantes. Avec la baisse du niveau des eaux, j'ai l'impression que le nombre d'espèces envahissantes augmente. J'ai cependant besoin d'un échantillon plus large pour appuyer ma théorie."}},
[9785] = {"La bénédiction des anciens",nil, {"L'imploratrice céleste Sabot-noir veut que vous alliez parler aux anciens Ashyen et Keleth au Refuge cénarien, pour obtenir leur bénédiction."}},
[9786] = {"Les ruines de Boha'mu",nil, {"L'anachorète Ahuurn de Telredor veut que vous exploriez les Ruines de Boha'mu."}},
[9787] = {"Les idoles des Tourbe-farouches",nil, {"Récupérez 6 Idoles Tourbe-farouche et rapportez-les à l'anachorète Ahuurn à Telredor."}},
[9788] = {"Un lieu sombre et humide",nil, {"Cherchez les affaires d'Ikeyen à l'intérieur d'une caverne au sud d'Umbretourbe. Rapportez-les à Ikeyen au Refuge cénarien dans le marécage de Zangar."}},
[9789] = {"Maîtrise de la chasse aux sabots-fourchus",nil, {"Chassez 30 Sabots-fourchus et retournez voir Hemet Nesingwary au safari de Nesingwary, en Nagrand, pour prouver votre valeur."}},
[9790] = {"Ailes diaphanes",nil, {"Haalrun de Telredor veut que vous lui rameniez 8 Ailes diaphanes de n'importe quelles lucioles du marécage de Zangar."}},
[9791] = {"Les crocs-du-marais menaçants",nil, {"Tuez 10 Eventreurs Croc-du-marais et retournez voir Noraani à Telredor."}},
[9792] = {"Un message pour Telaar",nil, {"Parlez au Gardien Moi’bff Jill à Telaar, en Nagrand."}},
[9793] = {"La destinée de Tuurem",nil, {"Apportez la Lettre du redresseur de torts Idaar à Andarl au Bastion allérien dans la forêt de Terokkar."}},
[9794] = {"La curiosité est un vilain défaut",nil, {"Apportez la Lettre de Timothy à Kialon à Sylvanaar dans les Tranchantes."}},
[9795] = {"La menace ogre",nil, {"Rendez-vous au bastion des Sire-tonnerre dans les Tranchantes, et parlez à Nekthar."}},
[9796] = {"Des nouvelles du marécage de Zangar",nil, {"Parlez à Mokasa, au fort des Brise-pierres, dans la forêt de Terokkar."}},
[9797] = {"Des renforts pour Garadar",nil, {"Parlez au capitaine Kroghan à Garadar en Nagrand."}},
[9798] = {"Les plans des elfes de sang",nil, {"Apportez les Plans des elfes de sang au redresseur de torts Aldar au point d'impact du val d'Ammen."}},
[9799] = {"Un travail de botanique",nil, {"Cueillez 3 Fleurs corrompues et apportez-les à l'apprentie Vishael au point d'impact du val d'Ammen."}},
[9800] = {"Une graine rare",nil, {"L'Elémentalise Lo'ap, au Trône des éléments de Nagrand, veut que vous lui apportiez 15 Caracolis digérés"}},
[9801] = {"Rassembler les composants",nil, {"L’anachorète Ahuurn, de Telredor, veut que vous lui rameniez 4 Yeux de sporoptères et 4 Tentacules de trotteur des tourbières."}},
[9802] = {"Les plantes du marécage de Zangar",nil, {"Apportez 10 Morceaux de plantes non identifiées à Lauranna Thar'well, au Refuge cénarien dans le marécage de Zangar."}},
[9803] = {"Un message pour Tourbe-farouche",nil, {"Une fois dans les ruines de Boha’mu, buvez l’Élixir d’Ahuurn. Lorsque vous serez sous forme d’arakkoa, allez parler à l’Ancien Kuruti."}},
[9804] = {"Esprits agités de Chanteciel",nil, {"L'élémentaliste Lo'ap, du Trône des éléments en Nagrand, veut que vous détruisiez 8 Esprits du lac."}},
[9805] = {"La bénédiction d'Incineratus",nil, {"L’Elémentaliste Lo’ap, au Trône des éléments, en Nagrand, veut que vous détruisiez la Grande hutte, la Hutte de l’ouest, la Hutte du sud et la Hutte de l’est à Ventejonc.nnRendez le Feu vivant à l’Elémentaliste Lo’ap quand vous aurez terminé votre tâche."}},
[9806] = {"Des spores fertiles",nil, {"Gshaff veut que vous récoltiez 6 Spores fertiles sur les Sporoptères et Marcheurs des marais du marécage de Zangar. Revenez voir Gshaff à Sporeggar quand vous aurez terminé cette tâche."}},
[9807] = {"Plus de spores fertiles",nil, {"Nous avons toujours l’usage de spores fertiles, <race>. Les Grands-pieds sont des champignons extrêmement utiles. Pouvoir les cultiver nous permet de faire d’innombrables choses."}},
[9808] = {"Les chapeluisants",nil, {"Apportez 10 Chapeluisants à Msshi'fn de Sporeggar, dans le marécage de Zangar."}},
[9809] = {"Plus de chapeluisants",nil, {"Je vous prendrai tous les chapeluisants que vous pourrez trouver. Notre village en a toujours besoin."}},
[9810] = {"L'esprit pollué",nil, {"L’Elémentaliste Lo’ap, au Trône des éléments, en Nagrand, veut que vous détruisiez l’Essence polluée de Watoosun et 8 Surgisseurs du lac."}},
[9811] = {"L'amitié des Sin'dorei",nil, {"Apportez la Tête de Dar'Khan à Lor'themar Theron, à Lune-d'argent."}},
[9812] = {"Emissaire auprès de la Horde",nil, {"Remettez la Lettre de Lor'themar Theron à dame Sylvanas Coursevent à Fossoyeuse, la capitale des Réprouvés dans les clairières de Tirisfal."}},
[9813] = {"Rencontre avec les Orcs",nil, {"Dame Sylvanas Coursevent veut que vous remettiez la Lettre scellée par Sylvanas au Haut seigneur Saurcroc, à Orgrimmar, la capitale de Durotar."}},
[9814] = {"Des têtes-tonnantes, mon pote !",nil, {"Le sorcier-docteur Tor'gash veut 6 Têtes-tonnantes."}},
[9815] = {"Un plongeon dans la boue",nil, {"L'Elémentaliste Lo'ap, au Trône des éléments, en Nagrand, veut que vous récupériez 5 Noyaux couverts de boue."}},
[9816] = {"Ouvrez, ouvrez la cage aux…",nil, {"Prenez la Cage à Murlocs et posez-la sur le rocher plat, près de Tourbedague."}},
[9817] = {"Le chef des Écailles-sanglantes",nil, {"Tuez Rajis Fyashe au Lac des Serpents et revenez voir le gardien Hamoot au refuge cénarien dans le marécage de Zangar pour recevoir votre récompense."}},
[9818] = {"Les entrailles de la terre",nil, {"Communiquer avec Gordawg."}},
[9819] = {"La terre torturée",nil, {"Gordawg, du Trône des Éléments en Nagrand, vous a demandé de détruire 10 Esprits de la terre torturés."}},
[9820] = {"ON RECHERCHE : Boss Grog'ak",nil, {"Le chasseur des ombres Denjai, de Zabra'jin, veut que vous lui apportiez la Tête du boss Gorg'ak."}},
[9821] = {"Damnation au menu",nil, {"Gordawg, au Trône des éléments, en Nagrand, vous a demandé de lui apporter 10 Noyaux d'écraseur enragé."}},
[9822] = {"Une attaque imminente",nil, {"Le chasseur des ombres Denjai, de Zabra’jin, veut que vous lui apportiez les Plans d’attaque des Ango’rosh."}},
[9823] = {"Eux ou nous",nil, {"Tuez le Suzerain Poing-sanglant, 10 Marteleurs Ango'rosh et 10 Mangeurs-d'âmes Ango'rosh, et faites votre rapport au chasseur des ombres Denjai à Zabra'jin."}},
[9824] = {"Troubles arcaniques",nil, {"Utilisez le Cristal de divination pourpre près des sources d'eau souterraines dans la Cave du maître, puis retournez voir l'archimage Alturus à l'extérieur de Karazhan."}},
[9825] = {"L'agitation des sans-repos",nil, {"Apportez 10 Essences fantomatiques à l'Archimage Alturus, à l'extérieur de Karazhan."}},
[9826] = {"Un envoyé de Dalaran",nil, {"Apportez le Rapport d’Alturus à l’Archimage Cédric, à la périphérie du Cratère de Dalaran."}},
[9827] = {"Le baside racorni",nil, {"Apportez le Baside racorni à Ruam à Telredor."}},
[9828] = {"Le baside racorni",nil, {"Apportez le Baside racorni à Reavij au poste du Rat des marais."}},
[9829] = {"Khadgar",nil, {"Remettez le Rapport d’Alturus à Khadgar, à Shattrath, dans la forêt de Terokkar."}},
[9830] = {"Venin d'aiguillonneur",nil, {"Apportez 6 doses de Venin de suce-sang des Furoles à Puluu, au Havre d’Orebor."}},
[9831] = {"L'entrée de Karazhan",nil, {"Khadgar veut que vous entriez dans le Labyrinthe des Ombres d'Auchindoun pour récupérer le Premier fragment de la clé, dans le Récipient arcanique qui y est caché."}},
[9832] = {"Le deuxième et le troisième fragments",nil, {"Trouver le Deuxième fragment de la clé dans un récipient arcanique à l’intérieur du Réservoir de Glissecroc, et le Troisième fragment de la clé dans un récipient arcanique au Donjon de la tempête. Une fois que ce sera fait, revenir auprès de Khadgar à Shattrath."}},
[9833] = {"Les lignes de communication",nil, {"Puluu, du Havre d’Orebor, veut que vous tuiez 12 Trancheurs Croc-du-marais."}},
[9834] = {"Une armure naturelle",nil, {"Apportez 8 Peaux de tourbegriffes à Maktu au Havre d’Orebor."}},
[9835] = {"Les empiètements des Ango'rosh",nil, {"Tuez 10 Ogres Ango'rosh et 5 Chamans Ango'rosh, puis retournez voir Ikuti au Havre d'Orebor."}},
[9836] = {"Le toucher du maître",nil, {"Rendez-vous aux Grottes du temps et persuadez Medivh d’accepter votre Clé de l’apprenti réparée."}},
[9837] = {"Retour vers Khadgar",nil, {"Retournez voir Khadgar à Shattrath, et montrez-lui à la Clé du maître."}},
