[10040] = {"Qui sont-ils ?",nil, {"Parlez avec l'Initié ténébreux, le Travailleur ténébreux et le Conseiller ténébreux en portant le déguisement. Puis retournez voir le soldat Weeks à Grangol'var dans la forêt de Terokkar."}},
[10041] = {"Qui sont-ils ?",nil, {"Parlez avec l'Initié ténébreux, le Travailleur ténébreux et le Conseiller ténébreux en portant le déguisement. Puis retournez voir l'éclaireur Neftis à Grangol'var dans la forêt de Terokkar."}},
