[10057] = {"Se tourner vers les chefs",nil, {"Triomphez des fantômes de l'archimage Xintor et du lieutenant-commandant Thalvos, puis retournez voir le garde d'honneur Wesilow à la tour au nord-ouest du bastion de l'Honneur."}},
[10058] = {"Un cadeau ancien",nil, {"Récupérez les « Mystères de la Lumière » et rapportez-le au père Malgor Devidicus au bastion de l’Honneur."}},
[10059] = {"S'occuper de Zeth'Gor",nil, {"Tuez Grillok « Sombroeil » et fouillez son repaire à la recherche des Plans d'attaque de l'Orbite-sanglante."}},
[10060] = {"Désastre imminent",nil, {"Tuez 15 Grunts de l'Orbite-sanglante, 10 Nécrolytes de l'Orbite-sanglante, 5 Péons de l'Orbite sanglante et le seigneur de guerre Morkh. Puis faites votre rapport au garde de pierre Ambelan sur la crête Brise-épine."}},
[10061] = {"Les Inflexibles",nil, {"Grelag veut que vous vainquiez 12 Fantassins inflexibles, 10 Ensorceleurs inflexibles et 5 Chevaliers inflexibles à l'Armurerie de l'expédition, au sud du bastion de l'Honneur."}},
[10062] = {"Tourné vers le commandement",nil, {"Triomphez des fantômes de l'archimage Xintor et du lieutenant-commandant Thalvos, puis retournez voir Grelag sur la Crête brise-échine."}},
