[10702] = {"Un travail de grunt...",nil, {"Le suzerain Or'barokh du village d'Ombrelune dans la vallée d'Ombrelune vous demande de tuer 20 spécimens de la Faune de la vallée d'Ombrelune.nnCes créatures comprennent les Sangliers gangrenés, les Chimères Ailevile et les Pinceurs Brûlecoquille. N'importe quelle combinaison conviendra."}},
[10703] = {"Mettez des bottes...",nil, {"Le thane Yoregar du bastion des Marteaux-hardis dans la vallée d'Ombrelune vous demande de tuer 20 spécimens de la faune de la vallée d'Ombrelune.nnCes créatures comprennent les Sangliers gangrenés, les Chimères Ailevile et les Pinceurs Brûlecoquille. N'importe quelle combinaison fera l'affaire."}},
[10704] = {"Comment pénétrer dans l’Arcatraz",nil, {"A’dal vous a <chargé/chargée> de récupérer la Pièce supérieure et la Pièce inférieure de la clé de l’Arcatraz. Rapportez-les-lui et il s’en servira pour vous confectionner la Clé de l’Arcatraz."}},
[10705] = {"Le voyant Udalo",nil, {"Trouvez le voyant Udalo à l'intérieur de l'Arcatraz au Donjon de la Tempête."}},
[10706] = {"Un mystérieux présage",nil, {"Retournez voir Akama à la Cage de la gardienne dans la vallée d'Ombrelune."}},
[10707] = {"La terrasse ata'mal",nil, {"Allez au sommet de la Terrasse Ata'mal dans la vallée d’Ombrelune, et procurez-vous le Cœur de fureur. Retournez voir Akama à la Cage de la gardienne dans la vallée d'Ombrelune quand vous aurez accompli cette tâche."}},
[10708] = {"La promesse d'Akama",nil, {"Apportez le Médaillon de Karabor à A'dal, à Shattrath."}},
[10709] = {"Réunion",nil, {"Parlez à Rexxar, au bastion des Sire-tonnerre, dans les Tranchantes."}},
[10710] = {"Vol d'essai : la Crête chantante",nil, {"Signez la Décharge de Tally, puis allez parler à Rally Rossecoquin pour utiliser le Zephyrium Capacitorium."}},
[10711] = {"Vol d'essai : le point d'ancrage de Razaan",nil, {"Parlez à Rally Rossecoquin pour utiliser le Zephyrium Capacitorium."}},
[10712] = {"Vol d'essai : la sylve Ruuan",nil, {"Parlez à Rally Rossecoquin pour utiliser le Zephyrium Capacitorium. Lors du vol vers la Sylve Ruuan, faites tourner la Girouette néantine. Remettez la Girouette néantine tournoyante à O'Mally Rossecoquin au Bosquet éternel."}},
[10713] = {"… et un temps pour l'action",nil, {"Tuez 10 Elagueurs du culte du Ver."}},
[10714] = {"Sur les ailes de l'Esprit",nil, {"Trouvez un Sous-chef de la Masse-Sanglante et un Devin de la Masse-Sanglante en pleine conversation, et utilisez le Sifflet de Rexxar pour appeler Esprit, afin qu’il les espionne."}},
[10715] = {"Dans le Goulet bouillonnant",nil, {"Procurez-vous 7 Glandes à venin de perceur de croûte."}},
[10716] = {"Test Flight: Raven's Wood <needs reward>",nil, {"Parlez à Rally Rossecoquin pour utiliser le Zephyrium Capacitorium. Puis retournez voir Tally Rossecoquin au Poste de Toshley."}},
[10717] = {"Du braconnage chez les braconniers",nil, {"Procurez-vous 5 Filets du culte du Ver."}},
[10718] = {"Les esprits entendent des voix...",nil, {"Parlez à Rexxar au Bastion des Sire-tonnerre, dans les Tranchantes."}},
[10719] = {"Vous avez eu la note ?",nil, {"Apportez la Note de rendez-vous au Gardien des arbres Chawn au Bosquet éternel dans les Tranchantes."}},
[10720] = {"Les plus petites des créatures",nil, {"Utilisez le Tambour en cuir de kodo au Terrier des marmottes. Quand vous aurez pris le contrôle de la Marmotte, empoisonnez les trois tonneaux dans le camp des Masse-Sanglante."}},
[10721] = {"Un tour de cochon pour Grulloc",nil, {"Procurez-vous le Sac de Grulloc."}},
[10722] = {"Rendez-vous au convent de l'Aile noire",nil, {"Rassemblez suffisamment de Guenilles sur les différents sectateurs du Ver pour vous fabriquer un Déguisement de surveillant. Utilisez ce déguisement pour vous rendre au rendez-vous de Kolphis Sombrécaille."}},
[10723] = {"Gorgrom le Mange-dragon",nil, {"Procurez-vous 3 Totems macabres sur les Ogres de Roche'mok. Utilisez le Piège de Sablecrin à l'Autel de Gorgrom pour invoquer et tuer Gorgrom le Mange-dragon.nnUne fois Gorgrom mort, placez les 3 Totems macabres près de son cadavre."}},
[10724] = {"Prisonnier de la Flèchelame",nil, {"Tuez des Ogres de Flèchelame pour vous procurer la Clé de la cage à wyverne. Une fois que vous l'aurez, allez à la Cage à wyverne et utilisez le Sifflet d'Esprit pour appeler Esprit, qui déverrouillera la cage. Une fois Leokk libéré, revenez voir Rexxar au village des Sire-tonnerre dans les Tranchantes."}},
