[10745] = {"Des nouvelles de la victoire",nil, {"Retournez au village d'Ombrelune et parlez au suzerain Or'barokh."}},
[10747] = {"Les dragonnets du culte du Ver",nil, {"Utilisez le Filet à dragonnet noir pour capturer 10 Dragonnets noirs du culte du Ver."}},
