[10749] = {"Le poison du baron Sablecrin",nil, {"Livrez le Poison du baron Sablecrin à Rexxar, au bastion des Sire-tonnerre, dans les Tranchantes."}},
[10750] = {"La voie de la Conquête",nil, {"Voyagez vers la Voie de la Conquête dans la Vallée d'Ombrelune. Une fois que vous aurez fait une évaluation préliminaire de la zone, utilisez le Lance-fusée de Kor'kron pour faire signe à un coursier du vent de Kor'kron.nnSi vous perdez votre Lance-fusée de Kor'kron, allez voir le suzerain Or'barokh pour en obtenir un autre."}},
[10751] = {"Ouvrir la voie",nil, {"Tuez 20 Soldats éclipsions, 10 Cavaliers éclipsions et 10 Lieurs de sort éclipsions."}},
[10752] = {"En direction d'Orneval",nil, {"Parler à Raene Mène-loup à Astranaar."}},
[10753] = {"Tuer les bêtes sauvages",nil, {"Tuer 4 Gangreliges ailécaille, 4 Gangreliges Crocs-lames et 2 Corrupteurs gangrenés."}},
[10754] = {"L'entrée dans la citadelle",nil, {"Apportez le Moule à clé préparé au commandant de corps Danath au bastion de l'Honneur dans la péninsule des Flammes infernales."}},
[10755] = {"L'entrée dans la citadelle",nil, {"Apportez le Moule à clé préparé à Nazgrel, à Thrallmar dans la péninsule des Flammes infernales."}},
[10756] = {"Le grand maître Rohok",nil, {"Apportez le Moule à clé préparé à Rohok à Thrallmar, dans la Péninsule des Flammes infernales."}},
[10757] = {"La demande de Rohok",nil, {"Apportez 4 Barres de gangrefer, 2 Poussières arcaniques et 4 Granules de feu à Rohok à Thrallmar dans la péninsule des Flammes infernales."}},
[10758] = {"Plus chaud que l'enfer",nil, {"Détruisez un Saccageur gangrené dans la péninsule des Flammes infernales et plongez le Moule à clé inachevé dans ce qu'il en reste. Apportez le Moule à clé carbonisé à Rohok à Thrallmar."}},
[10759] = {"Trouver le déserteur",nil, {"Trouvez Parshah dans les Champs de magma et parlez-lui."}},
[10760] = {"Les débris de Sketh'lon",nil, {"Récupérez la Page 1 du journal du commandant de Sketh'lon, la Page 2 du journal du commandant de Sketh'lon et la Page 3 du journal du commandant de Sketh'lon sur des arakkoa du sombre Conclave aux Débris sketh'lon et apportez-les au sergent Kargrul au village d'Ombrelune."}},
[10761] = {"Trouver le déserteur",nil, {"Trouvez Parshah dans les Champs de magma et parlez-lui."}},
[10762] = {"Le grand maître Dumphry",nil, {"Apportez le Moule à clé préparé à Dumphry au bastion de l'Honneur."}},
