[10766] = {"Le site d'invasion Cataclysme",nil, {"Présentez-vous à Plexi au Site d'invasion Cataclysme."}},
[10767] = {"Le site d'invasion Cataclysme",nil, {"Présentez-vous à Nakansi au Site d'invasion Cataclysme."}},
[10768] = {"Les tabards des Illidari",nil, {"Récupérez 10 Tabards illidari."}},
[10769] = {"De la dissension dans les rangs...",nil, {"Tuez 5 Colosses affolés en portant le déguisement d’un Elfe de sang illidari, et retournez voir le suzerain Or'barokh au village d’Ombrelune dans la vallée d’Ombrelune. Il voudra aussi que vous lui rendiez son Lance-fusée kor'kron."}},
[10770] = {"Petites braises",nil, {"Boimoussu l'Ancien veut que vous tuiez 8 Diablotins de brûlure avant de retourner le voir à la Sylve Ruuan."}},
[10771] = {"Tiré des cendres",nil, {"Boimoussu l'Ancien veut que vous voyagiez jusqu'à Skald pour y planter des Graines de racine-de-fer dans trois monticules différents de Terre volcanique fertile et que vous retourniez ensuite le voir dans la Sylve Ruuan."}},
[10772] = {"La voie de la Conquête",nil, {"Voyagez vers la Voie de la Conquête dans la Vallée d'Ombrelune. Une fois que vous aurez fait une évaluation préliminaire de la zone, utilisez le Lance-fusée marteau-hardi pour faire signe à un Chevaucheur de griffon marteau-hardi.nnSi vous perdez votre Lance-fusée marteau-hardi, allez voir le thane Yoregar au Bastion des Marteaux-hardis dans la Vallée d'Ombrelune pour en obtenir un autre."}},
[10773] = {"Ouvrir la voie",nil, {"Tuez 20 Soldats éclipsions, 10 Cavaliers éclipsions et 5 Fils de Corok."}},
[10774] = {"Elfe de sang + Géant = ???",nil, {"Tuez la Chancelière Feuillesang et Corok le Puissant, et récupérez les Ordres d’Illidan."}},
[10775] = {"Les tabards des Illidari",nil, {"Récupérez 10 Tabards illidari."}},
[10776] = {"De la dissension dans les rangs...",nil, {"Tuez 5 Colosses affolés en portant le déguisement d’un agent d’Illidan, et retournez voir le thane Yoregar au bastion des Marteaux-Hardis dans la vallée d’Ombrelune. Il voudra aussi que vous lui rendiez son Lance-fusée marteau-hardi."}},
[10777] = {"Le totem d'Asghar",nil, {"Apportez le Totem de guerre de Sketh'lon à Parshah dans les Champs de magma."}},
[10778] = {"Le bâtonnet de Lianthe",nil, {"Procurez-vous la Clé de Lianthe sur un Elfe de sang de la Halte de l'éclipse, utilisez-la pour ouvrir le Coffre-fort de Lianthe et prenez-y le Bâtonnet de Lianthe. Apportez le bâtonnet à Parshah dans les Champs de magma."}},
[10779] = {"La voie du chasseur",nil, {"Parler à la chasseresse Kella Arquenuit QUELQUE PART."}},
[10780] = {"Les plumes de Sketh'lon",nil, {"Rassemblez 8 Plumes de Sketh'lon et apportez-les à Parshah dans les Champs de magma."}},
[10781] = {"La bataille du Guet cramoisi",nil, {"Combattez les forces d’Illidan au Guet cramoisi et survivez !nnAllez voir A’dal à Shattrath si vous avez réussi à vaincre."}},
[10782] = {"L'imprégnation du bourdon",nil, {"Voyagez jusqu'à l'Autel de la damnation et utilisez le Bourdon inachevé en vous tenant près de Gul’dan. Quand le bourdon aura été imprégné d'énergie, retournez voir Parshah dans les Champs de magma."}},
[10783] = {"Le baron Sablecrin",nil, {"Parlez au Baron Sablecrin au Cercle de sang, dans les Tranchantes."}},
[10784] = {"Anéantir le camp Masse-Sanglante",nil, {"Tuez 10 Marteleurs de la Masse-Sanglante et 5 Démonistes de la Masse-Sanglante."}},
[10785] = {"C'est un piège !",nil, {"Apportez le Piège de feu draconique à Rexxar au bastion des Sire-tonnerre dans les Tranchantes."}},
[10786] = {"Carnage à Roche'mok",nil, {"Massacrez 5 Brutes de Roche'mok et 3 Chamans de Roche'mok, puis retournez voir Tor'chunk Doublegriffes au bastion des Sire-tonnerre, dans les Tranchantes."}},
[10788] = {"Retourner voir Talionia",nil, {"Parlez à Talionia à Lune-d'argent."}},
[10789] = {"Retourner voir Carendin Halgar",nil, {"Parlez à Carendin Halgar à Fossoyeuse."}},
[10790] = {"Retourner voir Gan’rul Œil-sanglant",nil, {"Parlez à Gan’rul Œil-sanglant à Orgrimmar."}},
[10791] = {"Accueillir l'esprit du loup",nil, {"Utilisez l'Encens de cérémonie près des bougies dans la hutte de Malukaz et assistez à la cérémonie. Quand elle sera terminée, parlez à nouveau à Malukaz."}},
[10792] = {"Que Zeth'Gor brûle !",nil, {"Tuez les Péons de l'Orbite sanglante pour prendre leurs torches, allumez celles-ci à la forge de Zeth'Gor, puis jetez les torches enflammées sur les bâtiments de Zeth'Gor."}},
[10793] = {"Le journal de Val'zareq : Des présages de guerre",nil, {"Trouvez Marcus Auralion, grand général des Sha'tar, au Guet cramoisi.nnCherchez-le à l'intérieur d'une prison cristalline."}},
[10794] = {"Les voleurs de la Main-Brisée",nil, {"Parlez à Shenthul à Orgrimmar."}},
[10795] = {"Un rendez-vous avec Dorgok",nil, {"Le commandant Haephus Lamuraille vous a demandé de tuer Dorgok au camp Masse-sanglante. Revenez le voir au Bosquet éternel dans les Tranchantes quand ce sera fait."}},
[10796] = {"Anéantissez le camp Masse-sanglante !",nil, {"Le lieutenant Beautemps vous a <chargé/chargée> de tuer 10 Marteleurs de la Masse-sanglante et 5 Démonistes de la Masse-sanglante. Retournez ensuite la voir au Bosquet éternel dans les Tranchantes."}},
[10797] = {"Une faveur des gronns",nil, {"Apportez la Faveur de Gorgrom au commandant Haephus Lamuraille au Bosquet éternel, dans les Tranchantes."}},
[10798] = {"Rendre visite au baron",nil, {"Parlez au Baron Sablecrin au Cercle de sang, dans les Tranchantes."}},
