[10810] = {"Le masque endommagé",nil, {"Apporter le Masque endommagé à O'Mally Rossecoquin dans la sylve Ruuan."}},
[10811] = {"Trouvez Neltharaku",nil, {"Partez à la recherche de Neltharaku, le protecteur du Vol de l’Aile-du-Néant."}},
[10812] = {"Le masque mystère",nil, {"Apportez le Masque mystère au seigneur sauvage Antelarion au Bosquet éternel, dans les Tranchantes."}},
[10813] = {"Les yeux de Grillok",nil, {"Utilisez l'Éclat de Zezzak pour capturer un Oeil de Grillok, puis approchez-vous du Chaudron de Zezzak pour l'extraire. Une fois que ce sera fait, rendez son éclat à Zezzak."}},
[10814] = {"L'histoire de Neltharaku",nil, {"Parlez à Neltharaku et écoutez son histoire."}},
[10815] = {"Le journal de Val'zareq : Des présages de guerre",nil, {"Trouvez Marcus Auralion, grand général des Sha'tar, au Guet cramoisi.nnCherchez-le à l'intérieur d'une prison cristalline."}},
[10816] = {"La reconquête des terres sacrées",nil, {"Le redresseur de torts Aluumen veut que vous tuiez 8 Pourfendeurs ombrelunes, 8 Élus ombrelunes et 4 Tisseurs d’ombre ombrelunes.nnAccomplir des quêtes pour l’Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10817] = {"La grande vindicte",nil, {"Varen le Récupérateur veut que vous tuiez 8 Pourfendeurs ombrelunes, 8 Élus ombrelunes et 4 Tisseurs d’ombre ombrelunes.nnAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l’Aldor."}},
[10818] = {"Le baron Sablecrin veut vous voir",nil, {"Parlez au Baron Sablecrin au Cercle de sang, dans les Tranchantes."}},
[10819] = {"Le masque à gaz de Gangrelige",nil, {"Portez le Masque à gaz de Gangrelige et utilisez le Communicateur de la Légion au Camp de Forge : Colère, dans les Tranchantes."}},
[10820] = {"Ton ennemi tu tromperas",nil, {"Tuez 4 Domestiques Forge-ruine et 4 Ingénieurs Forge-ruine."}},
[10821] = {"Vous êtes virée !",nil, {"Tuez Vocifuneste."}},
[10822] = {"Une chevalière Solfurie",nil, {"Even the smallest contribution to our cause is noted, <nom>. Our enemies are many, but we shall prevail!"}},
[10823] = {"De nouvelles chevalières Solfurie",nil, {"How fares the battle against Kael's forces?"}},
[10824] = {"Les chevalières Solfurie",nil, {"La Mage de bataille Vyara du Sanctum des Étoiles veut que vous lui apportiez 10 Chevalières Solfurie.nnAccomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de la faction Aldor."}},
[10825] = {"La vérité toute nue",nil, {"Apportez l'Orbe des Grishna au gardien des arbres Chawn à la sylve Ruuan dans les Tranchantes."}},
[10826] = {"Les marques de Sargeras",nil, {"Remettez 10 Marques de Sargeras au Messager Saronen, dans la Vallée d’Ombrelune.nnAccomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10827] = {"De nouvelles marques de Sargeras",nil, {"Do you bear more of our enemies' insignias, <class>? The Burning Legion continues to be a threat and we continue to pursue them anywhere that we find them."}},
[10828] = {"Une marque de Sargeras",nil, {"I'll take any leftover marks you might have. Do not worry, your actions will be duly noted."}},
[10829] = {"Arbrobole doit savoir",nil, {"Parlez à Arbrobole, un Ancien du savoir qui vit dans le Bois aux corbeaux, dans les Tranchantes."}},
[10830] = {"Exorciser les arbres",nil, {"Collectez 5 Orbes grishnath et 5 Plumules redoutables puis combinez-les pour en faire des Plumes d'exorcisme. Utilisez ces plumes pour procéder à l'exorcisme et tuer 5 Esprits Kui-kui sortis des Barbes-feuillues du Bois aux corbeaux."}},
[10831] = {"Devenir un tailleur d'étoffe lunaire",nil, {"Utilisez le Carré de tisse-néant imprégné en vous tenant près du puits de lune du Refuge cénarien pour créer un Échantillon d'étoffe lunaire primordiale. Apportez l'échantillon terminé à Nasmara Lunechant dans la Ville basse de Shattrath.nn*ATTENTION !* Vous ne pouvez choisir qu'une spécialisation en couture."}},
[10832] = {"Devenir un tailleur de feu-sorcier",nil, {"Apportez un échantillon d'Essence d'âme en peine du Néant à Gidge Tisse-sort dans la Ville basse de Shattrath.nn*ATTENTION !* Vous ne pouvez choisir qu'une spécialisation en couture."}},
[10833] = {"Devenir un tailleur de tisse-ombre",nil, {"Utilisez le Cristal des ombres profondes près de l'autel des Ombres pour renforcer votre harmonisation avec l'autel, puis retournez voir Andrion Sombrefileur dans la Ville basse de Shattrath.nn*ATTENTION !* Vous ne pouvez choisir qu'une spécialisation en couture."}},
[10834] = {"Grillok « Sombrœil »",nil, {"Tuez Grillok Sombrœil et rapportez le Couvre-œil de Grillok à Zezzak au Poste de Brise-échine."}},
[10835] = {"L'apothicaire Antonivitch",nil, {"Apportez les Découvertes d'Albreck à l'Apothicaire Antonivitch à Thrallmar."}},
[10836] = {"Infiltrer la forteresse Gueule-de-dragon",nil, {"Neltharaku, qui vole au-dessus des Champs de l’Aile-du-Néant dans la Vallée d’Ombrelune, veut que vous tuiez 15 Orcs Gueule-de-dragon.nnN’importe quels Orcs de la Forteresse Gueule-de-dragon feront l’affaire."}},
[10837] = {"Vers l'escarpement de l'Aile-du-Néant !",nil, {"Neltharaku, qui vole au-dessus des champs de l’Aile-du-Néant dans la vallée d’Ombrelune, veut que vous ramassiez 12 cristaux vignéants à l’escarpement de l’Aile-du-Néant."}},
[10838] = {"Le scrutateur démonien",nil, {"Utilisez le Scrutateur démonien dans le Bassin des Flammes infernales, attendez qu'il soit harmonisé, puis faites un relevé.nnRapportez le Scrutateur démonien et le Relevé du scrutateur démonien à l'Apothicaire Antonivitch à Thrallmar."}},
[10839] = {"Voile Skith : Sombrepierre de Terokk",nil, {"Utilisez le Bâtonnet de purification sur la Sombrepierre de Terokk au Voile Skith puis retournez auprès de Kirrik l'Eveillé à la Caravane des réfugiés, dans le désert des Ossements."}},
[10840] = {"Le tombeau des lumières",nil, {"Le Grand prêtre Orglum veut que vous vous rendiez au Tombeau des lumières et que vous tuiez 10 Néantomanciens éthériens et 10 Pilleurs éthériens avant de revenir le voir à la Caravane de réfugiés."}},
[10841] = {"Le messager vengeur",nil, {"[PH] Activer le bidule."}},
[10842] = {"Âmes vengeresses",nil, {"Le grand prêtre Orglum veut que vous vous rendiez au Tombeau des lumières et que vous vainquiez 5 Draeneï vengeurs."}},
[10843] = {"Depuis des temps immémoriaux…",nil, {"Tuez Gnosh Brognat, le chef des Ogres de l'avant-poste Flèchelame."}},
[10844] = {"Le camp de forge Colère",nil, nil},
[10845] = {"Tuer la mère des couvées",nil, {"Tuez Aile-de-Peur."}},
[10846] = {"Comprendre les Mok'Nathal",nil, {"Tuez Vekh, chef des arakkoa vekh'nir."}},
[10847] = {"Les yeux de Skettis",nil, {"Apportez l'Œil de Reskk et l'Œil de Shienor à Rilak le Racheté dans la Ville basse de Shattrath."}},
[10848] = {"Voile Rhaze : Un mal sans vie",nil, {"Tuez 4 Esprits Serres-mort, 4 Esprits hurlants et 2 Esprits corbeaux puis revenez voir Kirrik l'Eveillé à la Caravane des réfugiés."}},
[10849] = {"À la recherche de Kirrik",nil, {"Apportez la Missive de Rilak à Kirrik l'Eveillé à la Caravane des réfugiés, dans le désert des Ossements."}},
[10850] = {"De l'essence de Néant dans un moteur gangrefeu",nil, {"Vous découvrez une ouverture grâce à laquelle le carburant est versé dans le saccageur gangrené."}},
[10851] = {"Les totems de mon ennemi",nil, {"Collectez 5 Totems flèchelame."}},
[10852] = {"Les amis perdus",nil, {"Ethan veut que vous trouviez 12 de ses amis disparus au Voile Skith et que vous reveniez ensuite le voir à la Caravane des réfugiés."}},
[10853] = {"L'appel des esprits",nil, {"Utilisez les Totems d'appel des esprits pour collecter 8 Esprits de drakes du Néant inférieurs."}},
[10854] = {"La force de Neltharaku",nil, {"Neltharaku, qui vole au-dessus des champs de l’Aile-du-Néant dans la vallée d’Ombrelune, veut que vous libériez 5 Drakes de l'Aile-du-Néant asservis."}},
[10855] = {"Les saccageurs gangrenés ne passeront pas !",nil, {"Récupérez 5 mesures de Gaz de Néant condensé auprès des Mekgénieurs gan'arg de la Base de forge : Oubli, au nord-ouest de la Foudreflèche, et chargez-les dans un Saccageur gangrené inactif proche. Revenez voir le Traqueur-du-Néant Nauthis quand vous aurez terminé."}},
[10856] = {"La meilleure défense",nil, {"Le Traqueur-du-Néant Nauthis veut que vous tuiez 12 Porte-courroux à la Base de forge Géhenne, au nord de la Foudreflèche."}},
[10857] = {"Téléportez-moi ça !",nil, {"Le Traqueur-du-Néant Nauthis veut que utilisiez le Bâtonnet d'interférence mentale sur les mo’arg du Camp de forge Géhenne, et utilisez leur capacité pour Faire exploser le téléporteur sur les trois téléporteurs du camp de forge."}},
[10858] = {"Karynaku",nil, {"Trouvez Karynaku à la Forteresse Gueule-de-dragon."}},
[10859] = {"Réunir les orbes",nil, {"Utilisez le Totem collecteur d'orbes pour collecter 15 Orbes de lumière razaani."}},
[10860] = {"Délices de Mok'Nathal",nil, {"La matrone Varah vous a donné la liste des ingrédients nécessaires à la préparation de ses deux plats préférés, sur laquelle figurent 3 Côtelettes de raptor et 3 Chairs de serpent. Apportez-les-lui à Mok'Nathal dans les Tranchantes."}},
[10861] = {"Voile Lithic : Frappe préventive",nil, {"Cassez les œufs maudits au Voile Lithic. Rachetez 3 Oisillons et tuez 3 Oisillons maléfiques avant de retourner auprès de Kirrik l'Eveillé à la Caravane des réfugiés."}},
[10862] = {"Se rendre à la Horde",nil, {"Rokag veut que vous trouviez Rilak le Racheté à Shattrath et que vous exigiez que lui et son peuple se rendent à la Horde."}},
[10863] = {"Les secrets des arakkoa",nil, {"Dame Dena Kennedy veut que vous alliez trouver Rilak le Racheté à Shattrath pour découvrir les secrets des arakkoa qu'il est prêt à partager."}},
[10864] = {"Le fardeau des âmes",nil, {"Tuez 20 Orcs de la Main brisée, puis retournez voir l'Apothicaire Antonivitch à Thrallmar."}},
[10865] = {"Informez Leoroxx !",nil, {"Parlez à Leoroxx de ce que préparent les éthériens razaani. Vous le trouverez au village de Mok'Nathal dans les Tranchantes."}},
[10866] = {"Zuluhed le Fourbu",nil, {"Tuez Zuluhed le Fourbu et récupérez la Clé de Zuluhed. Utilisez-la sur les Chaînes de Zuluhed pour libérer Karynaku."}},
[10867] = {"La seule réponse possible",nil, {"Récupérez la Collection d'âmes."}},
[10868] = {"Le sentier de la guerre arakkoa",nil, {"La conseillère Faila vous demande de tuer 14 Serrénites shienor et 6 Ensorceleurs shienor avant de revenir la voir au Fort des Brise-pierres."}},
[10869] = {"Restreindre la nuée",nil, {"Le lieutenant Gravillemartel vous demande de tuer 14 Serrénites shienor et 6 Ensorceleurs shienor avant de revenir le voir au Bastion allérien."}},
[10870] = {"Allié du vol du Néant",nil, {"Permettez à Karynaku de vous ramener auprès de Mordenai dans les Champs de l'Aile-du-Néant."}},
[10871] = {"Allié du vol du Néant",nil, {"Permettez à Karynaku de vous ramener auprès de Mordenai dans les Champs de l'Aile-du-Néant."}},
[10872] = {"Zuluhed le Fourbu",nil, {"Tuez Zuluhed le Fourbu et récupérez la Clé de Zuluhed. Utilisez-la sur les Chaînes de Zuluhed pour libérer Karynaku."}},
[10873] = {"Pris dans la nuit",nil, {"L'Éclaireur Navrin veut que vous vous rendiez sur la crête de Toile-néant et que vous libériez 6 Guerriers sha'tar entoilés. Revenez ensuite le voir à l'Avant-poste sha'tar."}},
[10874] = {"Voile Shalas : Feux de signal",nil, {"Kirrik l'Eveillé de la Caravane des réfugiés vous demande d'éteindre les quatre feux de signal du Voile Shalas."}},
[10875] = {"Se présenter à Nazgrel",nil, {"Parlez avec Nazgrel à Thrallmar, dans la Péninsule des Flammes infernales."}},
[10876] = {"Le pied de la citadelle",nil, {"Tuez le Commandant de corps Gorax, placez le Défi de la Horde sur son cadavre, tuez la Main de Kargath, puis rapportez la Tête de la Main de Kargath à Nazgrel dans Thrallmar."}},
[10877] = {"La relique de l'effroi",nil, {"Chênuin veut que vous partiez vers l'est pour la Caravane abandonnée afin de récupérer la Relique de l'effroi. Revenez voir Chênuin quand cette tâche sera achevée."}},
[10878] = {"Avant que l'ombre...",nil, {"Mekeda de la Caravane de réfugiés de la forêt de Terokkar veut que vous tuiez 8 Tirailleurs de la Cabale, 4 Tisse-sorts de la Cabale et 2 Initiés de la Cabale."}},
[10879] = {"L'offensive de Skettis",nil, {"Repoussez les attaquants et parlez à Rilak le Racheté."}},
[10880] = {"Les ordres de la Cabale",nil, {"Remettez les Ordres de la Cabale à Mekeda au Camp de réfugiés de la forêt de Terokkar."}},
[10881] = {"La tombe des ombres",nil, {"Rendez-vous dans la Tombe des ombres, à l’ouest de la Caravane de réfugiés. Récupérez-y le Drapé d’Arunen, le Marteau de K’alen et le Parchemin d’Atalor. Lorsque vous aurez terminé, retournez voir Mekeda à la Caravane de réfugiés."}},
[10882] = {"Un messager funeste",nil, {"Vous avez été <chargé/chargée> d'aller sur le satellite d'Arcatraz du donjon de la Tempête et de tuer le Messager Cieuriss. Revenez voir A'dal à la Terrasse de la Lumière de Shattrath quand ce sera fait."}},
[10883] = {"La clé de la Tempête",nil, {"Parlez à A'dal à Shattrath."}},
[10884] = {"L'épreuve des naaru : Miséricorde",nil, {"A'dal de Shattrath vous demande de prendre la Hache inutilisée du bourreau dans les Salles brisées de la citadelle des Flammes infernales.nnCette quête doit être accomplie en mode de difficulté du donjon Héroïque."}},
[10885] = {"L'épreuve des naaru : Force",nil, {"A'dal de Shattrath vous demande de récupérer le Trident de Kalithresh et l'Essence de Marmon.nnCette quête doit être accomplie en mode de difficulté du donjon Héroïque."}},
[10886] = {"L'épreuve des naaru : Ténacité",nil, {"A'dal de Shattrath vous demande de sauver Milhouse Tempête-de-Mana de l'Arcatraz du donjon de la Tempête.nnCette quête doit être accomplie en mode de difficulté du donjon Héroïque."}},
[10887] = {"Sortir du tombeau",nil, {"Aidez Akuno a trouver le chemin de la Caravane de réfugiés de la forêt de Terokkar. Lorsque vous aurez terminé cette quête, parlez à Mekeda."}},
[10888] = {"L'épreuve des naaru : Magtheridon",nil, {"A'dal de Shattrath vous demande de tuer Magtheridon."}},
[10889] = {"Retour à Shattrath",nil, {"Rendez-vous à Shattrath et parlez à Rilak le Racheté dans la Ville Basse."}},
[10891] = {"L'armure impériale en plaques",nil, {"Parlez à Derotain Siroteboue à Gadgetzan."}},
[10892] = {"L'armure impériale en plaques",nil, {"Parlez à Derotain Siroteboue à Gadgetzan."}},
[10893] = {"Longue-queue est le pivot",nil, {"Éliminez Draaca Longue-queue, le chef des sectateurs du Ver dans le Tunnel Crâne-du-ver."}},
[10894] = {"Le guetteur du Crâne-du-ver",nil, {"Apportez la Note de rendez-vous au Guetteur Lunombrage à l'entrée du tunnel Crâne-du-ver dans les Tranchantes."}},
[10895] = {"Que Zeth'Gor brûle !",nil, {"Utilisez les Balises fumigènes sur chacune des tours de Zeth'Gor, puis retournez voir le Chef d'escadrille Dabir'ee à la Halte de l'expédition."}},
[10896] = {"Les protecteurs infestés",nil, {"Lakotae veut que vous tuiez 25 Mites des bois puis que vous reveniez le voir à la Caravane de réfugiés. On peut trouver les mites à l'intérieur des corps des Enragés des forêts pourrissants et des Marche-racine infestés."}},
[10897] = {"Maître des potions",nil, {"Lauranna Thar'well veut que vous vous rendiez dans la Botanica du donjon de la Tempête et que vous récupériez le Guide pratique du botaniste sur le Grand botaniste Freywenn. De plus, elle veut que vous lui rameniez 5 Potions de super-soins, 5 Potions de super-mana et 5 Potions de sommeil sans rêve majeures.nn*ATTENTION !* Vous ne pouvez choisir qu’une seule spécialité d’alchimie."}},
[10898] = {"Aile-du-ciel",nil, {"Escortez Aile-du-ciel et allez voir Rilak le Racheté à Shattrath."}},
[10899] = {"Maître de la transmutation",nil, {"Zarevhi, de la Foudreflèche du Raz-de-Néant, veut que vous lui rameniez 4 Pouvoirs primordiaux.nn*ATTENTION !* Vous ne pouvez choisir qu’une seule spécialité d’alchimie."}},
[10900] = {"La marque de Vashj",nil, nil},
[10901] = {"Le casse-tête de Kar'desh",nil, {"Skar’this l’Hérétique, dans les Enclos aux esclaves héroïques du Réservoir de Glissecroc, veut que vous lui apportiez la Chevalière terrestre et la Chevalière flamboyante."}},
[10902] = {"Maître des élixirs",nil, {"Partez pour les Grottes du temps dans le Noir Marécage et procurez-vous 10 Essences d'infinité sur les Seigneurs des failles et Gardiens des failles. Apportez-les avec 5 Élixirs de défense majeure, 5 Élixirs de maîtrise et 5 Élixirs d'agilité majeure à Lorokeem dans la Ville basse de Shattrath.nn*ATTENTION !* Vous ne pouvez choisir qu'une spécialisation en alchimie."}},
[10903] = {"Retour au bastion de l'Honneur",nil, {"Parlez à Klatu, à l’auberge du Bastion de l’Honneur."}},
[10904] = {"La récolte des munitions gangrenées",nil, {"Récupérez 5 Boulets de gangrecanon."}},
[10905] = {"Maître des potions",nil, {"Parlez à Lauranna Thar'well au Refuge cénarien dans le Marécage de Zangar.nn*ATTENTION !* Vous ne pouvez choisir qu'une spécialisation en alchimie."}},
[10906] = {"Maître des élixirs",nil, {"Parlez à Lorokeem dans la Ville basse de Shattrath.nn*ATTENTION !* Vous ne pouvez choisir qu’une spécialisation en alchimie."}},
[10907] = {"Maître de la transmutation",nil, {"Parlez à Zarevhi à la Foudreflèche au Raz-de-Néant.nn*ATTENTION !* Vous ne pouvez choisir qu'une spécialisation en alchimie."}},
[10908] = {"Parler à Rilak le Racheté",nil, {"Kirrik l'Éveillé veut que vous parliez à Rilak le Racheté dans la Ville basse de Shattrath."}},
[10909] = {"Les esprits gangrenés",nil, {"Klatu au Bastion de l’Honneur vous a demandé d’utiliser la Relique d'anachorète à l’Avant-poste de Gor’gaz, au sud-ouest du Bastion. Vous devrez ensuite tuer des Orcs de la Main-Brisée près de la relique, puis détruire les Esprits gangrenés libérés pour venger le Colonel Jules."}},
[10910] = {"La Porte de la mort",nil, {"Utilisez le Signal druidique à la Porte de la mort pour appeler et parler à un Druide du Bosquet éternel."}},
[10911] = {"Feu à volonté !",nil, {"Utilisez les Munitions naturalisées pour prendre le contrôle des Gangrecanons de la Porte de la mort. Utilisez-les pour détruire le Portail dimensionnel sud et le Portail dimensionnel nord."}},
[10912] = {"Le maître-chien",nil, {"Tuez Baelmon le Maître-chien."}},
[10913] = {"Des funérailles inadéquates",nil, {"Le commandant Ra'vaj de l'Avant-poste shat'ari du désert des Ossements, dans la forêt de Terokkar, veut que vous brûliez 8 Guerriers auchenaï tués et 8 Redresseurs de torts sha'tar tués."}},
[10914] = {"Il nous faut un héros",nil, {"Le commandant Ra’vaj, du Camp de base shat’ari dans le désert des Ossements de la forêt de Terokkar, veut que vous tuiez 12 Initiés auchenaï et 5 Auspices-funestes auchenaï."}},
[10915] = {"L'exarque déchu",nil, {"Le commandant Ra’vaj, du Camp de base shat’ari dans le désert des Ossements de la forêt de Terokkar, veut que vous détruisiez le contenu du Cercueil auchenaï."}},
[10916] = {"À la recherche de chapelets",nil, {"Retrouvez le Chapelet draeneï et rapportez-le à l’Assistant Klatu dans le Bastion de l’Honneur."}},
[10917] = {"La souffrance du paria",nil, {"Apportez 30 Plumes d'arakkoa à Vekax dans la Ville basse de Shattrath."}},
[10918] = {"Plus de plumes",nil, {"Les laquais de Skettis nous surveillent de près, nous autres parias. Si l’un d’entre nous met un pied hors de Shattrath, c’est comme s’il était mort.nnApportez-moi plus de plumes comme preuves de votre victoire sur eux."}},
[10919] = {"Les friandises de Fei Fei",nil, {"Fei Fei vous regarde, avec l'air d'attendre quelque chose..."}},
[10920] = {"Au nom des disparus",nil, {"Le redresseur de torts Haylen veut que vous tuiez 20 Veuves croc-d'effroi, puis que vous reveniez le voir juste à l'ouest de la Crête de Toile-néant."}},
[10921] = {"Terokkarantula",nil, {"Le redresseur de torts Haylen veut que vous alliez tuer Terokkarantula et que vous reveniez le voir à l'ouest de la crête de Toile-néant."}},
[10922] = {"En fouillant dans les os",nil, {"Escortez Leglas et son équipe et protégez-les pendant les fouilles. Si vous réussissez, retournez voir Nanowitz à l'Avant-poste sha'tari."}},
[10923] = {"L'approche du mal",nil, {"Chênuin veut que vous emportiez la Relique de l'effroi au Monticule grouillant. Là-bas, tuez des nécrorateurs et des auspices-funestes auchenaï pour collecter 20 Crânes du destin. Trouvez ensuite le Cercle d'invocation du Monticule grouillant et utilisez la Relique de l'effroi pour invoquer et détruire Teribus le Maudit. Revenez voir Chênuin quand ce sera fait."}},
[10924] = {"La part du diabl-otin !",nil, {"Sab'aoth de la Cosmovrille veut 8 fioles de Sang de poursuivant dimensionnel."}},
[10925] = {"L'approche du mal",nil, nil},
[10926] = {"Retour au camp de base sha'tari",nil, {"Le redresseur de torts Haylen veut que vous retourniez au Camp de base sha'tari, dans le Désert des Ossements et que vous y parliez à l'Éclaireur Navrin."}},
[10927] = {"Tuer les rampantes",nil, {"La sentinelle Murmelune, à côté du tunnel entre le marécage de Zangar et les Tranchantes, vous demande de tuer 6 Rampantes des cavernes et de vous présenter au commandant Ombreciel à Sylvanaar."}},
[10928] = {"Tuer les rampantes",nil, {"Le Grunt Grahk, à côté du tunnel entre le marécage de Zangar et les Tranchantes, veut que vous tuiez 6 Rampantes des cavernes, puis que vous alliez rendre compte à Tor'chunk Doublegriffes au bastion des Sire-tonnerre."}},
[10929] = {"Martelheurt",nil, {"Nanowitz, du Camp de base sha’tari du désert des Ossements, veut que vous lui rameniez 8 Carcasses de cribleurs des ossements adultes."}},
[10930] = {"Le gros ver des ossements",nil, {"Nanowitz, du Camp de base sha’tari du désert des Ossements, veut que vous trouviez des Organes d’énorme ver des ossements.nnIl veut aussi qu’on lui rapporte son Martelheurt."}},
[10931] = {"Retour à l’Exodar",nil, {"Parlez à Caedmos à l’Exodar."}},
[10932] = {"Retour à l'Exodar",nil, {"Parlez à Caedmos à l'Exodar."}},
[10933] = {"Retour à Lune-d’Argent",nil, {"Parlez à Aldrae à Lune-d’Argent."}},
[10934] = {"Retour à Lune-d’Argent",nil, {"Parlez à Aldrae à Lune-d’Argent."}},
[10935] = {"L'exorcisme du colonel Jules",nil, {"Parlez à l’Anachorète Barada. Utilisez le chapelet pour l’aider pendant le rituel, puis parlez au Colonel Jules quand il sera sauvé. Pour finir, retournez voir l’Assistant Klatu."}},
[10936] = {"Trollemort vous cherche !",nil, {"L’Assistant Klatu vous a indiqué que le Commandant de corps Danath Trollemort vous cherchait. Allez lui parler à la caserne du Bastion de l’Honneur dans la Péninsule des Flammes infernales."}},
[10937] = {"Détruisez le maître de manœuvre",nil, {"Le Commandant de corps Danath Trollemort vous a ordonné de tuer au plus vite le Maître de manœuvre Zurok. Retournez voir le commandant de corps au Bastion de l'Honneur dans la Péninsule des Flammes infernales une fois que le maître de manœuvre sera mort."}},
[10938] = {"Suite de Bénédictions de Sombrelune",nil, {"Rapporter la suite de Bénédictions à la foire de Sombrelune quand elle reviendra en ville."}},
[10939] = {"Suite de Tempêtes de Sombrelune",nil, {"Rapporter la suite de Tempêtes à la foire de Sombrelune quand elle reviendra en ville."}},
[10940] = {"Suite de Furies de Sombrelune",nil, {"Rapporter la suite de Furies à la foire de Sombrelune quand elle reviendra en ville."}},
[10941] = {"Suite de Déraison de Sombrelune",nil, {"Rapporter la suite de Déraison à la foire de Sombrelune quand elle reviendra en ville."}},
[10942] = {"La Semaine des enfants",nil, {"Utiliser le Sifflet d'orphelin elfe de sang pour parler à l'enfant que vous surveillerez durant la Semaine des enfants."}},
[10943] = {"La Semaine des enfants",nil, {"Utiliser le Sifflet d'orphelin draeneï pour parler à l'enfant que vous surveillerez durant la Semaine des enfants."}},
[10944] = {"Un secret compromis",nil, {"Allez à la Cage de la gardienne dans la vallée d'Ombrelune et parlez avec Akama."}},
[10945] = {"Hch'uu et le peuple champignon",nil, {"Emmenez votre orpheline, Salandria, à Sporeggar dans le marécage de Zangar. N’oubliez pas de l’appeler si elle n’est pas présente lorsque vous arrivez. Puis aller parler à Hch'uu."}},
[10946] = {"La ruse des Cendrelangues",nil, {"Pénétrez dans le Donjon de la Tempête et tuez Al'ar en portant la Capuche de Cendrelangue. Retournez voir Akama dans la Vallée d'Ombrelune une fois que ce sera fait."}},
[10947] = {"Un artéfact du passé",nil, {"Allez aux Grottes du temps en Tanaris, et rendez-vous à la Bataille du mont Hyjal. Quand vous y serez, triomphez de Rage Froidhiver et rapportez le Phylactère chronophasé à Akama dans la vallée d'Ombrelune."}},
[10948] = {"L'âme otage",nil, {"Rendez-vous à Shattrath pour soumettre la requête d'Akama à A'dal."}},
[10949] = {"L'entrée dans le Temple noir",nil, {"Rendez-vous à l'entrée du Temple noir dans la vallée d'Ombrelune et allez parler à Xi'ri."}},
[10950] = {"Auchindoun et le Cercle d'observance",nil, {"Emmenez votre orpheline, Dornaa, à la Pierre de rencontre du Cercle d'observance, au centre d'Auchindoun. Auchindoun est situé au centre du désert des Ossements de la forêt de Terokkar. N’oubliez pas de l’appeler si elle n’est pas présente lorsque vous arrivez."}},
[10951] = {"Une visite à la Porte des ténèbres",nil, {"Emmenez votre orpheline, Salandria, en haut de l'escalier du Destin, pour qu'elle puisse passer devant la Porte des ténèbres dans la péninsule des Flammes infernales. N’oubliez pas de l’appeler si elle n’est pas présente lorsque vous arrivez."}},
[10952] = {"Une visite à la Porte des ténèbres",nil, {"Emmenez votre orpheline, Dornaa, en haut de l'escalier du Destin pour qu'elle passe devant la Porte des ténèbres dans la péninsule des Flammes infernales. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez."}},
[10953] = {"Une visite au Trône des éléments",nil, {"Emmenez votre orpheline, Salandria, au Trône des éléments à Nagrand. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez. Ensuite, allez parler à l'Élémentaliste Sharvak."}},
[10954] = {"Jheel est au point d'ancrage Aeris !",nil, {"Emmenez votre orpheline, Dornaa, au Point d'ancrage Aeris en Nagrand. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez. Ensuite, parlez avec Jheel."}},
[10955] = {"Morthis Murmedaile",nil, {"Allez parler à Morthis Murmedaile au Refuge cénarien dans le marécage de Zangar."}},
[10956] = {"Le Siège du naaru",nil, {"Emmenez votre orpheline, Dornaa, voir O'ros au pied du Siège du naaru dans l'Exodar, sur l'île de Brume-azur. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez.nnRappelez-vous que vous pouvez utiliser votre carte à l'intérieur de la ville."}},
[10957] = {"La rédemption du Cendrelangue",nil, {"Aidez Akama à reprendre le contrôle de son âme en vainquant l’Ombre d’Akama dans le Temple noir. Retournez voir le Voyant Kanai quand ce sera fait."}},
[10958] = {"À la recherche des Cendrelangues",nil, {"Trouvez les Ligemorts d’Akama à l’intérieur du Temple noir."}},
[10959] = {"La chute du Traître",nil, {"Le voyant Kanai veut que vous vainquiez Illidan à l'intérieur du Temple noir."}},
[10960] = {"Quand je serai grande...",nil, {"Emmenez votre orpheline, Salandria, voir les maîtres des paladins sur la Place des Pérégrins, dans Lune-d'argent. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez. Ensuite, parlez avec Dame Liadrin, la matriarche des chevaliers de sang.nnRappelez-vous que vous pouvez utiliser votre carte à l'intérieur de la ville, et parler aux gardes pour trouver les maîtres des paladins."}},
[10961] = {"La protection de l'éveil",nil, {"Apportez 10 Fleurs des tourbières, 10 Lichens anciens et 10 Glauriers à Morthis Murmedaile, au Refuge cénarien."}},
[10962] = {"Il est temps de visiter les Grottes",nil, {"Emmenez votre orpheline, Dornaa, devant Zaladormu, le dragon qui se trouve sur l'estrade au milieu des Grottes du temps. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez.nnEnsuite, achetez-lui une Figurine de dragon auprès de l'Intendant des Gardiens du temps, Alurmi, près du fond du tunnel d'entrée."}},
[10963] = {"Il est temps de visiter les Grottes",nil, {"Emmenez votre orpheline, Salandria, devant Zaladormu, le dragon qui se trouve sur l'estrade au milieu des Grottes du temps. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez.nnEnsuite, achetez-lui une Figurine de dragon auprès de l'Intendant des Gardiens du temps, Alurmi, près du fond du tunnel d'entrée."}},
[10964] = {"Le réveil du dormeur",nil, {"Utilisez le Gardien de l'éveil pour aider Clintar Marcherêve à sortir du Rêve d'émeraude. Une fois qu'il a repris connaissance, parlez-lui."}},
[10965] = {"Plus qu'un simple rêve",nil, {"Protégez l'Esprit de Clintar Marcherêve pendant qu'il recueille les reliques d'Aviana, puis allez trouver la Garderêve Lurosa."}},
[10966] = {"Retour à l'orphelinat",nil, {"Ramenez votre protégée à l'orphelinat de Shattrath, en rendant le Sifflet d'orphelin draeneï à Miss Hérikorde, directrice de l'orphelinat, dans la Ville basse."}},
[10967] = {"Retour à l'orphelinat",nil, {"Ramenez votre protégée à l'orphelinat de Shattrath, en rendant le Sifflet d'orphelin elfe de sang à Miss Hérikorde, directrice de l'orphelinat, dans la Ville basse."}},
[10968] = {"En visite chez le long-voyant",nil, {"Emmenez votre orpheline, Dornaa, rendre visite au Long-voyant Nobundo dans le Hall de Cristal à l'intérieur de l'Exodar, qui se trouve sur l'Île de Brume-azur. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez.nnRappelez-vous que vous pouvez consulter votre carte même à l'intérieur de la ville."}},
[10969] = {"Rechercher Ameer",nil, {"Trouvez le Commandant Ameer au Poste de garde du Protectorat."}},
[10970] = {"Une mission miséricordieuse",nil, {"Procurez-vous la Clé de la prison de l'Ethereum récupérée pour le Commandant Ameer au Poste de garde du Protectorat."}},
[10971] = {"Secrets de l'Ethereum",nil, {"Le Commandant Ameer, au Poste de garde du Protectorat au Raz-de-Néant, veut que vous lui apportiez une Plaque d'identité de prisonnier de l'Ethereum."}},
[10972] = {"Répertorier les prisonniers de l'Ethereum",nil, {"Il me faut plus de plaques d'identité, <nom>. Pour l'instant, j'ai découvert qu'il ne s'agit pas seulement d'un moyen de recenser les prisonniers, mais aussi d'en indiquer la localisation !nnUne fois le code décrypté, nous pourrons déterminer exactement où nos alliés sont retenus et les libérer avec le minimum de réaction hostile."}},
[10973] = {"Un millier de mondes",nil, {"Le Commandant Ameer, au Poste de garde du Protectorat au Raz-de-Néant, veut que vous lui apportiez 5 Clés de la prison de l'Ethereum."}},
[10974] = {"Les chambres de stase de Bash'ir",nil, {"Utilisez la Tablette-clé de l'Ethereum - Alpha pour ouvrir la Chambre de stase alpha au Point d'ancrage de Bash'ir dans les Tranchantes.nnRapportez les Indices de l'alpha à l'Image du commandant Ameer au Point d'ancrage de Bash'ir."}},
[10975] = {"La purge des chambres de Bash'ir",nil, {"Nous sommes dans une impasse, <nom>. Les données que vous avez recueillies nous ont fourni assez d'éléments pour libérer nos alliés détenus dans les petites prisons du Raz-de-Néant, mais nous devons aller de l'avant et éradiquer la plus grave menace que constitue l'Ethereum au point d'ancrage de Bash'ir.nnJoignez-vous à la bataille contre l'Ethereum ! Apportez-moi des clés de prison de l'Ethereum et je vous forgerai des clés de chambres de stase que vous pourrez utiliser à Bash'ir."}},
[10976] = {"La Marque du roi-nexus",nil, {"L'Image du commandant Ameer au Point d'ancrage de Bash'ir dans les Tranchantes vous demande de lui rapporter une Marque du roi-nexus."}},
[10977] = {"Les chambres de stase des Tombes-mana",nil, {"L'Image du commandant Ameer au Point d'ancrage de Bash'ir dans les Tranchantes vous demande d'utiliser la Clé de la chambre de stase des Tombes-mana dans les Tombes-mana d'Auchindoun.nnCette quête ne peut être accomplie qu'au niveau de difficulté Héroïque."}},
[10978] = {"Retourner voir Morthis Murmedaile",nil, {"Apportez les Reliques d'Aviana à Morthis Murmedaile au Refuge cénarien dans le marécage de Zangar."}},
[10979] = {"En route pour le Bosquet éternel",nil, {"Allez parler à Arthorn Chant-du-vent au Bosquet éternel dans les Tranchantes."}},
[10980] = {"Le Livre du Corbeau",nil, {"Utilisez un Œil de raie de l'éther avec votre Pierre de prophète pour voir les fantômes du culte du corbeau. Cherchez l'esprit de Sai'kkal l'Ancien dans la Cime du vortex et interrogez-le au sujet du livre du corbeau, puis retournez auprès d'Arthorn Chant-du-vent."}},
[10981] = {"La chambre personnelle du prince-nexus Shaffar",nil, {"Seul le plus solide et aguerri des aventuriers répondra à mon appel, <nom>. Si vous souhaitez affronter les horreurs de la seconde chambre de stase des Tombes-mana, je peux vous forger une clé. Apportez-moi une marque du roi-nexus, et je la fabriquerai."}},
[10982] = {"L'œil d'Haramad",nil, {"Vous avez fait beaucoup pour le Protectorat et le Consortium, <nom>. Il est donc bien normal que la première de nos nouvelles clés vous revienne. Avec elle, vous pourrez ouvrir la chambre de stase personnelle de Shaffar quand vous le voudrez, sans que la clé ne disparaisse.nnnnElle est indestructible, et sera à vous pour toujours."}},
[10983] = {"Mog'dorg le Ratatiné",nil, {"Allez voir Mog'dorg le Ratatiné au sommet de l'une des tours qui se trouvent devant le Cercle de sang dans les Tranchantes."}},
[10984] = {"Parler avec l'ogre",nil, {"Allez voir l'Ogre, Grok, dans le quartier de la Ville basse de Shattrath."}},
[10985] = {"Une distraction pour Akama",nil, {"Assurez-vous qu'Akama et Maiev pénètrent bien dans le Temple noir de la Vallée d'Ombrelune une fois que les forces de Xi'ri auront fait diversion."}},
[10986] = {"Des yeux dans le ciel",nil, {"Allez parler à la Gardienne Elaira sur la Crête du Crépuscule, en Nagrand."}},
[10987] = {"La chasse à la crécerelle",nil, {"Utilisez le Filet à crécerelles pour capturer une Crécerelle sauvage, et ramenez-la à la Gardienne Elaira sur la Crête du Crépuscule."}},
[10988] = {"Les pierres du Corbeau",nil, {"Rassemblez 8 Pierres du Corbeau à Skettis et apportez-les à Arthorn Chant-du-vent au Bosquet éternel dans les Tranchantes."}},
[10989] = {"Mog'dorg le Ratatiné",nil, {"Allez voir Mog'dorg le Ratatiné au sommet de la tour devant laquelle se tient Chort, juste devant le Cercle de sang dans les Tranchantes."}},
[10990] = {"L'essence de l'aigle",nil, {"Terrassez le Gardien de l'Aigle et emparez-vous de l'Essence de l'Aigle. Utilisez le Sifflet à crécerelle d'Arthorn pour appeler la Crécerelle d'Arthorn afin de transmettre la nouvelle de votre succès.nnSi vous perdez le Sifflet à crécerelle d'Arthorn, retournez voir Arthorn Chant-du-vent au Bosquet éternel pour le faire remplacer."}},
[10991] = {"L'essence de l'épervier",nil, {"Terrassez le Gardien de l'Épervier et emparez-vous de l'Essence de l'Épervier. Utilisez le Sifflet à crécerelle d'Arthorn pour appeler la Crécerelle d'Arthorn afin de transmettre la nouvelle de votre succès.nnSi vous perdez le Sifflet à crécerelle d'Arthorn, retournez voir Arthorn Chant-du-vent au Bosquet éternel pour le faire remplacer."}},
[10992] = {"L'essence du faucon",nil, {"Terrassez le Gardien du Faucon et apportez l'Essence du faucon à Arthorn Chant-du-vent au Bosquet éternel."}},
[10993] = {"Le retour au Refuge cénarien",nil, {"Apportez le Colis d'Arthorn à Morthis Murmedaile au Refuge cénarien."}},
[10994] = {"La chasse à la pierre de lune",nil, {"Apportez une Pierre de lune de Furie-du-Sud à Morthis Murmedaile au Refuge cénarien."}},
[10995] = {"Grulloc a deux crânes",nil, {"Emparez-vous du Crâne de dragon de Grulloc."}},
[10996] = {"Le coffre au trésor de Maggoc",nil, {"Emparez-vous du Coffre au trésor de Maggoc et apportez-le à Mog'dorg le Ratatiné au sommet de la tour du Cercle de sang dans les Tranchantes."}},
[10997] = {"L'étendard et la manière",nil, {"Emparez-vous de l'Étendard de Skori et apportez-le à Mog'dorg le Ratatiné au sommet de la tour du Cercle de sang dans les Tranchantes."}},
[10998] = {"Le tome de sa voix",nil, {"Emparez-vous du Grimoire infâme de Vim'gol."}},
[11000] = {"Face au Broyeur-d'âmes",nil, {"Récupérez l’Âme de Crânoc."}},
[11001] = {"Triompher du dieu corbeau",nil, {"Tuez le Dieu corbeau et retournez voir Morthis Murmedaile au Refuge cénarien.nnCette quête doit être accomplie en mode de difficulté du donjon Héroïque."}},
[11002] = {"La chute de Magtheridon",nil, {"Rapportez la Tête de Magtheridon au Commandant de corps Danath Trollemort au bastion de l'Honneur dans la péninsule des Flammes infernales."}},
[11003] = {"La chute de Magtheridon",nil, {"Rapportez la Tête de Magtheridon à Nazgrel à Thrallmar dans la péninsule des Flammes infernales."}},
[11004] = {"Un monde d'ombres",nil, {"Séverin souhaite que vous vous aventuriez dans Skettis et que vous récupériez 6 Poussières ombreuses sur les arakkoa qui s'y trouvent."}},
[11005] = {"Les secrets des prêtres de la Serre",nil, {"Procurez-vous un Élixir des ombres auprès de Séverin et utilisez-le pour trouver et tuer le Prêtre de la serre Ishaal, le Prêtre de la serre Skizzik et le Prêtre de la serre Zellek à Skettis. Retournez voir le commandant Adaris lorsque vous aurez accompli cette tâche."}},
[11006] = {"Plus de poussière ombreuse",nil, {"Il semblerait que le commandant n’aie pas été en proie à un délire fiévreux, finalement. Apportez-moi plus de poussière, et je vous préparerai un nouvel élixir."}},
[11007] = {"Kael'thas et la sphère verdoyante",nil, {"Apportez la Sphère verdoyante à A'dal dans Shattrath."}},
[11008] = {"Un déluge de feu sur Skettis",nil, {"Cherchez les Œufs de kaliri monstrueux au sommet des habitations de Skettis, et utilisez les Détonateurs de la Garde-ciel pour les détruire. Revenez voir ensuite le Sergent Doryn de la Garde-ciel."}},
[11009] = {"Le paradis des Ogres",nil, {"Mog'dorg le Ratatiné vous demande d'aller parler à Chu'a'lor à Ogri'la dans les Tranchantes."}},
[11010] = {"Mission de bombardement",nil, {"Utilisez les Bombes de la Garde-ciel pour détruire 15 Piles de boulets de gangrecanon."}},
[11011] = {"Une vigilance éternelle",nil, {"Parlez à Morthis Murmedaile pour recevoir une Pierre de lune imprégnée d'essence."}},
[11012] = {"Serment de sang de l'Aile-du-Néant",nil, {"Nos terres sont perdues, nos enfants emportés dans la nuit, réduits à l’esclavage… ou pire encore.nn<La voix de Mordenai se crispe.>nnL’heure est venue, mon allié. L’heure de reprendre ce qui était nôtre ! Vous avez prouvé que vous êtes un farouche combattant d’une grande bonté d’âme.nnJurez-vous d’aider l’Aile-du-Néant à reconquérir nos terres et à reconstruire nos foyers ? Jurez-vous de veiller sur le vol et d’aider à la renaissance de notre lignée ? Jurez-le, et partons au combat pour notre terre et ceux que nous aimons !"}},
[11013] = {"Au service des Illidari",nil, {"Rendez-vous à l'Escarpement de l'Aile-du-Néant et remettez la Lettre de service illidari au Suzerain Mor'ghor au Campement des Gueules-de-dragon."}},
[11014] = {"Le sous-chef entre en scène",nil, {"Allez parler au Sous-chef Varkule Souffle-de-dragon au Campement des Gueules-de-dragon."}},
[11015] = {"Les cristaux de l'Aile-du-Néant",nil, {"Le Sous-chef Varkule Souffle-de-dragon du Campement des Gueules-de-dragon dans la vallée d'Ombrelune veut que vous lui rapportiez 30 Cristaux de l'Aile-du-Néant."}},
[11016] = {"Des peaux d'écorcheurs mine-néant",nil, {"Le Sous-chef Varkule Souffle-de-dragon du Campement des Gueules-de-dragon dans la vallée d'Ombrelune veut que vous lui rapportiez 35 Peaux d'écorcheurs mine-néant."}},
[11017] = {"Du pollen de pruinéante",nil, {"Le Sous-chef Varkule Souffle-de-dragon du Campement des Gueules-de-dragon dans la vallée d'Ombrelune veut que vous lui rapportiez 40 échantillons de Pollen de pruinéante."}},
[11018] = {"Du minerai de néanticite",nil, {"Le Sous-chef Varkule Souffle-de-dragon du Campement des Gueules-de-dragon dans la vallée d'Ombrelune veut que vous lui rapportiez 40 échantillons de Minerai de néanticite."}},
[11019] = {"Un ami à l'intérieur",nil, {"Vous ne pensiez quand même pas qu'on vous laisserait affronter l'armée des gangr'orcs tout seul, si ?nn<Les yeux de Yarzill scintillent d'une lueur bleue.>nnOui, nous sommes là avec vous, <nom>. À chaque service que vous rendrez aux gangr'orcs dans votre quête pour reprendre les terres de l'Aile-du-Néant, nous serons à vos côtés pour nous assurer qu'ils subissent un contrecoup suffisant.nnIl faudra garder profil bas pour veiller à ne pas attirer l'attention, pendant vos missions de sabotage."}},
[11020] = {"Une mort lente",nil, {"Yarzill le Mercenaire du Campement des Gueules-de-dragon de la Vallée d'Ombrelune veut que vous utilisiez la Viande de mouton de Yarzill associée à des Gangreglandes pour empoisonner 12 Campements de péons Gueules-de-dragon."}},
[11021] = {"L'almanach d'Ishaal",nil, {"Apportez l'Almanach d'Ishaal au Commandant de la Garde-ciel Adaris, au nord de Skettis."}},
[11022] = {"Parler à Mog'dorg",nil, {"Allez parler à Mog'dorg le Ratatiné. Il se trouve au sommet de la tour située du côté est du Cercle de sang dans les Tranchantes."}},
[11023] = {"Bombardez-les encore !",nil, {"Utilisez les Bombes de la Garde-ciel pour détruire 15 Piles de boulets de gangrecanon."}},
[11024] = {"Un allié dans la Ville basse",nil, {"Apportez l'Almanach d'Ishaal à Rilak le Racheté dans la Ville basse, à Shattrath."}},
[11025] = {"Les cristaux",nil, {"Chu'a'lor vous a confié pour mission de collecter 5 Éclats apogides. Apportez-les-lui à Ogri'la au sommet des Tranchantes."}},
[11026] = {"Bannissez les démons",nil, {"Utilisez le Cristal de bannissement pour bannir 15 démons au camp de forge : Courroux ou au camp de forge : Terreur."}},
[11027] = {"Z'avez la sombrerune ?",nil, nil},
[11028] = {"Compte à rebours avant anéantissement",nil, {"Retournez voir le Commandant Adaris de la Garde-ciel au Raie'odrome de Noirvent, dans la Forêt de Terokkar, avec les nouvelles concernant le retour de Terokk."}},
[11029] = {"Un déguisement miteux",nil, {"Utilisez le Déguisement arakkoa miteux pour obtenir les Lignées antagonistes de Sahaak, puis retournez auprès de Hazzik."}},
[11030] = {"Notre fils veut devenir patrouilleur de la Garde-ciel",nil, {"Procurez-vous un Flacon instable de la bête."}},
[11031] = {"Quitter la voie de l'archimage",nil, nil},
[11032] = {"Quitter la voie du protecteur",nil, nil},
[11033] = {"Quitter la voie de l'assassin",nil, nil},
[11034] = {"Quitter la voie du guérisseur",nil, nil},
[11035] = {"Les cieux pas si cléments…",nil, {"Yarzill le Mercenaire du Campement des Gueules-de-dragon de la Vallée d'Ombrelune veut que vous récupériez 10 Reliques de l'Aile-du-Néant."}},
[11036] = {"Marchandise extraordinaire !",nil, {"Apportez les Fruits en conserve au Chef-de-fusée Karlingue dans la Zone 52."}},
[11037] = {"Une vision étrange",nil, {"Parlez au Chef-de-fusée Karlingue dans la Zone 52."}},
[11038] = {"Aider l'exarque Orelis",nil, {"Parlez à l'Exarque Orelis dans la Zone 52."}},
[11039] = {"Se présenter au maître-espion Thalodien",nil, {"Présentez-vous au Maître-espion Thalodien dans la Zone 52."}},
[11040] = {"Des pièces détachées pour le chef-de-fusée",nil, {"Apportez les Pièces détachées de Bossi au Chef-de-fusée Karlingue dans la Zone 52."}},
[11041] = {"Un travail à terminer…",nil, {"Tuez 10 Écorcheurs Supremine et Barash la Mère de la tanière. Revenez voir le Suzerain Mor'ghor au Campement des Gueules-de-dragon si vous réussissez."}},
[11042] = {"Une vision mystique",nil, {"Parlez au Chef-de-fusée Karlingue dans la Zone 52."}},
[11043] = {"Le griffon de l'avenir",nil, {"Parlez à Brunn Flammebarbe au Bastion des Marteaux-hardis."}},
[11044] = {"Visions d'anéantissement",nil, {"Parlez au Chef d'escadrille Nuainn au Bastion des Marteaux-hardis."}},
[11045] = {"Zorus le Judicateur",nil, {"Parlez à Zorus le judicateur au Bastion des Marteaux-hardis."}},
[11046] = {"L'apothicaire en chef Hildagard",nil, {"Parlez à l'Apothicaire en chef Hildagard au village d'Ombrelune."}},
[11047] = {"La requête de l'apprenti",nil, {"Parlez à Dama Crin-sauvage au village d'Ombrelune."}},
[11048] = {"Le rapport de Kroghan",nil, {"Parlez au Garde de sang Gulmok au village d'Ombrelune."}},
[11049] = {"La ruée vers les œufs de l'Aile-du-Néant",nil, {"Yarzill le Mercenaire, du campement des Gueules-de-dragon dans la vallée d'Ombrelune, veut que vous vous procuriez un Œuf de l'Aile-du-Néant."}},
[11050] = {"Tous les œufs sont bons à prendre",nil, {"Il ne faut pas que ces œufs tombent entre de mauvaises mains, <nom>. Apportez-moi tous les autres œufs que vous pourriez trouver.nnMais n'oubliez pas de garder profil bas et évitez d'attirer les soupçons !"}},
[11051] = {"Bannissez plus de démons",nil, {"Utilisez le Cristal de bannissement pour bannir 15 démons au Camp de forge : Courroux ou au Camp de forge : Terreur."}},
[11052] = {"La promesse d'Akama",nil, {"Apportez le Médaillon de Karabor à A'dal, à Shattrath."}},
[11053] = {"Debout, surveillant !",nil, {"Parlez au Suzerain Mor'ghor au Campement des Gueules-de-dragon dans la vallée d'Ombrelune."}},
[11054] = {"Être surveillant : savoir faire les bons choix",nil, {"Le Surveillant-chef Tadboue du campement des Gueules-de-dragon dans la vallée d'Ombrelune veut que vous lui apportiez 10 morceaux de Cuir granuleux et 1 Peau de Tyrantus durcie."}},
[11055] = {"Le botterang : un traitement pour les péons bons à rien",nil, {"Le Surveillant-chef Tadboue du campement des Gueules-de-dragon dans la vallée d'Ombrelune veut que vous punissiez 20 Péons Gueules-de-dragon paresseux. Vous devrez aussi rapporter votre Botterang."}},
[11056] = {"Le marché d'Hazzik",nil, {"Récupérez le Paquet d’Hazzik dans sa demeure, à l’est de Skettis, et remettez-le-lui."}},
[11057] = {"Des ennuis en contrebas",nil, {"Chu'a'lor vous a demandé de parler à Mog'dorg le Ratatiné, en haut d'une des tours juste à l'extérieur du Cercle de sang dans les Tranchantes."}},
[11058] = {"Une relique apogide",nil, {"Obtenir des Vibrations apogides d’une Relique apogide."}},
[11059] = {"Le gardien du monument",nil, {"Procurez-vous la tête d’un Gardien apogide."}},
[11060] = {"Une sombrerune cristalforgée",nil, nil},
[11061] = {"Le devoir d'un père",nil, {"Procurez-vous un Flacon instable du sorcier pour lui."}},
[11062] = {"L'avant-poste de la Garde-ciel",nil, {"Parlez au Commandant Keller de la Garde-ciel à l'Avant-poste de la Garde-ciel, dans les Tranchantes."}},
[11063] = {"Mériter ses ailes…",nil, {"Alors on veut rejoindre les Brise-ciel, hein ? Tu as fait tes preuves comme Gueule-de-dragon, c’est entendu, mais dans les cieux de l’Outreterre, ça ne veut rien dire. La question est : EST-CE QUE TU SAIS VOLER ?nnPas juste monter sur un drake du Néant et livrer un chargement de cristaux au fort, comme ces nullards de transporteurs. Je parle d’être le meilleur… L’as des orcs…nnÀ l’ouest, tu trouveras six chevaucheurs. Triomphe d’eux, et je te donnerai tes ailes de Brise-ciel."}},
[11064] = {"Course Gueule-de-dragon : la ballade de Vieillot McVieux",nil, {"Suivez Murg « Vieillot » Bourbegueule en tentant de rester sur votre monture volante. Si vous parvenez à le battre, informez Ja'y Nosliw de votre victoire au Campement des Gueules-de-dragon dans la Vallée d'Ombrelune."}},
[11065] = {"Allez dompter quelques raies de l’éther !",nil, {"Utilisez le Lasso pour capturer 5 Raies de l’éther."}},
[11066] = {"Allez dompter d’autres raies de l’éther !",nil, {"Utilisez le Lasso pour capturer 5 Raies de l’éther."}},
[11067] = {"Course Gueule-de-dragon : Trope le Rote-crasse",nil, {"Suivez Trope le Rote-crasse en tentant de rester sur votre monture volante. Si vous parvenez à le battre, informez Ja'y Nosliw de votre victoire au Campement des Gueules-de-dragon dans la vallée d'Ombrelune."}},
[11068] = {"Course Gueule-de-dragon : Corlok le Briscard",nil, {"Suivez Corlok le Briscard en tentant de rester sur votre monture volante. Si vous parvenez à le battre, informez Ja'y Nosliw de votre victoire au Campement des Gueules-de-dragon dans la vallée d'Ombrelune."}},
[11069] = {"Course Gueule-de-dragon : Chef d'escadrille Ichman",nil, {"Suivez le Chef d'escadrille Ichman en tentant de rester sur votre monture volante. Si vous parvenez à le battre, informez Ja'y Nosliw de votre victoire au Campement des Gueules-de-dragon dans la vallée d'Ombrelune."}},
[11070] = {"Course Gueule-de-dragon : Chef d'escadrille Mulverick",nil, {"Suivez le Chef d'escadrille Mulverick en tentant de rester sur votre monture volante. Si vous parvenez à le battre, informez Ja'y Nosliw de votre victoire au Campement des Gueules-de-dragon dans la vallée d'Ombrelune."}},
[11071] = {"Course Gueule-de-dragon : Capitaine Fracasse-ciel",nil, {"Suivez le Capitaine Fracasse-ciel en tentant de rester sur votre monture volante. Si vous parvenez à le battre, informez Ja'y Nosliw de votre victoire au Campement des Gueules-de-dragon dans la vallée d'Ombrelune."}},
[11072] = {"Lignées antagonistes",nil, {"Trouvez les Piles de crânes au milieu des cercles d'invocation de Skettis. Invoquez et vainquez chacun des descendants des adversaires de Terokk en utilisant 10 Parchemins perdus dans le temps à la Pile de crâne. Retournez auprès de Hazzik au Raie'odrome de Noirvent avec un gage de chacun d'eux."}},
[11073] = {"La chute de Terokk",nil, {"Emportez l'Offrande perdue dans le temps préparée par Hazzik à la Pile de crânes qui se trouve au milieu de Skettis, puis invoquez et terrassez Terokk. Retournez voir le Commandant Adaris de la Garde-ciel quand vous aurez accompli votre mission."}},
[11074] = {"Les attributs des descendants",nil, {"Rassemblez des Parchemins perdus dans le temps détenus par les arakkoa de Skettis, et emportez-les jusqu'à une Pile de crânes au milieu d'un cercle d'invocation de Skettis. Invoquez et vainquez les descendants des adversaires de Terokk et rapportez les Serres d'Akkarai, l'Échine de Garrok, l'Écaille de Vekkaz et les Griffes de Gezzarak à Hakkiz."}},
[11075] = {"Les mines de l'Aile-du-Néant",nil, {"Allez voir la Maîtresse des mines sur l'Escarpement de l'Aile-du-Néant."}},
[11076] = {"Ramasser les morceaux…",nil, {"La Maîtresse des mines, des Mines de l’escarpement de l’Aile-du-Néant de la vallée d’Ombrelune, veut que vous récupériez 15 caisses de Cargaison de Mine-néant."}},
[11077] = {"Les dragons sont les derniers de nos soucis",nil, {"Le Contremaître Gueule-de-dragon des Mines de l’Aile-du-Néant de la vallée d’Ombrelune veut que vous tuiez 15 Ecorcheurs mine-néant et 5 Ravageurs mine-néant."}},
[11078] = {"Pour régner sur les cieux",nil, {"Récupérez des Dents de dragon."}},
[11079] = {"Un gangrefouet pour Gahk",nil, {"Procurez-vous un Gangrefouet."}},
[11080] = {"Les émanations des reliques",nil, {"Obtenir des Émanations apogides d’une Relique apogide."}},
[11081] = {"La grande révolte des Bourbesang",nil, {"Apportez les Plans d'évasion bourbesang à la Maîtresse des mines."}},
[11082] = {"À la recherche de la vérité",nil, {"Trouvez un Surveillant bourbesang et interrogez-le. Retournez voir la Maîtresse des mines avec les Informations des Bourbesang et la Main du surveillant."}},
[11083] = {"Affolés et perturbés",nil, {"Ronag l'Esclavagiste, des Mines de l'Aile-du-Néant dans la vallée d'Ombrelune, vous demande de tuer le Contremaître bourbesang affolé et 5 Mineurs bourbesang affolés."}},
[11084] = {"Haut les cœurs, capitaine !",nil, {"Parlez au Suzerain Mor'ghor au Campement des Gueules-de-dragon dans la vallée d'Ombrelune."}},
[11085] = {"L'évasion de Skettis",nil, {"Escorter le Prisonnier de la Garde-ciel à l’abri, puis se présenter au Sergent Doryn de la Garde-ciel."}},
[11086] = {"Perturber la porte du Crépuscule",nil, {"Le Suzerain Mor'ghor du campement des Gueules-de-dragon dans la vallée d'Ombrelune vous demande de tuer 20 Agents ombremort."}},
[11089] = {"Le canon à âmes de Reth'hedron",nil, {"Le Seigneur illidari Balthas du campement des Gueules-de-dragon dans la vallée d'Ombrelune veut que vous lui apportiez 2 Barres de gangracier, 1 Structure en adamantite, 1 Batterie en khorium et 1 Essence des arcanes sans défaut."}},
[11090] = {"Dominer le Dominateur",nil, {"Le Seigneur illidari Balthas du campement des Gueules-de-dragon dans la vallée d'Ombrelune vous a <chargé/chargée> d'utiliser le Canon à âmes sur Reth'hedron le Dominateur en Nagrand.nnVous devrez aussi rapporter le Canon à âmes."}},
[11091] = {"Remerciement spécial",nil, {"Chu'a'lor vous a suggéré de parler à Jho'nass, à Ogri'la, au sommet des Tranchantes."}},
[11092] = {"Salut, commandant !",nil, {"Parlez au Suzerain Mor'ghor au Campement des Gueules-de-dragon dans la vallée d'Ombrelune."}},
[11093] = {"Raies du Néant affamées",nil, {"Utilisez la Cage de raie du Néant dans les bois qui se trouvent au sud du Raie'odrome de Noirvent et tuez des Poursuivants dimensionnels près de la Raie du Néant affamée. Retournez voir le Dresseur Deesak de la Garde-ciel quand vous aurez terminé."}},
[11094] = {"Tuez-les tous !",nil, {"Avertissez l'Arcaniste Thelis du contenu de votre mission et apportez-lui le Lance-fusée gueule-de-dragon au Sanctum des Étoiles dans la vallée d'Ombrelune."}},
[11095] = {"Le commandant Hobb",nil, {"Donnez le Lance-fusée gueule-de-dragon au Commandant Hobb au Sanctum des Étoiles dans la vallée d'Ombrelune."}},
[11096] = {"La menace vient d'en haut",nil, {"Tuez 20 Ogres Gordunni de n'importe quelle variété dans la Barrière, puis retournez voir Yuula à Shattrath."}},
[11097] = {"Le plus mortel des pièges",nil, {"Aidez le Commandant Hobb et les Clairvoyants à défendre le Sanctum des Étoiles. Une fois que l'ensemble des forces Gueule-de-dragon aura été vaincu, retournez voir le suzerain Mor'ghor au Campement des Gueules-de-dragon avec les Médailles de Clairvoyant."}},
[11098] = {"En route pour Skettis !",nil, {"Apportez le Paquet d'explosifs au Sergent Doryn de la Garde-ciel au Raie'odrome de Noirvent à l'extérieur de Skettis."}},
[11099] = {"Tuez-les tous !",nil, {"Avertissez l'Anachorète Ceyla du contenu de votre mission et apportez-lui le Lance-fusée gueule-de-dragon à l'Autel de Sha'tar."}},
[11100] = {"Le commandant Arcus",nil, {"Donnez le Lance-fusée gueule-de-dragon au Commandant Arcus à l'Autel de Sha'tar dans la vallée d'Ombrelune."}},
[11101] = {"Le plus mortel des pièges",nil, {"Aidez le Commandant Arcus et l'Aldor à défendre l'Autel de Sha'tar. Une fois que l'ensemble des forces Gueule-de-dragon aura été vaincu, retournez voir le suzerain Mor'ghor au Campement des Gueules-de-dragon avec les Médailles de l'Aldor."}},
[11102] = {"Mission de bombardement",nil, {"Utilisez les Bombes de la Garde-ciel pour détruire 15 Piles de boulets de gangrecanon."}},
[11103] = {"Quitter la voie du sage",nil, nil},
[11104] = {"Quitter la voie du guérisseur",nil, {"The power within your ring is malleable, though reversing your choice is not easy. Are you sure you want to replace its current form?"}},
[11105] = {"Quitter la voie du champion",nil, nil},
[11106] = {"Quitter la voie du défenseur",nil, {"The power within your ring is malleable, though reversing your choice is not easy. Are you sure you want to replace its current form?"}},
[11107] = {"À genoux devant le généralissime !",nil, {"Parlez au Suzerain Mor'ghor au Campement des Gueules-de-dragon dans la vallée d'Ombrelune."}},
[11108] = {"Le seigneur Illidan Hurlorage",nil, {"Rencontrez Illidan Hurlorage. Si jamais vous survivez, allez trouver Barthamus dans la Ville-basse de Shattrath."}},
[11109] = {"Jorus le drake de l'Aile-du-Néant cobalt",nil, nil},
[11110] = {"Malfas le drake de l'Aile-du-Néant violet",nil, nil},
[11111] = {"Onyxian le drake de l'Aile-du-Néant onyx",nil, {"C'est un honneur de vous rencontrer, <nom>. Nous suivons tous vos aventures de près, et nous vous sommes reconnaissants de tout ce que vous avez fait pour l'Aile-du-Néant.nnSi vous me choisissez comme compagnon, je vous emmènerai jusqu'au bout des cieux de ce monde. Quoi que vous me demandiez, je ferai de mon mieux pour vous obéir.nnAttention, vous ne pouvez choisir que l'un d'entre nous pour vos voyages en Outreterre. Une fois votre choix fait, il ne sera pas possible de revenir dessus."}},
[11112] = {"Suraku le drake de l'Aile-du-Néant azur",nil, nil},
[11113] = {"Voranaku le drake de l'Aile-du-Néant pourpre",nil, {"C'est un honneur de vous rencontrer, <nom>. Nous suivons tous vos aventures de près, et nous vous sommes reconnaissants de tout ce que vous avez fait pour l'Aile-du-Néant.nnSi vous me choisissez comme compagnon, je vous emmènerai jusqu'au bout des cieux de ce monde. Quoi que vous me demandiez, je ferai de mon mieux pour vous obéir.nnAttention, vous ne pouvez choisir que l'un d'entre nous pour vos voyages en Outreterre. Une fois votre choix fait, il ne sera pas possible de revenir dessus."}},
[11114] = {"Zoya le drake de l'Aile-du-Néant viride",nil, nil},
[11117] = {"Attraper le Wolpertinger sauvage !",nil, {"Archedor Chassebécasse vous a demandé de capturer 5 Wolpertingers sonnés avec le Filet à wolpertinger. Il a indiqué qu'il fallait porter des Lunettes Synthébière pour les voir."}},
[11118] = {"Le défilé des elekk roses",nil, {"Rendez-vous aux guinguettes à l'extérieur de Hurlevent, Darnassus et l'Exodar. Supprimez trois elekk dans chacune d'elles, puis revenez voir Archedor Chassebécasse. Vous devez vous soûler pour voir les elekk roses."}},
[11119] = {"Haro sur le point d'ancrage de Bash'ir",nil, {"Parlez au Techno-éther apprenti à l’Avant-poste de la Garde-ciel, dans les Tranchantes."}},
[11120] = {"Le défilé des elekk roses",nil, {"Rendez-vous aux guinguettes à l’extérieur de Lune-d’Argent, des Pitons-du-Tonnerre et de Fossoyeuse. Supprimez 3 Elekk roses dans chacune d’elles, puis revenez voir le Chassebécasse Goro. Vous devez porter des Lunettes Synthébière pour voir les créatures."}},
[11122] = {"Histoire d'un aller et retour",nil, {"Obtenez un tonneau auprès de Flynn Brassefeu à Kharanos et rapportez-le à Pol Distillambre. Si vous perdez votre bélier, demandez-en un autre à Neill Ramstein."}},
[11123] = {"Inspection des ruines",nil, {"Parlez à l'Inspecteur Tarem à l'Auberge du Repos ombragé."}},
[11124] = {"Inspecting the Ruins",nil, {"Investigate the Shady Rest Inn in at the border of the Barrens in Dustwallow Marsh."}},
[11126] = {"Des traîtres parmi nous",nil, {"Démasquez 5 Agitateurs des déserteurs puis retournez voir Calia Hastings près des quais de Theramore."}},
[11128] = {"Guerre de propagande",nil, {"Remettez la Propagande des déserteurs à Calia Hastings près des quais de Theramore."}},
[11129] = {"Kyle a disparu !",nil, {"Ahab Sabot-de-blé, à Sabot-de-sang en Mulgore, veut que vous nourrissiez son précieux chiot, Kyle le Frénétique.nnDonnez de la Viande de trotteur tendre à Kyle puis retournez voir Ahab Sabot-de-blé."}},
[11130] = {"Houuuu, ça brille !",nil, {"Eskrokar veut que vous remettiez la Note d'Eskrokar à Budd, dans les Terres fantômes, à l’extérieur de Zul'Aman."}},
[11131] = {"Éteignez les incendies !",nil, {"La Directrice d'orphelinat costumée vous demande d'aider à éteindre tous les foyers d'incendie du village. Une fois qu'ils seront éteints, retournez lui parler."}},
[11132] = {"Des promesses, toujours des promesses...",nil, {"Budd Truducou, dans les collines de la Cognée, veut que vous récupériez sa carte. Elle se trouve sur la terrasse du Grand prêtre Nalorakk à Zul’Aman."}},
[11133] = {"Discréditer les déserteurs",nil, {"Calia Hastings veut que vous distribuiez les Pamphlets trafiqués à 6 Gardes de Theramore. Ensuite, retournez la voir près des quais de Theramore."}},
[11134] = {"La fin des déserteurs",nil, {"Capturez Gavis Targegrise, puis allez faire votre rapport à Calia Hastings près des quais de Theramore."}},
[11135] = {"Le Cavalier sans tête",nil, {"Trouvez le Sanctuaire de la citrouille du Cavalier sans tête."}},
[11136] = {"Un développement inquiétant",nil, {"Présentez-vous au capitaine Wymor à la Halte de la vigie."}},
[11137] = {"Des défias à Âprefange ?",nil, {"Récupérez les Ordres des défias sur Garn Mathers, et remettez-les au Capitaine Wymor à la halte de la Vigie."}},
[11138] = {"Renn McBranchie",nil, {"Apportez les Ordres des défias à Renn McBranchie dans les eaux au large du Rivage de Troubleffroi."}},
[11139] = {"Équipement de plongée d'occasion",nil, {"Apportez le Jeu d'outils et l'Équipement de plongée endommagé à Renn McBranchie dans le marécage d'Âprefange."}},
[11140] = {"Récupérez la cargaison !",nil, {"Remettez 6 Coffres récupérés à Renn McBranchie, dans le marécage d’Âprefange."}},
[11141] = {"Jaina doit être informée",nil, {"Parler à dame Jaina Portvaillant à Theramore."}},
[11142] = {"Survoler l'île d'Alcaz",nil, {"Parlez à Cassa Cramoissaile et utilisez l’un de ses griffons pour survoler l’île d’Alcaz. Lorsque vous aurez achevé vos observations, parlez à dame Jaina Portvaillant à Theramore."}},
[11143] = {"Un lien Sinistre",nil, {"Présentez-vous au Capitaine Darill à la Halte nord."}},
[11144] = {"Confirmer les soupçons",nil, {"Remettez le Plan de bataille du Totem-sinistre au Capitaine Darill, à la Tour de la halte nord."}},
[11145] = {"Prisonniers des Totems-sinistres",nil, {"Le Sergent Lukas, de la Tour de la halte nord, veut que vous libériez 5 Prisonniers de Theramore."}},
[11146] = {"Rapt de raptor",nil, {"Le Sergent Lukas, de la Tour de la halte nord, veut que vous capturiez 6 raptors. Vous pouvez capturer des Raptors Rougefange ou des Hurleurs Rougefange, au choix."}},
[11147] = {"Lâcher les raptors",nil, {"Le Sergent Lukas, de la Tour de la halte nord, veut que vous placiez l’Appât à raptor près du moulin à vent de Sabot-Noir."}},
[11148] = {"Les armes des Totems-sinistres",nil, {"[Captain Darill at North Point Tower wants you to bring him 7 Blackhoof Armaments.]"}},
[11149] = {"L'aide de Tabetha",nil, {"Parlez à Tabetha dans sa ferme."}},
[11150] = {"Rasez le poste de Navrecorne !",nil, {"Brûlez la Tente nord, la Tente est et la Tente nord-est du Poste de Navrecorne, puis revenez voir Tabetha dans sa ferme."}},
[11151] = {"Justice pour les Hyal",nil, {"Parlez au Capitaine Garran Vimaire à la citadelle de Theramore."}},
[11152] = {"Enfin la paix",nil, {"[Captain Garran Vimes at Foothold Citadel wants you to lay the Wreath at the Hyal Family Monument. ]"}},
[11156] = {"Les écumeurs navrecorne",nil, {"Tuez 12 Totem-sinistre de Navrecorne puis revenez voir l'Apprenti Morlann à la Ferme de Tabetha."}},
[11158] = {"Les plumes de Rougefange",nil, {"Brogg de Bourbe-à-brac veut 5 Plumes de Rougefange."}},
[11159] = {"Les esprits du bastion Cognepierre",nil, {"Brogg de Bourbe-à-brac veut que vous apportiez le repos à 10 Esprits Cognepierre."}},
[11160] = {"La bannière des Cognepierres",nil, {"Récupérez la Bannière cognepierre et remettez-la à Brogg de Bourbe-à-brac."}},
[11161] = {"L'essence de l'ennemi",nil, {"Apportez 10 Essences de draconien noir à Brogg à Bourbe-à-brac."}},
[11162] = {"Un défi au Vol noir",nil, {"Plantez la Bannière cognepierre près de l'entrée du Repaire d'Onyxia, attendez la réponse d'Onyxia, puis rendez la bannière à Brogg de Bourbe-à-brac."}},
[11163] = {"Taupe secrète",nil, {"Présentez-vous à Donna Brascoe afin de recevoir une récompense pour votre héroïsme à Zul'Aman. Donna est actuellement postée dans les Terres fantômes, juste à l'extérieur de Zul'Aman."}},
[11164] = {"Illégitimes défenses",nil, {"Dasnob veut que vous récupériez 10 Défenses de trolls des forêts sur les trolls de Zul'Aman. Apportez-les-lui au camp de Budd dans les Terres fantômes."}},
[11165] = {"Un troll parmi les trolls",nil, {"Dasnob vous a <chargé/chargée> de trouver son cousin Zungam pour lui venir en aide, quelque part à l'intérieur de Zul'Aman."}},
[11166] = {"Le X indique… votre mort !",nil, {"Entrez dans Zul’Aman et visitez la Chambre d’Halazzi, la Plateforme de Jan’alai et la Plateforme d’Akil’zon. Rendez compte des détails de ces zones à Budd, dans son camp des Terres fantômes."}},
[11169] = {"L'arme des Totems-sinistres",nil, {"L’Apprenti Garion, de la ferme de Tabetha, veut que vous testiez le Totem capturé sur 10 Crocilisques tachetés Sèchefange ou Crocs-lames Sèchefange."}},
[11171] = {"Seigneur des maléfices ? Ha !",nil, {"Budd Truducou, aux Collines de la Cognée, veut que vous alliez tuer le Seigneur des maléfices Malacrass à Zul’Aman."}},
[11172] = {"L'épave du zeppelin",nil, {"Parler à Moxie Grilledacier à l'Épave de Beezil."}},
[11173] = {"Le voleur de composants",nil, {"Apporter 6 fioles de Venin des marais à l'Apprenti Garion à la Ferme de Tabetha."}},
[11174] = {"Prévention de la corrosion",nil, {"Moxie Grilledacier vous demande de dissoudre 10 limons avant de retourner la voir à l'Épave de Beezil."}},
[11177] = {"L'ermite du manoir des Flammeroles",nil, {"Parlez à Jarl « Oeil-des-marais » au Manoir des Flammeroles."}},
[11178] = {"Le sang du seigneur de guerre",nil, {"Apportez le Sang de Zul'jin à Budd à son campement dans les Terres fantômes, à l'extérieur de Zul'Aman."}},
[11180] = {"Qui hante la Colline des sorcières ?",nil, {"Mordant Grimsby du manoir des Flammeroles vous demande de collecter des informations auprès de 10 Carcasses ressuscitées ou Esprits ressuscités."}},
