[11666] = {"Succomber à vos appâts",nil, {"Apportez un Dard aileron-noir au vieux Barlo. Vous le trouverez en train de pêcher au nord-est de Shattrath, au bord du lac Silmyr."}},
[11667] = {"Celui qui s'échappa",nil, {"Attrapez le Plus grand éperlan du monde et apportez-le au Vieux Barlo. Vous le trouverez en train de pêcher au nord-est de Shattrath au bord du lac Silmyr."}},
[11668] = {"Les crevettes, c'est pas pour les mauviettes",nil, {"Le Vieux Barlo veut 10 Crevettes géantes d'eau douce. Vous pouvez le trouver au nord-est de Shattrath, près du lac Silmyr."}},
[11669] = {"Filet de gangresang",nil, {"Apportez un Lutjan gangresang monstrueux au Vieux Barlo. Vous le trouverez en train de pêcher au nord-est de Shattrath, au bord du lac Silmyr."}},
