[3090] = {"Parchemin contaminé", {"Ah, pendant que vous n’étiez pas là un parchemin est arrivé pour vous, <nom>.","J’évite normalement de traiter avec des démonistes, si c’est possible, mais je pense que vous devriez le lire, quand vous en aurez le temps. Si je ne me trompe pas, il provient du maître qui vous a été récemment assigné, Nartok. Il souhaiterait s’entretenir avec vous, quand vous jugerez le moment venu."}, {"Lisez le Parchemin contaminé, et parlez à Nartok dans la Vallée des Épreuves."}},
[3091] = {"Note simple", {"Il y a quelques instants, une messagère était à votre recherche, <nom>. Je crois qu’elle était envoyée par le Maître des guerriers, Harutt. Si ce message est bien de Harutt, je ne tarderais pas à le lire, à votre place."}, {"Lire la Note simple, et parler à Harutt Corne-tonnerre, au Camp Narache."}},
[3092] = {"Note gravée", {"Il y a quelques instants, une messagère était à votre recherche, <nom>. Je crois qu’elle était envoyée par le maître des chasseurs, Lanka. Si ce message est bien de Lanka, je ne tarderais pas à le lire, à votre place."}, {"Lire la Note gravée, et parler à Lanka Vise-loin, au Camp Narache."}},
[3093] = {"Note runique", {"Il y a quelques instants, une messagère était à votre recherche, <nom>. Je crois qu’elle était envoyée par le Maître des chamans, Meela. Si ce message est bien de Meela, je ne tarderais pas à le lire, à votre place."}, {"Lire la Note runique, et parler à Meela Aube-glorieuse, au Camp Narache."}},
[3094] = {"Note verdoyante", {"Il y a quelques instants, une messagère était à votre recherche, <nom>. Je crois qu’elle était envoyée par le Maître des druides, Gart. Si ce message est bien de Gart, je ne tarderais pas à le lire, à votre place."}, {"Lire la Note verdoyante, et parler à Gart Cours-la-Brume, au Camp Narache."}},
[3095] = {"Parchemin simple", {"Ah, pendant que vous vous occupiez du Fléau, ce parchemin est arrivé pour vous. Je suppose qu’il est de quelque importance, car il porte le sceau du Maître des guerriers, Dannal. À votre place, je prendrais le temps de le lire avant de repartir."}, {"Lire le Parchemin simple, et parler à Dannal Stern, au Glas."}},
[3096] = {"Parchemin codé", {"Ah, pendant que vous vous occupiez du Fléau, ce parchemin est arrivé pour vous. Je suppose qu’il est de quelque importance, car il porte le sceau du Maître des voleurs, David Trias. À votre place, je prendrais le temps de le lire avant de repartir."}, {"Lire le Parchemin codé, et parler à David Trias, au Glas."}},
[3097] = {"Parchemin béni", {"Ah, pendant que vous vous occupiez du Fléau, ce parchemin est arrivé pour vous. Je suppose qu’il est de quelque importance, car il porte le sceau du Maître des prêtres, Duesten. À votre place, je prendrais le temps de le lire avant de repartir."}, {"Lire le Parchemin béni, et parler au sombre clerc Duesten, dans l’église du Glas."}},
[3098] = {"Rouleau glyphé", {"Ah, pendant que vous vous occupiez du Fléau, ce parchemin est arrivé pour vous. Je suppose qu’il est de quelque importance, car il porte le sceau du Maître des mages, Isabella. À votre place, je prendrais le temps de le lire avant de repartir."}, {"Lire le Rouleau glyphé, et parler à Isabella, dans l’église du Glas."}},
[3099] = {"Rouleau contaminé", {"Ah, pendant que vous vous occupiez du Fléau, ce parchemin est arrivé pour vous. Je suppose qu’il est de quelque importance, car il porte le sceau du Maître des démonistes, Maximillion. À votre place, je prendrais le temps de le lire avant de repartir."}, {"Lire le Rouleau contaminé, et parler à Maximillion, au Glas."}},
[3100] = {"Une lettre simple", {"<nom>, vous vous êtes <chargé/chargée> des espions rochenoires ? On m’a demandé de vous apporter ceci dès votre retour. Il semble que ce soit une lettre scellée aux armes de Llane, notre maître des guerriers. À votre place, je la lirais avant d’entreprendre quoi que ce soit d’autre, ici, à l’abbaye."}, {"Lire la Lettre simple et parler à Llane Beshere, à l’Abbaye de Comté-du-Nord."}},
[3101] = {"La lettre consacrée", {"<nom>, vous vous êtes <chargé/chargée> des espions rochenoires ? On m’a demandé de vous apporter ceci dès votre retour. Il semble que ce soit une lettre scellée aux armes de frère Sammuel, notre maître des paladins. À votre place, je la lirais avant d’entreprendre quoi que ce soit d’autre, ici, à l’abbaye."}, {"Lire la Lettre consacrée, et parler à Frère Sammuel, à l’Abbaye de Comté-du-Nord."}},
[3102] = {"Lettre codée", {"On m'a demandé de vous remettre cela dès que vous seriez de retour des camps des kobolds, <nom>. Il s'agit d'une lettre portant le sceau de Jorik, l'un de nos Maîtres locaux. À votre place, je n'hésiterais pas à la lire avant de faire quoi que ce soit d'autre, ici, dans l'Abbaye."}, {"Lire la Lettre codée, et parler à Jorik Kerridan, dans l’écurie, derrière l’Abbaye de Northshire."}},
[3103] = {"La lettre bénie", {"<nom>, vous vous êtes <chargé/chargée> des espions rochenoires ? On m’a demandé de vous apporter ceci dès votre retour. Il semble que ce soit une lettre scellée aux armes de la prêtresse Anetta, notre maître des prêtres. À votre place, je la lirais avant d’entreprendre quoi que ce soit d’autre, ici, à l’abbaye."}, {"Lire la Lettre bénie, et parler à la prêtresse Anetta, à l’abbaye de Comté-du-Nord."}},
[3104] = {"La lettre glyphée", {"<nom>, vous vous êtes <chargé/chargée> des espions rochenoires ? On m’a demandé de vous apporter ceci dès votre retour. Il semble que ce soit une lettre scellée aux armes de Khelden, notre maître des mages. À votre place, je la lirais avant d’entreprendre quoi que ce soit d’autre, ici, à l’abbaye."}, {"Lire la Lettre glyphée, et parler à Kheldon Bremen, à l’intérieur de l’Abbaye de Comté-du-Nord."}},
[3105] = {"La lettre contaminée", {"<nom>, vous vous êtes <chargé/chargée> des espions rochenoires ? On m’a demandé de vous apporter ceci dès votre retour. Il semble que ce soit une lettre scellée aux armes de Drusilla, l’un de nos maîtres. À votre place, je la lirais avant d’entreprendre quoi que ce soit d’autre, ici, à l’abbaye."}, {"Lire la Lettre contaminée, et parler à Drusilla La Salle, à proximité de l’Abbaye de Comté-du-Nord."}},
[3106] = {"Rune simple", {"Pendant que vous m’aidiez, on m’a donné cette rune pour vous, <nom>. Prenez le temps de la lire. Je pense qu’elle provient du maître des guerriers, Thran. Jetez-y un coup d’œil et allez le retrouver à l’intérieur de Courbenclume."}, {"Lisez la Rune simple, et parlez à Thran Khorman, dans la Vallée des Frigères."}},
[3107] = {"Rune consacrée", {"Pendant que vous m’aidiez, on m’a donné cette rune pour vous, <nom>. Prenez le temps de la lire. Je pense qu’elle provient du maître des paladins, Bromos. Jetez-y un coup d’œil et allez le retrouver à l’intérieur de Courbenclume."}, {"Lisez la Rune consacrée, et parlez à Bromos Grummneur, à Courbenclume."}},
[3108] = {"Rune gravée", {"<nom>, on m’a donné cette rune pour vous. Prenez le temps de la lire pendant que vous le pouvez. Je pense qu’elle provient du maître des chasseurs, Thorgas. Jetez-y un coup d’œil et allez le retrouver, à l’intérieur de Courbenclume."}, {"Lisez la Rune gravée et parlez à Thorgas Grimson, à Courbenclume."}},
[3109] = {"Rune codée", {"Pendant que vous m’aidiez, on m’a donné cette rune pour vous, <nom>. Prenez le temps de la lire. Je pense qu’elle provient du maître des voleurs, Solm. Jetez-y un coup d’œil et allez le retrouver à l’intérieur de Courbenclume."}, {"Lisez la Rune codée, et parlez à Solm Hargrin, à Courbenclume."}},
[3110] = {"Rune bénie", {"Pendant que vous m’aidiez, on m’a donné cette rune pour vous, <nom>. Prenez le temps de la lire. Je pense qu’elle provient du maître des prêtres, Branstock. Jetez-y un coup d’œil et allez le retrouver à l’intérieur de Courbenclume."}, {"Lisez la Rune bénie, et parlez à Branstock Khalder, à Courbenclume."}},
[3112] = {"Note de service simple", {"Pendant que vous m'aidiez, on m'a donné cette note de service pour vous. Prenez le temps de la lire. Je pense qu'elle provient du Maître des guerriers, Thran. Jetez-y un coup d'œil et allez le retrouver, à l'intérieur de Courbenclume, quand vous en aurez le temps."}, {"Lire la Note de service simple, et parler à Thran Khorman, dans la Vallée des Frigères."}},
[3113] = {"Note de service codée", {"Pendant que vous m'aidiez, on m'a donné cette note de service pour vous. Prenez le temps de la lire. Je pense qu'elle provient du Maître des voleurs, Solm. Jetez-y un coup d'œil et allez le retrouver, à l'intérieur de Courbenclume, quand vous en aurez le temps."}, {"Lire la Note de service codée, et parler à Solm Hargrin, dans la Vallée des Frigères."}},
[3114] = {"Note de service glyphée", {"Pendant que vous m'aidiez, on m'a donné cette note de service pour vous. Prenez le temps de la lire. Je pense qu'elle provient du Maître des mages, Marryk. Jetez-y un coup d'œil et allez le retrouver, à l'intérieur de Courbenclume, quand vous en aurez le temps."}, {"Lire la Note de service glyphée, et parler à Marryk Nurribit, dans Courbenclume, au-dessus de la Vallée des Frigères."}},
[3115] = {"Rune contaminée", {"Pendant que vous m’aidiez, on m’a donné ce mémorandum pour vous. Prenez le temps de le lire. Je pense qu’il vient de Saripal, le maître des démonistes. Allez la retrouver dans Courbenclume quand vous en aurez le temps.","Et faites attention à vous, <nom>, on n’apprécie pas trop les oiseaux dans votre genre, sur ces terres."}, {"Lisez la Rune contaminée et parlez à Saripal Couvebrassin, à Courbenclume, au-dessus de la Vallée des Frigères."}},
[3116] = {"Le cachet simple", {"Ce cachet m’a été donné par un messager de notre maître des guerriers, Alyissia. Elle souhaiterait vous parler, dès que vous aurez un moment à lui accorder. Lisez ce message et rapportez-le-lui."}, {"Allez voir Alyissia, à l’intérieur d’Aldrassil, au Sombrevallon."}},
[3117] = {"Le cachet gravé", {"Ce cachet m’a été donné par un messager de notre maître des chasseurs, Ayanna. Elle souhaiterait vous parler, dès que vous aurez un moment à lui accorder. Lisez ce message et rapportez-le-lui."}, {"Allez voir Ayanna Vassanfin, à l’intérieur d’Aldrassil, au Sombrevallon."}},
[3118] = {"Le cachet codé", {"Ce cachet m’a été donné par un messager de notre maître des voleurs, Frahun. Il souhaiterait vous parler, dès que vous aurez un moment à lui accorder. Lisez le message et rapportez-le-lui."}, {"Lisiez le Cachet codé et allez parler à Frahun Murmombre, à l’intérieur d’Aldrassil, au Sombrevallon."}},
[3119] = {"Le cachet béni", {"Ce cachet m’a été donné par un messager de notre maître des prêtres, Shanda. Elle souhaiterait vous parler, dès que vous aurez un moment à lui accorder. Lisez le message et rapportez-le-lui."}, {"Allez voir Shanda, à l’intérieur d’Aldrassil, au Sombrevallon."}},
[3120] = {"Le cachet verdoyant", {"Ce cachet m’a été donné par un messager de notre maître des druides, Mardant. Il souhaiterait vous parler, dès que vous aurez un moment à lui accorder. Lisez le message et rapportez-le-lui."}, {"Lisez le Cachet verdoyant et allez parler à Mardant Chênefort, à l’intérieur d’Aldrassil, au Sombrevallon."}},
[3121] = {"Une Requête étrange", {"Xerash Lamefeu, au pavillon de Lariss, au nord du Camp Mojache, a ce qu'il me faut pour travailler ici ! Il a préparé un mélange dont j'ai besoin pour... enfin vous verrez.","Mettez-vous en chemin pour voir Xerash. Tenez, prenez ceci. C'est une tête réduite que j'ai promise à Xerash en échange des herbes. C'est la tête d'un pauvre nain qui a croisé ma route dans la forêt. Je suis certain qu'il appréciera."}, {"Porter la Tête réduite à Xerash Lamefeu, au Pavillon de Lariss, en Féralas."}},
[3122] = {"Retourner voir le féticheur Uzer'i", {"Voici les herbes, <nom>. Portez-les au féticheur Uzer'i.","Dites-lui que je suis très content de la tête réduite."}, {"Livrer une Poche d'herbes de Xerash au Féticheur Uzer'i, en Féralas."}},
[3123] = {"Test du récipient", {"Il va s'agir d'un test, qui mettra à l'épreuve le rituel que j'ai accompli comme vos capacités. Prenez ce récipient, <nom>. Avec cela, vous pourrez réduire la taille des créatures et les y capturer.","Partez pour les étendues Sauvages de l'est et cherchez des bêtes. Vos cibles sont les vicieux ours Ferpoil et coureurs Longues-dents, <nom>. Tuez-les, puis utilisez le récipient à muisek pour les faire rétrécir et les capturer avant que leurs esprits ne s'échappent."}, {"Tuez 10 Ours Ferpoil ou Coureurs Longues-dents, puis utilisez le Récipient à muisek de bête pour faire rétrécir et capturer les bêtes vaincues.","Ramenez 10 Muiseks de bêtes et le Récipient à muisek de bête au Féticheur Uzer'i, en Féralas."}},
[3124] = {"Muisek d'hippogriffe", {"Puisque vous avez montré que vous êtes à la fois digne de confiance et <doué/douée> pour utiliser le récipient à muisek, je souhaite à présent que vous capturiez une autre créature.","Les hippogriffes ont été les compagnons loyaux des elfes de la nuit durant plusieurs années. Leur esprit est résolu et ferme.","Votre tâche consiste à tuer 10 hippogriffes Aigreplume et de rapidement les faire rétrécir et les capturer avec le récipient, afin que leur muisek soit préservé. Vous trouverez les hippogriffes dans les plateaux Sauvages au sud d'ici."}, {"Tuer 10 Hippogriffes Aigreplume de n'importe quel type, puis utiliser le Récipient à muisek pour les faire rétrécir et les capturer."}},
[3125] = {"Muisek de dragon féerique", {"À présent, <nom>, je souhaite que vous capturiez une créature fragile en apparence, mais dont l'esprit puissant nous intéresse. Je veux parler des dragons féeriques. Ils ont été les alliés des elfes de la nuit. Nous devons en capturer afin que la Horde puisse également bénéficier de cette force spirituelle.","Voici le récipient, <nom>. Tuez 8 Fées fléchetteuses ou Petits dragons féeriques, et capturez leur muisek. On peut en trouver à l'ouest d'ici."}, {"Tuez 8 Fées fléchetteuses ou Petits dragons féeriques. Utiliser le Récipient à muisek pour rétrécir et capturer les dragons féeriques vaincus."}},
[3126] = {"Muisek de tréant", {"Les marcheurs des bois errants qu’on voit louvoyer dans la forêt sont notre prochaine cible, <nom>. Les elfes de la nuit ont gagné les tréants à leur cause dans leur combat contre la Horde. Leur vitesse de déplacement est faible, mais leur rôle en tant que protecteurs est indéniable.","Bientôt, nous pourrons utiliser leur pouvoir à notre propre compte.","Trouvez trois marcheurs des bois errants, tuez-les et utilisez le récipient à muisek pour les miniaturiser et les capturer."}, {"Tuez 3 Marcheurs des bois errants. Utilisez le Récipient à muisek pour miniaturiser et capturer les tréants vaincus."}},
[3127] = {"Muisek de géant des montagnes", {"La dernière chose que je vous demande est de partir vers le nord-ouest pour trouver l'ennemi final.","Les géants des montagnes ont quitté leurs montagnes pour aider les elfes de la nuit quand la Légion ardente est apparue. Vous les reconnaîtrez à leur stature massive ainsi qu’aux vibrations du sol à mesure que vous avancerez dans leur contrée rocailleuse.","J'ai à cœur de maîtriser une puissance d'une telle taille. Tuez 7 géants des montagnes puis utilisez le Récipient pour capturer leur puissant muisek."}, {"Tuer 7 Marcheurs des terres ou Géants des falaises. Utiliser le Récipient à muisek pour faire rétrécir et pour capturer les Géants des montagnes."}},
[3128] = {"Matériaux naturels", {"Je vais avoir besoin de matériaux pour finir ce que j'ai commencé... Vous pouvez les rechercher tout en accomplissant les autres tâches que je vais vous confier.","J'aurai besoin de bûches fendues de tréant de Féralas. De minerai incrusté, que l'on peut trouver sur les géants des montagnes ou les dragons féeriques, à l'ouest. Et aussi, trouvez-moi des tendons résistants des chouettards des Hinterlands ou des hippogriffes de Féralas. Enfin, il me faudrait une grande quantité de fragments métalliques de chacune des créatures mentionnées."}, {"Apporter 2 Bûches fendues, 6 Minerais incrustés, 20 Morceaux de tendons résistants et 40 Fragments métalliques au Féticheur Uzer'i au Camp Mojache."}},
[3129] = {"Armes de l'Esprit", {"Imprégner les armes de muisek est un processus difficile. Maintenant que nous avons les matériaux nécessaires et le muisek des créatures que vous avez capturées, je peux commencer. Attendez s'il vous plaît ; je reviens dans un moment."}, {"Le Féticheur Uzer'i veut que vous patientez pendant qu'il prépare les armes."}},
[3130] = {"Contre les Hainecrêtes", {"À présent, je vais vous diriger vers mon second, Latronicus. Il a gardé un œil attentif sur les forces des nagas jusqu'alors. J'estimerai la situation générale ici selon les nagas. Non pas que je veuille envoyer une force compacte les combattre, laissant ainsi la forteresse ouverte à une attaque directe de la Horde, ou pire...","Mais passons, Latronicus attend. Vous avez fait du bon travail jusque-là, <nom>. Continuez comme ça.","Vous pouvez disposer."}, {"Se présenter devant Latronicus Lancelune au bastion de Pennelune."}},
[3141] = {"Loramus", {"Donc vous souhaitez vous battre contre des démons ? Vous feriez bien d'écouter mon histoire."}, {"Écoutez l'histoire de Loramus Thalipedes."}},
[3161] = {"La gahz'ridienne", {"<nom>, je suis un scientifique mais au fond de moi, je suis aussi un chasseur de trésor !","Il y a longtemps, quand les trolls occupaient ces terres, ils ont créé de nombreux ornements à l'image de Gahz'rilla, une hydre qu'ils adorent comme divinité. Ces ornements sont sculptés à partir d'une matière que j'appelle la « gahz'ridienne », d'après le nom du dieu hydre.","J'en ai déjà trouvé un peu moi-même, mais j'aimerais en avoir plus à étudier ; peut-être pourrez-vous m'en trouver ?","J'ai même inventé un détecteur qui fait de la recherche de gahz'ridienne un jeu d'enfant ! Vous n'avez qu'à le porter et la recherche peut commencer !"}, {"Marvon Chercherivet, à Tanaris, vous a demandé de ramasser 30 Ornements en gahz'ridienne."}},
[3181] = {"La corne de la bête", {"La corne gigantesque est couverte d'égratignures et de fissures. Un objet de métal inhabituel s'est logé sous la surface dure.","Après de plus amples observations, vous remarquez qu'il s'agit d'une lame de hache brisée. Sur le métal sont gravés les mots suivants :","Propriété de la montagnarde Minigravillon.","Quand vous essayez de déloger la lame, elle s'effrite en mille morceaux.","Oups !"}, {"Porter la Corne Gigantesque de Margol à la Montagnarde Minigravillon."}},
[3182] = {"Titre de propriété", {"Et vous pensez que je vais avaler une histoire pareille ? Le fer de ma hache se serait brisé et serait coincé dans la véritable corne, s'il s'agissait bien d'elle.","Écoutez, apportez ce faux grossier au conservateur en chef Thorius à Forgefer. S'il est capable de valider vos dires, vous recevrez un certificat de haut fait. Rapportez-moi cette preuve et je vous remettrai la clé de la gorge des Vents brûlants.","Allez-y !"}, {"Porter la Corne gigantesque de Margol au conservateur en chef Thorius à Forgefer."}},
[3201] = {"Enfin !", {"Je n'arrive pas à croire que Minigravillon ait pu douter de l'authenticité de la corne. Entre vous et moi, je pense qu'elle est seulement jalouse. Je sais qu'elle tenait vraiment à tuer elle-même cette bête, à cause de « l'incident » et tout.","Donc vous avez besoin d'un document comme preuve ? Après la contribution que vous venez de faire au musée, ce ne sera pas un problème.","<Le conservateur en chef Thorius commence à remplir un grand document>","Et voilà ! Vous pouvez présenter ceci à Minigravillon."}, {"Apporter le document de preuve à la Montagnarde Minigravillon."}},
[3221] = {"Parler à Renferrel", {"Tandis que vous étiez en train d'aider nos nécrotraqueurs, l'apothicaire Renferrel vous a fait appeler. Il n'a rien précisé mais a dit qu'il voulait vous parler à propos des cœurs de loup que vous lui aviez donnés."}, {"Parler à l'apothicaire Renferrel au Sépulcre."}},
[3261] = {nil, {"Nos routes se séparent, <nom>. Pour en apprendre plus, vous devrez aller au Camp Taurajo, vers le sud et parler à Jorn Oeil-des-cieux. Il continuera de vous guider."}, {"Parler à Jorn Oeil-des-cieux, au Camp Taurajo."}},
[3281] = {nil, {"Vous avez peut-être tué les raptors, mais l'argent qu'ils ont volé doit être récupéré ! Des rapports indiquent que les raptors ont un important site de nidification au sud de Cabestan. Cet endroit est connu sous le nom de Terres des raptors. Il est probable qu'ils y aient apporté notre argent volé.","Allez dans ce repaire de raptors et cherchez-le. Quand vous l'aurez trouvé, rapportez-le-moi."}, {"Rapportez l'Argent volé à Gazrog à la Croisée."}},
[3301] = {"Mura Totem-runique", {"Ma camarade de clan, Mura Totem-runique, est partie pour les Pins-Argentés afin d'aider nos alliés morts-vivants. C'est une forêt qui dépérit doucement, et elle avait l'espoir d'y insuffler la vie à nouveau. Peut-être que la vie insolite que nous trouvons ici dans les Tarides l'aiderait à réaliser ses souhaits pour les Pins-Argentés ?","Prenez avec vous une des carapaces que vous avez apportées, et donnez-la à Mura Totem-runique aux Pins-Argentés. Elle séjourne au Sépulcre. Avec un peu de chance, l'étude de cette carapace l’aidera à trouver une solution pour guérir cette forêt à l'agonie."}, {"Allez voir Mura Totem-runique au Sépulcre, dans la Forêt des Pins-Argentés."}},
[3321] = {"Est-ce que ceci est à vous ?", {"Vous avez beaucoup fait pour l'Ordre, <nom>. Le total dévouement nécessaire pour parvenir jusqu'ici aurait rebuté toute personne ordinaire. Il ne fait aucun doute dans mon esprit, ni dans celui de Galvan, que vous faites partie des nôtres.","C'est la raison pour laquelle j'estime que vous méritez une petite récompense. Ce n'est pas grand-chose en fait, un simple colifichet. Il se peut que vous connaissiez déjà ses propriétés, car je crois que c'est ce qui vous a <amené/amenée> à nous en premier lieu."}, {"Attendre que Trenton ait terminé son travail."}},
[3341] = {"L’anéantissement", {"Il se trouve dans les Tarides un horrible fouillis de ronces entremêlées, les Souilles de Tranchebauge. Bien que nos premières observations à cet endroit n’aient révélé que peu de dangers, nos découvertes récentes sont beaucoup plus alarmantes…","Les hurans des Souilles de Tranchebauge se sont alliés au Fléau. Une liche du nom de Amnennar, dite le Porte-Froid, les contrôle à présent et utilise le pouvoir de son imposante connaissance pour diriger chacun de leurs mouvements.","Amnennar est liée à Ner’zhul par la télépathie ; nous devons trancher ce lien, <nom>. Il faut éliminer le Porte-Froid."}, {"Andrew Brownell veut que vous tuiez Amnennar le Porte-Froid et que vous lui rameniez son crâne."}},
[3361] = {"Les malheurs d'un réfugié", {"J’étais en route pour Gnomeregan, où j’allais rejoindre mes semblables, quand subitement, tout a été horriblement de travers !","J’ai quitté Courbenclume avec armes et bagages, et de la neige jusqu’à la taille. C’est alors qu’il y a eu cet incroyable tremblement de terre, et tout a basculé. J’ai échoué ici, mais ce qui est plus grave, c’est que les Trolls ont volé toutes mes affaires !","<nom>, j’ai déjà fait suffisamment étalage de mon incompétence depuis un jour et demi. Vous voulez bien vous glisser dans le camp des Trolls au sud-ouest, et me rapporter mes affaires ?"}, {"Récupérer la Boîte de Felix, le Coffre de Felix et le Seau de boulons de Felix."}},
[3362] = {"La vallée des Chardonniers", {"Avez-vous déjà vu la vallée des Chardonniers ? C’est un endroit dangereux, <nom>.","Il y a d’étranges bêtes là-bas, imprévisibles ! Je crois qu’on les appelle les chardonniers noueux et les chardonniers taille-racine...","Je veux accéder à la vallée, mais avec tous ces chardonniers dans les parages c’est bien trop risqué. Si vous vouliez bien aller en tuer quelques-uns, je pourrais envisager d’y retourner. Qu’en pensez-vous, <nom> ? M’aiderez-vous ?"}, {"Tran'rek, à Gadgetzan, voudrait que vous tuiez 8 Chardonniers noueux et 8 Chardonniers taille-racines."}},
[3363] = {"La fleur-de-chant corrompue", nil, nil},
[3364] = {"Une livraison de thé brûlant", {"Ah ! Je devais apporter un Thé brûlant à Durnan Coupefourrure, à Courbenclume, mais je dois d’abord en porter un à Grelin. Je n'arriverai jamais à Courbenclume avant qu'il ne refroidisse !","Vous m’avez l'air rapide. Vous pouvez le faire. Cette boisson ne restera chaude que 5 minutes et Durnan n'a pas commandé un Thé \"glacé\", alors partez tout de suite. Courbenclume est un village dans la montagne, au nord-est.","Merci, <nom>, et n'oubliez pas de me rapporter ma tasse !"}, {"Apporter un Thé brûlant à Durnan Coupefourrure, dans Courbenclume, dans les cinq minutes imparties avant qu'il ne refroidisse !"}},
[3365] = {"Rapporter la Tasse", {"Ah ! Vous êtes <arrivé/arrivée> à point nommé ! Rien de tel qu'un bon thé chaud, non, BRÛLANT, par une froide journée d'hiver pour réchauffer les méandres de votre esprit.","Tenez, <nom>, soyez assez aimable pour rapporter cette tasse vide à Nori pour moi, voulez-vous ?"}, {"Apporter la Chope de Nori à Nori Fièroblique dans la Vallée des Frigères."}},
[3366] = {"L'Éclat luminescent", {"L'éclat est d'un vert brillant, et bien que compact, ce qui ressemble à de la fumée tourbillonne juste au-dessous de sa surface. Vous ne pouvez vous empêcher de contempler les profondeurs du cristal, bien qu'en même temps, vous sentiez qu'il y réside quelque chose de très inquiétant.","Peut-être devriez-vous voir si quelqu'un à Cabestan pourrait vous en apprendre plus à propos de cet étrange éclat..."}, {"Aller à Cabestan pour trouver la raison d’être de l’Éclat de cauchemar."}},
[3367] = {"Les pierres de Suntara", {"<Dorius gémit.>","Je suis grièvement blessé, <race>. Je me sens… la tête toute légère… tout… tournoie… je saigne… à l'intérieur.","Je dois retourner à Forgefer. Aidez-moi, <nom>."}, {"Aider Dorius à retourner à Forgefer."}},
[3368] = {"Les pierres de Suntara", {"Mon frère,","Si vous lisez cette lettre, c'est que je n'existe plus. Comment vous est-elle parvenue, je l'ignore. Mais remerciez et récompensez ceux qui ont fait en sorte que vous l'ayez reçue.","À propos des pierres : elles existent, les Sombrefers les ont en leur possession. Leur chef, Lathoric le Noir, utilise le pouvoir de ces pierres pour asservir mon équipe d'archéologues. Ils ont été emmenés au Chaudron. Leurs esprits sont à présent asservis et ils travaillent nuit et jour pour achever une chose qui répond au nom d'Obsidion.","Secourez-les. Il s'agit là de la dernière volonté d'un homme mort.","-Dorius"}, {"Apporter la Lettre roussie à Thorius à Forgefer. Il se trouve dans le Hall des Explorateurs."}},
[3369] = {"Au coeur des cauchemars", {"Cet éclat détient de grands secrets. Il s'agit de l'essence pure du Rêve d'émeraude. Ce que je vois dans cet éclat n'a rien d'un rêve cependant. On pourrait même appeler cela une vision de cauchemar.","Mon but n'est pas de vous alarmer, <nom>, mais je crois qu'il serait mieux pour vous de remettre cet éclat à un grand druide, et ce le plus tôt possible.","La personne que vous devriez rencontrer à la Cime des Anciens se nomme Hamuul Runetotem. Partez pour Thunder Bluff. Je ne peux pas vous en dire plus."}, {"Apporter l'Eclat de cauchemar à Hamuul Runetotem à la Cime des Anciens."}},
[3370] = {"Au cœur des cauchemars", {"Cet éclat détient de grands secrets. Il s'agit de l'essence pure du Rêve d'émeraude. Ce que je vois dans cet éclat n'a rien d'un rêve cependant. On pourrait même appeler ce genre de vision un cauchemar.","Mon souhait n'est pas de vous alarmer, <nom>, mais je crois qu'il serait mieux pour vous de remettre cet éclat aux mains d'un grand druide, et ce dès que vous le pouvez.","La personne que vous devez trouver au Cercle cénarien se nomme Mathrengyl Pasdelours. Partez pour Darnassus. Je ne peux vous en dire plus."}, {"Apportez l'Éclat de cauchemar à Mathrengyl Pasdelours à Darnassus."}},
[3371] = {"La justice des nains", {"Il y a, à présent, un creux vide de sens, là où se trouvait autrefois mon cœur, <nom>. Je ne me remettrai pas de la perte de Dorius. Je dois faire en sorte que sa dernière volonté soit accomplie. Justice doit être rendue contre ceux qui ont commis ce meurtre de sang-froid.","Retournez à la Gorge des Vents brûlants et respectez sa dernière volonté : libérez ses hommes. Les nains sombrefers forcent leurs esclaves à travailler dans une mine étouffante appelée la Fosse aux scories. Allez chercher les hommes de Dorius là-bas."}, {"Retourner à la Gorge des Vents brûlants et trouver l'Equipe d'archéologues de Dorius."}},
[3372] = {"La libération", {"Libérez-les, <race>. Leurs corps sont morts, mais la puissance de Suntara les oblige à achever leur travail.","L'autel de Suntara est la clé pour les libérer. Allez au nord-ouest, dans les collines au-dessus de la caverne… Le Marteau du crépuscule y garde une relique. Emparez-vous-en et placez-la sur l'autel de Suntara.","<L'archéologue mourant s'effondre.>"}, {"Trouvez l'Artéfact mystérieux gardé par les Idolâtres du Marteau du crépuscule dans le quart nord-ouest de la Gorge des Vents brûlants, et rapportez-le à l'Autel de Suntara, dans la Fosse aux scories."}},
[3373] = {"L'Essence d'Eranikus", {"Alors que vous touchez la gemme, une voix grave résonne dans votre esprit.","« <Mortel/Mortelle>, considérez ma requête. Je suis Eranikus des dragons verts, gardien de ce temple maudit. J'avais pour charge de m'assurer que les trolls n'amènent plus en ces lieux leurs dieux abominables. Mais j'ai échoué. Alors que j'imprègne cette gemme d'une partie de mon essence, je sens que mon étreinte sur le Rêve s'évanouit vers le néant. Je vous en prie, placez cette gemme dans le Réceptacle d’essence de mon antre, et que notre magie en ces lieux purge l'infection de mon esprit et me permette de reposer. »"}, {"Placer l'Essence d'Eranikus dans le Réceptacle d’essence situé dans son antre dans le Temple englouti."}},
[3374] = {"L'Essence d'Eranikus", {"Vous activez la Pierre de vœu et Itharius parle directement à votre esprit à travers elle.","« En acceptant la charge contenue dans cette Pierre de vœu, vous vous engagez à soutenir ma cause et celle des dragons d'Ysera, qui est de racheter l'esprit corrompu d'Eranikus. Le premier sacrifice, parmi les nombreux que vous et moi ferons sans aucun doute, va être de m'abandonner la gemme qui a enchaîné ce qu'il reste de volonté dans l'esprit d'Eranikus.","Faites-le, <nom>, et nous œuvrerons de concert au rachat d'une âme digne de rédemption. »"}, {"Apportez la Pierre de voeu des dragons d'Ysera et l'Essence d'Eranikus enchaînée à Itharius au marais des Chagrins. C'est là que vous choisirez d'aider ou non les dragons d'Ysera."}},
[3375] = {"La flasque de remplacement", {"Hum, vous avez besoin d'une autre flasque de divination ? C'est une plaisanterie, n'est ce pas ? Qu'avez-vous fait de la première ?! Nous avons du pain sur la planche, <race>. Ces flasques ne poussent pas sur les arbres ! En fait, j'aimerais bien que ce soit le cas, car je m'y rendrais tout simplement et en cueillerais une, puisqu'il ne m'en reste plus !!","Pour que je puisse vous la préparer, j'ai besoin des choses suivantes : une potion de mana, un morceau de charbon et du nectar sucré. Ne me demandez pas pourquoi, rapportez-moi simplement tout ceci et je vous en ferai une autre."}, {"Apportez une Potion de mana, du Charbon, et du Nectar sucré à Talvash del Kissel, à Forgefer, pour remplacer la Flasque de divination de Talvash."}},
[3376] = {"La fin de Brochaigu !", {"<nom>, j'ai entendu parler de vous. Peut-être saurez-vous nous venir en aide là où d'autres ont échoué.","Nous, taurens, avons fait de cette terre notre foyer. Mais cela n'a pas été sans pertes. Les hurans dos-hirsutes, du ravin de Roncelame, menés par leur chef Brochaigu Mantépine, nous accablent sans cesse. Je vous demande de me rapporter la tête de leur chef !","Vous pourrez le trouver dans le ravin, à l'est, dans leur village de fortune."}, {"Apportez la Tête du chef Brochaigu Mantépine à Brave Plume-des-vents à la Mesa de Nuage rouge."}},
[3377] = {"Prière à Élune", {"Me viendrez-vous en aide, <mortel/mortelle> ? Écoutez mon récit."}, {"Écouter le récit de Zamael."}},
[3378] = {"Prière à Élune", {"Me viendrez-vous en aide, <mortel/mortelle> ? Trouverez-vous la prière à Élune ?","Comme je l'ai dit, cette prière a été gravée sur une tablette en or. Elle doit se trouver sur l'une de ces crapules vénérant des idoles.","Si vous parvenez à retrouver ma prière, apportez-la à Astarii Cherchétoile à Darnassus. Il importe peu que ma foi ait été ravivée après les innombrables actions malveillantes que j'ai infligées à ce monde et à ses habitants, mais ma prière doit être entendue. La dernière chose que je demande est l'absolution."}, {"Trouver la Prière à Élune et l'apporter à Astarii Cherchétoile à Darnassus."}},
[3379] = {"Les tisseuses d'ombre", {"Les araignées de la gorge des Vents brûlants sont une source inestimable de Soie des ténèbres. Comme vous le savez, la Soie des ténèbres est très recherchée par les artisans de notre métier.","Malheureusement, un groupe de braconniers a récemment empiété sur ces terres, et massacre en masse les araignées sans se soucier des effets à long terme de leurs actions. Aussi, la première chose que je vous demanderai sera assez simple : trouvez-les, et... tuez-les.","Commencez vos recherches au nord.","Si vous avez besoin d'assistance, parlez avec Raze."}, {"Tuer le groupe de braconniers Ombresoie qui rôde dans la gorge des Vents brûlants. Nilith a de bonnes raisons de croire qu'ils doivent être exterminés tous les cinq. Retourner voir Nilith dans la gorge des Vents brûlants lorsque vous aurez terminé."}},
[3380] = {"Le Temple englouti", {"Il existe en ce monde des esprits qui ne connaissent pas le repos, <nom>. Il existe des esprits viciés par leur solitude, liés à un mal antique…","J'ai entendu parler d'un endroit se trouvant profondément sous la surface de la terre, banni en ce lieu par des puissances plus grandes que je n'aie jamais rencontrées.","Si vous souhaitez en apprendre davantage sur cet endroit, le Temple englouti, partez à la recherche d'un gobelin nommé Marvon Chercherivet. Il a étudié de nombreuses parties du monde, et aux dernières nouvelles, il a quitté sa demeure à Cabestan pour étudier les ruines des trolls à Tanaris."}, {"Trouver Marvon Chercherivet à Tanaris."}},
[3381] = {"Une rencontre avec le maître", {"Ah ! <Un/Une> jeune vient parler à Sanath. Quelle journée merveilleuse ! Quel plaisir que vous m'honoriez de la grâce de votre présence. Merci, <race> !","S'il vous plaît, n'ayez pas l'arrogance de considérer mon sarcasme comme un compliment. Que les choses soient claires : je ne vous apprécie pas ! Mais cette petite conversation n'a strictement rien à voir avec nous. Elle a à voir avec mon maître.","Apportez-moi une paire de plumes d'hippogriffe pour mes flèches et je vous octroierai l’autorisation d'aller le voir. Il a expressément demandé que je ne lui envoie <d'aventuriers que s'ils/d'aventurières que si elles> s'en montrent suffisamment dignes."}, {"Apporter 2 Plumes d'hippogriffe sans défaut à Sanath Lim-Yo, en Azshara, si vous souhaitez parler à son maître."}},
[3382] = {"Doux whisky", {"Nous étions près de la côte et cherchions un endroit sûr où jeter l'ancre. Le soleil ne s'était pas couché depuis plus d'une heure lorsqu'ils ont attaqué… Ces maudits nagas !","Je ne sais pas ce qu'ils cherchaient, mais il est clair qu'ils ne l'ont pas trouvé. Ils sont déjà revenus une fois. Deux de mes éclaireurs sont de garde à l'entrée de la vallée pour nous avertir lorsque la prochaine attaque aura lieu.","Cela me désole que vous arriviez à ce moment, <nom>, mais je n'y peux rien.","Êtes-vous <prêt/prête> pour le combat de votre vie ?"}, {"Défendre le capitaine Vanessa Beltis, son équipage et les passagers de “l’Horizon” contre l'attaque des nagas. Le capitaine Vanessa Beltis doit survivre."}},
[3385] = {"Le Sous-marché", {"Ils se sont donnés le nom de Sous-marché : des commerçants sans vergogne qui vendront, intimideront, commerceront, ou feront à peu près n'importe quoi contre la bonne somme d'argent.","Mais les choses empirent, <nom>. Ils ont établi une base d'opérations ici dans la gorge des Vents brûlants. Kovic dirige les opérations et entretient des liens étroits avec les nains sombrefers dans la région.","Nous devons frapper fort et envoyer un message clair à leurs dirigeants. Tuez Kovic, ainsi que son agent Clonk. Apportez-moi sa sacoche du marchand lorsque vous en aurez terminé.","Partez pour le Chaudron à présent !"}, {"Tuer le Maître d'artisanat Kovic et son agent Clonk. Récupérer sur son corps la Sacoche du marchand et l'apporter à Nilith."}},
[3402] = {"Le Sous-marché", {"Je veux que vous informiez personnellement Vizzklick à Gadgetzan de la défaite de Kovic. Il sera si heureux d'apprendre sa mort qu'il vous remettra sûrement une récompense."}, {"Parler à Vizzklick à Gadgetzan."}},
[3421] = {"Le voyage de retour", nil, nil},
[3441] = {"Châtiment divin", {"Écoutez mon récit, <aventurier/aventurière>."}, {"Écouter le récit de Kalaran Lamevent."}},
[3442] = {"La flamme parfaite", {"Vous avez pris la bonne décision, <nom>.","Nous devons frapper à l'endroit où nous ferons le plus de mal : aux quatre tours de garde.","Lorsque les quatre tours seront en feu, nous pourrons lancer notre offensive avant même qu'ils aient le temps de réagir.","Vous allez avoir besoin du bon outil pour incendier les tours. Et pour cet outil, nous devons trouver quelques ingrédients.","Apportez-moi quatre Cœurs des flammes, et quatre globes d'Huile de golem, et je ferai naître la torche de flamme parfaite.","Vous devriez prendre tout d'abord pour cibles les golems et les élémentaires de ces terres."}, {"Réunir 4 Cœurs des flammes et 4 globes d'Huile de golem puis rapporter ces objets à Kalaran Lamevent."}},
[3443] = {"Forger le manche", {"Pour porter la flamme parfaite, nous aurons besoin d'un manche et d'une enveloppe. Vous devez tout d'abord trouver un manche convenable.","Les sous-chefs et les esclavagistes Sombrefer du Chaudron se servent d'armes en thorium enchanté, un alliage extrêmement solide. Si vous m'apportez huit dagues plaquées en thorium, je pourrai les fondre et utiliser le métal pour fabriquer le manche."}, {"Apporter 8 Dagues plaquées en thorium à Kalaran Lamevent."}},
[3444] = {"Le Cercle de pierres", {"J'ai pénétré une fois dans les premières salles du Temple englouti. Le mal de ces lieux oppresse quiconque y entre. Je n'ai réussi à trouver la sortie qu'à grand-peine, ne progressant que difficilement à travers le limon qui recouvre à présent le sol. Je ne me souviens que d'avoir trébuché dans une grande pièce circulaire, remplie de statues de serpents...","J'ai récemment découvert une pierre ronde qui portait le même symbole de serpent gravé sur sa face. Je l'ai fait envelopper et envoyer à mon atelier à Cabestan. Récupérez-la, et il se peut que je puisse vous en dire davantage."}, {"Récupérer le Cercle de pierres à l'Atelier de Marvon Chercherivet à Cabestan."}},
[3445] = {"Le Temple englouti", {"J'ai entendu parler d'un endroit se trouvant profondément sous la surface de la terre, banni en ce lieu par de plus grandes puissances que je n'aie jamais rencontrées.","Si vous souhaitez en apprendre davantage sur cet endroit, le Temple englouti, partez à la recherche d'un gobelin nommé Marvon Chercherivet. Il a étudié de nombreuses régions du monde, et aux dernières nouvelles, a quitté sa demeure à Cabestan pour étudier les ruines des trolls à Tanaris.","Je vous souhaite bonne chance dans vos recherches, cela fait longtemps que personne ne l'a vu."}, {"Trouver Marvon Chercherivet à Tanaris."}},
[3446] = {"Dans les profondeurs", {"J'ai trouvé à l'intérieur du temple une pièce circulaire avec six stèles, chacune étant une statue de serpent. La curiosité m’amena à pousser l'une d'entre elles, simplement pour voir ce qui allait se passer. C'était un piège, et l'explosion m'a presque emmené jusqu’aux portes de la mort. Alors que je reposais là, sur le bord, je regardais en bas pour m’apercevoir qu'il y avait une autre statue dans la partie inférieure de la pièce : un autel si l'on peut dire... Hakkar, je crois qu'il s'appelait comme ça.","Je pense que cet autel est la clé du mystère : vous devez apporter ce symbole à cet autel du Temple englouti."}, {"Trouver l'Autel d'Hakkar dans le Temple englouti du marais des Chagrins."}},
[3447] = {"Le secret du cercle", {"Ma mémoire est brumeuse, mais je vais vous raconter ce dont je me souviens. Je crois que le cercle de statues situé dans le Temple englouti détient, malgré les dangers qui s'y cachent, un secret permettant de découvrir un trésor encore plus important.","Je pouvais activer une série de lumières depuis l'autel principal. Il est possible que si vous parveniez à comprendre la signification de ces lumières, vous puissiez découvrir les mystères de cette pièce.","Je vous mets quand même en garde, <nom>. De puissants maléfices protègent ces lieux. Il est probable que quoi que ce soit qui ait de la valeur soit gardé avec ferveur."}, {"Voyager jusqu'au Temple englouti et découvrir le secret du cercle de statues."}},
[3448] = {"La transmission du fardeau", {"On m'a récemment donné la responsabilité d'étudier Azshara, un endroit autrefois magnifique que les elfes considéraient comme leur demeure. C'était à l'époque où ils pratiquaient la magie. Cet endroit est à présent plus dangereux que beau, mais cela ne nous empêche pas de retourner chaque pierre qui s'y trouve pour découvrir des réponses sur notre passé.","Un de mes compagnons du nom de Tymor m'a proposé son aide pour étudier ce lieu, car il s'intéresse particulièrement à de tels sujets.","Il possède une petite demeure en ville, à droite du Hall des mystères."}, {"Parlez au mage Tymor à Forgefer."}},
[3449] = {"Les runes des Arcanes", {"Certains elfes vénéraient autrefois la magie tout autant qu'Élune. Des mages, parmi les plus puissants, conçurent des runes et des tablettes citant les règles des arcanes et des aperçus de leurs découvertes. Ils les laissèrent dans des lieux publics pour que chacun puisse les méditer.","J'aimerais beaucoup obtenir des reproductions de ces runes des ruines d'Eldarath. J'ai ici pour vous une trousse qui vous permettra d'effectuer ces reproductions, mais il vous appartiendra de trouver ces runes. Faites attention quand même, la Ligue des Explorateurs n'est pas la seule à s'intéresser à Eldarath."}, {"À l'aide de la Trousse de dessin, effectuer des reproductions de la Rune de Beth'Amara, de la Rune de Jin'yael, de la Rune de Markri, de la Rune de Sael'hai, puis se diriger vers la petite île près de la Péninsule méridionale d'Azshara et signaler au Pilote Xiggs Allumèche de venir les prendre."}},
[3450] = {"Une collecte facile", {"Je veux que les reproductions soient apportées à ma tour, mais il n'est pas nécessaire que vous fassiez tout le chemin jusque là-bas. Je vais simplement les faire transporter par les airs depuis Azshara.","Xiggs Allumèche me doit un service ou deux et il sera bientôt dans le coin. On l'a embauché pour surveiller la côte. Allez lui parler avant de partir. Il pourra vous donner un lance-fusée pour que vous puissiez lui envoyer un signal. Lorsque vous serez en possession des reproductions, dirigez-vous vers l'endroit qu'il vous indiquera et donnez-les-lui pour qu'il puisse les emporter en toute sécurité jusqu'à ma tour. Il devrait être au hangar du Quartier militaire."}, {"Parlez à Xiggs Allumèche à Forgefer."}},
[3451] = {"Signal de la récupération", {"Ah, ainsi Tymor vous envoie, hein ? Oui, je lui dois un service ou deux. Qu'est-ce que c'est ? Eh bien, quel diable est-ce là... Oui, je peux lui donner un coup de main avec ça. Vous aurez besoin d'un lance-fusée et d'un lieu de rendez-vous… c'est simple.","Nous avons construit un site d'atterrissage sur une petite île au large de la péninsule au sud d'Azshara. Prenez ce lance-fusée, et utilisez-le là-bas. Je verrai votre signal et viendrai récupérer la marchandise... quoi que ce soit."}, {"Parlez à Xiggs Allumèche à Forgefer pour obtenir un Lance-fusée standard."}},
[3452] = {"L'enveloppe de la flamme", {"Nous avons besoin d'une dernière pièce pour former la torche.","Vous trouverez un grand campement du Marteau du crépuscule dans les montagnes au nord-ouest. La rumeur dit qu'ils portent des symboles et des idoles afin de rendre hommage à leur dieu. Il se peut que ces symboles et ces idoles portent en eux suffisamment d'enchantement pour former une enveloppe magique. Apportez un seul symbole de Ragnaros et je tenterai d'en extraire la magie pour créer l'enveloppe de la flamme."}, {"Trouver et apporter un Symbole de Ragnaros à Kalaran Lamevent."}},
[3453] = {"La Torche de vindicte", {"Laissez-moi un moment afin que je combine les différentes parties et que j'y applique les enchantements nécessaires."}, {"Attendre que Kalaran Lamevent termine la Torche de vindicte."}},
[3454] = {"La Torche de vindicte", {"La torche doit à présent être liée à <son/sa> propriétaire. Prenez-la, <nom> !"}, {"Prendre la Torche de vindicte."}},
[3461] = {"Le retour auprès de Tymor", {"Tymor ne m'a rien donné avec lequel je puisse vous payer, aussi je suggère que vous abordiez le sujet directement avec lui lorsque vous retournerez à Forgefer. Il est certain qu'il occupe toujours la même place dans la Garde mystique. Frappez simplement à sa porte et assurez-vous qu'il vous rétribue correctement. Cela n'aura rien d'un pique-nique... c'est sûr...","Merci encore pour l'aide que vous lui avez apportée cependant, et bonne chance.","À propos, je n'ai plus besoin de la trousse de dessin. Pourquoi ne la rapporteriez-vous pas à Tymor lorsque vous irez le voir ?"}, {"Rapporter la Trousse de dessin du mage Tymor au mage Tymor, à Forgefer."}},
[3462] = {"L'écuyer Maltrake", {"Adressez-vous à mon représentant, l'écuyer Maltrake. Il vous en apprendra davantage sur l'utilisation de la Torche de vindicte."}, {"Parler à l'écuyer Maltrake."}},
[3463] = {"Allumer le feu !", {"Écoutez bien, <nom>. Bien que les quatre tours ne puissent jamais être détruites, vous pouvez vous servir de la torche de châtiment afin d'y mettre le feu pour une longue période. Un incendie qu'aucun élément ou qu'aucune force ne pourra éteindre.","Tant qu'elles seront en flammes, aucun garde ou aucune sentinelle ne pourra y résider. Ainsi, leur première ligne de défense n'existera simplement plus.","Vous devez pénétrer dans chaque tour et utiliser la Torche de vindicte à l'intérieur des fortifications. Méfiez-vous des guetteurs des Sombrefers, <nom>."}, {"Mettre le feu aux Tours de garde nord, sud, est et ouest en utilisant la Torche de vindicte à l'intérieur de chaque tour."}},
[3481] = {"Bibelots...", {"Le coffre a l'air fermé."}, {"Ouvrir le coffre."}},
[3482] = {nil, {"C'est la version de la quête de la Boîte noire pour la Horde."}, {"Porter la boîte à Kravel."}},
[3483] = {"Signal de la récupération", nil, nil},
[3501] = {"Tout se compte en grandes quantités", {"Même si vous ne pourrez jamais trouver une sphère de draenéthyste sans défaut, vous découvrirez sans doute divers fragments imparfaits de draenéthyste en explorant ces terres.","Je doute que Kum'isha puisse les utiliser pour rentrer à la maison mais ils peuvent avoir une autre utilité pour les draeneï.","Apportez tous les fragments imparfaits de draenéthyste que vous pourrez et je vous récompenserai."}, {"Apporter des Fragments imparfaits de draenéthyste à Kum'isha et toucher une récompense pour chaque fragment rapporté."}},
[3502] = {"Au rebut pour les draeneï…", {"Eh bien, après cette démonstration d'avidité vorace, j'ai la nausée.","Cela dit, je ne vais pas m'étendre trop longtemps sur votre penchant.","Pour chaque fragment imparfait de draenéthyste que vous m'apporterez, je vous récompenserai avec le même genre d'affaires bonnes pour le rebut. Mon tas d'affaires va diminuer tandis que le vôtre va devenir de plus en plus lourd. Sur Draenor, nous appelons cela une situation « gagnant-gagnant »."}, {"Apportez des Fragments imparfaits de draenéthyste à Kum'isha. N'importe quel monstre des Terres Foudroyées peut détenir un fragment."}},
[3503] = {"Réunion avec le Maître", nil, nil},
[3504] = {"Trahison", {"Une traîtresse, une voleuse ! C'est ce qu'elle sera toujours !","J'ai fait une grave erreur, <nom>. J'ai fait confiance à un elfe de sang. Pire, à UNE elfe de sang !","Elle prétendait qu'Azshara renfermait de grandes richesses... de puissants objets. Je n'avais qu'à envoyer quelques guerriers avec elle pour la protéger et j'en serais récompensé. Que j'ai été bête... Elle les a tous tués.","Je réclame vengeance, <nom>, mais je ne peux pas envoyer quelqu'un d'autre. Allez voir Ag'tor à Valormok, au nord du campement des Haldarr. Il vous en dira plus."}, {"Apporter la Lettre scellée à Ag'tor Poing-de-sang en Azshara."}},
[3505] = {nil, {"J'ai réfléchi.","Nous commençons notre croisade contre ses ouvriers au sud des falaises de la péninsule du nord d'Azshara. Tuez-les avec une colère sanglante, qu'ils sachent le prix de la trahison.","Ne vous occupez pas d'elle pour l'instant : elle passe son temps en Azshara avec ses gardes du corps. Une fois ses ouvriers morts, il faudra trouver son campement personnel et faire une reconnaissance du terrain avant de l'attaquer. Nous trouverons peut-être quelque chose à utiliser pour la provoquer."}, {"Tuer 10 Vengeurs elfes de sang et 10 Géomètres elfes de sang avant de rechercher le campement du magus Rimtori. Une fois le camp trouvé, chercher quelque chose d'important pour elle et qui peut être utilisé pour l'obliger à se battre."}},
[3506] = {"Trahison", {"Quelques instants de réflexion vous donnent des idées, dont une vous fait sourire : la destruction des cristaux suffira peut-être à provoquer l'elfe de sang responsable du meurtre de tant d'orcs et de la trahison dont a été victime Belgrom ?","Vous continuez à déchiffrer le texte et les images du livre et vous rendez compte que l'un des cristaux est lié au créateur du cercle ; les autres sont liés à quelque chose de beaucoup plus sinistre.","Vous fermez le livre et commencez à chercher quel cristal détruire."}, {"Détruire le Cristal de focalisation des arcanes lié au magus Rimtori et l'assassiner ensuite. Puis apporter la tête du magus Rimtori à Ag'tor Poing-de-sang en Azshara."}},
[3507] = {nil, {"Retournez voir Belgrom à Orgrimmar. Il voudra voir sa tête de ses yeux ! Nous reviendrons bientôt.","Ah, c'est en effet une bonne journée. Merci pour votre aide, <nom>."}, {"Aller voir Belgrom Cogneroche à Orgrimmar."}},
[3508] = {"Briser l'Amulette", {"Vous êtes téméraire, <nom>. C'est une qualité admirable de votre espèce, le fait de foncer aveuglément dans la bataille alors qu'il n'y a aucune chance de succès, ou si peu. Je vous aiderai, ne serait-ce que pour voir tomber un seul des sbires de Razelikh.","Briser la tutelle de Razelikh sur ses subalternes va prendre du temps et nécessitera l’utilisation d'artéfacts rares.","Ne bougez plus, je dois commencer le rituel de Lumière. L'information que j'en tirerai m'aidera à façonner des armes de grand pouvoir."}, {"Attendre que Loramus ait achevé le rituel de Lumière."}},
[3509] = {"Le nom de la bête", {"Il n'y a qu'une façon de nuire au seigneur démon et à ses sbires. Il faut forger une arme qui portera son véritable nom.","Si nous parvenons à forger une telle arme, le démon et toute sa clique seront vulnérables.","Un autre seigneur se trouve en Azshara, le seigneur Arkkoroc. Vous devez aller le trouver, mais attention, ses sbires n'hésiteront pas à vous éliminer s'ils vous découvrent.","Vous aurez certainement un prix à payer pour cette information."}, {"Aller voir le seigneur Arkkoroc en Azshara. Commencer la recherche au bord de l'eau pour trouver le temple d'Arkkoran."}},
[3510] = {"Le nom de la bête", {"Elle tue mes enfants, <mortel/mortelle>. Celle qui se proclame souveraine de ces océans. Une fausse déesse, rien de plus. Écrasez-la et apportez-moi chacune de ses têtes.","En échange, vous obtiendrez le nom que vous cherchez et autre chose, d'importance égale.","Elle rôde sur la couvée... Elle se nomme Hetaera.","<Le seigneur Arkkoroc désigne le sud-ouest.>","Allez-y !"}, {"Assassiner Hetaera et apporter la Tête ensanglantée d'Hetaera, la Tête battue d'Hetaera et la Tête meurtrie d'Hetaera au seigneur Arkkoroc."}},
[3511] = {"Le nom de la bête", {"Comme promis, je vais vous révéler le nom du seigneur-démon : Razelikh le Souilleur. Mais tout d'abord, je dois préparer quelque chose de la plus grande importance pour votre voyage.","L'arme que vous cherchez à confectionner nécessite une trempe particulière. Laissez-moi quelques instants pour vider le sang de ces têtes.","<Le seigneur Arkkoroc presse les têtes entre ses gigantesques poings, exprimant ainsi tout le sang.>","Apportez la trempe à Loramus, ainsi que le nom de la bête : Rakh'likh."}, {"Rapporter le durcisseur à Loramus Thalipedes."}},
[3512] = {"Par les mots d'Eranikus", {"<nom>, nous devons d'abord inventer une méthode pour parler à Eranikus par l'intermédiaire de la gemme. Il se trouve entre le sommeil et la mort, dans un enfer de cauchemars. Même si l'essence que vous m'avez donnée est infectée, nous avons une chance de l'atteindre.","Vous devez aller jusqu'aux terres glacées de l'est du Berceau-de-l'Hiver et chercher un de nos alliés mortels, l'implorateur d'esprits Umbranse. Il parle aux morts. Quoiqu'Eranikus ne soit pas vraiment mort, il pourra peut-être nous aider."}, {"Rendez-vous dans la partie orientale du Berceau-de-l'Hiver et allez voir Umbranse l'implorateur d'esprit."}},
[3513] = {"Le Parchemin runique", {"Ce parchemin à l'air très officiel est fermement entouré d'un ruban argenté. Vous pensez que ça intéresserait sûrement quelqu'un de le voir, mais qui ?","Il y a un avant-poste au nord de la frontière d'Orneval et si l'information sur ce rouleau concerne les elfes de la nuit, c'est le bon endroit pour l'apporter."}, {"Apporter le Parchemin runique à la tour de garde Nord dans les Tarides."}},
[3514] = {"Présence de la Horde", {"Ce parchemin vient de la dryade rusée nommée Rynthariel, Maître des clés de l'antre des Serres, au pic des Serres-Rocheuses.","Elle ne pourra plus contacter la personne sur qui vous l'avez pris, hein ? Mais nous devons renforcer notre position. La Horde ne tolérera pas qu’on lui dispute ces lieux.","<nom>, partez pour l'antre des Serres. Tuez Rynthariel, et placez un symbole que tous pourront voir et comprendre. Prenez le drapeau de la Horde, <nom>. Hissez-le dans l'antre des Serres, qu'ils n'aient pas la témérité d'envoyer un autre messager."}, {"Tuer le chef de l'Antre des Serres, Rynthariel le Maître des clés."}},
[3517] = {"L'art du vol", {"J'admets avoir profité de ma situation en Azshara, <nom>. La punition de Belgrom a eu de bons côtés.","J'y ai fait des recherches et vous seriez <étonné/étonnée> de voir combien il y a d'objets de valeur ici.","Je vous en dirai plus sur mes affaires et vous ferai participer aux profits, si vous m'aidez à les récupérer.","J'ai besoin de tablettes disposées partout dans les ruines d'Eldarath, à l'est d'ici. Les quatre portent le nom de sorciers bien-nés. Trouvez-les pour moi et nous discuterons."}, {"Trouver la Tablette de Beth'Amara, la Tablette de Jin'yael, la Tablette de Markri et la Tablette de Sael'hai puis retourner voir Jediga en Azshara."}},
[3518] = {"Livraison à Magatha", {"La première tablette va à une taurène... Elle s'appelle Magatha. Elle se trouve parmi les anciens aux Pitons-du-Tonnerre et elle est l'un des partisans de l'alliance entre les taurens et les Réprouvés. Plus que n'importe lequel des anciens, elle sent que les taurens et leurs connaissances peuvent aider les Réprouvés à redevenir des hommes.","Apportez-lui la Tablette de Beth'Amara. La tablette est peut-être le lien dont elle a besoin pour aider les Réprouvés... ou du moins c'est ce qu'elle dit."}, {"Apportez la Tablette de Beth'Amara à Magatha aux Pitons-du-Tonnerre."}},
[3519] = {"Un ami dans le besoin", {"Ahhh... J'ai été mordu par une araignée nommée Githyiss la Vile en explorant sa caverne, tout près d'ici. Une grande quantité de poison m'a été injectée ; vous devez m'aider.","Prévenez Dirania Brillargent. Elle sera capable de me secourir.","Vite... J'ai des vertiges de plus en plus alarmants..."}, {"Allez Dirania Brillargent, au Sombrevallon."}},
[3520] = {"Esprits de Hurleurs", {"Je cherche les esprits des hurleurs des vallées de Féralas. Ils sont cousins d'un dieu fort ancien et leurs esprits lui sont liés.","Prenez ce roncier magique et allez en Féralas pour y trouver les hurleurs des vallées, au sud et au centre. Tuez-les, puis agitez ma baguette au-dessus d'eux. Cela exhortera leur esprit à se révéler. Touchez les esprits et prenez leur essence.","Revenez me voir avec ma baguette et les esprits et je vous en dirai plus de ce dieu ancien dont je vous ai parlé."}, {"Capturer l’Esprit de 3 Hurleurs en Féralas, puis retourner voir Yeh'kinya à Port Gentepression."}},
[3521] = {"L'antidote d'Iverron", {"Je m’inquiète beaucoup pour mon ami Iverron. Il est parti depuis près d’une journée, et j’ai peur qu’il ait été empoisonné… ou pire.","Je veux que vous récupériez les composants nécessaires à la fabrication d’un antidote, juste au cas où.","Cueillez des champignons jacinthe à la base des arbres, les lys pétales de lune près de l’eau, et de l’ichor d’araignée Tissebois sur l’une de ces créatures, au nord.","Même s’il a été empoisonné, les mélanger pour préparer l’antidote ne devrait pas vous poser de problèmes. Il est parti vers le nord, en direction de la grotte de Sombrefil. Avec un peu de chance, vous pourrez l’y trouver."}, {"Collectez 7 Champignons jacinthe, 4 Lys pétales de lune et 1 Ichor d'araignée Tissebois."}},
[3522] = {"L'antidote d'Iverron", {"L'antidote est prêt, <nom>. Faites en sorte qu'Iverron le boive.","Vous devez savoir une chose : l'antidote ne reste efficace que pendant 5 minutes. Vous devez le lui apporter à temps.","Que la vitesse soit avec vous, <nom>."}, {"Apporter l’Antidote d'Iverron à Iverron avant que la limite de temps ne soit atteinte. Iverron se trouve près de la grotte, au nord."}},
[3523] = {"Le Fléau des Souilles", {"Remarquable ! Mon nom est Belnistrasz et je vous remercie de m’avoir secouru. Je me voyais déjà mort… ou pire, transformé en repas pour cette vermine infestée par le Fléau. Oui, j’ai dit le Fléau à dessein : ici, ils ont négocié une sorte d’accord avec les hurans et ce cauchemar en est le résultat.","Écoutez, plutôt que de fuir, voulez-vous m’aider à en finir ? C’est probablement dangereux, mais vous semblez être du genre à ne pas vous inquiéter pour si peu."}, {"Pour aider Belnistrasz, lui parler de nouveau et lui rendre la Pierre de voeu qu’il vous avait donnée."}},
[3524] = {"Échouage", {"De majestueuses créatures marines se jettent sur le littoral de Sombrivage, où elles restent échouées jusqu’à leur mort. Dernièrement, le nombre de ces bêtes n’a cessé d’augmenter. C’est le temple de la Lune qui m’envoie pour enquêter, mais la présence des murlocs, sur le littoral, a freiné mes recherches.","Il y a une créature géante échouée au sud d’Auberdine, et beaucoup de ces maudits murlocs grisebrumes aux alentours. Pourriez-vous aller récupérer des os sur la carcasse de cette créature ? Ils nous serviront pour nos études."}, {"Récupérer des Os de créature marine sur la créature échouée, au sud d’Auberdine, puis les apporter à Gwennyth Bly'Leggonde, à Auberdine."}},
[3525] = {"L’extinction de l’idole", {"Près de l’entrée, il y a une idole qui sert de four aux hurans, ces bêtes… ils consument ceux qu’ils enlèvent dans les Tarides. Je peux faire en sorte qu’ils ne puissent plus jamais s’en servir, mais j’ai besoin de votre aide. Voici mon plan : escortez-moi jusqu’à l’idole, quand nous arriverons, je commencerai le rituel. Pendant ce temps, je ne pourrai pas communiquer avec vous et encore moins me défendre. Protégez-moi pendant que je détruis l’idole. S’ils parviennent à m’abattre, tout est fini.","Êtes-vous avec moi ?"}, {"Escorter Belnistrasz jusqu’à l’Idole des hurans dans les Souilles de Tranchebauge.","Protéger Belnistrasz tandis qu’il exécute le rituel pour arrêter l’idole."}},
[3526] = {"Ingénierie des gobelins", {"La meilleure ingénierie est représentée par deux disciplines : celle des gnomes et celle des gobelins. L'Ingénierie des gobelins est clairement le choix de tous les ingénieurs brillants. Apprendre à vaincre le monde par l'utilisation judicieuse d'explosifs et d'incroyables mécanismes !","<nom>, lisez ce manuel. Si vous voulez en apprendre plus, apportez-le à Nixx Elastirouage à Gadgetzan. Mais attention : l’adhésion à cette discipline est définitive et exclusive, alors soyez <sûr/sûre> de faire le bon choix !"}, {"Pour en apprendre davantage sur l'ingénierie gobeline, apporter le Manuel d'Ingénierie à Nixx Elastirouage dans Gadgetzan."}},
[3527] = {"La prophétie de Mosh'aru", {"La prophétie ancienne de Mosh'aru explique comment contenir l'essence du dieu Hakkar. Elle a été écrite sur deux tablettes, apportées à la cité trolle de Zul'Farrak, à l'ouest de Gadgetzan.","Apportez-moi les tablettes Mosh'aru !","La première tablette est détenue par le troll Theka le Martyr, mort depuis longtemps. On raconte que ses bourreaux ont été changés en scarabées et qu'ils parcourent à présent son autel.","La seconde tablette est détenue par l'hydromancienne Velratha, près du bassin sacré de Gahz'rilla.","Une fois que vous aurez les tablettes, apportez-les-moi."}, {"Apportez les Première et Deuxième tablettes Mosh'Aru à Yeh'kinya, à Tanaris."}},
[3528] = {"Le dieu Hakkar", {"Maintenant il faut capturer l'essence de l'avatar d'Hakkar.","Apportez l'Œuf d'Hakkar au Sanctuaire du dieu déchu dans le Temple englouti d'Azeroth. Invoquez le pouvoir de l'œuf pour réveiller le dieu mort.","Il enverra ses sbires contre vous. Combattez-les et prenez leur sang hakkari, puis versez-le sur les flammes éternelles qui retiennent l'esprit d'Hakkar. Une fois les flammes éteintes, l'avatar d'Hakkar entrera dans notre monde.","Combattez-le et placez son essence dans l'œuf d'Hakkar."}, {"Apportez l’Œuf d'Hakkar rempli à Yeh'kinya dans Tanaris."}},
[3541] = {"Livraison pour Jes'rimon", {"J'ai un copain à Orgrimmar. Il réside dans la rue principale. Il est difficile à trouver si vous ne savez pas où regarder, mais allez voir dans les bâtiments les plus hauts et vous devriez sans peine tomber sur lui.","Faites-moi une faveur, n'allez pas répéter à tout Kalimdor qu'il est là : ce serait mauvais pour lui comme pour moi.","Il nous donnera un prix plus qu'honnête pour la Tablette de Jin'yael. Je ne sais pas pourquoi, mais vous auriez dû voir comme il était excité quand je lui en ai parlé."}, {"Apportez la Tablette de Jin'yael à Jes'rimon à Orgrimmar."}},
[3542] = {"Livraison à Andron Gant", {"Andron Gant est mon contact suivant. Il se terre habituellement à Fossoyeuse, tout près de l'Apothicarium.","Quand il est venu me voir, il était plus loquace qu'à l'ordinaire et il m'a dit pourquoi il voulait la Tablette. Apparemment, des mages de Fossoyeuse veulent étudier certains des charmes mystérieux que les Bien-nés utilisaient. Ils pensent que cette tablette les y aidera. Markri était expert en invocation de créatures, surtout des élémentaires.","Apportez la Tablette de Markri et revenez me voir pour votre récompense."}, {"Apportez la Tablette de Markri à Andron Gant à Fossoyeuse."}},
[3561] = {"Livraison à l'archimage Xylem", {"Cette dernière tablette est destinée à quelqu'un de très dangereux, <nom>. Croyez-moi, je lui fais encore moins confiance qu'aux autres.","L'archimage Xylem habite une tour en haut des collines qui entourent Azshara. Il est humain, mais il agit comme aucun autre humain que j'aie jamais rencontré... ou tué. La puissance se dégage de tout son être et je n'ose lui demander ce qu'il veut faire de la tablette.","Il est venu me voir, mais il m'a dit que son garde du corps Sanath m'aiderait à parvenir à la tour lorsque je serais prête à le rencontrer. Partez au nord à la recherche de Sanath."}, {"Apportez la Tablette de Sael'hai à l'archimage Xylem en Azshara."}},
[3562] = {nil, {"Portez cette note à Jediga. Dites-lui que son paiement l'attendra à son retour. Et dites-lui que son travail est satisfaisant.","Faites vite, <nom>. Dites à votre maîtresse que je l'attendrai ici."}, {"Apporter la Note de Magatha à Jediga en Azshara."}},
[3563] = {"Le paiement de Jes'rimon à Jediga", {"Portez cette lettre à Jediga. Faites-lui savoir qu'notre accord a été rentable et qu'son travail me satisfait.","Dites-lui d'rev'nir vite à Orgrimmar et qu'Jes'rimon y prendra bien soin d'elle.","Dites-lui tout cela et j'aurai p't-êt' à nouveau b'soin d'vos services plus tard, <nom>."}, {"Apporter la Note de Jes'rimon à Jediga en Azshara."}},
[3564] = {"Le paiement d'Andron à Jediga", {"Ici... Portez... <hic> cela à Jediga. Bonne chance !"}, {"Apporter la Note d'Andron à Jediga en Azshara."}},
[3565] = {"Le paiement de Xylem à Jediga", {"Qu'y a-t-il ? Oh oui, votre paiement. Bien sûr.","Prenez ceci et partez. Je suis sûr que Jediga attend avec une grande, grande patience votre retour."}, {"Apporter la Note de Xylem à Jediga en Azshara."}},
[3566] = {"Debout, Obsidion !", {"C'était... C'était... C'était un piège.","Pardonnez... moi.","Tuez ce traître… Tuez-le pour nous… pour Thorius."}, {"Assassiner Lathoric le Noir et Obsidion, puis retourner voir Thorius, à Forgefer, avec la Tête de Lathoric le Noir et le Cœur d'Obsidion."}},
[3567] = {"Au sommet", nil, nil},
[3568] = {"Corruption suintante", {"Regardez cette taurène derrière moi, celle qui a l'air de se trouver mal. Elle était en Azshara récemment. Elle affirme avoir été blessée par des nagas en traversant les ruines d'Eldarath.","Après les avoir repoussés, elle s'est reposée sur la plage et a bu dans l'une des cuvettes de marée. Maintenant elle est malade.","Je veux découvrir ce qu'elle a bu. Prenez cette boîte, avec quelques fioles vides que j'ai étiquetées. Remplissez chacune d'elle dans une cuvette différente et revenez me voir."}, {"Avant de retourner voir le Chimiste Cuely, remplir les 4 Fioles vides dans les cuvettes de marées le long de la côte des Ruines d'Eldarath, en Azshara."}},
[3569] = {"Corruption suintante", {"Donnez-le à Thersa. Peut-être que ça va guérir sa... Ou peut-être pas. Nous ne le saurons que lorsqu'elle aura bu."}, {"Donner l’élixir de Cuely à Thersa Chant-du-vent, à Fossoyeuse."}},
[3570] = {"Corruption Suintante", nil, nil},
[3601] = {"Kim'jael évidemment !", {"Kim'jael, effectivement ! Savez-vous ce que Kim'jael signifie chez les elfes de sang, <nom> ? Petit rat... ou quelque chose comme ça. Quels imbéciles !","Ils m'ont embauché pour aider à reconnaître le secteur et pour chercher des genres de trésors perdus. Je me suis évidemment précipité lorsque j'ai entendu le mot \"trésor\" et le montant qu'ils allaient me payer, mais ils m'ont bien entendu trahi et ont essayé de me tuer... MOI !","C'est pour vous dire, on ne peut jamais faire confiance à un elfe de sang.","Maintenant, le problème, c'est qu'ils sont en possession de mon équipement et que je ne peux pas le récupérer. Vous voulez m'aider ?"}, {"Fouiller le Camp de base thalassien pour trouver la Boussole, la Lunette, le Poulet farci et le Wizzlegoober de Kim'Jael, puis les lui rapporter en Azshara."}},
[3602] = {"Azsharite", {"Les anciens géants des falaises de la région du sud d'Azshara se nourrissent de la roche et des constructions locales. Leurs excréments forment un cristal très malléable et extrêmement solide appelé azsharite.","On le trouve dans le Cercle du sud, mais on en récupère peu en raison du tempérament extrêmement violent des géants.","Vous devez aller au sud et récupérer suffisamment d'azsharite pour créer le corps de l'arme. Mes gangrechiens vous aideront à trouver le cristal."}, {"Trouvez 20 Azsharites cristallisées au sud d'Azshara. Utilisez éventuellement le gangrechien pour « renifler » l'emplacement où sont intelligemment cachés les dépôts.","Retournez voir Loramus une fois votre tâche accomplie."}},
[3621] = {"La formation du gangreplaie", {"Les armes sont prêtes à être forgées. Vous devez apporter l'azsharite trempée à quelqu'un qui saura en exploiter l'énergie.","Un tel forgeron se trouve dans la vallée de Strangleronce. Galvan l'Ancien, le chef de l'ordre du Mithril, pourra fabriquer ces armes. Vous trouverez son campement entre Zul'Gurub et le tertre des ogres mosh'Ogg.","Puissent votre détermination et votre foi vous porter dans cette épreuve. Si vous échouez, sachez que votre dieu vous regarde en souriant, <mortel/mortelle>."}, {"Apporter la cargaison à Galvan l'Ancien dans la vallée de Strangleronce."}},
[3625] = {"Armes gangrenées en azsharite enchantée", {"<nom>, la création de chaque arme va prendre un peu de temps.","De plus, vous ne pourrez utiliser le pouvoir de la Malédiction gangreneuse qu'une fois toutes les trois minutes. Ses propriétés d'affaiblissement contre Razelikh le Souilleur et ses sbires peuvent varier dans la durée. C'est pourquoi vous devez prendre garde d'utiliser la Malédiction gangreneuse au bon moment !","À présent, donnez-moi une minute pour terminer votre arme, <nom>."}, {"Attendre que Galvan ait fini de forger les armes."}},
[3626] = {"Retour dans les terres Foudroyées", {"L'heure de la rédemption est proche, <nom>. Vous devez retourner dans les terres Foudroyées avec votre nouvel équipement et parler au Héros de la Horde tombé au combat pour recevoir votre objectif final."}, {"Retourner voir le Héros de la Horde tombé au combat, au Croisement de Lance-brisée, au Marais des Chagrins."}},
[3627] = {"Réunir les morceaux de l'amulette brisée", {"Pour atteindre le Souilleur, vous devez d'abord détruire la triade de pouvoir et récupérer les morceaux d'amulette. Quand vous aurez récupéré les trois morceaux, revenez me voir et je vous donnerai de nouvelles instructions.","Allez, <nom> ! Partez dans les terres Foudroyées ! Sevine, Allistarj et Grol doivent mourir !"}, {"Assassiner Grol le Destructeur, dame Sevine et l'archimage Allistarj. Prendre sur leurs cadavres l'Amulette de Grol, l'Amulette de Sevine et l'Amulette d'Allistarj.","N'oubliez pas d'UTILISER vos armes gangrenées en azsharite enchantée pour affaiblir la triade (ils ne peuvent pas être tués autrement), mais faites-le sagement..."}},
[3628] = {"Son nom est Rakh’likh", {"Razelikh se terre sur la plus haute montagne des terres Foudroyées. Le seul moyen d'y accéder est d'utiliser les runes de téléportation près de la montagne. Tant que vous tenez l'amulette, vous pouvez vous y transporter. Une fois là-bas, vous devez neutraliser la bête. L'amulette est votre atout, <nom>. Utilisez-la sur l'Autel du Souilleur.","Si vous arrivez à le vaincre, prenez sa corne. Elle sera signe d'espoir pour ceux qui la verront, la preuve que les héros existent dans ce monde déchiré par la guerre."}, {"Détruire Razelikh le Souilleur et apporter la Corne brisée du Souilleur au Héros de la Horde tombé au combat. Rendre aussi l'Amulette du Souilleur au Héros pour qu'il puisse la détruire, afin de l'empêcher de tomber entre de mauvaises mains."}},
[3629] = {"Ingénierie des gobelins", {"L'ingénierie pratiquée au niveau d'expert connaît deux disciplines : celle des gnomes et celle des gobelins. Je ne mentionne l'Ingénierie des gobelins que parce que je suis un Maître juste. Ils aiment faire des choses qui explosent et se soucient rarement des conséquences.","<nom>, lisez ce manuel. Si vous voulez apprendre comment vous faire sauter, apportez-le à Nixx Elastirouage dans Gadgetzan. Mais le choix de cette discipline est définitif et exclut l'autre, alors soyez <sûr/sûre> de votre choix !"}, {"Pour en apprendre davantage sur l'ingénierie gobeline, apporter le Manuel d'Ingénierie à Nixx Elastirouage dans Gadgetzan."}},
[3630] = {"Ingénierie des gnomes", {"L'ingénierie pratiquée à un niveau d'expert connaît deux disciplines : celle des gnomes et celle des gobelins. L'ingénierie gnome est de loin supérieure ! Elle consiste à construire de merveilleux dispositifs qui rendent la vie meilleure !","<nom>, lisez ce manuel. Si vous voulez en apprendre plus sur l'ingénierie gnome, apportez ce manuel au maître-artisan Suprétincelle dans Forgefer. Mais le choix de cette discipline est définitif et exclut l'autre, alors soyez <sûr/sûre> de votre choix !"}, {"Pour en apprendre davantage sur l'ingénierie gnome, apporter le Manuel d'Ingénierie au maître-artisan Suprétincelle, à Forgefer."}},
[3631] = {"Invoquer un Palefroi corrompu", {"Hum. À mon avis, vous n’avez ni la force ni les compétences requises, mais cela ne me regarde pas puisque Strahad pense autrement, et que c’est lui qui décide... cette fois.","Il voudrait vous parler le plus vite possible. Vous le trouverez, avec ses acolytes, dans la petite tour qui domine Cabestan, dans les Tarides.","Ne traînez pas."}, {"Parlez à Strahad Farsan, dans les Tarides."}},
[3632] = {"Ingénierie des gnomes", {"L'ingénierie pratiquée à un niveau d'expert connaît deux disciplines : celle des gnomes et celle des gobelins. L'ingénierie gnome est de loin supérieure ! Elle consiste à construire de merveilleux dispositifs qui rendent la vie meilleure !","<nom>, lisez ce manuel. Si vous voulez en apprendre plus sur l'ingénierie gnome, apportez ce manuel au maître-artisan Suprétincelle dans Forgefer. Mais le choix de cette discipline est définitif et exclut l'autre, alors soyez <sûr/sûre> de votre choix !"}, {"Pour en apprendre davantage sur l'ingénierie gnome, apporter le Manuel d'Ingénierie au maître-artisan Suprétincelle, à Forgefer."}},
[3633] = {"Ingénierie des gobelins", {"La meilleure ingénierie est représentée par deux disciplines : celle des gnomes et celle des gobelins. L'Ingénierie des gobelins est clairement le choix de tous les ingénieurs brillants. Apprendre à vaincre le monde par l'utilisation judicieuse d'explosifs et d'incroyables mécanismes !","<nom>, lisez ce manuel. Si vous voulez en apprendre plus, apportez-le à Nixx Elastirouage à Gadgetzan. Mais attention : l’adhésion à cette discipline est définitive et exclusive, alors soyez <sûr/sûre> de faire le bon choix !"}, {"Pour en apprendre davantage sur l'ingénierie gobeline, apporter le Manuel d'Ingénierie à Nixx Elastirouage, à Gadgetzan, en Tanaris."}},
[3634] = {"Ingénierie des gnomes", {"L'ingénierie au niveau d'expert connaît deux disciplines : celle des gnomes et celle des gobelins. Bien que ce soit du gâchis à mon sens, vous devriez vous spécialiser dans celle des gnomes. Ils fabriquent des objets et des gadgets qui se prétendent révolutionnaires, mais qui fonctionnent rarement.","<nom>, lisez ce manuel. Si ça vous intéresse, apportez-le au maître-artisan Suprétincelle dans Forgefer. Mais le choix de cette discipline est définitif et exclut l'autre, alors soyez <sûr/sûre> de votre choix !"}, {"Pour en apprendre davantage sur l'ingénierie gnome, apporter le Manuel d'Ingénierie au maître-artisan Suprétincelle, à Forgefer."}},
[3635] = {"Ingénierie des gnomes", {"L'ingénierie au niveau d'expert enseigne deux disciplines : celle des gnomes et celle des gobelins. Bien que ce soit du gâchis à mon sens, vous devriez connaître celle des gnomes. Ils fabriquent des objets et des gadgets pour prétendument changer le monde, mais ils fonctionnent rarement.","<nom>, lisez ce manuel. Si ça vous intéresse, apportez-le à Orguignard Odipnoticus à Baie-du-Butin. Mais attention : l’adhésion à cette discipline est définitive et exclusive, alors soyez <sûr/sûre> de faire le bon choix !"}, {"Pour en apprendre davantage sur l'ingénierie gnome, apporter le Manuel d'Ingénierie à Orguignard Odipnoticus à Baie-du-Butin."}},
[3636] = {"Apportez la Lumière", {"Le Fléau est une menace persistante pour l’Alliance, vous le savez fort bien. Récemment, nous avons découvert quelque chose, <nom>.","Dans les Tarides, il y a un amas de ronces, les Souilles de Tranchebauge. C’était autrefois la demeure de hurans normaux, mais maintenant un pacte y a été fait avec le Fléau, pour créer des créatures beaucoup plus mauvaises… Elles sont gouvernées par la liche Amnennar, dite le Porte-Froid.","Soyez le porte-parole de la Lumière, <nom> ; mettez fin à cette mauvaise union."}, {"Tuer Amnennar le Porte-Froid, aux Souilles de Tranchebauge, pour l’Archevêque Benedictus."}},
[3637] = {"Ingénierie des gnomes", {"L'ingénierie au niveau d'expert enseigne deux disciplines : celle des gnomes et celle des gobelins. Bien que ce soit du gâchis à mon sens, vous devriez connaître celle des gnomes. Ils fabriquent des objets et des gadgets pour prétendument changer le monde, mais ils fonctionnent rarement.","<nom>, lisez ce manuel. Si ça vous intéresse, apportez-le à Orguignard Odipnoticus à Baie-du-Butin. Mais attention : l’adhésion à cette discipline est définitive et exclusive, alors soyez <sûr/sûre> de faire le bon choix !"}, {"Pour en apprendre davantage sur l'ingénierie gnome, apporter le Manuel d'Ingénierie à Orguignard Odipnoticus à Baie-du-Butin."}},
[3638] = {"Le Vœu du secret", {"L'ingénierie des gobelins traite de l'utilisation pratique des explosifs ! Nos schémas nous permettent de faire des bombes et des armes puissantes ; c’est le thème du manuel que vous lisez.","Depuis toujours, ce manuel est lié par un Vœu du secret couvrant tous nos schémas. Une fois que vous serez Ingénieur gobelin, vous ne pourrez plus accéder à l'Ingénierie des gnomes. Si vous voulez toujours devenir Ingénieur gobelin, signez ce document vous engageant au secret et revenez me voir. Réfléchissez soigneusement, <nom>."}, {"Pour devenir un Ingénieur gobelin, cliquer avec le bouton droit sur le Vœu de secret et retourner voir une fois encore Nixx Elastirouage dans Gadgetzan."}},
[3639] = {"Montrez votre travail", {"Cette lueur dans vos yeux me dit tout ce que j’ai besoin de savoir : vous voulez faire sauter des trucs. Vous êtes un ingénieur habile, <nom>, mais la question n'est pas là. Je veux analyser votre travail pour orienter vos efforts futurs dans une direction plus… gobeline. J'ai besoin des échantillons suivants : 20 grandes bombes en fer, 20 bâtons de dynamite solide et 5 moutons explosifs – avec leurs crans de sûreté armés, merci.","Soumettez-moi vos réalisations et je ferai de vous un ingénieur gobelin officiel !"}, {"Apporter 20 Grandes bombes en fer, 20 Dynamites solides et 5 Moutons explosifs à Nixx Elastirouage dans Gadgetzan."}},
[3640] = {"Le Vœu du secret", {"L'ingénierie des gnomes consiste à mettre en forme la théorie de l'ingénierie pour servir nos besoins ! Nos schémas nous permettent de construire des appareils contrôlant le monde qui nous entoure. Le manuel que vous avez lu traite de ce sujet.","Il traite également du Vœu du secret qui recouvre nos schémas. Lorsque vous adhérez à l'Ingénierie des gnomes, vous ne pouvez plus revenir en arrière. Vous ne pourrez jamais plus accéder à l'Ingénierie des gobelins.","Si vous souhaitez toujours devenir Ingénieur gnome, signez ce document qui vous engage à garder le secret à vie et revenez me parler. Prenez votre décision après mûre réflexion, <nom>."}, {"Pour devenir un Ingénieur gnome, cliquer avec le bouton droit sur le Vœu de secret et retourner voir une fois encore le maître-artisan Suprétincelle, à Forgefer."}},
[3641] = {"Montrez votre travail", {"Si vous vous sentez <prêt/prête> à fabriquer des objets vraiment faramineux, vous êtes au bon endroit. Je vois bien que vous êtes déjà un ingénieur compétent. Cependant, je souhaite analyser votre travail pour savoir comment introduire les techniques gnomes dans vos travaux futurs. J'ai besoin des échantillons suivants : six tubes en mithril, une lunette de précision et deux leurres perfectionnés.","Soumettez-moi ces objets et je ferai de vous un ingénieur gnome officiel, ayant accès à tous les schémas !"}, {"Apportez 6 Tubes en mithril, 1 Lunette de précision et 2 Leurres perfectionnés au Maître-bricoleur Suprétincelle, à Forgefer."}},
[3642] = {"Le vœu du secret", {"L'ingénierie des gnomes consiste à mettre en forme la théorie de l'ingénierie pour servir nos besoins ! Nos schémas nous permettent de construire des appareils contrôlant le monde qui nous entoure. Le manuel que vous avez lu traite de ce sujet.","Il traite également du Vœu du secret qui recouvre nos schémas. Lorsque vous adhérez à l'Ingénierie des gnomes, vous ne pouvez plus revenir en arrière. Vous ne pourrez jamais plus accéder à l'ingénierie des gobelins.","Si vous souhaitez toujours devenir Ingénieur gnome, signez ce document qui vous engage à garder le secret à vie et revenez me parler. Prenez votre décision après mûre réflexion, <nom>."}, {"Pour devenir un Ingénieur gnome, cliquer avec le bouton droit sur le Vœu de secret et retourner voir une fois encore Oglethrope Odipnoticus à Baie-du-Butin."}},
[3643] = {"Montrez votre travail", {"Si vous vous sentez <prêt/prête> à fabriquer des objets vraiment faramineux, vous êtes au bon endroit. Je vois bien que vous êtes déjà un ingénieur compétent. Cependant, je souhaite analyser votre travail pour savoir comment introduire les techniques gnomes dans vos travaux futurs. J'ai besoin des échantillons suivants : six tubes en mithril, une lunette de précision et deux leurres perfectionnés.","Soumettez-moi ces objets et je ferai de vous un ingénieur gnome officiel, ayant accès à tous les schémas !"}, {"Apportez 6 Tubes en mithril, 1 Lunette de précision et 2 Leurres perfectionnés à Orguignard Odipnoticus à Baie-du-Butin."}},
[3644] = {"Le renouvellement de la Carte de membre", nil, nil},
[3645] = {"Le renouvellement de la Carte de membre", nil, nil},
[3646] = {"Le renouvellement de la Carte de membre", nil, nil},
[3647] = {"Le renouvellement de la Carte de membre", nil, nil},
[3661] = {"Dans les faveurs d'Élune ?", {"Les indomptables sont des créatures tout à fait imprévisibles, <nom>. Capables d'une douceur incroyable, elles sont féroces si on menace ce qui est important à leurs yeux.","On dit que ces bêtes auraient été créées par Élune, et j’aimerais savoir si c’est vraiment le cas.","J’ai récemment entendu parler d’une population d’indomptables vivant dans les Hinterlands ; des chouettards vicieux, des chouettards primitifs et des chouettards sauvages. Ils hantent les terres sauvages, là-bas. Pourriez-vous aller collecter quelques-unes de leurs plumes pour moi ?"}, {"Collecter 15 Plumes d'Indomptable dans les Hinterlands, pour Erelas Ambreciel au village de Rut'theran."}},
[3681] = {"Le Tome de divinité", {"Il me navre d'interrompre vos voyages, <race>, mais je pense que vous serez <intéressé/intéressée> d'apprendre que Hurlevent a récemment envoyé un message disant que tous les paladins devaient s'y rendre pour y être entraînés.","Si je n'en savais pas plus, je dirais que vous êtes sur le point d'atteindre un certain stade et qu'il est temps pour vous de commencer l'étude des Tomes.","Cherchez Duthorian Rall à Hurlevent. D'habitude, il enseigne aux paladins dans la cathédrale de la Lumière."}, {"Parler à Duthorian Rall à Hurlevent."}},
[3701] = {"Les ruines fumantes de Thaurissan", {"Rendez-vous dans les ruines fumantes de Thaurissan, dans les steppes Ardentes, et réunissez autant de renseignements que possible.","On raconte que de mystérieuses reliques jonchent les ruines. Peut-être devriez-vous commencer vos recherches par elles, <nom>. Attention aux gardiens de Thaurissan. Ces assassins veillent à ce que les secrets qui y sont enfouis ne s'échappent pas."}, {"Allez dans les ruines de Thaurissan, dans les Steppes Ardentes. Récupérez des informations sur les Reliques de Thaurissan, puis retournez auprès de l’Historienne royale Archesonus."}},
[3702] = {"Les ruines fumantes de Thaurissan", {"Est-ce que vous aimeriez entendre une petite histoire sur les nains sombrefers ?"}, {"Écouter l'historienne royale Archesonus raconter l'histoire de Thaurissan."}},
[3721] = {"Un OOX rien que pour vous", nil, nil},
[3741] = {"Le collier d'Hilary", {"Mon amie Hilary a perdu son collier, et nous ne parvenons pas à le retrouver. Nous avons pêché toute la journée ici, et nous avons déjà cherché aux alentours. Je crois qu'il est tombé dans le lac. Nos parents ne veulent pas que nous y nagions... Ma maman dit qu'il y a des choses méchantes sous l'eau. Mais vous n'avez pas l'air d'avoir peur des choses méchantes.","Vous pouvez retrouver son collier, s'il vous plaît ?"}, {"Trouver le Collier d'Hilary et le rapporter à Hilary à Comté-du-Lac."}},
[3761] = {"L’humus d’Un’Goro", {"Il paraît que la terre du Cratère d'Un'Goro est naturellement riche en propriétés magiques puissantes. Le Cercle cénarien souhaite étudier cette terre. Rendez-vous au Cratère d'Un'Goro et prélevez suffisamment de terre pour que nous puissions travailler un bon moment.","Cette terre riche s’agglomère parfois, et certaines bêtes peuvent en porter sur elles des fragments utilisables. Apportez-les tous à Ghede. Cherchez simplement des monticules de terre et des taurens en train de beugler des ordres et vous le trouverez. C'est un bon gars, mais ne l'énervez pas."}, {"Apporter 20 Échantillons d'humus d'Un'Goro à Ghede, aux Pitons-du-Tonnerre."}},
[3762] = {"L'aide à l'archidruide Totem-runique", {"Pardonnez-moi, <classe>, mais j'aimerais prendre quelques instants de votre temps, si cela est possible.","Le Cercle cénarien des Pitons-du-Tonnerre est très intéressé par des <aventuriers aguerris tels/aventurières aguerries telles> que vous pour lui prêter main-forte dans un projet crucial. Bien que je n’en connaisse pas les détails, je peux vous dire que Hamuul Totem-runique lui-même mène ce projet.","Je vous en prie, si cela vous intéresse, allez lui parler en personne à la cime des Anciens aux Pitons-du-Tonnerre."}, {"Parler à l’Archidruide Hamuul Totem-Runique aux Pitons-du-Tonnerre."}},
[3763] = {"L'aide à l'archidruide Forteramure", {"Pardonnez-moi, <classe>, mais j'aimerais prendre quelques instants de votre temps si la chose est possible.","Le Cercle cénarien à Darnassus recherche des <aventuriers aguerris tels/aventurières aguerries telles> que vous pour lui prêter main-forte dans des recherches capitales. Bien que je ne connaisse pas les détails, je peux vous dire que l'archidruide Fandral Forteramure en personne mène ces recherches.","Si cela vous intéresse, allez directement lui parler à l'Enclave cénarienne à Darnassus."}, {"Parler à l’Archidruide Fandral Forteramure à Darnassus."}},
[3764] = {"L’humus d’Un’Goro", {"On raconte que l'humus du Cratère d'Un'Goro est enrichi de propriétés magiques puissantes. Je veux séparer le vrai de la rumeur ; partez pour les terres sauvages du Cratère d'Un'Goro et récupérez suffisamment d'échantillons de qualité de son sol afin que nous puissions l'étudier sans discontinuer.","Vous pourriez considérer que cette simple tâche de récupération est indigne de vous, mais ce genre de considération ne vous ferait apprécier ni de moi ni du Cercle cénarien. Apportez les échantillons à Jenal, qui doit sans aucun doute perdre son temps dehors derrière cet arbre. Cherchez simplement le tas de terre. C'est lui qui s'occupera de la logistique, pas moi."}, {"Apporter 20 Échantillons d'humus d'Un'Goro à Jenal dans l'Enclave cénarienne à Darnassus."}},
[3765] = {"La dépravation au loin", {"Hurlevent. Tellement bouillonnant d’activité !","Je viens de loin pour chercher de l’aide. On dit que de courageux aventuriers foulent les rues de cette cité.","Mon frère, Gershala, a besoin d’aide à Sombrivage. Il demeure dans la ville côtière d’Auberdine.","Le voyage est long et périlleux. C’est pourquoi je demande l’aide d’<un/une:c> <classe> <expérimenté/expérimentée:c> comme vous."}, {"Aller voir Gershala Murmenuit à Auberdine."}},
[3781] = {"Recherches sur la fibre d'aurore", {"Nous avons planté des graines de Tharlendris dans de la terre du Cratère d'Un'Goro, avec ce que nous appelons une Sacoche de printemps. Ces graines ont germé et donné un grand nombre d'herbes fortes et variées. Parmi elles, il y a la Fibre d'aurore, une herbe dont on sait peu de chose. J'ai l’intention de percer ses mystères.","Apportez ceci à Mathrengyl, en bas, et il vous remettra quelques graines afin que vous puissiez commencer. Si vous veniez à en manquer, vous devrez lui en acheter."}, {"Apporter le Bon pour des graines de l'archidruide à Mathrengyl Pasdelours dans l'Enclave cénarienne à Darnassus."}},
[3782] = {"La recherche sur la Fibre d'aurore", {"Nous avons planté des graines de Tharlendris dans de la terre du Cratère d'Un'Goro, avec ce que nous appelons une Sacoche de printemps. Ces graines ont germé et donné un grand nombre d'herbes fortes et variées. Parmi elles, il y a de la Fibre d'aurore, une herbe dont on sait peu de chose. J'espère pouvoir percer ses mystères, pour le Cercle.","Apportez ce bon à ma sœur Bashana et elle vous remettra quelques graines afin que vous puissiez nous aider. Si vous veniez à en manquer, vous devrez lui en acheter."}, {"Apporter le Bon pour des graines à Bashana Totem-Runique aux Pitons-du-Tonnerre."}},
[3783] = {nil, {"Mes amis ne cessent d'avoir de grandes idées. Et je n'aurais jamais cru être capable de les concurrencer… jusqu'à ce jour ! Je viens juste de dessiner les plans de quelque chose de vraiment merveilleux ! Si vous me promettez de ne pas le répéter, je vais vous dire mon secret...","Je vais construire un yéti mécanique ! Non seulement mes amis ne vont pas le croire quand ils le verront, mais une fois qu'ils se seront remis de leur surprise, je sais qu'ils seront vraiment impressionnés !","Pour commencer, j'aurais besoin de plusieurs fourrures épaisses de yétis, prélevées sur des yétis Chardon de glace. Voudriez-vous m'aider ? Vous en trouverez au sud-est."}, {"Récupérez 10 Fourrures épaisses de yéti pour Umi Sarcafroisse, à Long-Guet."}},
[3784] = {"L'aide à l'archidruide Totem-runique", {"Pardonnez-moi, <classe>, mais j'aimerais prendre quelques instants de votre temps, si cela est possible.","Le Cercle cénarien des Pitons-du-Tonnerre est très intéressé par des <aventuriers aguerris tels/aventurières aguerries telles> que vous pour lui prêter main-forte dans un projet crucial. Bien que je n’en connaisse pas les détails, je peux vous dire que Hamuul Totem-runique lui-même mène ce projet.","Je vous en prie, si cela vous intéresse, allez lui parler en personne à la cime des Anciens aux Pitons-du-Tonnerre."}, {"Parler à l’Archidruide Hamuul Totem-Runique aux Pitons-du-Tonnerre."}},
[3785] = {"Recherches sur la fibre d'aurore", {"Voici la Sacoche de printemps dont vous aurez besoin pour utiliser les graines de Tharlendris. Vous devrez avoir également des échantillons d'humus d'Un'Goro sous la main comme fertiliseur pour les graines. Servez-vous juste de la sacoche lorsque vous serez en possession de tout ce qui est nécessaire. Elle s'occupera du reste.","Lorsque vous aurez suffisamment de fibre d'aurore, apportez-les-moi ici à l'Enclave. Veuillez ne pas déranger l'archidruide. C'est un homme très occupé, et il ne s'agit pas de le déranger à chaque fois que quelqu'un arrive avec un nouveau sac de fibre d'aurore !"}, {"Utiliser une Sacoche de printemps avec une pochette de graines de Tharlendris et deux Échantillons d'humus d'Un'Goro pour essayer de cultiver des Fibres d'aurore.","Apporter 10 Fibres d'aurore à Mathrengyl Pasdelours à Darnassus."}},
[3786] = {"Recherches sur la Fibre d'aurore", {"Voici la Sacoche de printemps dont vous aurez besoin pour utiliser les graines de Tharlendris. Vous devrez avoir également des échantillons d'humus d'Un'Goro sous la main comme fertilisant pour les graines. Servez-vous juste de la sacoche lorsque vous serez en possession de tout ce qui est nécessaire. Elle s'occupera du reste.","Lorsque vous aurez suffisamment de fibres d'aurore, apportez-le-moi ici sur la cime des Anciens. Veuillez ne pas déranger l'archidruide. Ses recherches l'occuperont beaucoup, de même que ses nombreux soucis le préoccuperont bien assez."}, {"Utiliser une Sacoche de printemps avec une pochette de graines de Tharlendris et deux Échantillons d'humus d'Un'Goro pour essayer de cultiver des Fibres d'aurore.","Apporter 10 Fibres d'aurore à Bashana Totem-runique aux Pitons-du-Tonnerre."}},
[3787] = {"La requête de Gentecime", {"Excusez-moi, mon <ami/amie>…","L'un de mes collègues voudrait vous entretenir à propos des recherches que vous effectuez conjointement avec le Cercle cénarien. Il est druide et se nomme Quintis Gentecime. Il demeure au bastion de Pennelune, en Féralas, et voudrait vous parler de la Fibre d'aurore. Il insiste pour que notre conversation n'atteigne en aucun cas les oreilles de l'archidruide…","Si cela vous intéresse, allez lui parler. Il ne fait aucun doute que tout vous sera révélé si vous allez le voir."}, {"Parler à Quintis Gentecime dans le bastion de Pennelune."}},
[3788] = {"La requête de Gentecime", {"Excusez-moi, mon <ami/amie>…","L'un de mes collègues voudrait vous entretenir à propos des recherches que vous effectuez conjointement avec le Cercle cénarien. Il est druide et se nomme Quintis Gentecime. Il demeure ici au bastion de Pennelune et voudrait vous parler de la Fibre d'aurore. Il insiste pour que notre conversation n'atteigne en aucun cas les oreilles de l'archidruide.","Si cela vous intéresse, allez lui parler. Il ne fait aucun doute que tout vous sera révélé si vous allez le voir."}, {"Parler à Quintis Gentecime dans le bastion de Pennelune."}},
[3789] = {"Assister l'archidruide Forteramure", {"Pardonnez-moi, <classe>, mais j'aimerais prendre quelques instants de votre temps si la chose est possible.","Le Cercle cénarien à Darnassus recherche des <aventuriers aguerris tels/aventurières aguerries telles> que vous pour lui prêter main-forte dans des recherches capitales. Bien que je ne connaisse pas les détails, je peux vous dire que l'archidruide Fandral Forteramure en personne mène ces recherches.","Si cela vous intéresse, allez directement lui parler à l'Enclave cénarienne à Darnassus."}, {"Parler à l’Archidruide Fandral Forteramure à Darnassus."}},
[3790] = {"Assister l'archidruide Forteramure", {"Pardonnez-moi, <classe>, mais j'aimerais prendre quelques instants de votre temps si la chose est possible.","Le Cercle cénarien à Darnassus recherche des <aventuriers aguerris tels/aventurières aguerries telles> que vous pour lui prêter main-forte dans des recherches capitales. Bien que je ne connaisse pas les détails, je peux vous dire que l'archidruide Fandral Forteramure en personne mène ces recherches.","Si cela vous intéresse, allez directement lui parler à l'Enclave cénarienne à Darnassus."}, {"Parler à l’Archidruide Fandral Forteramure à Darnassus."}},
[3791] = {"Le Mystère des Fibres d'aurore", {"Je vais être franc avec vous, <nom>. Cela m'intéresse – non, cela m'inquiète ! – de savoir pourquoi l'archidruide de Darnassus utilise le couvert du Cercle cénarien, tout autant que ses ressources, pour se procurer d’aussi grandes quantités de Fibre d'aurore. Je suis chercheur et mon domaine concerne les malédictions. J'ai entendu dire que la Fibre d'aurore, placée dans certaines conditions, développe des propriétés similaires à celles d'autres herbes utilisées dans des malédictions primitives.","Je veux en savoir davantage, et je sollicite votre aide. Apportez-moi dix Fibres d'aurore et je m’occuperai de vous."}, {"Apporter 10 Fibres d'aurore à Quintis Gentecime au bastion de Pennelune."}},
[3792] = {"De la Fibre d'aurore au bastion de Feathermoon", nil, nil},
[3801] = {"Héritage Sombrefer", {"Salutations, <nom>. Je me nomme Franclorn Le Forgebusier. Oui, le Franclorn Le Forgebusier, inventeur des méthodes d'approche architectonique de la roche. Vous êtes sans aucun doute déjà <passé/passée> près de l'une de mes créations lors de vos aventures dans notre monde ravagé par la guerre. Vous y êtes peut-être même <entré/entrée>.","Vous vous demandez probablement ce que je fais ici et ce que j'attends de vous. Deux excellentes questions.","J'ai besoin de votre aide, bien sûr. En échange, je vous remettrai la clé des profondeurs de ma cité maudite."}, {"Parler à Franclorn Le Forgebusier à la Tombe du Forgebusier, au Mont Rochenoire, si obtenir une clé de la Cité majeure vous intéresse."}},
[3802] = {"Héritage Sombrefer", {"Vous trouverez Fineous Sombrevire au-delà du Cercle de la loi, dans la Chambre de l'artisanat. Tuez ce misérable chien et récupérez Souillefer.","Apportez Souillefer au sanctuaire de Thaurissan et placez le marteau à la place qui est la sienne : dans les mains de la statue érigée en mon honneur.","Alors le compartiment dans lequel j'ai placé la clé maîtresse s'ouvrira. Plus important encore, Souillefer restera entre mes mains pour l'éternité. S'ils tentent de l'enlever, la statue et le marteau se briseront, à jamais perdus."}, {"Tuer Fineous Sombrevire et récupérer le grand marteau, Souillefer. Apporter Souillefer au sanctuaire de Thaurissan et le placer sur la statue de Franclorn Le Forgebusier."}},
[3803] = {"De la fibre d'aurore pour Darnassus", nil, nil},
[3804] = {"Des Fibres d'aurore pour les Pitons du Tonnerre", nil, nil},
[3821] = {"Le Rocher des Cognepeurs", {"Sha'ni a été envoyée dans les steppes Ardentes pour enquêter sur les rites étranges des ogres brûlentrailles. C'était il y a plus de trois semaines.","Sangredent dit qu'il n'a plus de nouvelle de son régiment et que je ferais mieux de l'oublier et de me concentrer sur ce que j'ai à faire. Incroyable, non ? C'est ma femme !","Je suis dans l'impossibilité de quitter mon poste, <nom>, mais je dois savoir ce qui lui est arrivé. Partez à sa recherche dans les steppes Ardentes, je vous en prie."}, {"Aventurez-vous dans les Steppes Ardentes et découvrez ce qu'il est advenu de Sha'ni Fière-défense."}},
[3822] = {"Krom'Grul", {"Tout mon régiment a été capturé, <nom>. Tous ont été tués, sauf moi.","Leur chef, Krom'Grul, voulait me réserver pour un genre de rituel.","J'ai été enchaînée à cet autel et battue à mort par ce monstre. J’ai rendu l’âme après une heure de cette torture. Alors, il a arraché mon anneau de nez de mariage et l'a passé à son doigt. Je n'ai jamais vu une telle sauvagerie !","Trouvez Krom'Grul et tuez-le ! Rapportez mon anneau à Thal'trak.","<nom>, dites-lui que nous nous rencontrerons à nouveau... dans une autre vie."}, {"Récupérez l’Anneau de nez de Sha'ni sur Krom'Grul, et rapportez-le à Thal'trak, à Kargath. Fouillez les différentes grottes et mines du Rocher des Cognepeurs."}},
[3823] = {"Exterminer les Brûlentrailles", {"Nous devons frapper nos ennemis avant qu'ils ne nous frappent !","Je serai bref, soldat.","Les ogres brûlentrailles des steppes Ardentes sont les alliés des orcs rochenoires. Ils collaborent étroitement avec ces maudits orcs en gardant le rocher des Cognepeurs, riche en minerais et en veillant à la sécurité des lignes d'approvisionnement.","Laissez votre signature, <nom>. Bousculez leurs rangs et revenez me voir quand vous aurez accompli votre travail.","<Oralius serre ses poings sur du vide, comme s'il étranglait quelqu'un.>","JE VAIS TE TUER, WINKY !"}, {"Tuer 15 Ogres-mages brûlentrailles, 7 Ogres brûlentrailles et 7 Brutes brûlentrailles."}},
[3824] = {"Le seigneur-brute Gor'tesh", {"Ils faiblissent, je vous assure ! Nous devons à présent porter un coup décisif.","Le seigneur-brute Gor'tesh doit être tué. Plus important encore, vous devez me rapporter sa tête afin que je puisse préparer la troisième partie de notre attaque sur les ogres brûlentrailles.","Attention, soldat : Gor'tesh n'est pas établi au rocher des Cognepeurs lui-même, mais dans un bâtiment des orcs rochenoires, à l'ouest du rocher des Cognepeurs.","Vos ordres sont simples : trouvez Gor'tesh et décapitez-le. Revenez me voir une fois votre travail accompli.","FERME-LA, WINKY !"}, {"Trouver la Tête coupée de Gor'tesh."}},
[3825] = {"Une tête d'ogre au bout d'une pique = une fête", {"Il vous reste une dernière chose à faire, soldat.","J'ai pris la liberté de planter la tête de Gor'tesh sur une pique. À présent, tout ce que vous avez à faire, c'est d'apporter cette pique au sommet du rocher des Cognepeurs et de la ficher dans le sol. Après cela, vous pourrez admirer ces lâches d'ogres courir pour sauver leur vie ! Après vous être <amusé/amusée> un peu, revenez et je vous récompenserai pour votre dur labeur.","<Oralius semble perdre contact avec la réalité.>"}, {"Prendre la Tête coupée de Gor'tesh et la placer au sommet du Rocher des Cognepeurs. Chercher un monticule de terre meuble pour planter la pique."}},
[3841] = {"Un orphelin à la recherche d'un foyer", {"Oh, salut <nom> ! Cela me fait plaisir de vous revoir. Comment s'est passé votre voyage à Darnassus ?","Je crois que vous serez <heureux/heureuse> d'apprendre que nous avons fait pas mal de progrès pendant que vous étiez <parti/partie>. Nous avons même sauvé un nid plein d'œufs. Malheureusement, la mère est morte avant que nous puissions l'atteindre.","Dites-moi, en voudriez-vous un pour en faire un animal de compagnie ? Ils semblent bientôt prêts à éclore et ont besoin <d'un père attentif/d'une mère attentive>. Qu'en dites-vous ?","Si cela vous intéresse, apportez l'un de ces œufs à Quentin. Il se trouve à la piste de course des Salines. Il pourra vous en apprendre davantage."}, {"Parler à Quentin aux Mille pointes."}},
[3842] = {"Une courte incubation", {"Les Tisselune, hein ? Fascinant. J'aimerais que les Fées fléchetteuses comprennent ce que cela veut dire d'avoir ces deux-là veillant sur elles.","Les Tisselune sont tout à fait compétentes.","Ainsi, ce sont elles qui vous ont offert l'œuf. Dans ce cas, vous allez en être responsable aussi longtemps que vous le souhaitez. Il semble qu'il sera bientôt prêt à éclore, mais nous devons nous assurer qu'il va bien et qu'il n'a subi aucun dommage.","J'ai simplement besoin de deux élixirs de robustesse pour lancer un petit sort afin de renforcer sa santé. J’ai ici le reste des ingrédients."}, {"Apporter 2 Elixirs de Robustesse à Quentin, aux Mille pointes."}},
[3843] = {"Un tout nouveau membre dans la famille", {"Un spécimen magnifique, <nom>. Si cette créature arrive à terme, ce sera un animal plein de santé !","Si vous avez l'intention de garder cette créature, permettez-moi de vous suggérer une dernière chose. Rendez-vous au Nid-de-l'Aigle dans les Hinterlands. Allez parler au nain Agnar Domptebêtes, c'est un dresseur d'animaux renommé et il vous conseillera pour mener à bien les dernières étapes de la gestation.","Mais dépêchez-vous, <nom>, cet œuf est fragile et nécessite des soins appropriés."}, {"Emmenez votre Œuf fragile de Fée fléchetteuse dans les Hinterlands et parlez à Agnar Domptebêtes. Dépêchez-vous, vous n’avez qu’une heure pour le trouver avant que le petit ne meure dans l’œuf."}},
[3844] = {"C'est un secret pour tout le monde", {"Un radeau brisé repose le long du rivage. Vu qu'il ne s'agit que d'un petit étang, on peut se demander comment ce radeau a échoué ici. Les traces sur les arbres aux alentours indiquent cependant que le niveau de l'eau pourrait varier.","Explorer ce secteur pourrait révéler des indices supplémentaires..."}, {"Rechercher dans l'étang un indice sur l'identité du propriétaire du radeau brisé."}},
[3845] = {"C'est un secret pour tout le monde", {"Après vous être <emparé/emparée> du paquet, vous l'apportez jusqu'au rivage. Peut-être que si vous examinez les objets qui l'emplissent, vous pourrez en découvrir davantage sur l'identité de son propriétaire.","Quelques voyageurs égarés se trouvent au refuge des Marshal. Il se peut que vous en appreniez plus en ces lieux."}, {"Examiner les objets du paquet et les apporter à leur propriétaire."}},
[3861] = {"COT-COT-CODEC !", {"« Cot... cot... cot... CODEEEC !","Cot... cot... cot. »","La poule vous regarde puis commence à griffer la terre. On dirait qu’elle est en train d’écrire un mot sur le sol. Est-ce que cela pourrait être... S-A-L-D-E-A-N ? Avant que vous ne puissiez demander quoi que ce soit, la poule s’ébroue et retourne vaquer à ses occupations."}, {"Trouver de la Nourriture spéciale pour poulet et l'apporter à la poule que vous voulez aider. Lorsque vous l'aurez trouvée, encouragez-la (/acclame) avant d'essayer de lui donner la Nourriture spéciale pour poulet."}},
[3881] = {"Le sauvetage de l'expédition", {"Je ne sais pas comment vous êtes <parvenu/parvenue> jusqu'ici, mais votre arrivée me réjouit ! Je me nomme Williden Marshal et je mène une expédition neutre. Notre dernière tentative s'est soldée par d'innombrables désastres. Nous avons sous-estimé le danger représenté par le Cratère d'Un'Goro.","Alors que nous fuyions nos campements, dans le sud-ouest et le nord-est du cratère, nous avons laissé derrière nous des fournitures d’une importance vitale. Vous avez l’air valeureux, pouvez-vous nous aider à les récupérer ?"}, {"Apporter une Caisse de nourriture et de l’Équipement de recherche à Williden Marshal dans le Cratère d'Un'Goro."}},
[3882] = {"Faites rouler les os", {"On pourrait penser qu'être chef terrassier pour les Expéditions Marshal signifie que j'aime creuser. Il y a des choses bien plus désagréables à faire en ce monde… mais aucune ne me vient à l'esprit.","Nous étions supposés creuser dans les Coteaux de la terreur - quel nom FANTASTIQUE, soit dit en passant - à la recherche de stégodons et de dimeurtrodons morts ! Eh bien, figurez-vous qu'il en reste aussi beaucoup de vivants ! Wil et Hol insistent encore pour étudier ce piège mortel, alors nous devons ramener ces os de dinosaures. Entre vous et moi, s'ils proviennent de créatures vivantes, ils feront tout aussi bien l'affaire."}, {"Apporter 8 Os de dinosaure à Étincelle Minerien, au cratère d'Un'Goro."}},
[3883] = {"Écologie étrangère", {"On ne va pas arrêter les recherches à cause d'un échec ! Avant d'arriver ici, nous faisions des études sur les insectes Gorishi dans la partie sud du cratère. Leur présence est une nouveauté dans l'écologie d'Un'Goro et nous voulons savoir pourquoi.","Dans leur ruche, il doit exister une écloserie pour les larves. Vous pouvez identifier ces lieux parce que des larves sont suspendues. Utilisez cette fiole pour prélever un échantillon de la paroi de la ruche près de l'une des écloseries. Rapportez-la-moi pour que je l'étudie !"}, {"Utiliser la Fiole de raclement pour récolter un Échantillon de paroi de la ruche dans une des nurseries de la ruche Gorishi, à l’intérieur du cratère d'Un'Goro. Chercher les chambres qui contiennent des larves suspendues.","Apporter l’Échantillon de paroi de la ruche à Hol'anyee Marshal dans le cratère d'Un'Goro."}},
[3884] = {"Le journal de Williden", {"Vous avez découvert un journal en piteux état. On peut encore en déchiffrer certaines parties, cependant. Notamment le nom de son propriétaire, inscrit à l'intérieur : Williden Marshal. Si le journal dit vrai, alors Williden devrait se trouver quelque part dans la partie nord du cratère. D'après ce que raconte le journal, lui et ses hommes sont partis pour des terres plus élevées et à l'abri des bêtes sauvages lorsque leurs camps de base se sont vus submergés."}, {"Rapporter le Journal à Williden Marshal quelque part dans le Cratère d'Un'Goro."}},
[3901] = {"Faites cliqueter les thorax !", {"Vous avez montré vos capacités aux Réprouvés, dans des conditions normales, <nom>. À présent, voyons ce que vous pouvez faire lorsque vous êtes sous pression.","Les squelettes Cliquethorax, d'autres sbires décérébrés du roi-liche, sont un peu plus résistants que les zombies auxquels vous avez fait face jusqu'alors. Réduisez leur nombre et démontrez votre appartenance aux Réprouvés. Quand vous aurez fini, ne vous attardez pas, revenez me voir."}, {"Tuez 12 Squelettes Cliquethorax, puis retournez voir le prêtre des ombres Sarvis au Glas."}},
[3902] = {"La chasse aux trésors", {"Vous, là ! Si vous cherchez à vous rendre utile, écoutez bien !","Nous avons besoin de recrues fraîchement déterrées pour se rendre au Glas et rechercher tout équipement utile. Il est sûrement remisé dans des piles de boîtes. Nous attendons de nouvelles recrues mais, à moins que nous ne voulions les voir déambuler tout nus, nous ferions mieux de commencer à faire de la récupération !","Allez-y, au travail, misérable sac d'os ! Et je ne récompenserai pas ceux qui n'y mettent pas d'ardeur."}, {"Fouiller Le Glas et ses alentours pour trouver 6 pièces de fournitures de récupération, et les ramener au Garde noir Saltain."}},
[3903] = {"Milly Osworth", {"Votre fiabilité est démontrée, <classe>. Vous n'avez pas peur de vous salir les mains, hein ?","J'ai une amie du nom de Milly Osworth, elle a un gros problème. Elle et son chariot se trouvent de l'autre côté de l'abbaye, près de l'étable. Je suis sûr que vos deux bras devraient lui être utiles, <nom>."}, {"Parler à Milly Osworth."}},
[3904] = {"Les vendanges de Milly", {"Aidez-moi, je vous en prie ! J'ai ramassé pratiquement tout mon raisin dans des seaux mais je les ai laissés dans les vignes, au sud-est.","Rapportez-moi ces seaux ! Sauvez ma récolte !"}, {"Apportez 8 seaux de la Récolte de Milly à Milly Osworth, à l’Abbaye de Comté-du-Nord."}},
[3905] = {"Le manifeste des vendanges", {"À présent que mes vendanges sont sauvées, apportez ce Manifeste des vendanges à Frère Neals. Il s'occupe du magasin de nourriture et de boisson de Comté-du-Nord, et il est certain qu'il sera ravi d'apprendre que nous avons du raisin frais.","Vous trouverez Frère Neals dans la tour de l'abbaye. C'est là qu'il aime déguster son vin."}, {"Apportez le Manifeste des vendanges à Frère Neals dans l'Abbaye de Comté-du-Nord."}},
[3906] = {"Discordance des flammes", {"Les anciens ont ressenti une perturbation parmi les éléments. Les nains sombrefers ont invoqué en notre monde un esprit vil qui menace de recouvrir nos terres d'un froid éternel. Nous devons enquêter sur cette dissonance de la flamme.","Partez pour le mont Rochenoire. Passez par l'Abîme de flamme et rendez-vous dans la carrière. C'est là que vous trouverez le seigneur du Feu : le Grand maître Pyron.","Rétablissez l'harmonie et revenez me voir."}, {"Partir pour la carrière dans le mont Rochenoire et tuer le Grand maître Pyron. Retourner voir Cœur-de-tonnerre une fois votre mission accomplie."}},
[3907] = {"Discordance des flammes", {"Je peux sentir la souillure de l'air qui vous entoure, <nom>. Il y en a un autre, caché dans les profondeurs de Rochenoire. C'est lui qui contrôle cette souillure.","Le vent et la terre pleurent son nom : Seigneur Incendius… mais quelqu'un… quelque chose… commande cet être. Il n'est qu'un émissaire.","Trouvez-le et découvrez où se cache son maître. Revenez me voir lorsque vous aurez obtenu cette information."}, {"Pénétrez dans les Profondeurs de Rochenoire et traquez le Seigneur Incendius. Tuez-le puis donner tous les renseignements que vous pourrez trouver à Cœur-de-tonnerre."}},
[3908] = {"C'est un secret pour tout le monde", {"Vous comprenez, j'ignore comment j'ai échoué ici. Et personne ne peut me le dire. C'est un mystère pour tout le monde ! Il semble que je ne puisse me souvenir de rien…","Ces objets que vous m'avez apportés… ils me rappellent quelque chose, quand même.","Mon épée... j'étais supposé la renforcer de quelque manière, mais je ne sais pas vraiment pourquoi. Cela me navre de ne pas pouvoir m'en souvenir et d'être incapable de vous en dire plus, je suis désolé !","Mais ce que je peux vous dire, c'est à qui l'apporter : Donova Antreneige, au Berceau-de-l'Hiver. Vous la trouverez près des sources chaudes. Elle peut y tremper l'épée."}, {"Apporter l’Épée de Linken à Donova Antreneige au Berceau-de-l'Hiver."}},
[3909] = {"L'élixir de Videre", {"Non, ce n'est pas vrai ! Linken ? Il a OUBLIÉ ?","Ah, je vois… il a perdu la mémoire…","Bien, voici son épée. Elle n'est pas terminée, cependant. Je connais celui qui pourrait l'achever, mais… parvenir à lui parler pourrait s'avérer difficile.","Écoutez, je sais qui peut vous aider. Ce serait trop long à expliquer en détail, mais vous devez obtenir de l'élixir de Videre. La seule personne qui peut vous aider à le trouver se nomme Gregan Gerbebière. Il est difficile de le localiser, mais aux dernières nouvelles, il campait dans les terres sauvages de Féralas."}, {"Rechercher Gregan Gerbebière au nord de Féralas. Apprendre de lui comment vous pouvez acquérir l'élixir de Videre, puis retourner voir Donova Antreneige, au Berceau-de-l'Hiver."}},
[3911] = {"Le dernier élément", {"Il y a du travail pour ceux qui s'aventurent dans les Profondeurs, <nom>.","Les Sombrefers peuvent créer des golems extrêmement puissants.","Les premiers rapports de nos espions indiquent que les nains se servent d'une unique source de puissance pour donner à leurs créations une puissance de destruction incomparable.","Imaginez simplement ce que nous pourrions faire de nos abominations si nous mettions la main sur cette Essence des éléments ! Fouillez les moindres recoins de cette cité si cela est nécessaire, mais ne revenez pas tant que vous n'avez pas découvert cette essence ! Votre récompense sera à la mesure du risque encouru."}, {"Partir pour les Profondeurs de Rochenoire et trouver 10 Essences des éléments. Commencer vos recherches par les golems et par les créateurs de golems. Vivian Lagrave a aussi marmonné quelque chose à propos d'élémentaires."}},
[3912] = {"Rendez-vous à la tombe", {"Cet élixir va vous permettre de vous entretenir avec quelqu'un que vous ne pourriez même pas voir en temps normal…","Apportez l'élixir de Videre et l'épée de Linken jusqu'au cimetière au-dehors de Gadgetzan, à Tanaris. Une fois <arrivé/arrivée>, buvez la potion. Les conséquences pourraient vous surprendre, mais en aucun cas n'ayez crainte.","Une fois que vous aurez votre \"nouvelle\" forme, dirigez-vous vers le nord jusqu'à ce que vous atteigniez les montagnes. Vous trouverez la personne à qui vous devez parler dans l'une des lézardes rocheuses. Il se nomme Gaeriyan.","Souvenez-vous ! Il ne vous parlera que si vous avez adopté cette forme..."}, {"Se rendre au cimetière au-dehors de Gadgetzan et boire l'élixir de Videre. Suivre le reste des instructions de Donova pour trouver Gaeriyan."}},
[3913] = {"Une situation grave", {"Si vous retournez au cimetière dont vous venez, <nom>, vous y trouverez une pierre tombale particulière. Celle-ci se démarquera des autres. Elle renferme des secrets.","Poussez la pierre, et vous les apprendrez…"}, {"Ressusciter, puis rechercher la pierre tombale que Gaeriyan vous a demandé de trouver."}},
[3914] = {"L'épée de Linken", {"Après un grand flash, vous pouvez à nouveau vous saisir de l'épée. Elle s'est transformée !"}, {"Remettre l’Épée excellente de Linken à Linken dans le cratère d'Un'Goro."}},
[3921] = {"Wenikee Boltbucket", {"Il se peut que j'aie cassé le Samophlange. Et ce n'est pas bon, car j'en avais déjà parlé à des collègues à Undermine et ils s'y intéressaient beaucoup. Nous devons trouver un moyen de le réparer !","Apportez le Samophlange à Wenikee Boltbucket, à l'avant-poste de la Horde entre les Tarides et Ashenvale. Elle est très intelligente et très curieuse. Si vous lui montrez le Samophlange, je suis sûr qu'elle sera enthousiaste à l'idée de le réparer."}, {"Apporter le Samophlange cassé à Wenikee Boltbucket."}},
[3922] = {"Les petites pièces", {"Je veux réparer ce samophlange, mais pour ce faire je vais avoir besoin de certaines choses. Je n'ai plus de rondelles de dix-sept cinq et il va m'en falloir un sacré paquet pour le travail qui nous attend.","Les endroits les plus proches où l'on peut en trouver sont la station de recherche non loin d'ici et la Videfange, à l'est. La KapitalRisk en conserve généralement dans ses seaux d'outils. Regardez dans les seaux à l'un ou l'autre endroit, et revenez me voir lorsque vous aurez récupéré suffisamment de rondelles de métal."}, {"Trouvez 10 Rondelles de métal dans des seaux autour de la Videfange."}},
[3923] = {nil, {"Hmm, ça n'a pas marché. J'ai placé des jauges supplémentaires sur le samophlange pour en mesurer l'intégrité structurelle, et elles donnent comme résultat une intégrité constante proche de zéro. Je pourrais ajouter d'autres cadrans et peut-être un ou deux leviers pour essayer de le réparer, mais... je crains que ça ne fasse qu'empirer les choses.","Vous devriez apporter le samophlange à Rilli Grassougob. Il travaillait autrefois pour la KapitalRisk. Si quelqu'un est capable de le réparer, c'est bien Rilli.","Vous pourrez le trouver à l'Atelier de Nogg à Orgrimmar, dans la Vallée de l'Honneur."}, {"Apportez le Samophlange cassé à Rilli Grassougob à Orgrimmar."}},
[3924] = {nil, {"Je pense pouvoir réparer le samophlange, mais je vais avoir besoin du manuel d'utilisation tout d'abord. Lorsque je travaillais pour la KapitalRisk, j'avais un rival du nom de Chef Cuivroncteur, qui connaissait des tas de secrets sur le samophlange, et qui donnait quelques rares informations sur l'appareil à ceux envers qui il avait confiance. Je ne faisais pas partie des rares élus...","Le Chef Cuivroncteur était généralement à la mine des Pierriers dans la partie nord-est des Tarides. Parvenez à en obtenir le manuel, et s'il en a distribué des pages à quiconque, vous devrez également les récupérer."}, {"Apportez le Manuel du samophlange à Rilli Grassougob à Orgrimmar."}},
[3941] = {"Un gnome dans le besoin", {"Réfléchir… réfléchir… je sais que je suis venu ici pour quelque chose d'important…","J'ai une idée, <nom>. Dans la caverne proche se trouve une gnome du nom de J.D. Elle a été très aimable envers moi lorsque je suis arrivé ici. Peut-être que désorienté comme je l'étais, je lui ai dit quelque chose dont elle pourrait se souvenir ?","Pourquoi n'iriez-vous pas la voir et lui demander ? Oh, mais attention ! Elle joue toujours avec ces cristaux brillants que j'ai vus dans le cratère. Je ne sais pas vraiment ce qu'elle prépare là-bas !"}, {"Trouver J.D. Collie dans la caverne du refuge des Marshal."}},
[3942] = {"Les souvenirs de Linken", {"Oh… Linken ? Oui, c'est un drôle de type. La première fois qu'il est venu ici, il marmonnait des propos incohérents. Il a dû recevoir un sacré coup sur la tête, si vous voulez mon avis.","Mais je ne peux pas m'empêcher de me souvenir de quelque chose qu'il disait, tout de même. Comme quoi il construisait un genre de totem. Il disait qu'il devait parler à quelqu'un du nom d'Eridan Ventebleu... dans un endroit appelé Gangrebois... je crois ?","Je ne pense pas qu'il puisse lui-même vous en dire plus. Si vous voulez en apprendre davantage, peut-être devriez-vous chercher en personne Eridan ?"}, {"Trouver Eridan Ventebleu dans le sud de Gangrebois."}},
[3961] = {"L'aventure de Linken", {"Et dire que pendant tout ce temps la raison pour laquelle Linken est venu ici était de nous débarrasser de Coursembrase… Il semble être une personne ordinaire, mais j'ai remarqué qu'il ne s'intéressait pas le moins du monde à mes expériences et à mes gadgets, qui passionnent d'ordinaire les gnomes. Il doit être un grand guerrier.","Apportez-lui le totem. Voyez si cela l'aide à se remémorer la raison pour laquelle il est venu ici."}, {"Apporter le Totem d'argent d'Aquementas à Linken au refuge des Marshal."}},
[3962] = {"C'est dangereux d'y aller seul", {"<nom>, je m'en souviens enfin… Je suis venu ici pour combattre Coursembrase. J'ai fait un long voyage, et il s'agissait de la dernière étape.","Mais ce n'est plus moi le héros… c'est vous. Avec tout ce que vous avez déjà accompli, c'est à vous d'en finir, pas à moi.","Confrontez-vous à Coursembrase, et battez-le. Il garde un artéfact appelé la Flamme dorée. Il procure une puissance plus vaste que tout ce que j'ai pu connaître, et il est plus que dangereux entre les mains de quelqu'un au cœur maléfique.","Ne partez pas <seul/seule>, ce serait trop dangereux."}, {"Partir pour la crête de la Fournaise, au sud du refuge des Marshal.","Trouver Coursembrase et utiliser le Totem pour dissiper son aura protectrice. Une fois Coursembrase vaincu, se procurer la Flamme dorée."}},
[3981] = {"Le commandant Gor'shak", {"Pauvre Gargal… il ne l'a même pas vue venir. SCHLACK ! La flèche est entrée exactement au centre du sommet de sa tête. Déjà que ce n'était pas une lumière avant, alors maintenant…","<Galamav grimace.>","Lorsque nous avons délogé la flèche, nous avons trouvé un message enroulé autour.","Sur ce message, il y avait un orc maladroitement dessiné derrière des barreaux et la signature du commandant Gor'shak.","S'il est toujours en vie dans les Profondeurs, il faut que vous le retrouviez.","Un piège, vous dites ? Sans blague ! C'est bien pour ça que c'est VOUS qui y allez !"}, {"Trouver le commandant Gor'shak dans les Profondeurs de Rochenoire.","Vous vous souvenez que le dessin maladroit représentait également des barreaux au-dessus du portrait. Peut-être devriez-vous rechercher un genre de prison."}},
[3982] = {"Que se passe-t-il ?", {"Je suis venu sous les auspices de Thrall. Pensiez-vous réellement que ces bouffons auraient pu me capturer ? Je me suis arrangé pour être fait prisonnier et pour pouvoir trouver des informations.","<Gor'shak chuchote.>","La princesse de Forgefer a été enlevée par l'Empereur Thaurissan. La Princesse Barbe-de-bronze est dans cette cité même ! Je parie que le cœur du vieux Magni s'est presque arrêté de battre lorsqu'il a appris la nouvelle.","<Gor'shak rit.>","Chuuut ! Vous entendez ? Des gardes arrivent ! DÉFENDEZ-VOUS !"}, {"Défendre Gor'shak."}},
[4001] = {"Que se passe-t-il ?", {"Jusqu'à présent, mes entretiens avec le nain, Kharan, dans la cellule de l'autre côté du mur, ont été plus que limités. J'ai tout de même réussi à le convaincre que je (ou plutôt que nous) ne lui voulions aucun mal.","Allez lui parler et obtenez autant d'informations que possible sur la princesse et sur ses ravisseurs. Puis allez raconter au Chef de guerre ce que vous avez appris.","Thrall ne veut pas qu'il soit fait le moindre mal à la princesse. Bien au contraire, il a prévu un plan pour qu'elle retourne en toute sécurité à Forgefer. Kharan sait déjà tout cela... Allez-y !"}, {"Parler à Kharan Force-martel et obtenir autant de renseignements que possible sur l'enlèvement de la princesse Moira Barbe-de-bronze. Apporter ces renseignements à Thrall à Orgrimmar.","Gor'shak a dit que Kharan était détenu dans une cellule proche."}},
[4002] = {"Les royaumes de l'Est", {"Vous apportez d'excellents renseignements, <nom>.","En tant que Chef de guerre, il est de mon devoir d'accomplir ce qu'il y a de mieux pour la Horde. De m'assurer que les générations qui nous suivent ne soient pas astreintes à une existence sans espoir.","La possibilité de construire nos alliances futures nous échoit à tous. Un geste d'aide envers les nains pourrait fortement renforcer nos liens avec les royaumes de l'Est. Voici la raison pour laquelle vous ne devez pas échouer !","Êtes-vous <prêt/prête> ?"}, {"Parler à Thrall si vous êtes <prêt/prête> à vous charger de la mission qu'il a prévue."}},
[4003] = {"Un sauvetage royal", {"Ce coup porté aux nains sombrefers pourrait créer une onde de choc perceptible dans tout leur royaume.","La princesse Barbe-de-bronze est sous le contrôle de l'Empereur Thaurissan. Vous devez tuer l'Empereur afin de la libérer. Mais attention, votre quête est dangereuse à plus d'un titre, car la princesse elle-même vous attaquera sans retenue tant qu'elle sera sous le contrôle de l'Empereur. NE LUI FAITES PAS DE MAL !"}, {"Tuer l'Empereur Thaurissan et libérer la Princesse Moira Barbe-de-bronze de son sort maléfique."}},
[4004] = {"La Princesse sauvée ?", {"Vous avez assassiné mon mari, <race> ! Mon enfant naîtra et grandira dans ce monde sans avoir de père.","<La princesse Moira vous montre du doigt.>","Je vous jure pourtant que cet enfant sera le prochain souverain du royaume de Forgefer. Vous et ceux de votre espèce seront chassés et abattus comme des chiens, sans répit et sans merci.","<Vous pensez que la princesse Moira souffre peut-être des effets résiduels du sort de Thaurissan.>","Laissez-moi, <race>. Je pense que votre chef de guerre est avide de connaître le résultat de son ingérence."}, {"Retourner voir Thrall à Fort Grommash, à Orgrimmar."}},
[4005] = {"Aquementas", {"Apportez le sac de fournitures à Tanaris. Rendez-vous à la crique des Gréements, et cherchez un cercle de pierres.","La péninsule la plus à l'est, là est le secret…","Lisez le Livre d'Aquor une fois que vous serez dans ce cercle. Invoquez la seule créature qui soit capable de transformer vos objets en un Totem pour combattre Coursembrase.","Aquementas n'aimera pas être ainsi éveillé, mais souvenez-vous que vous devez rester dans le cercle pour qu'il transforme les objets."}, {"Apporter le Livre d'Aquor, les Griffes argentées et le Cœur d'Arbrefer à Tanaris.","Utiliser le Livre d'Aquor pour invoquer Aquementas.","Après avoir terminé le totem, l’apporter à J.D. Collie au refuge des Marshal."}},
[4021] = {"La contre-attaque !", {"La Horde loue vos victoires contre les Kolkars, mais ils sont à présent fous furieux. On me rapporte qu'un contingent, épris de vengeance, a quitté leur patrie en Désolace pour arriver dans les Tarides. Aux dernières nouvelles, ils prennent d'assaut les fortins depuis l'ouest !","Combattez les envahisseurs, et mettez un terme à la menace qu'ils représentent pour les Tarides. Tuez-en jusqu'à ce que leur chef de guerre, Krom'zar, apparaisse. Battez-le et apportez-moi un morceau de sa bannière comme preuve de votre exploit.","Nous comptons sur vous, <nom>."}, {"Tuez des centaures kolkars jusqu’à ce que Krom’zar apparaisse, puis tuez-le et arrachez un Morceau de sa bannière."}},
[4022] = {"Un goût de flammes", {"Les dragons sont au courant de vos œuvres dans la gorge des Vents brûlants.","Montrez-la-moi, <race>. L'écaille, imbécile."}, {"Montrer à Cyrus Lerepenti l'Ecaille des dragons noirs que vous avez reçue de Kalaran Lamevent."}},
[4023] = {"Un goût de flammes", {"Vous recherchez les dragons ? Présentez-moi la preuve de votre valeur, <mortel/mortelle>."}, {"Montrer à Cyrus Lerepenti une preuve de votre valeur.","Vous avez le sentiment que Cyrus sait déjà que vous êtes indigne."}},
[4024] = {"Un goût de flammes", {"Je souhaite goûter aux flammes ardentes de la montagne de feu. Laisser la lave fondue cascader tout le long de mon être. Cette essence, cette chaleur fulgurante, c'est cela dont j'ai soif.","Bael'Gar demeure dans les Profondeurs de Rochenoire : un être fait de flammes pures, né des feux de Ragnaros. Tuez Bael'Gar et extrayez de ses restes brûlants son feu intérieur.","Servez-vous de l'écaille pour arracher sans danger le feu intérieur à sa dépouille.","Si vous réussissez à me le rapporter, vous serez <récompensé/récompensée>."}, {"Partez pour les Profondeurs de Rochenoire et tuez Bael'Gar.","Vous savez seulement que le géant demeure à l'intérieur des Profondeurs de Rochenoire. N'oublier pas d'utiliser l'Ecaille des dragons noirs altérée pour capturer l'Essence ardente.","Apporter l'Essence ardente recouverte à Cyrus Lerepenti."}},
[4041] = {"L'élixir de Videre", nil, nil},
[4061] = {"La révolte des machines", {"Nous avons découvert les machines lors de nos premières incursions dans la gorge des Vents brûlants. Après une enquête plus minutieuse, nous avons découvert qu'elles étaient importées des steppes Ardentes, et peut-être des profondeurs de Rochenoire. C'est pour cela que nous avons besoin de vous, <nom>.","Votre première mission sera de pénétrer dans les steppes Ardentes et de récupérer des fragments d'élémentaires sur des automates de roc et sur des réalisations en roc moindres de la région. Lorsque vous en aurez suffisamment, revenez me voir et je déterminerai leur origine."}, {"Aventurez-vous dans les Steppes Ardentes et récupérez 10 Fragments d'élémentaires cassés pour la Hiérophante Theodora Mulvadania.","Theodora vous a indiqué que les golems et les élémentaires étaient la source de ces fragments."}},
[4062] = {"La révolte des machines", {"S.G.A.","Voici les « initiales » gravées sur les fragments d'élémentaires. Au-dessous, on peut lire un genre de rang. Les machines que vous avez détruites dans les steppes Ardentes étaient de « Rang IV ». Je présume qu'il s'agit d'un rang peu élevé.","Apportez cet échantillon de fragment à Lotwil Veriatus dans le camp à l'est. C'est un érudit qui a un certain renom dans les sciences sur les élémentaires. Il se peut qu'il puisse nous venir en aide dans notre quête."}, {"Apporter l'Échantillon de fragment d'élémentaire à Lotwil Veriatus.","Theodora vous a dit qu'il se trouvait dans un camp à l'est."}},
[4063] = {"La révolte des machines", {"Argelmach ? Impossible ! Argelmach a été tué il y a dix ans. Je le sais, c'est moi qui l'ai tué.","Il s'agissait d'un nain abject, obsédé par l'idée de ruiner le travail de ma vie afin d'atteindre ses diaboliques desseins. On dirait que les méchants parviennent toujours à échapper à la justice.","S'il s'agit de l'œuvre d'Argelmach, il faut le détruire. Je vais avoir besoin de spécimens de ses dernières créations. Si l'on me donne les bons, il se peut que je parvienne à mettre un terme à ce soulèvement. Les créations gardent Argelmach dans les Profondeurs de Rochenoire."}, {"Trouver et tuer le Seigneur des golems Argelmach. Rapporter sa tête à Lotwil. Collecter également 10 Noyaux d'élémentaire intacts sur les Golems ravarage et les Assemblages porteguerre qui protègent Argelmach. Vous savez cela car vous êtes médium."}},
[4081] = {"TUER À VUE : nains sombrefers", {"Par ordre du chef de guerre Sangredent, commandant du Corps Expéditionnaire de Kargath :","Tous les membres des forces du général Forgehargne présents dans les Profondeurs de Rochenoire doivent être annihilés.","Les hommes de Forgehargne sont des tueurs de sang-froid, responsables du massacre de masse de la 109e division du corps expéditionnaire de Kargath. Les héros doivent exercer avec une prudence extrême lorsqu'ils combattent ces brutes.","Quiconque détruira la première ligne des forces du général Forgehargne dans les Profondeurs de Rochenoire recevra un tribut."}, {"Pénétrez dans les Profondeurs de Rochenoire et tuez les vils agresseurs !","Le chef de guerre Sangredent veut que vous tuiez 15 Gardes ragenclumes, 10 Gardiens ragenclumes et 5 Fantassins ragenclumes. Retourner le voir une fois votre mission accomplie."}},
[4082] = {"TUER À VUE : Officiers de haut rang sombrefers", {"Par ordre du chef de guerre Sangredent, commandant du Corps Expéditionnaire de Kargath :","Le chef du service de renseignement du C.E.K., le grand maître Lexlort, et revenu avec des informations concernant l'endroit approximatif où se trouveraient les officiels de haut rang sombrefers. Derrière une porte verrouillée et près du cœur de la cité, ils entraînent leurs forces pour faire périr nos hommes. Partez pour les profondeurs de Rochenoire et tuez-les !","La réussite de cette mission vous vaudra un tribut."}, {"Pénétrez dans les Profondeurs de Rochenoire et tuez les vils agresseurs !","Le Chef de guerre Sangredent veut que vous tuiez 10 Médecins ragenclumes, 10 Soldats ragenclumes et 10 Officiers ragenclumes. Retourner le voir une fois votre mission accomplie."}},
[4083] = {"Le Calice spectral", nil, nil},
[4084] = {"Cœur d'argent", {"Je ne connais pas la personne dont vous parlez, <nom>.","Mais ne vous inquiétez pas ; si votre ami était en voyage pour venir me trouver, je sais ce qu'il recherchait.","Au cœur du cratère d'Un'Goro demeure un mal sous sa forme la plus pure. Ce mal prend la forme d'un élémentaire du nom de Coursembrase... Il est protégé par une aura que personne ne peut dissiper.","Personne... à part ceux qui ont suffisamment de pouvoir pour y parvenir. Je peux vous apprendre ce pouvoir, <nom>.","D'abord, faites ce que je vous demande. Trouvez 11 Griffes d'argent et 1 Cœur d'Arbrefer sur les créatures de Gangrebois, puis revenez."}, {"Trouver 11 Griffes d'argent et 1 Cœur d'Arbrefer pour Eridan Ventebleu à Gangrebois."}},
[4101] = {nil, {"Les plantes procurent de la vie à Gangrebois, mais elles-mêmes souffrent des souillures de la corruption. Je connais les moyens de les en débarrasser, mais je ne peux enseigner une telle connaissance qu’à ceux en qui j’ai une entière confiance. Prouvez-moi que vous soutiendrez véritablement la cause du Cercle cénarien !","Vous trouverez les cavernes d’Arbrefer, au nord. Les élémentaires de Crochebois qui s’y trouvent ne peuvent plus être purifiés, à présent. Je vous demande de m’apporter leur essence, appelée l’ambre de sang, afin que je l’étudie dans l’espoir de sauver les autres formes de vie ici."}, {"Apportez 15 Ambres de sang à Arathandris Argeciel, à Gangrebois."}},
[4102] = {"Purifiez Gangrebois", {"Les plantes procurent de la vie à Gangrebois, mais elles-mêmes souffrent des souillures de la corruption. Je connais les moyens de les en débarrasser, mais je ne peux enseigner une telle connaissance qu'à ceux en qui j'ai une entière confiance. Prouvez-moi que vous soutiendrez véritablement la cause du Cercle cénarien !","Vous trouverez au nord les cavernes d'Arbrefer. Les élémentaires de Crochebois qui s'y trouvent ne peuvent plus à présent être purifiés. Je vous demande de m'apporter leur essence (l'Ambre de sang) afin que je l'étudie dans l'espoir de sauver les autres formes de vie ici."}, {"Apporter 15 Ambres de sang à Maybess Brise-des-rivières à Gangrebois."}},
