[3372] = {"Liberta os outros", {"Encontre o Artefato Misterioso que está protegido pelos Idólatras do Martelo do Crepúsculo no quadrante noroeste da Garganta Abrasadora e leve-o para o Altar de Suntara, no Fosso de Lava."}},
[3373] = {"A Essência de Erânicos", {"Coloque a Essência de Erânicos na Fonte de Essência encontrada neste covil do Templo Submerso."}},
[3374] = {"A Essência de Erânicos", {"Leve a Pedra do Juramento da Revoada de Ysera e a Essência Acorrentada de Erânicos para Itharius no Pântano das Mágoas. Lá, você decidirá se deseja ajudar a Revoada Dragônica de Ysera ou não."}},
[3375] = {"Frasco reserva", {"Leve Carvão, Néctar Doce e uma Poção de Mana para Didiê Du Mocó, em Altaforja, para receber um Frasco de Vidência do Didiê."}},
[3376] = {"Quebra-presadura!", {"Leve a cabeça do Chefe Presadura Mantospinho para a Valente Pena ao Vento na Chapada Nuvem Vermelha."}},
[3377] = {"Oração a Eluna", {"Ouça a história de Zamael."}},
[3378] = {"Oração a Eluna", {"Encontre a Oração a Eluna e leve-a para Astarii Mirestela em Darnassus."}},
[3379] = {"Tecessombras", {"Mate os Larápios de Seda Sombria que rondam a Garganta Abrasadora. Nilith acredita que cinco deles precisam ser exterminados. Fale com Nilith novamente na Garganta Abrasadora quando tiver cumprido essa tarefa."}},
[3380] = {"O Templo Submerso", {"Encontre Marlon Catarrebite em Tanaris."}},
[3381] = {"Para encontrar um mestre", {"Leve 2 Penas Intactas de Hipogrifo para Sanath Lim-yo em Azshara, se quiser falar com o mestre dele."}},
[3382] = {"Sob fogo cerrado", {"Defenda a Capitão Vanessa Beltis, sua tripulação e os passageiros do Batedor do Horizonte do ataque das nagas. A Capitão Vanessa Beltis deve sobreviver."}},
[3385] = {"O Submercado", {"Mate o Mestre Mercador Kovic e seu lacaio Clanque. Pegue a Algibeira do Comerciante do corpo dele e leve-a para Nilith."}},
[3402] = {"O submercado", {"Fale com Linhagulha em Geringontzan."}},
[3421] = {"A viagem de volta", {"Vou levá-lo de volta ao acampamento de Sanath agora.","","Não repare nos modos ruins dele. Ele é rude com todo mundo exceto o mestre. Talvez ele aprenda a lição algum dia."}},
[3441] = {"Castigo divino", {"Ouça Kalaran Finalâmina contar a história dele."}},
[3442] = {"A chama perfeita", {"Colete 4 Corações das Chamas e 4 globos de Óleo de Golem, e leve-os para Kalaran Finalâmina."}},
[3443] = {"Forjando o bastão", {"Leve 8 Punhais de Placas de Tório para Kalaran Finalâmina."}},
[3444] = {"O círculo de pedra", {"Busque o Círculo de Pedra na oficina de Marlon Catarrebite em Vila Catraca."}},
[3445] = {"O Templo Submerso", {"Encontre Marlon Catarrebite em Tanaris."}},
[3446] = {"Nas profundezas", {"Encontre o Altar de Hakkar no Templo Submerso, que fica no Pântano das Mágoas."}},
[3447] = {"Segredo do círculo", {"Entre no Templo Submerso e descubra o segredo escondido no círculo de estátuas."}},
[3448] = {"Passando o fardo", {"Fale com Timor em Altaforja."}},
[3449] = {"Runas arcanas", {"Usando o Kit de Desenho, faça decalques da Runa de Beth'Amara, da Runa de Jin'yael, da Runa de Markri e da Runa de Sael'hai antes de ir para a pequena ilha ao largo da península meridional de Azshara e sinalizar para o Piloto Xisto Acendefase buscá-los."}},
[3450] = {"Uma coleta tranquila", {"Fale com Xisto Acendefase em Altaforja."}},
[3451] = {"Me chama que eu vou!", {"Fale com Xisto Acendefase e consiga uma Pistola Sinalizadora Modelo Padrão com ele em Altaforja."}},
