[3453] = {"A Tocha da Retribuição", {"Espere enquanto Kalaran Finalâmina termina de preparar a Tocha da Retribuição."}},
[3454] = {"A Tocha da Retribuição", {"Pegue a Tocha da Retribuição."}},
[3461] = {"Fale novamente com Timor", {"Devolva o Kit de Desenho de Timor a ele em Altaforja."}},
[3462] = {"Escudeiro Maltraque", {"Fale com o Escudeiro Maltraque."}},
[3463] = {"Taca fogo neles!", {"Incendeie as Torres de Sentinela Norte, Sul, Leste e Oeste usando a Tocha da Retribuição dentro de cada prédio."}},
[3481] = {"Berloques...", {"Abra o baú."}},
[3482] = {"<NYI> <TXT> The Pocked Black Box", {"Leve essa caixa para Kravel."}},
[3483] = {"Me chama que eu vou!", {"Ah, outro sinalizador, é? Isso eu sei fazer.","","E não se esqueça: na ponta sul de Azshara. É uma ilhotinha que foi transformada em plataforma de pouso. Não dá para não ver se você estiver no lugar certo."}},
[3501] = {"Cada pedacinho conta", {"Leve Fragmentos Imperfeitos de Draenetista para Kum'isha e ganhe uma recompensa por cada fragmento entregue."}},
[3502] = {"O lixo de um draenei...", {"Leve Fragmentos Imperfeitos de Draenetista para Kum'isha. Qualquer monstro da Barreira do Inferno pode estar carregando um fragmento desses."}},
[3503] = {"Chega de nhenhenhém!", {"Já houve um tempo em que eu podia exigir qualquer coisa daqueles que desejavam uma audiência com meu mestre. Mas agora basta qualquer um me pedir, e eu despacho vocês até ele.","","Isso me irrita, mas eu não questiono as ordens. Minha única diversão é fazer pouco das roupas ridículas que você usa, ou da expressão patética no seu rosto. Mas mesmo isso me entedia...","","E esse nhenhenhém também! Quer encontrar meu mestre ou não?"}},
[3504] = {"Traição", {"Entregue a Carta Lacrada para Ag'tor a Ag'tor Punho de Sangue, em Azshara."}},
[3505] = {"Traição", {"Mate 10 Recuperadores Sanguinélficos e 10 Topógrafos Sanguinélficos antes de procurar o acampamento da Maga Rimtori. Quando localizá-lo, procure algo que possa ser importante para ela e possa ser usado para forçá-la a lutar."}},
[3506] = {"Traído", {"Destrua o cristal de foco arcano preso à Maga Rimtori e mate-a. Depois, leve a Cabeça da Maga Rimtori para Ag'tor Punho de Sangue em Azshara."}},
[3507] = {"Traição", {"Fale com Belgrom Pedramalho em Orgrimmar."}},
[3508] = {"Quebrando a proteção", {"Espere Loramus para completar o feitiço de iluminação."}},
[3509] = {"O nome da fera", {"Fale com o Lorde Arkkoroc em Azshara. comece sua busca na beira da água para encontrar o Templo de Arkkoran."}},
[3510] = {"O nome da fera", {"Mate Hetaera e leve a Cabeça Ensanguentada de Hetaera, a Cabeça Arrebentada de Hetaera e a Cabeça Ferida de Hetaera para o Lorde Arkkoroc."}},
[3511] = {"O nome da fera", {"Leve a têmpera para Loramus Talípedes."}},
[3512] = {"Nas palavras do próprio Erânicos", {"Viaje até o leste de Hibérnia e fale com Umbranse, o Médium."}},
