[3514] = {"Presença da Horda", {"Mate a líder do Retiro do Gadanho, Rynthariel, a Guardiã da Chave."}},
[3517] = {"Roubando conhecimento", {"Encontre a Tabuleta de Beth'Amara, a Tabuleta de Jin'yael, a Tabuleta de Markri e a Tabuleta de Sael'hai. Depois volte a falar com Jediga, em Azshara."}},
[3518] = {"Entrega para Magatha", {"Leve a Tabuleta de Beth'Amara para Magatha no Penhasco do Trovão."}},
