[3823] = {"Extinção dos Pirobuchos", {"Mate 15 Ogros-magos Pirobucho, 7 Ogros Pirobucho e 7 Brutamontes Ogros Pirobucho e fale novamente com Oralius quando terminar."}},
[3824] = {"Gor'tesh, o lorde dos brutamontes", {"Encontre Gor'tesh em algum lugar a oeste do Rochedo Malhorrendo. Mate Gor'tesh e depois leve a Cabeça Decepada do Gor'tesh de volta a Oralius nas Estepes Ardentes."}},
[3825] = {"A festa da cabeça cortada", {"Pegue a Cabeça Decepada do Gor'Tesh e coloque no topo do Rochedo Malhorrendo. Procure um montinho de terra macia para espetar a estaca."}},
[3841] = {"Um órfão em busca de um lar", {"Fale com Quentin em Mil Agulhas."}},
