[3907] = {"Desarmonia do fogo", {"Entre no Abismo Rocha Negra e encontre o Lorde Incendius. Mate-o e leve todas as informações que puder para o Coração Trovejante."}},
[3908] = {"É segredo para todo mundo", {"Leve a Espada de Linken para Bruna Covaneve em Hibérnia."}},
[3909] = {"O elixir Videre", {"Procure Gregan Cospechope ao norte de Feralas. Descubra com ele como obter o elixir Videre, depois fale novamente com Bruna Covaneve em Hibérnia."}},
[3911] = {"O último elemento", {"Vá até o Abismo Rocha Negra e obtenha 10 Essências dos Elementos. Seu primeiro impulso é procurar nos golens e criadores de golens. Você lembra que Vivian Latumba também mencionou elementais."}},
[3912] = {"Encontro na sepultura", {"Vá até o cemitério perto de Geringontzan e use o Elixir Videre. Siga as outras instruções de Bruna para achar Gaeriyan."}},
[3913] = {"A sepultura", {"Ressuscite, depois procure a lápida que Gaeriyan disse para você encontrar."}},
