[4288] = {"A torre oeste", {"Descubra a localização da Torre de Cristal Oeste, que fica na Cratera Un'Goro."}},
[4289] = {"Os macacos de Un'Goro", {"Leve 2 Pelagens de Gorila de Un'Goro, 2 Pelagens de Pisoteador de Un'Goro, 2 Pelagens de Trovejador de Un'Goro para Torwa Mateiro na entrada da Cratera Un'Goro."}},
[4290] = {"A dieta de Lar'korwi", {"Encontre a carcaça mais recente de uma presa morta por Lar'korwi e roube um Pedaço de Carcaça de Manguadonte. Depois disso, fale novamente com Torwa Mateiro na Cratera Un'Goro."}},
