[4975] = {"O Orbe de Nor'Orahil concluído", {"Espere Menara Nihila completar o Orbe de Nor'Orahil e fale com ela novamente."}},
[4976] = {"Devolução do orbe purificado", {"Leve o Orbe Infernal Purificado a Menara Nihila nos Sertões."}},
[4981] = {"Agente Biju", {"Vá ao Pico da Rocha Negra e descubra o que aconteceu com Biju."}},
[4982] = {"Pertences da Biju", {"Encontre os Pertences de Biju e devolva-os a ela. Ela mencionou tê-los guardado no nível mais profundo da cidade."}},
