[4984] = {"A vida selvagem também sofre", {"Destrua 8 Lobos Doentes e fale novamente com Mulgris Riofundo no Antro Repelente, que fica nas Terras Pestilentas Ocidentais."}},
[4985] = {"A vida selvagem também sofre", {"Destrua 8 Ursos-cinzentos Doentes e fale novamente com Mulgris Riofundo no Antro Repelente, que fica nas Terras Pestilentas Ocidentais."}},
[4986] = {"Galho de carvalho com glifos", {"Baseado na magia imbuída no galho de carvalho com glifos, sua entrega ao Círculo Cenariano em Darnassus é o próximo passo que o druida tauren buscava. Procure a ajuda de um dos druidas."}},
[4987] = {"Galho de carvalho com glifos", {"Baseado na magia imbuída no galho de carvalho com glifos, sua entrega ao Círculo Cenariano no Penhasco do Trovão é o próximo passo que o druida Tauren buscava. Procure a ajuda de um dos druidas."}},
[5001] = {"Pertences da Biju", {"Encontre os Pertences de Biju e devolva-os a ela. Boa sorte!"}},
[5002] = {"Mensagem para Maxwell", {"Viaje para as Estepes Ardentes e dê as Informações de Biju para o Oficial Maxwell."}},
[5021] = {"Antes tarde do que nunca", {"As divagações da mulher fantasmagórica indicavam que ela precisava que um pacote fosse entregue. Ela afirmou que ele estava no mesmo lugar que seu cavalo. Quanto ao local onde o cavalo está ou onde o pacote deve ser entregue, continua sendo impossível entender o fantasma."}},
[5022] = {"Antes tarde do que nunca", {"Fale com os Administradores Reais de Ventobravo para saber o paradeiro da Bia Pedravil. Geralmente tem um agente do censo na Prefeitura."}},
[5023] = {"Antes tarde do que nunca", {"Fale com os Feitores Reais da Cidade Baixa para saber o paradeiro da Jeremias Pedravil. Geralmente tem um agente do censo perto do registro de guilda e tabardo."}},
[5041] = {"Suprimentos para a Encruzilhada", {"Encontre e devolva os Caixotes de Suprimentos da Encruzilhada a Thork, nos Sertões."}},
[5042] = {"Força de Agamaggan", {"Eu vou precisar de 4 fragmentos de sangue se você quiser ter a força de Agamaggan, <race>.","","Seus inimigos tremerão ante sua força se você receber essa bênção.","","Os maiores guerreiros dos Crinavalha são infundidos com esse poder!"}},
[5043] = {"Agilidade de Agamaggan", {"Os maiores batedores da minha tribo são considerados incomparáveis em termos de precisão, e nenhum ladino <bufa> está à altura de sua rapidez em batalha.","","Traga-me 4 estilhaços de sangue e tornarei você ainda mais ágil do que qualquer javatusco que já tenha existido."}},
[5044] = {"Sabedoria de Agamaggan", nil},
[5045] = {"Espírito ascendente", nil},
[5046] = {"Couro de Navalha", {"Todos aqueles que <bufa> entram em batalha precisam de proteção. Seus ossos devem ser fortes; sua pele, dura. <bufa> Minha tribo sabe disso também e, para aumentar a proteção natural que nossos espinhos e couro nos dão, <bufa> nós sempre rogamos a Agamaggan para que nos proteja do mal antes de irmos para a guerra.","","Traga-me 4 estilhaços de sangue <bufa> e eu lhe darei a mesma proteção."}},
[5047] = {"Jonas Tiragosto ao seu dispor!", {"Fale com Málifus Trevamalho em Visteterna."}},
[5048] = {"Bondosa Bia", {"Encontre a Tia Bia em Ventobravo e veja se, de fato, ela é Bia Pedravil. Se for, talvez ela possa querer o pacote que Janice Pedravil lhe mandou."}},
[5049] = {"O fado de Jeremias", {"Encontre a Jeremias Mascotte na Cidade Baixa e veja se, de fato, ele é Jeremias Pedravil. Se for, talvez ele possa querer o pacote que Janice Pedravil lhe mandou."}},
[5050] = {"Pingente da Sorte", {"Devolva o Meio-pingente da Sorte para Janice Pedravil nas Terras Pestilentas Ocidentais."}},
[5051] = {"Um só coração", {"Localize a outra metade do Pingente da Sorte em algum lugar da Fazenda Pedravil, nas Terras Pestilentas Ocidentais, e monte-o novamente.","","Dê o Pingente da Sorte a Janice Pedravil, na Fazenda Pedravil, que fica nas Terras Pestilentas Ocidentais."}},
[5052] = {"Estilhaços de Sangue de Agamaggan", {"Leve um Estilhaço de Sangue para Denterroto no Acampamento Taurajo, que fica nos Sertões.","","Colete mais Estilhaços de Sangue se você quiser que ele <o/a> abençoe com o poder de Agamaggan."}},
[5054] = {"Úrsius, o Dentilhaço", {"Ventania Casco Escuro, de Visteterna, Hibérnia, quer que você mate Úrsius."}},
[5055] = {"Brumeran dos Ventogelante", {"Ventania Casco Escuro, de Visteterna, Hibérnia, quer que você mate Brumeran."}},
[5056] = {"Shy-Rotam", {"Ventania Casco Escuro, de Visteterna, Hibérnia, quer que você derrote Shy-Rotam."}},
[5057] = {"Feitos passados", {"Leve o Marcador de Ventania Casco Escuro para Melor Casco de Pedra no Penhasco do Trovão."}},
[5058] = {"Diário da Sra. Dalson", {"Um diário com páginas amareladas repousa aberto na última anotação:","","\"Não sei o que vai acontecer agora que Haroldo está transformado. Dei tudo de mim para arrastá-lo ao único lugar que descobri ser possível mantê-lo preso em segurança. A chave está escondida comigo e rezo para que dê certo...","","Espero conseguir sair daqui, mas preciso de dinheiro para isso. Acho que nossos pertences estão todos no gabinete... e, por falar nisso, o Haroldo não guarda a chave consigo?","","Preciso descansar, estou morrendo de cansaço agora.\""}},
[5059] = {"Trancafiado", {"A porta da casinha começou a se romper nas bordas, como se tivesse sido repetida e incessantemente forçada para abrir. No momento, ela está trancada pelo lado de fora.","","Quando você sacode a porta, escuta um gemido gutural vindo de dentro da casinha! O que quer que esteja lá dentro, não parece nada amigável."}},
[5060] = {"Trancafiado", {"Um armário extremamente resistente se encontra na sua frente, trancado firmemente com uma fechadura embutida. A frente do armário tem marcas e arranhões profundos. Pode-se inferir disso que foram numerosas e violentas as tentativas fracassadas de revelar quaisquer segredos que o gabinete oculte.","","Se você quiser abrir este armário, precisa encontrar a chave."}},
[5061] = {"Forma aquática", {"Volte a Darnassus e mostre a Mathrengyl Ursivagus o Pingente do Leão Marinho."}},
[5062] = {"Fogo sagrado", {"Reúna 10 punhados de Agave Incendia, depois consulte Magatha Temível Totem no Platô dos Anciãos, que fica no Penhasco do Trovão."}},
[5063] = {"Casquete do Sábio Escarlate", {"Para criar o Casquete do Sábio Escarlate, você precisa levar os seguintes itens para Málifus Trevamalho: 1 Pelego Imaculado da Fera; 5 Suturas de Abominação Puídas; 8 Cristais Arcanos; 5 Fios Escarlate Encantados.","","Você também deverá pagar 50 ouros pela fabricação do item."}},
[5064] = {"Espiando os Temível Totem", {"Localize e recupere os três Bilhetes Secretos no Pináculo da Nuvem Negra."}},
[5065] = {"As tabuletas Mosh'Aru perdidas", {"Leve a Terceira e a Quarta Tabuletas Mosh'Aru ao Prospector Botaférrea, em Tanaris."}},
[5066] = {"Um chamado às armas: as Terras Pestilentas!", {"Procure o Comandante Ashlam Punhobom. Ele montou base no Acampamento Ventogelante, ao norte das Montanhas de Alterac."}},
[5067] = {"Perneiras do Arcanismo", {"Para criar as Perneiras do Arcanismo, você precisa levar os seguintes itens para Málifus Trevamalho: 1 Pelego Imaculado da Fera; 5 Suturas de Abominação Puídas; 5 Barras de Arcanita; 5 Fluidos Embalsamadores de Friomúrmuro.","","Você também deverá pagar 50 ouros pela fabricação do item."}},
[5068] = {"Peitoral da Sede de Sangue", {"Para criar o Peitoral da Sede de Sangue, você precisa levar os seguintes itens para Málifus Trevamalho: 1 Pelego Imaculado da Fera; 10 Suturas de Abominação Puídas; 5 Barras de Arcanita; 5 Peles de Sombra.","","Você também deverá pagar 50 ouros pela fabricação do item."}},
[5081] = {"A missão de Maxwell", {"Vá para o Pico da Rocha Negra e mate o Senhor da Guerra Voone, o Grão-lorde Omokk e o Lorde Supremo Wyrmthalak. Fale novamente com o Oficial Maxwell quando terminar o trabalho."}},
[5082] = {"A ameaça dos Invernosos", {"Bruna Covaneve, de Hibérnia, quer que você mate 8 Desbravadores Invernosos, 8 Vigias do Covil dos Invernosos e 8 Totêmicos Invernosos."}},
[5083] = {"Aguardente Invernosa", {"Leve o Frasco Vazio de Aguardente para Bruna Covaneve em Hibérnia."}},
[5084] = {"Cedendo à corrupção", {"Investigue o acampamento dos pelursos Lenha Morta na parte norte de Selva Maleva para encontrar alguma pista."}},
[5085] = {"Meleca misteriosa", {"Informe Bruna Covaneve sobre as suas descobertas."}},
[5086] = {"Horrores tóxicos", {"Colha 3 Gotinhas de Horror Tóxico para Bruna Covaneve em Hibérnia."}},
[5087] = {"Mensageiros Invernosos", {"Encontre os Mensageiros Invernosos e impeça-os de entregar o carregamento à Aldeia dos Invernosos."}},
[5088] = {"Arikara", {"Mate Arikara. Leve seus restos mortais e o pó de Incendia para Motega Barba de Fogo no Posto Confinalvo como prova do seu feito."}},
[5089] = {"Comando do General Drakkisath", {"Leve o Comando do General Drakkisath para o Oficial Maxwell nas Estepes Ardentes."}},
[5090] = {"Um chamado às armas: as Terras Pestilentas!", {"Procure o Comandante Ashlam Punhobom. Ele montou base no Acampamento Ventogelante, ao norte das Montanhas de Alterac."}},
[5091] = {"Um chamado às armas: as Terras Pestilentas!", {"Procure o Comandante Ashlam Punhobom. Ele montou base no Acampamento Ventogelante, ao norte das Montanhas de Alterac."}},
[5092] = {"Abrindo caminho", {"Mate 10 Esfoladores Descarnados e 10 Carniçais Salivantes na Colina do Pesar.","","Fale novamente com o Comandante Ashlam Punhobom no Acampamento Ventogelante, que fica nas Terras Pestilentas Ocidentais."}},
[5093] = {"Um chamado às armas: as Terras Pestilentas!", {"Procure o Alto-executor Arcabuz. A base de operações dele fica no Baluarte, a leste das Clareiras de Tirisfal e da Cidade Baixa."}},
[5094] = {"Um chamado às armas: as Terras Pestilentas!", {"Procure o Alto-executor Arcabuz. A base de operações dele fica no Baluarte, a leste das Clareiras de Tirisfal e da Cidade Baixa."}},
[5095] = {"Um chamado às armas: as Terras Pestilentas!", {"Procure o Alto-executor Arcabuz. A base de operações dele fica no Baluarte, a leste das Clareiras de Tirisfal e da Cidade Baixa."}},
[5096] = {"Distrações escarlate", {"Vá até a base de operações da Cruzada Escarlate, que fica entre o Campo Pedravil e as Lágrimas de Dalson, e destrua a Tenda de Comando.","","Plante a bandeira do Flagelo no acampamento e fale novamente com o Alto-executor Arcabuz no Baluarte, nas Terras Pestilentas Ocidentais."}},
[5097] = {"Você não vê as torres?", {"Usando a Tocha Sinalizadora, marque cada torre em Andorhal. Você tem que ficar na entrada da torre para conseguir marcá-la.","","Depois de marcar as quatro torres, leve a Tocha Sinalizadora de volta para o Comandante Ashlam Punhobom no Acampamento Ventogelante, que fica nas Terras Pestilentas Ocidentais."}},
[5098] = {"Você não vê as torres?", {"Usando a Tocha Sinalizadora, marque cada torre em Andorhal. Você tem que ficar na entrada da torre para conseguir marcá-la.","","Depois de marcar as quatro torres, leve a Tocha Sinalizadora de volta para o Alto-executor Arcabuz no Baluarte, nas Terras Pestilentas Ocidentais."}},
[5101] = {"Lee's Ultimate Test Quest... of Doom!", {"Mate 6 ovelhas para o Dito e depois corra pela sua vida!","","Tá... brincadeirinha... basta matar as ovelhas."}},
[5102] = {"O fim do General Drakkisath", {"Viaje de volta ao Pico da Rocha Negra e destrua o General Drakkisath. Depois, fale novamente com o Oficial Maxwell."}},
[5103] = {"Morte muito ardente", {"Alguém neste mundo deve saber o fazer com essas manoplas. Boa sorte!"}},
[5121] = {"Chefe Supremo Invernoso", {"Bruna Covaneve em Hibérnia quer que você derrote o Chefe Supremo Invernoso."}},
[5122] = {"O Medalhão da Fé", nil},
[5123] = {"A peça final", {"Leve o Registro Escrito Grosseiramente para Bruna Covaneve em Hibérnia."}},
[5124] = {"Manoplas de placa abrasadoras", {"Leve 6 Barras de Tório Encantado, 2 Essências do Fogo e 4 Rubis-estrela para Málifus Trevamalho. Você também vai ter que entregar suas Manoplas de Placa Crua."}},
[5125] = {"O ajuste de contas de Aurius", nil},
[5126] = {"O conto de Vegax", {"Fale com Vegax. Escute o que ele tem a dizer."}},
[5127] = {"A forja demoníaca", {"Viaje para o Pico da Rocha Negra e encontre Goraluk Rachadastra. Mate-o e use o Pique Manchado de Sangue no cadáver dele. Quando a alma dele tiver sido canalizada, o pique ficará Manchado de Almas.","","Você também precisa encontrar o Peitoral Rúnico de Metal Bruto.","","Leve o Pique Manchado de Almas e o Peitoral Rúnico de Metal Bruto para Vegax, em Hibérnia."}},
[5128] = {"Palavras do Chefe Supremo", {"Leve o Registro Escrito Grosseiramente para Kelek Guardacéus, na parte sul da Selva Maleva."}},
[5141] = {"Couraria Dragônica", {"Leve 2 Peitorais Escorpídeos Resistentes, 2 Luvas Escorpídeas Resistentes e 10 Escamas Gastas de Dragão para Pedro Galho em Azshara.","","Completar essa missão lhe dará acesso à arte da Couraria Dragônica.","","Completar a missão impedirá que você aprenda Couraria Elemental e Couraria Tribal; tenha certeza de que esta é a trilha que você deseja seguir antes de fazer sua escolha."}},
[5142] = {"A pequena Pâmela", {"Encontre Pâmela Trilharrubra."}},
[5143] = {"Couraria tribal", {"Leve um Colete de Couro Silvestre e um Capacete de Couro Silvestre para Caryssia Caçaluna em Feralas.","","Ao completar essa missão, você terá acesso às artes de Couraria Tribal.","","A conclusão da missão irá <impedí-lo/impedí-la> de aprender Couraria Dragônica e Couraria Elemental. Certifique-se que este é o caminho que deseja seguir antes de continuar."}},
[5144] = {"Couraria Elemental", {"Traga 2 Corações de Fogo, 2 Globos de Água, 2 Núcleos da Terra e 2 Sopros Eólicos para Sarah Tanner na Garganta Abrasadora.","","Ao cumprir esta missão, você terá acesso à arte da Couraria Elemental.","","Com isso, você não poderá aprender Couraria Dragônica e Couraria Tribal. Por isso, antes de escolher este caminho, é preciso ter certeza de que é isso que você realmente quer."}},
[5145] = {"Couraria dragônica", {"Leve 2 Peitorais Escorpídeos Resistentes, 2 Luvas Escorpídeas Resistentes e 10 Escamas Gastas de Dragão para Thorkaf Olho-de-tigre em Azshara.","","O cumprimento desta missão lhe dará acesso à arte da Couraria Dragônica.","","Por outro lado, isso também impedirá que você aprenda Couraria Elemental e Couraria Tribal. Certifique-se de que esse é o caminho que você deseja seguir antes de fazer sua escolha."}},
[5146] = {"Couraria elemental", {"Leve 2 Corações de Fogo, 2 Globos de Água, 2 Núcleos da Terra e 2 Sopros Eólicos para Brum Casco Invernal em Pedre'gor, que fica no Planalto Arathi.","","Ao cumprir esta missão, você terá acesso à arte da Couraria Elemental.","","Com isso, você não poderá aprender Couraria Dragônica e Couraria Tribal. Tenha certeza de que é este o caminho que deseja seguir."}},
[5147] = {"Procura-se: Arnak Temível Totem", {"Mate Arnak Temível Totem e leve uma prova de seu feito para o Vigia do Rochedo Chifre Longo, na Aldeia Vento Livre."}},
[5148] = {"Couraria tribal", {"Leve um Colete de Couro Silvestre e um Capacete de Couro Silvestre para Se'Jib, na Selva do Espinhaço.","","Ao cumprir a missão, você terá acesso às artes da Couraria Tribal.","","Ao cumprir a missão, o aprendizado de Couraria Dragônica e Couraria Elemental lhe será proibido. Tenha certeza de sua escolha antes de tomar o passo que está prestes a dar."}},
[5149] = {"A boneca de Pâmela", {"Encontre a boneca de Pâmela."}},
[5150] = {"Dadanga está com fome!", {"Conforme você se aproxima de Dadanga, ela ergue o rosto para você. Parece que ela está esperando alguma coisa...","","Ela deve estar com fome!","","Deve haver alguma coisa que cresce na cratera e que seria o lanchinho perfeito para uma grande herbívora. Dadanga é bem grandinha, então o que quer que traga para ela, tem que ser em grandes quantidades!"}},
[5151] = {"Dispositivo hipercapacitador", {"Mate a fera e traga de volta o Dispositivo Hipercapacitador e a Chave da Jaula da Pantera para Vizlo Limpajunta."}},
[5152] = {"Tia Marlene", {"Fale com Marlene, ao sul das Ruínas de Andorhal."}},
[5153] = {"Uma historiadora estranha", {"Leve o Anel de Casamento do Josefo para Crona."}},
[5154] = {"Os Anais da Vila das Flechas", {"Leve os Anais da Vila das Flechas para Crona em Andorhal."}},
[5155] = {"Forças de Jaedenar", {"Entre em Jaedenar e mate 4 Cães de Jaedenar, 4 Guardas de Jaedenar, 6 Adeptos de Jaedenar e 6 Sectários de Jaedenar. Depois, fale novamente com Greta Musgo no Casco, na Selva Maleva."}},
[5156] = {"Verificar a corrupção", {"Procure e destrua 2 Feras Entrópicas e 2 Horrores Entrópicos enquanto explora o Vale das Escaras Pungentes. Depois de ver se as crateras ainda estão cheias de água contaminada com vileza, fale novamente com Taronn Pena Vermelha, na Selva Maleva."}},
[5157] = {"Coletar a água corrompida", {"Leve o Cantil Vazio para o Poço Lunar no meio de Jaedenar e encha-o. Depois, fale novamente com Greta Musgo no Casco na Selva Maleva."}},
[5158] = {"À procura de auxílio espiritual", {"Leve a Água Corrompida do Poço Lunar para Islen Vidente da Água, nos Sertões."}},
[5159] = {"O retorno da água purificada à Selva Maleva", {"Leve a Água Purificada do Poço Lunar para Greta Musgo no Casco, na Selva Maleva."}},
[5160] = {"A protetorada-máter", {"Viaje para Hibérnia e encontre Haleh. Entregue para ela a Escama do Albi."}},
[5161] = {"Ira da Revoada Azul", {"Fale com Haleh se quiser continuar."}},
[5162] = {"Ira da Revoada Azul", {"Fale com Jezibá nas Terras Pestilentas. Ele mora em Andorhal."}},
[5163] = {"Eu yeti falar uma coisa...", {"Leve o Yeti Mecânico da Umi e assuste os amigos dela:","","Legacki, em Visteterna (Hibérnia),nSguisha, em Geringontzan (Tanaris) enQuixxil, no Refúgio do Marshal (Cratera Un'Goro).","","Quando terminar, leve o Yeti Mecânico de volta para Umi."}},
[5164] = {"Catálogo do Imprevisível", {"Leia o Catálogo do Imprevisível."}},
[5165] = {"Extinguindo as chamas da proteção", {"Use a Água Purificada do Poço Lunar para extinguir os quatro braseiros de proteção no interior da Fortaleza do Concílio das Sombras, em Selva Maleva, depois fale novamente com Greta Musgo no Casco, no Santuário Esmeralda."}},
[5166] = {"Peitoral da Revoada Cromática", {"Para forjar o Peitoral da Revoada Cromática, você deverá levar os seguintes itens para Jezibá, o Escultor:","","1 Carapaça Cromática.","","10 Escamas Cromáticas Brilhantes.","","10 Sangues dos Heróis.","","10 Suturas de Abominação Puídas."}},
[5167] = {"Coxotes do Desafiante Cromático", {"Para forjar os Coxotes do Desafiante Cromático, você deverá levar os seguintes itens para Jezibá, o Escultor:","","1 Carapaça Cromática.","","10 Escamas Cromáticas Brilhantes.","","10 Sangues de Heróis.","","5 Peles de Sombra."}},
[5168] = {"Heróis da Vila das Flechas", {"Leve o Incunábulo de Davil e o Escudo de Trilharrubra para Carlin Trilharrubra."}},
[5181] = {"Vilões da Vila das Flechas", {"Leve o Crânio de Horgus e a Espada Estilhaçada de Marduk para Carlin Trilharrubra na Capela Esperança da Luz."}},
[5201] = {"Invasão dos Invernosos", {"Ripárion Glaciares quer que você mate 5 Xamãs Invernosos e 5 Ursinos Invernosos."}},
[5202] = {"Uma estranha chave vermelha", {"Procure em Jaedenar o que pode ser aberto pela Chave Vermelho Sangue."}},
[5203] = {"Resgate de Jaedenar", {"Escolte Arko'narin até onde Forjaleve e o equipamento dela estão, e então a proteja enquanto escapam da Fortaleza do Concílio das Sombras. Depois, procure Jessir Lunarco em Selva Maleva para informá-la sobre o que houve com os amigos dela."}},
[5204] = {"Retaliação da Luz", {"Volte a Jaedenar e mate o súcubo Rakaiah antes de encontrar os Restos Mortais de Trey Forjaleve."}},
[5206] = {"Espoliadores da Vila das Flechas", {"Leve 5 Crânios Ressonantes e o Cristal Místico para Carlin Trilharrubra nas Terras Pestilentas Orientais."}},
[5210] = {"Irmão Carlin", {"Leve os Anais Estendidos da Vila das Flechas para Carlin Trilharrubra nas Terras Pestilentas Orientais."}},
[5211] = {"Defensores da Vila das Flechas", {"Liberte 15 Espíritos da Vila das Flechas e fale novamente com Carlin Trilharrubra."}},
[5212] = {"A carne não mente", {"Recupere 10 Amostras de Carne Infectada de Stratholme e leve-as para Betina Bigozinco. Você desconfia que qualquer criatura em Stratholme poderia ter essa amostra de carne."}},
[5213] = {"O agente ativo", {"Vá para Stratholme e vasculhe os zigurates. Encontre novos Dados do Flagelo e leve-os para Betina Bigozinco."}},
[5214] = {"A tentação de Fras Siabi", {"Encontre a tabacaria de Fras Siabi em Stratholme e recolha uma caixa do Tabaco da Lata do Fras Siabi. Fale novamente com Leandro Leonardo depois que tiver terminado o serviço."}},
[5215] = {"Os caldeirões do Flagelo", {"Fale com a Alta-sacerdotisa Donélia no Morro do Ventogelante, que fica nas Terras Pestilentas Ocidentais."}},
[5216] = {"Alvo: Campo Pedravil", {"Vá ao Campo Pedravil nas Terras Pestilentas Ocidentais para localizar e derrotar o Mestre do Caldeirão de lá. Ele pode ter uma chave que dê acesso ao caldeirão. Você precisa estar com a Garrafa do Campo Pedravil Vazia para guardar uma amostra dos venenos usados no caldeirão."}},
[5217] = {"Retorno ao Acampamento Ventogelante", {"Leve a Garrafa do Campo Pedravil Cheia à Alta-sacerdotisa Donélia no Acampamento Ventogelante, que fica nas Terras Pestilentas Ocidentais."}},
[5218] = {"Caldeirão do Campo Pedravil", nil},
[5219] = {"Alvo: Lágrimas de Dalson", {"Vá a Lágrimas de Dalson nas Terras Pestilentas Ocidentais para localizar e derrotar o Mestre do Caldeirão de lá, e use a chave dele para ter acesso ao caldeirão. Você precisa estar com a Garrafa das Lágrimas de Dalson Vazia para guardar uma amostra dos venenos usados no caldeirão."}},
[5220] = {"Retorno ao Acampamento Ventogelante", {"Leve a Garrafa das Lágrimas de Dalson Cheia à Alta-sacerdotisa Donélia no Acampamento Ventogelante, que fica nas Terras Pestilentas Ocidentais."}},
[5221] = {"Caldeirão das Lágrimas de Dalson", nil},
[5222] = {"Alvo: Antro Repelente", {"Vá ao Antro Repelente nas Terras Pestilentas Ocidentais para localizar e derrotar o Mestre do Caldeirão de lá, e use a chave dele para ter acesso ao caldeirão. Você precisa estar com a Garrafa do Antro Repelente Vazia para guardar uma amostra dos venenos usados no caldeirão."}},
[5223] = {"Retorno ao Acampamento Ventogelante", {"Leve a Garrafa do Antro Repelente Cheia à Alta-sacerdotisa Donélia no Acampamento Ventogelante, que fica nas Terras Pestilentas Ocidentais."}},
[5224] = {"Caldeirão do Antro Repelente", nil},
[5225] = {"Alvo: Aridez de Garrão", {"Vá a Aridez de Garrão nas Terras Pestilentas Ocidentais para localizar e derrotar o Mestre do Caldeirão de lá, e use a chave dele para ter acesso ao caldeirão. Você precisa estar com a Garrafa da Aridez de Garrão Vazia para guardar uma amostra dos venenos usados no caldeirão."}},
[5226] = {"Retorno ao Acampamento Ventogelante", {"Leve a Garrafa da Aridez de Garrão Cheia à Alta-sacerdotisa Donélia no Acampamento Ventogelante, que fica nas Terras Pestilentas Ocidentais."}},
[5227] = {"Caldeirão da Aridez de Garrão", nil},
[5228] = {"Os caldeirões do Flagelo", {"Fale com a Sacerdotisa Sombria Vandis, em Baluarte, que fica nas Terras Pestilentas Ocidentais."}},
[5229] = {"Alvo: Campo Pedravil", {"Vá ao Campo Pedravil nas Terras Pestilentas Ocidentais para localizar e derrotar o Mestre do Caldeirão de lá. Ele pode ter uma chave que dê acesso ao caldeirão. Você precisa estar com a Garrafa do Campo Pedravil Vazia para guardar uma amostra dos venenos usados no caldeirão."}},
[5230] = {"Retorno a Baluarte", {"Leve a Garrafa do Campo Pedravil Cheia à Sacerdotisa Sombria Vandis em Baluarte, que fica nas Terras Pestilentas Ocidentais."}},
