[5237] = {"Missão cumprida!", {"<name>, sua reengenharia bem-sucedida dos caldeirões é uma tremenda vitória para nós!","","nnQue todos em minha presença possam ouvir: pelos serviços prestados ao Reino de Ventobravo e à Aliança além do mero cumprimento do dever — face a condições extremamente adversas e perigos indizíveis, devo acrescentar —, dou isto a <name>. Você tem nossa eterna gratidão como verdadeiro herói da Aliança. Parabéns!"}},
[5238] = {"Missão cumprida!", {"<name>, sua reengenharia bem-sucedida dos caldeirões é uma tremenda vitória para nós!","","nnQue todos em minha presença possam ouvir: pelos serviços prestados aos Renegados e à Horda além do mero cumprimento do dever — face a condições extremamente adversas e perigos indizíveis, devo acrescentar —, gostaria de dar isso a <name>. Você tem nossa eterna gratidão como verdadeiro herói da Horda. Viva!"}},
[5241] = {"Tio Carlin", {"Encontre Carlin Trilharrubra."}},
[5242] = {"O golpe final", {"Mate Moora e Salia, e leve a Cabeça do Lorde das Sombras Fel'dan para Greta Musgo no Casco em Selva Maleva."}},
[5243] = {"Casas do sagrado", {"Viaje para Stratholme, ao norte. Procure os caixotes de suprimentos que cobrem a cidade e recupere 5 Águas Bentas de Stratholme. Fale novamente com Leonid Bartolomeu, o Venerado, quando coletar fluido abençoado suficiente."}},
[5244] = {"Ruínas de Kel'Theril", {"Fale com Jaron Moldapedra na Aldeia Chuva Estelar."}},
[5245] = {"Espíritos atormentados de Kel'Theril", {"Use a Picareta do Jaron para encontrar os quatro Fragmentos da Relíquia dos Altaneiros. Leve-os para Aurora Clamacéus nas Terras Pestilentas Orientais."}},
[5246] = {"Fragmentos do passado", {"Encontre os Escritos Sagrados dos Altaneiros no Templo de Zin-Malor, em Azshara, e leve-os para Aurora Clamacéus, perto da Torre do Passo Norte nas Terras Pestilentas Orientais."}},
[5247] = {"Fragmentos do passado", {"Leve uma Barra de Tório Encantado, 5 Restaurações de Cristal e uma Ampola de Água Hedionda para Aurora Clamacéus perto da Torre do Passo Norte nas Terras Pestilentas Orientais."}},
[5248] = {"Atormentada pelo passado", {"Leve o Cristal de Zin-Malor ao Lago Kel'Theril, em Hibérnia, e encontre o espírito de um altaneiro que fale com você."}},
[5249] = {"Rumo a Hibérnia!", {"Encontre a Aldeia Chuva Estelar, em Hibérnia, e fale com Eol Correnoite."}},
[5250] = {"Chuva Estelar", {"Encontre a Chuva Estelar, em Hibérnia, e fale com Eol Correnoite."}},
[5251] = {"O arquivista", {"Viaje até Stratholme e encontre o Arquivista Galford da Cruzada Escarlate. Destrua-o e queime o Arquivo Escarlate."}},
[5252] = {"Altaneira arrependida", {"Fale com Eol Correnoite na Aldeia Chuva Estelar e pergunte qual é o lugar mais apropriado para o Cristal de Zin-Malor."}},
[5253] = {"O cristal de Zin-Malor", {"Leve o Cristal de Zin-Malor para o Arquidruida Fandral Guenelmo em Darnassus."}},
[5261] = {"Enzo Peleteiro", {"Fale com Enzo Peleteiro."}},
[5262] = {"A verdade desaba sobre todos", {"Leve a Cabeça do Balnazzar para o Duque Nicolau Zverenhoff nas Terras Pestilentas Orientais."}},
[5263] = {"Acima e além", {"Desbrave Stratholme e destrua o Barão Rivendare. Corte sua cabeça e leve-a de volta para o Duque Nicolau Zverenhoff."}},
[5264] = {"Lorde Maximiliano Tyrosus", {"Fale com o Lorde Maximiliano Tyrosus. Ele está dentro da igreja."}},
[5265] = {"O Castelo Violeta", {"Abra o Castelo Violeta e reivindique sua recompensa."}},
[5281] = {"As almas inquietas", {"Encontre Egan. Ele foi visto pela última vez nos arredores de Stratholme."}},
[5282] = {"As almas inquietas", {"Use o Canhão de Ectoplasma do Egan nos moradores fantasmagóricos e espectrais de Stratholme. Depois de livrá-los da forma de fantasma, use o canhão de novo e BLAU: liberdade!","","Liberte 15 Almas Inquietas, depois fale novamente com Egan."}},
[5283] = {"A arte de forjar armaduras", {"Para virar Ferreir<o/a> de Armaduras, tu vai ter que forjar os seguintes itens e levá-los pro Grumnus: quatro Elmos Ornados de Mithril, duas Botas Ornadas de Mithril e um Peitoral Ornado de Mithril."}},
[5284] = {"O caminho do armeiro", {"Para te tornares Armeir<o/a>, tu terás que forjar os seguintes itens e trazer para o Ironus: 4 Espadas Largas de Lunaço, 4 Machados Sólidos de Ferro, 2 Machados Pesados de Mithril e 2 Grandes Maças Pretas."}},
[5301] = {"A arte de forjar armaduras", {"Para virar Ferreir<o/a> de Armaduras, você vai ter que forjar os seguintes itens e levá-los para Okothos: quatro Elmos Ornados de Mithril, duas Botas Ornadas de Mithril e um Peitoral Ornado de Mithril."}},
[5302] = {"O caminho do armeiro", {"Para se tornar <um Armeiro/uma Armeira>, você precisa fazer os seguintes itens e levá-los para Borgosh: 4 Espadas Largas de Lunaço, 4 Machados Sólidos de Ferro, 2 Machados Pesados de Mithril e 2 Grandes Maças Pretas."}},
[5305] = {"Doce serenidade", {"Vá para Stratholme e mate o Forjador de Martelos Carmesim. Recolha o Avental do Forjador de Martelos Carmesim e leve-o para Lilith."}},
[5306] = {"A litofídia da caçadora sombria", {"Vá para o Pico da Rocha Negra e mate a Caçadora Sombria Vosh'gajin. Recolha a Litofídia de Vosh'gajin e fale novamente com Kilram."}},
[5307] = {"Corrupção", {"Encontre o Cuteleiro da Guarda Negra em Stratholme e destrua-o. Recolha a Insígnia da Guarda Negra e fale novamente com Seril Flagelicida."}},
[5321] = {"A adormecida despertou", {"Escolte Kerlonian Perenumbra até Liladris Luneflúvia no Entreposto de Maestra, situado no Vale Gris."}},
[5341] = {"A fortuna da família Barov", {"Desbrave Scolomântia e recupere a fortuna da família Barov. A fortuna é composta por quatro escrituras: A Escritura de Castro das Flechas, A Escritura de Montalvo, A Escritura da Serraria Tarren e A Escritura de Costa Sul. Fale novamente com Alexei Barov quando tiver terminado a tarefa."}},
[5342] = {"O último Barov", {"Vá para o Acampamento Ventogelante, no território da Aliança, e assassine Weldon Barov. Recolha a cabeça dele e fale novamente com Alexei Barov.","","Esteja avisad<o/a>: Weldon provavelmente estará muito bem protegido pela escória da Aliança."}},
[5343] = {"A fortuna da família Barov", {"Desbrave Scolomântia e recupere a fortuna da família Barov. A fortuna é composta por quatro escrituras: A Escritura de Castro das Flechas, a Escritura de Montalvo, a Escritura da Serraria Tarren e a Escritura de Costa Sul. Fale novamente com Weldon Barov quando tiver terminado a tarefa."}},
[5344] = {"O último Barov", {"Vá para o Baluarte, no território da Horda, e assassine Alexei Barov. Recolha a cabeça dele e fale novamente com Weldon Barov.","","Esteja avisad<o/a>: Alexei provavelmente estará muito bem protegido pela escória da Horda."}},
[5361] = {"Árvore genealógica", {"Leve o Relatório de Vigia do Rochedo Chifre Longo para Nataoka Chifre Longo no Entreposto do Espírito que Anda, situado em Desolação."}},
[5381] = {"Mão da Iruxos", {"Recolha a Caixa Demoníaca na Fortaleza Machado do Trovão e leve-a, junto com a Picareta do Diabo, para Taiga Crina Sábia na Aldeia Pescassombra."}},
[5382] = {"Doutor Theolen Krastinov, o Carniceiro", {"Encontre o Doutor Theolen Krastinov em Scolomântia. Destrua-o, depois queime os Restos Mortais de Eva Sarkhoff e os Restos Mortais de Lucien Sarkhoff. Fale novamente com Eva Sarkhoff depois que tiver terminado a tarefa."}},
[5383] = {"Saco de Horrores de Krastinov", {"Leve o Saco de Horrores para Eva Sarkhoff, em Castro das Flechas."}},
[5384] = {"Kirtonos, o Arauto", {"Volte a Scolomântia com o Sangue dos Inocentes em mãos. Encontre a varanda e derrame o Sangue dos Inocentes no braseiro. Kirtonos virá na intenção de devorar sua alma.","","Lute com bravura, não dê nenhuma brecha! Destrua Kirtonos e fale novamente com Eva Sarkhoff."}},
[5385] = {"Os Restos Mortais de Trey Forjaleve", {"Entregue os Restos Mortais de Trey Forjaleve a Jessir Lunarco, em Selva Maleva."}},
[5386] = {"Peixe do dia", {"Converse com Jinar'Zillen, na Aldeia Pescassombra, e depois leve 2 peixes pança-sangrenta para Nataoka Chifre Longo."}},
[5401] = {"Ordem da Aurora Argêntea", {"Todos os que lutam contra o mal em nome da Aurora Argêntea precisam envergar nossa comenda. Ao usá-la, você conquista o direito a reclamar as recompensas que nossos intendentes oferecem.","","Sob a ordem, você conseguirá adquirir as Pedras do Flagelo de Flagelos mortos, que são as marcas de insígnia que eles costumam portar. Procure por essas insígnias e traga-as para mim. Para cada conjunto que trouxer, você receberá nossa marca de heroísmo: o Símbolo de Bravura da Aurora Argêntea.","","Conquiste o seu destino, <class>, junte-se à luta hoje!"}},
[5402] = {"Pedra do Flagelo do Lacaio", {"As Pedras do Flagelo do Lacaio são a insígnia dos integrantes do Flagelo. Costumam ser encontradas com os mais fracos das tropas que ficam estacionadas nas Terras Pestilentas, embora o termo \"mais fracos\" não deva ser considerado fora de contexto. Onde quer que esteja, o Flagelo nunca deve ser subestimado.","","A Aurora Argêntea oferece um símbolo de bravura em troca de vinte insígnias, uma troca que faço com prazer, desde que você tenha conquistado pedras do flagelo suficientes."}},
[5403] = {"Pedra do Flagelo do Invasor", {"As Pedras do Flagelo do Invasor são a insígnia das tropas de elite do Flagelo. Costumam ser encontradas com os mais fortes das tropas que ficam estacionadas nas Terras Pestilentas. Matar uma criatura dessas é um grande desafio e um feito que deve ser reconhecido com uma comenda por derrotar o mal.","","A Aurora Argêntea oferece um símbolo de bravura em troca de dez insígnias dessas. Traga-as para mim que eu lhe concederei a justa recompensa."}},
[5404] = {"Pedra do Flagelo do Corruptor", {"As Pedras do Flagelo do Corruptor são a insígnia dos Lordes Supremos do Flagelo. Os seres mais poderosos, que ocupam posições de autoridade no Flagelo, vão sempre envergar uma dessas. A morte de uma dessas criaturas malignas é sempre um passo adiante para a causa da Aurora Argêntea, e de todas as boas causasnnEm nome da Aurora Argêntea, eu lhe concederei um símbolo de bravura para cada insígnia dessas que você trouxer. Tenha cuidado ao tentar conseguir a sua, <name>, não subestime criaturas tão poderosas."}},
[5405] = {"Ordem da Aurora Argêntea", {"Todos os que lutam contra o mal em nome da Aurora Argêntea precisam envergar nossa comenda. Ao usá-la, você conquista o direito a reclamar as recompensas que nossos intendentes oferecem.","","Sob a ordem, você conseguirá adquirir as pedras do flagelo de Flagelos mortos, que são as marcas de insígnia que eles costumam portar. Procure por essas insígnias e traga-as para mim. Para cada conjunto que trouxer, você receberá nossa marca de heroísmo: o Símbolo de Bravura da Aurora Argêntea.","","Conquiste o seu destino, <class>, junte-se à luta hoje!"}},
[5406] = {"Pedra do Flagelo do Corruptor", {"As Pedras do Flagelo do Corruptor são a insígnia dos Lordes Supremos do Flagelo. Os seres mais poderosos, que ocupam posições de autoridade no Flagelo, vão sempre envergar uma dessas. A morte de uma dessas criaturas malignas é sempre um passo adiante para a causa da Aurora Argêntea, e de todas as boas causasnnEm nome da Aurora Argêntea, eu lhe concederei um símbolo de bravura para cada insígnia dessas que você trouxer. Tenha cuidado ao tentar conseguir a sua, <name>, não subestime criaturas tão poderosas."}},
