[5408] = {"Pedra do Flagelo do Lacaio", {"As Pedras do Flagelo do Lacaio são a insígnia dos integrantes do Flagelo. Costumam ser encontradas com os mais fracos das tropas que ficam estacionadas nas Terras Pestilentas, embora o termo \"mais fracos\" não deva ser considerado fora de contexto. Onde quer que esteja, o Flagelo nunca deve ser subestimado.","","A Aurora Argêntea oferece um símbolo de bravura em troca de vinte insígnias, uma troca que faço com prazer, desde que você tenha conquistado pedras do flagelo suficientes."}},
[5421] = {"Peixe no balde", {"Eu dou recompensa pra quem deixar a minha vida mais fácil... eu gosto de fazer o menor esforço possível, cara!","","Eu coloquei uma porção de armadilhas pra lagostim debaixo das docas aqui da Aldeia Pescassombra. Se tu tiver a fim de fazer o meu trabalho e pegar os lagostins lá pra mim, eu posso te dar uma coisinha que tu vai se amarrar, tá ligado? Pra cada cinco lagostins que tu me trazer, eu te dou um peixe pança-sangrenta."}},
[5441] = {"Peões preguiçosos", {"Use o Cassetete do Encarregado nos Peões Preguiçosos enquanto estiverem dormindo. Acorde 5 peões, depois devolva o Cassetete do Encarregado ao Encarregado Thazz'ril, no Vale das Provações."}},
[5461] = {"Ras Friomúrmuro, o humano", {"Viaje para o Planalto Arathi, até as ruínas de Stromgarde. Vasculhe Stromgarde em busca de um Livro de Recordações. Se encontrar o item, retorne com ele até o Magistrado Marduque."}},
[5462] = {"Ras Friomúrmuro, o moribundo", {"Viaje para a Capela Esperança da Luz, nas Terras Pestilentas Orientais, e procure por Leonid Bartolomeu, o Venerado. Mostre a ele o Livro de Recordações e conte tudo o que descobriu."}},
[5463] = {"Dádiva de Menethil", {"Vá até Stratholme e encontre a Dádiva de Menethil. Ponha o Livro de Recordações no solo profanado."}},
[5464] = {"Dádiva de Menethil", {"Leve a Lembrança Vinculada para Leonid Bartolomeu, nas Terras Pestilentas Orientais."}},
[5465] = {"Lembrança Vinculada", {"Fale novamente com o Magistrado Marduque, no Castro das Flechas. Mostre-lhe a Lembrança Vinculada."}},
[5466] = {"Ras Friomúrmuro, o lich", {"Procure por Ras Friomúrmuro em Scolomântia. Quando o encontrar, use a Lembrança Vinculada em sua versão morto-vivo. Quando conseguir transformá-lo novamente em mortal, mate-o e recupere a Cabeça Humana de Ras Friomúrmuro. Leve a cabeça de volta ao Magistrado Marduque."}},
[5481] = {"Beijo do Gordo", {"Colha 3 Ervas-do-emo e entregue para o Boticário-júnior Hollanda, no cemitério de Montalvo."}},
[5482] = {"Erva-do-demo", {"Colha 10 Ervas-do-demo e entregue para o Boticário-júnior Hollanda."}},
[5501] = {"Ossos do ofício", {"Entregue 10 Ossos de Kodo do Cemitério dos Kodos para Bibbly Ferrafivela no Barraco do Kormek."}},
[5502] = {"Um protegido da Horda", {"Leve a criança de volta ao orfanato de Orgrimmar e entregue o Apito de Órfão Orc à Matriarca Uivadora no Vale da Honra."}},
[5503] = {"Ordem da Aurora Argêntea", nil},
[5504] = {"Dragonas da Aurora", {"Leve 10 Símbolos de Bravura da Aurora Argêntea à Intendente Hasana, no Baluarte, que fica nas Terras Pestilentas Ocidentais."}},
[5505] = {"A chave para Scolomântia", {"Bem, <name>, aqui está: a chave-mestra completa. Tenho certeza absoluta de que essa chave vai permitir que você entre nos confins da Scolomântia. Mas só tem um jeito de saber se ela funciona. Meu conselho: nem perca tempo com testes.","","Boa sorte, grande herói. Que você continue a emanar luz na escuridão."}},
[5507] = {"Dragonas da Aurora", {"Leve 10 Símbolos de Bravura da Aurora Argêntea ao Intendente Centelhuz, no Acampamento Ventogelante, que fica nas Terras Pestilentas Ocidentais."}},
[5508] = {"Pedra do Flagelo do Corruptor", {"As Pedras do Flagelo do Corruptor são a insígnia dos lordes supremos do Flagelo. Os seres mais poderosos, que ocupam posições de autoridade no Flagelo, vão sempre envergar uma dessas. A morte de uma dessas criaturas malignas é sempre um passo adiante para a causa da Aurora Argêntea, e de todas as boas causasnnEm nome da Aurora Argêntea, eu lhe concederei um símbolo de bravura para cada insígnia dessas que você trouxer. Tenha cuidado ao tentar conseguir a sua, <name>, não subestime criaturas tão poderosas."}},
