[5511] = {"A chave para Scolomântia", nil},
[5513] = {"Dragonas da Aurora", {"Leve 10 Símbolos de Bravura da Aurora Argêntea à Intendente Cerraculatra, na Capela Esperança da Luz, que fica nas Terras Pestilentas Orientais."}},
[5514] = {"Um molde que vale ouro", {"Leve os Fragmentos de Ossos Imbuídos e 15 moedas de ouro para Rugov A. Massoaço em Geringontzan."}},
[5515] = {"Saco de Horrores de Krastinov", {"Localize Janice Barov em Scolomântia e a destrua. Recupere o Saco de Horrores de Krastinov do cadáver dela. Entregue o saco a Eva Sarkhoff."}},
[5517] = {"Dragonas cromáticas da Aurora", {"Leve 25 Símbolos de Bravura da Aurora Argêntea à Intendente Cerraculatra, na Capela Esperança da Luz, que fica nas Terras Pestilentas Orientais."}},
[5518] = {"O traje de ogro Gordok", {"Leve 4 Peças de Runatrama, 8 Couros Rústicos, 2 Fios Rúnicos e Tanino Ôgrico para Fiapo Agulhacerta. Atualmente, ele está acorrentado dentro da ala Gordok do Gládio Cruel."}},
[5519] = {"O traje de ogro Gordok", nil},
[5521] = {"Dragonas cromáticas da Aurora", {"Leve 25 Símbolos de Bravura da Aurora Argêntea ao Intendente Centelhuz, no Acampamento Ventogelante, que fica nas Terras Pestilentas Ocidentais."}},
[5522] = {"Leonid Bartolomeu", {"Leve os Ovos Congelados para Leonid Bartolomeu, nas Terras Pestilentas Orientais."}},
[5524] = {"Dragonas cromáticas da Aurora", {"Leve 25 Símbolos de Bravura da Aurora Argêntea à Intendente Hasana, no Baluarte, que fica nas Terras Pestilentas Ocidentais."}},
[5525] = {"Liberdade ainda que tardia", {"É chamada de chave dos grilhões dos Gordok. Bom, pelo menos é esse o nome que essas coisinhas fofas usam. Acho que qualquer um dos ogros pode ter a sua, mas é mais provável que os guardas tenham um exemplar...","","Peraí, onde é que eu estou com a cabeça? Já está com você, não é mesmo? Está aqui?"}},
[5526] = {"Pedaços de Vinhavil", {"Encontre a Vinhavil no Gládio Cruel e obtenha uma lasca dela. É bem provável que você só consiga encontrar uma depois da derrota de Azzin, o Selvamorfo. Use o Relicário da Pureza para selar a lasca com segurança e leve-a de volta para Ravine Saturna, no Refúgio Noturno, que fica na Clareira da Lua."}},
[5527] = {"Relicário da Pureza", {"Viaje até Silithus e procure um Relicário da Pureza dentro das ruínas da Vila do Vento Sul. Se conseguir encontrá-lo, regresse com ele até Ravine Saturna, no Refúgio Noturno, que fica na Clareira da Lua."}},
[5528] = {"O teste de sabor dos Gordok", {"Como você é o dono do pedaço agora, vai provar de graça as minhas melhores cervejas! Elas levantam até defunto... e vão deixar você pra lá de Terralém! Essas são papa-fina mesmo, chefia. Dá uma provadinha e diga pro Kreeg o que você acha!"}},
