[6001] = {"Corpo e coração", {"Aplique Poeira de Lua Cenariana na Pedra Luniscante de Auberdine para trazer Lunagarra. Então, enfrente Lunagarra e ganhe a força de corpo e coração que ele possui.","","Fale com Mathrengyl Ursivagus em Darnassus quando você terminar."}},
[6002] = {"Corpo e coração", {"Aplique Poeira Lunar Cenariana na Pedra Luniscante entre Mulgore e os Sertões para evocar Lunagarra. Então, enfrente Lunagarra e ganhe a força de corpo e coração que ele possui.","","Fale com Turak Runa Totem no Penhasco do Trovão quando terminar."}},
[6004] = {"Negócios inacabados", {"Mate 2 Socorristas Escarlate, 2 Caçadores Escarlate, 2 Magos Escarlate e 2 Cavaleiros Escarlate antes de voltar a Kirsta Sombrafunda, nas Terras Pestilentas Ocidentais."}},
[6021] = {"Zaeldarr, o Proscrito", {"Leve a Cabeça de Zaeldarr ao Zelador Alen na Capela Esperança da Luz."}},
[6022] = {"Matar com propósito", {"Vá ao Cruzamento de Corin, a leste, e mate membros do Flagelo que habitam a cidade. Colete 7 pedaços de Putrefação Viva em menos de 10 minutos. Use o Almofariz e Pilão para moer a Putrefação Viva, de maneira a transformá-la em Putrefação Coagulada."}},
[6023] = {"Negócios inacabados", {"Mate o Guarda-caça Eiras e o Fidalgo Durgen antes de voltar à Kirsta Sombrafunda, nas Terras Pestilentas Ocidentais."}},
[6024] = {"A súplica de Hameya", {"Mate o Infiltrador Hameya. Use a chave dele no Monturo de Terra atrás da Chácara Secreta."}},
[6025] = {"Negócios inacabados", {"Entre em Amparo e encontre um lugar alto de onde possa ver a cidade e avaliar as forças da Cruzada Escarlate. Então fale novamente com Kirsta Sombrafunda nas Terras Pestilentas Ocidentais."}},
[6026] = {"Isso é pedir muito", {"Leandro Leonardo quer que você pegue 2 Barras de Tório, 1 Bastão Dourado, 8 Bombas de Alto-explosivo e 8 Gatilhos Instáveis.","","Você terá que encontrar um ferreiro e um engenheiro para esses itens."}},
[6027] = {"O Livro dos Anciãos", {"Pegue o Livro dos Anciãos e entregue-o para Azore Albamorte na torre de Ethel Rethor."}},
[6028] = {"O relatório de Visteterna", {"Entregue o Relatório de Visteterna à Oficial Argêntea Cândida no Acampamento Ventogelante, que fica nas Terras Pestilentas Ocidentais."}},
[6029] = {"O relatório de Visteterna", {"Entregue o Relatório de Visteterna ao Oficial Argênteo Garush no Baluarte, nas Terras Pestilentas Ocidentais."}},
[6030] = {"Duque Nicolau Zverenhoff", {"Entregue o livro \"Estudos sobre Comunicação com Espíritos\" ao Duque Nicolau Zverenhoff na Capela Esperança da Luz, que fica nas Terras Pestilentas Orientais."}},
[6031] = {"Runatrama", {"Leve 30 cortes de Runatrama para Meilosh no Domínio dos Presamatos."}},
[6032] = {"Tecido sagrado", {"Leve 2 cortes de Lunatrama para Meilosh no Domínio dos Presamatos."}},
[6041] = {"Quando Leandro canta, eu fico violento", {"Viaje ao Bosque Pestilento, a noroeste da Esperança da Luz. Destrua 8 Estruturas do Flagelo usando o Composto Químico Especial do Leandro na Marca de Detonação plantada dentro de cada construção. Os Zigurates e Abatedouros foram marcados por instrução de Leandro.","","Assim que você plantar a bomba, deixe a estrutura e contemple seu trabalho.","","A propósito, o Composto Químico Especial do Leandro é bastante... instável."}},
[6042] = {"Pequenos transtornos da Morte-em-Vida", {"Mate 20 Quirópragos Nóxios e 10 Quirópragos Monstruosos. Fale novamente com Nathanos Arauto da Praga quando você tiver terminado."}},
