[6801] = {"Lokholar, o Senhor do Gelo", {"Eu venho do Penhasco do Trovão, morto-vivo. Caerne me mandou pra cá para ajudar o Clã Lobo do Gelo nesses tempos difíceis.","","Mas não vamos perder mais tempo. Ataque nossos inimigos e me traga o sangue deles. Quando bastante sangue tiver sido coletado, o ritual de evocação pode começar.","","Obteremos a vitória quando o senhor elemental for atiçado contra o exército Lançatroz."}},
[6804] = {"Água contaminada", {"Use o Aspecto de Neptulon nos elementais contaminados das Terras Pestilentas Orientais. Leve 12 Braçadeiras Dissonantes e o Aspecto de Neptulon ao Duque Hidráxis em Azshara."}},
[6805] = {"Tempestades e estrondores", {"Mate 15 Tempestades de Poeira e 15 Estrondores do Deserto e fale novamente com o Duque Hidráxis em Azshara."}},
[6821] = {"O Olho do Mirabrasa", {"Leve o Olho do Mirabrasa ao Duque Hidráxis em Azshara."}},
[6822] = {"O Núcleo Derretido", {"Mate 1 Senhor do Fogo, 1 Gigante Derretido, 1 Cão-magma Antigo e 1 Irrompedor de Lava e depois fale novamente com o Duque Hidráxis em Azshara."}},
[6823] = {"Agente de Hidráxis", {"Alcance a reputação de <Honrado/Honrada> pelos Senhores das Águas Hidraxianos e depois fale com o Duque Hidráxis em Azshara."}},
[6824] = {"Mãos do inimigo", {"Leve as Mãos de Lúcifron, Sulfuron, Geena e Shazzrah ao Duque Hidráxis em Azshara."}},
