[7381] = {"O retorno de Korrak", {"Se Korrak, o Sanguinário, voltar ao Campo de Disputa, procure-o e destrua-o.","","Leve o Crânio de Korrak para o Mestre Guerreiro Laggrond no Contraforte de Eira dos Montes."}},
[7382] = {"Korrak, o de vida eterna", {"Se Korrak, o Sanguinário, voltar ao Campo de Disputa, encontre-o e destrua-o.","","Entregue o Crânio de Korrak para o Tenente Haggerdin no Contraforte de Eira dos Montes."}},
[7383] = {"Coroa da Terra", {"Encha o Frasco de Ametista e leve-o de volta para Corithras Lunafúria em Dolanaar."}},
[7385] = {"Um galão de sangue", {"Você também pode nos trazer quantidades maiores do sangue do inimigo. Aceitarei com prazer oferendas trazidas em galões, <name>."}},
