[7509] = {"Forjando a Quel'Serrar", {"Faça Onyxia soprar fogo na Lâmina Ancestral Virgem. Quando conseguir, pegue a agora Lâmina Ancestral Incandescente. Atenção, uma Lâmina Ancestral Incandescente não permanecerá assim para sempre — o tempo urge.","","O último passo antes de voltar para mim é matar a fera e enfiar a Lâmina Ancestral Incandescente no cadáver dela.","","Faça isso e Quel'Serrar será sua."}},
[7521] = {"Thunderaan the Windseeker", {"To free Thunderaan the Windseeker from his prison, you must present the right and left halves of the Bindings of the Wind Seeker, 10 bars of Elementium, and the Essence of the Firelord to Highlord Demitrian."}},
[7522] = {"Examine the Vessel", {"Examine the Vessel of Rebirth and return it to Highlord Demitrian."}},
[7541] = {"Serviço para a Horda", {"Levará tempo para nossos estudiosos estudarem o pingente que você nos trouxe. Mas gostaria de lhe oferecer algo como um pequeno sinal de agradecimento por seu serviço à Horda.","","Obrigado, <name>. Seus atos trazem honra a você e a nós."}},
[7562] = {"Mor'zul Trazangre", {"Fale com Mor'zul Trazangre nas Estepes Ardentes."}},
[7563] = {"Raiva de Sangue", {"Leve 30 garrafas de Sangue de Fera em Fúria para Mor'zul Trazangre nas Estepes Ardentes."}},
[7564] = {"Olholouco", {"Leve o Caixote de Sangue para Gorzeeki Olholouco nas Estepes Ardentes."}},
[7581] = {"Montando a prisão", {"Vá até o Gládio Cruel, em Feralas, e obtenha 15 amostras de Sangue de Sátiro dos Sátiros Criasselvagem que habitam o Distrito Lenhatorta. Fale novamente com Daio no Rasgo Infecto quando tiver feito isso."}},
[7582] = {"O revestimento da prisão", {"Vá à Garganta do Sussurro Sombrio em Hibérnia e recupere 5 Lágrimas dos Hederine dos demônios Hederine que ocupam o lugar. Fale novamente com Daio no Rasgo Infecto quando você tiver completado esta tarefa."}},
[7583] = {"Supressão", {"Aventure-se no Rasgo Infecto e encontre um Comandante Demonarca.","","Use a Prisão de Cristal Faiscante no Comandante Demonarca. Prepare-se para uma sequência violenta de ataques, enquanto o demônio tenta escapar de ser preso.","","Caso você tenha sucesso, leve o Demonarca Aprisionado para Daio no Rasgo Infecto."}},
[7601] = {"O que Niby ordena", {"Fale com Diabretim na Selva Maleva."}},
[7602] = {"Essência Vil Impecável", {"Leve três Essências Vis Impecáveis, originárias de três locais distintos, para Diabretim na Selva Maleva.","","Os Sátiros Legashi de Azshara possuem a Essência Vil Impecável da região deles, assim como os Legionários de Jaedenar e as Sentinelas Guardas Vis da Barreira do Inferno.","","Busque as Essências Vis Impecáveis e leve-as para Diabretim, na Selva Maleva."}},
[7603] = {"Núcleo Infernal de Kroshius", {"Procure os restos mortais de Kroshius no Vale das Escaras Pungentes, que fica na Selva Maleva. Ao encontrá-los, use o Fogo Vil perto deles e aguarde o retorno de Kroshius. Quando o infernal acordar, mate-o e retire do corpo dele o Núcleo Infernal de Kroshius.","","Leve o Núcleo para Niby, o Todo-poderoso na Selva Maleva."}},
[7604] = {"Contrato vinculante", {"Entregue o Contrato da Irmandade do Tório para Lokhtos Umbrarganha, caso tenha interesse em receber as instruções para Sulfuron."}},
[7621] = {"Um aviso", nil},
[7622] = {"Equilíbrio entre luz e sombra", {"Salve 50 Camponeses antes que 15 sejam mortos. Fale com Eris Abrigoardente se conseguir completar a tarefa.","","Você pode conferir o Poste da Morte para saber quantos Camponeses foram mortos."}},
[7623] = {"Lorde Nihilus", {"Compre Poções Sombrias de Gorzeeki nas Estepes Ardentes.","","Use as poções para viajar através de Jaedenar e fale com Lorde Nihilus."}},
[7624] = {"Ulathek, o traidor", {"Confronte Ulathek, depois leve o Coração do Traidor para o Lorde Nihilus em Jaedenar."}},
[7625] = {"Poeira Estelar Zorothiana", {"Compre Poeira Estelar Zorothiana de Ur'dan. Leve-a para Gorzeeki Olholouco nas Estepes Ardentes."}},
[7626] = {"Sino de Defimura", {"Leve 2 Elixires de Poder Sombrio para Gorzeeki Olholouco nas Estepes Ardentes."}},
[7627] = {"Roda da Marcha Negra", {"Leve 6 Estilhaços Brilhantes Grandes e 25 Minérios de Ferro Negro para Gorzeeki nas Estepes Ardentes."}},
[7628] = {"Vela do Juízo Final", {"Leve 3 Escamas de Dragão Negro para Gorzeeki Olholouco nas Estepes Ardentes."}},
[7629] = {"Entrega de diabrete", {"Leve o Diabrete numa Jarra para o laboratório de alquimia em Scolomântia. Depois que o pergaminho for criado, devolva a jarra para Gorzeeki Olholouco."}},
[7630] = {"Arcanita", {"Leve 1 Barra de Arcanita para Gorzeeki nas Estepes Ardentes."}},
[7631] = {"Corcel Medonho de Zoroth", {"Leia as Instruções de Mor'zul. Evoque um Corcel Medonho Zorothiano, derrote-o e então vincule o espírito dele a você."}},
