[7721] = {"Bota dez aí, chefia!", {"Colete 10 Âmagos dos Elementais de Água dos Elementais do Mar e dos Borrifos do Mar ao longo da Costa Esquecida de Feralas, e leve-os para Zorbin Cegasvista, próximo às docas."}},
[7722] = {"Segue o fluxo", {"Em algum lugar do Fosso de Lava do Caldeirão você encontra as Instruções Secretas: Fluxo Ígneo que o Feitor Maltorius roubou. Encontre as instruções e devolva-as ao Mestre Ferreiro Queimatar na Garganta Abrasadora."}},
[7723] = {"Malditos dedos gordos", {"Hansel Mão-pesada quer que você mate 20 Golens de Guerra Pesados.","","Os Golens de Guerra Pesados perambulam pelas áreas em torno do Caldeirão, na Garganta Abrasadora. Quando tiver matado a quantidade necessária, fale novamente com Hansel na Garganta Abrasadora."}},
[7724] = {"Toco cru pegando fogo", {"Hansel Mão-pesada quer que você mate 20 Grandes Aranhas de Lava na Garganta Abrasadora. Você pode encontrá-las nos platôs e poços de lava do local.","","Depois de matar as Grandes Aranhas de Lava, fale novamente com Hansel Mão-pesada na Garganta Abrasadora."}},
[7725] = {"Querida, encolhi mais gigantes", {"Use o Ultra-encolhedor de Zorbin para deixar qualquer tipo de gigante de Feralas com um tamanho mais compacto e manejável. Colete 10 Resíduos de Miniaturização encontrados nos gigantes encolhidos e leve para Zorbin Cegasvista, que fica nas docas da Costa Esquecida, em Feralas."}},
[7726] = {"Completa o tanque, chefia!", {"Colete 6 Âmagos dos Elementais de Água dos Elementais do Mar e dos Borrifos do Mar ao longo da Costa Esquecida de Feralas e leve-os para Zorbin Cegasvista, próximo às docas."}},
[7727] = {"Incendossauro? É de comer?", {"Hansel Mão-pesada quer que você mate 20 Incendossauros.","","Eles ocupam as poças dentro do Fosso de Lava. O Fosso de Lava fica no Caldeirão da Garganta Abrasadora.","","Depois de matar os Incendossauros, fale novamente com Hansel Mão-pesada na Garganta Abrasadora."}},
[7728] = {"OBJETOS ROUBADOS: Alcaraviz de Ferraria e Luneta do Espia", {"Encontre e devolva o Alcaraviz de Ferraria e a Luneta do Espia ao Capataz Scrange na Garganta Abrasadora.","","A única informação que você tem sobre esses itens é a seguinte: com certeza foram roubados por anões Ferro Negro. O Alcaraviz de Ferraria é uma ferramenta usada por ferreiros e a Luneta do Espia é um aparelho de monitoramento inestimável para vigias."}},
[7729] = {"TEMOS VAGAS: desbancando a concorrência", {"Mate 15 Capatazes Ferro Negro e 15 Feitores Ferro Negro. Depois, fale novamente com Capataz Scrange, na Garganta Abrasadora."}},
[7730] = {"Infestação Zukk'ash", {"Traga 20 Carapaças de Zukk'ash para Hadoken Passo Veloz na Aldeia Mojache, que fica em Feralas."}},
[7731] = {"Cravaçoite", {"Derrote Cravaçoite na Profundeza Atormentada, leve as glândulas para Hadoken Passo Veloz na Aldeia Mojache, que fica em Feralas."}},
[7732] = {"O relatório dos Zukk'ash", {"Entregue o Relatório sobre Zukk'ash da Aldeia Mojache para Zilzibin Tamborleiro. Ele fica no Bazar de Orgrimmar."}},
[7733] = {"Melhoria de qualidade", {"Leve 10 Pelegos de Yeti Cortifúria para Pradal McGrubben no Domínio de Plumaluna, em Feralas."}},
[7734] = {"Melhoria de qualidade", {"Leve 10 Pelegos de Yeti Cortifúria para Jangdor Passo Veloz na Aldeia Mojache, em Feralas."}},
[7735] = {"Pelego imaculado de Yeti", {"Leve o Pelego Imaculado de Yeti para Pradal McGrubben no Domínio de Plumaluna, em Feralas."}},
[7736] = {"Restaurando suprimentos de fluxo ígneo com Sangue-real", {"Chega de desperdiçar o meu precioso tempo com bobagem, <race>. A prioridade agora é reabastecer o nosso suprimento cada vez mais baixo de fluxo ígneo.","","Vou precisar que você me traga o seguinte:","","*Escamas de incendossauro.","","*Sangue-real.","","*Carvão.","","Quanto mais você trouxer, melhor!","","Mas se quiser conquistar pontos com a Irmandade, tem que ser rápido."}},
[7737] = {"Conquistando a confiança", {"Você trabalhou um bocado para subir na hierarquia corporativa da Irmandade, <race>. Começamos a ver você com bons olhos. Um feito e tanto!","","Se você estiver interessado em mais trabalho e em ser mais respeitado por nós, ouça bem.","","Pra refinar o nosso fluxo ígneo, é só a gente adicionar um simples resíduo de Ferro Negro à mistura. Esse resíduo de Ferro Negro pode ser encontrado no Abismo Rocha Negra.","","Quero que me traga todo o resíduo de Ferro Negro que você encontrar!"}},
[7738] = {"O pelego perfeito de yeti", {"Leve o Pelego Perfeito de Yeti para Jangdor Passo Veloz na Aldeia Mojache, que fica em Feralas."}},
[7741] = {"Praise from the Emerald Circle <NYI> <TXT>", nil},
[7761] = {"Comando do Mão Negra", {"Sem dúvida, um orc excepcionalmente burro. Parece que para acessar o Orbe de Comando é necessário ter a Marca de Drakkisath.","","A carta indica que o próprio General Drakkisath a guarda. Talvez você devesse investigar."}},
[7781] = {"O Senhor dos Rocha Negra", {"Leve a Cabeça de Nefarian para o Grão-lorde Bolvar Fordragon, em Ventobravo."}},
[7782] = {"O Senhor dos Rocha Negra", {"Vá até o Vale dos Heróis, em Ventobravo, e fale com o Marechal de Campo Afrasiabi."}},
[7783] = {"O senhor dos Rocha Negra", {"Leve a Cabeça de Nefarian para Thrall, em Orgrimmar."}},
[7784] = {"O senhor dos Rocha Negra", {"Fale com o Lorde Supremo Saurfang em Orgrimmar. Ele espera por você no Vale da Força."}},
[7785] = {"Examine o casulo", {"Examine o Casulo de Renascimento e entregue-o para o Grão-lorde Dimitriano."}},
[7786] = {"Trovejardus, o Perseguidor dos Ventos", {"Para libertar Trovejardus, o Perseguidor dos Ventos, da prisão, você deve entregar as metades direita e esquerda dos Grilhões do Perseguidor dos Ventos, 10 Barras de Elemêntio e a Essência do Senhor do Fogo para o Grão-lorde Dimitriano, em Silithus."}},
[7787] = {"Erga-se, Tormentária!", {"Entregue a Lâmina Latente Beijada pelo Vento para o Grão-lorde Dimitriano."}},
[7788] = {"Aniquile os invasores!", {"Vá à Ravina Brado Guerreiro, derrote a Horda, obtenha uma Marca de Honra da Ravina Brado Guerreiro e a entregue à Sentinela Cantálgures no Bosque Asa de Prata."}},
[7789] = {"Domine os usurpadores da Asa de Prata", {"Vá à Ravina Brado Guerreiro, derrote a Aliança, obtenha uma Marca de Honra da Ravina Brado Guerreiro e a entregue ao Capitão Estilhaça-crânio no Campo-base Mor'shan."}},
[7791] = {"Doação de lã", {"Estamos aceitando doações de tecidos de lã. A cidade de Ventobravo lhe será muito grata se você generosamente doar sessenta cortes de lã. Já conseguimos arrecadar alguma coisa, por isso só precisamos que você doe essa quantidade. O que faltar nós conseguiremos com outros doadores por todo o reino que também apoiam nossa campanha.","","Caso você tenha sessenta cortes de lã e queira doá-los, eu posso recebê-los agora."}},
[7792] = {"Uma doação de lã", {"Estamos aceitando doações de tecidos de lã. A cidade de Darnassus lhe será muito grata se você doar sessenta cortes de lã. Já conseguimos arrecadar alguma coisa, por isso só precisamos que você doe sessenta cortes. O que faltar, nós conseguiremos com outros doadores.","","Caso você tenha sessenta cortes de lã e queira doá-los, eu posso recebê-los agora."}},
[7793] = {"Doação de seda", {"Nosso estoque de seda está em baixa, aliás, como o de quase todos os tecidos. Com a quantidade que temos hoje, precisaríamos receber apenas sessenta peças de você. Com a sua doação e a de outras pessoas, conseguiremos alcançar nossa meta.","","Lembre-se que um presente generoso como esse certamente aumentará o seu prestígio com a comunidade! Caso você tenha sessenta cortes de seda e queira doá-los, eu posso recebê-los agora."}},
[7794] = {"Doação de magitrama", nil},
[7795] = {"Doação de runatrama", {"<name>, você tem sido um grande doador em nossa campanha de arrecadação de tecidos. Mas, mesmo trabalhando duro para reabastecer os estoques, ainda há um tecido em falta: runatrama. Precisamos de runatrama com urgência e esperamos que você possa nos ajudar, como já ajudou no passado!","","Caso queira ajudar, traga-me todos os cortes de runatrama que puder doar. Nós pedimos uma doação inicial de sessenta cortes. A partir dessa quantidade, aceitamos outras doações."}},
