[8304] = {"Querida Natália", {"O Comandante Mar'alith, no Forte Cenariano, em Silithus, quer que você interrogue os ocupantes do Acampamento Barbabronze. Ele fica diretamente ao sul do Forte Cenariano.","","Uma vez obtidas as informações solicitadas, fale novamente com o Comandante Mar'alith."}},
[8305] = {"Relembranças longínquas", {"Localize a Lágrima Cristalina em Silithus, e contemple suas profundezas."}},
[8306] = {"No ventre da loucura", {"O Comandante Mar'alith do Forte Cenariano em Silithus quer que você encontre Natália. A informação que conseguiu aponta para Colme'Régia, ao sul, como sendo a área em que você encontrará a Senhora Natália Mar'alith.","","Não se esqueça de falar com os anões no acampamento do Barbabronze antes de se aventurar na colmeia. Eles podem ter algum trabalho e conselhos para você.","","E, <name>, lembre-se das palavras do Comandante: \"Faça o que for necessário...\""}},
[8307] = {"Receita do deserto", {"Procure informações sobre como cozinhar Carne de Verme da Areia no acampamento do Martelo do Crepúsculo, a oeste do Forte Cenariano."}},
[8308] = {"A carta perdida de Brann Barbabronze", {"Leve a Carta de Brann Barbabronze para o Acampamento do Barbabronze na região sul de Silithus."}},
[8309] = {"À procura de glifos", {"Rutgar Formaglifo, no Acampamento Barbabronze, em Silithus, quer que você vá a Colme'Ashi, Colme'Zora e Colme'Régia e decalque os Cristais Entalhados de Glifos em cada colmeia.","","Use o Kit de Transcrição do Geólogo para tirar um decalque razoável dos glifos antigos. Fale novamente com Rutgar Formaglifo quando terminar a tarefa."}},
[8310] = {"Quebrando o código", {"Frankal Pontepedra, no Acampamento Barbabronze, em Silithus, quer que você obtenha um Cérebro de Silitídeo de Colme'Zora, um Cérebro de Silitídeo de Colme'Ashi e um Cérebro de Silitídeo de Colme'Régia. Fale novamente com Frankal quando tiver concluído a tarefa."}},
[8311] = {"Gostosuras da Noturnália para Jacinto!", {"Fale com os estalajadeiros de Ventobravo, Altaforja e Darnassus, e também com Didiê Du Mocó, em Altaforja. Pague as prendas que eles pedirem em troca de gostosuras.","","Leve um Marzipã de Darnassus, uma Jujuba de Gnomeregan, um Nougat de Ventobravo e uma Balinha de Altaforja para Jacinto, que está no Orfanato de Ventobravo."}},
[8312] = {"Gostosuras de Noturnália para Assustosa!", {"Fale com os estalajadeiros de Orgrimmar, da Cidade Baixa e do Penhasco do Trovão, e também com Kali Remik, na Aldeia Sen'jin. Pague as prendas que eles pedirem e receba doces como recompensa.","","Leve um Marzipã do Penhasco do Trovão, uma Jujuba Lançanegra, um Nougat de Orgrimmar e uma Balinha da Cidade Baixa para Assustosa, no Orfanato de Orgrimmar."}},
[8313] = {"Compartilhando o conhecimento", {"Leve a Página Rasgada de Receita para Calandrath na estalagem do Forte Cenariano."}},
[8314] = {"Desvendando o mistério", {"Rutgar Formaglifo, no Acampamento Barbabronze, quer que você entregue o Prisma de Cristal com Glifos para a Geóloga Matarriso no Forte Cenariano, que fica em Silithus."}},
[8315] = {"O chamado", {"Geóloga Larksbane, no Forte Cenariano em Silithus, quer que você pegue o Mecanismo de Destrancamento de Cristal do Emissário Qiraji.","","Você foi instruído a levar os Glifos de Evocação até os Ossos de Grakkarond ao sul do Forte Cenariano e desenhá-los na areia. Caso o Emissário Qiraji apareça, mate-o e recupere o Mecanismo de Destrancamento de Cristal. Fale novamente com a Geóloga Matarriso caso tenha sucesso.","","Reúna uma tropa para esta missão, <name>!"}},
