[8778] = {"A Brigada de Altaforja vai botar pra explodir!", {"Leve 6 Óleos de Imolação, 5 Combustíveis de Foguete Goblínico e 10 Pós Explosivos Densos para a Arcanista Molabico, que está perto de Colme'Zora, em Silithus. Você também precisa entregar as Instruções de Logística IV para completar esta tarefa."}},
[8779] = {"Matérias-primas de vidência", {"Leve 1 Estilhaço Brilhante Grande, 1 Estilhaço Radiante Grande e 1 Esmeralda Enorme para a Geóloga Matarriso, no Forte Cenariano, em Silithus. Você também precisa entregar as Instruções de Logística V para concluir esta missão."}},
[8780] = {"Kits de armadura para o campo", {"Leve 8 Kits de Armadura Rústica e 8 Kits de Armadura Pesada para Jenila Fortemalho no Posto Avançado da Brigada de Altaforja, perto da Colme'Zora, em Silithus. Você também precisa entregar as Instruções de Logística VII para concluir a missão."}},
[8781] = {"Armas para o campo", {"Leve 2 Espadas Largas de Lunaço para Jenila Fortemalho no Posto Avançado da Brigada de Altaforja, perto da Colme'Zora. Você também precisa entregar as Instruções de Logística VI para concluir a missão."}},
