[9138] = {"Vila Corona Solar", {"Mate 10 Guardas de Nerúbis e apresente-se ao Arcanista Vandril em Tranquillien, que fica na Terra Fantasma."}},
[9139] = {"Vila de Aurinévoa", {"Mate 6 Fantasmas Quel'dorei e 4 Espectros Quel'dorei na Vila de Aurinévoa e apresente-se ao Arcanista Vandril em Tranquillien, que fica na Terra Fantasma."}},
[9140] = {"Vila dos Correventos", {"Colete 6 Substâncias Fantasma e 4 Fragmentos de Gárgula. Devolva-os ao Arcanista Vandril em Tranquillien, que fica na Terra Fantasma."}},
