[9276] = {"Ataque a Zeb'Tela", {"Mate 8 Lança-sombras Pinhumbra e 8 Caçadores de Cabeças Pinhumbra. Depois fale novamente com a Andarilha Solanna no Enclave dos Andarilhos, que fica na Terra Fantasma."}},
[9277] = {"Ataque a Zeb'Nowa", {"Mate 10 Senhores dos Gatos Pinhumbra e 10 Mandingueiros Pinhumbra. Depois fale novamente com a Andarilha Solanna no Enclave dos Andarilhos, que fica na Terra Fantasma."}},
