[10056] = {"Suprimentos dos Olhos Sangrentos", {"Destrua 6 pilhas de Suprimentos dos Olhos Sangrentos e apresente-se à Tenente Amadi na Fortaleza da Honra."}},
[10057] = {"Espíritos de liderança", {"Derrote os fantasmas do Arquimago Xintor e do Tenente-comandante Talvos, e fale novamente com o Guarda de Honra Wesilow na torre noroeste da Fortaleza da Honra."}},
[10058] = {"Um velho presente", {"Recupere o livro Mistérios da Luz e leve-o para o Padre Malgor Devídicos na Fortaleza da Honra."}},
[10059] = {"Dando cabo de Zeth'Gor", {"Mate Grillok \"Olho-negro\" e vasculhe seu covil para encontrar os Planos de Ataque dos Olhos Sangrentos."}},
[10060] = {"Ruína iminente", {"Mate 15 Brutos dos Olhos Sangrentos, 10 Necrólitos dos Olhos Sangrentos, 5 Peões dos Olhos Sangrentos e o Senhor da Guerra Morkh. Então apresente-se ao Guarda Pétreo Ambelão, no Acampamento Quebra-espinha."}},
[10061] = {"O Obstinado", {"Grolag quer que você derrote 12 Soldados Rasos Obstinados, 10 Feiticeiros Obstinados e 5 Cavaleiros Obstinados no Arsenal da Expedição, ao sul da Fortaleza da Honra."}},
[10062] = {"Espíritos de liderança", {"Derrote os fantasmas do Arquimago Xintor e do Tenente-comandante Talvos, e fale novamente com Grolag no Acampamento Quebra-espinha."}},
[10063] = {"Liga dos Exploradores: isso lá é coisa de gnomo?", {"Fale com Xote Cabeçote, que está acampado em um velho casco de tartaruga na costa a norte do Entreposto Rubro."}},
[10064] = {"Fala com a minha mão!", {"Apresente-se à Batedora Jorli no Recanto do Vindicante."}},
