[10082] = {"Ancestrais inquietos", {"A Mãe Kashur quer que você dê fim a 8 espíritos orcs agitados."}},
[10084] = {"Assault on Mageddon", {"Kill Nightlord Malphas at Forge Base: Mageddon, and report to Force Commander Danath Trollbane at Honor Hold."}},
[10085] = {"Visita aos antepassados", {"A Mãe Kashur, das Terras Ancestrais, em Nagrand, quer que você vá até o Posto Solavera, as Ruínas Gargaveira, a Garadar e as Ruínas dos Olhos Sangrentos."}},
[10086] = {"Eu trabalho... pela Horda!", {"Leve 8 pedaços de Metal Recuperado e 8 pedaços de Madeira Recuperada para Megzeg Rebbentappunho, em Thrallmar."}},
[10087] = {"Acende aí... pela Horda!", {"Use a Tocha Flamejante para incendiar o Canhão Oriental da Aliança e o Canhão Ocidental da Aliança, e então fale novamente com Megzeg Rebbentappunho, em Thrallmar."}},
[10088] = {"When This Mine's a-Rockin'", {"Kill 12 Gan'arg Sappers and return to Foreman Razelcraz."}},
[10089] = {"Forge Camps of the Legion", {"Kill 10 Gan'arg Servants, 5 Forge Camp Legionnaires, and 5 Sisters of Grief, then search Forge Camp: Rage for evidence of the Legion's intentions."}},
