[10092] = {"Assault on Mageddon", {"Kill Nightlord Malphas at Forge Base: Mageddon, and return to Overlord Hun Maimfist at Reaver's Fall."}},
[10093] = {"O Templo de Telhamat", {"Fale com Amaan, o Sábio, no Templo de Telhamat, na parte ocidental da Península Fogo do Inferno."}},
[10094] = {"O Códice de Sangue", {"Leia o Códice de Sangue no Labirinto Soturno de Auchindoun."}},
[10095] = {"Rumo ao coração do labirinto", {"Destrua Murmúrio e informe a Espiã-chefe Melisa Coroalta no Terraço da Luz em Shattrath sobre os eventos que transcorreram no Labirinto Soturno."}},
[10096] = {"Salvar os esporlotes", {"Mate 10 Dragadores do Pântano, 10 Tocaieiros do Pântano e fale novamente com Laurana Thar'well no Refúgio Cenariano, no Pântano Zíngaro."}},
[10097] = {"Irmão contra irmão", {"Mate o Tecetrevas Syth nos Salões dos Sethekk e liberte Lakka do cativeiro. Fale novamente com Isfar do lado de fora dos Salões dos Sethekk quando concluir o resgate."}},
[10098] = {"O legado de Terokk", {"Leve a Máscara, a Pena e a Saga de Terokk a Isfar do lado de fora dos Salões dos Sethekk."}},
[10099] = {"O mandante", {"Localize e mate Z'kral nas minas embaixo da Fortaleza da Honra, e então apresente-se ao Encarregado Grandepar."}},
[10100] = {"The Mastermind", {"Locate and kill Urga'zz within the mines beneath Thrallmar, then return to Foreman Razelcraz."}},
[10101] = {"Quando os espíritos falam", {"A Mãe Kashur, das Terras Ancestrais, em Nagrand, quer que você investigue a visão que ela teve. Procure K'ure, em Oshu'gun."}},
[10102] = {"Segredo revelado", {"Fale com A'dal na cidade de Shattrath."}},
[10103] = {"Apresente-se a Zurai", {"Apresente-se a Zurai no Mocó do Rato Lamacento, que fica no Pântano Zíngaro."}},
[10104] = {"Preocupações quanto a Tuurem", {"Fale com Andarl na Fortaleza Alleriana, na Mata Terokkar."}},
[10105] = {"Notícias para Rakoria", {"Fale com Rakoria na Vila de Quebrapedra, na Mata Terokkar."}},
[10106] = {"As fortificações da cidadela", {"Capture o Mirante, o Estádio e o Monte Partido, e então fale novamente com a Primeiro-sargento Theresa Pardim na Península do Fogo do Inferno."}},
[10107] = {"Medidas diplomáticas", {"Jorin Olho Morto, de Garadar, em Nagrand, quer que você encontre e converse com Lantresor da Lâmina nas Ruínas da Lâmina Ardente."}},
[10108] = {"Medidas diplomáticas", {"Mo'mor, o Destruidor, da vila de Telaar, em Nagrand, quer que você converse com Lantresor da Lâmina, nas Ruínas da Lâmina Ardente."}},
[10109] = {"Eu preciso deles!", {"Wazat, da Vila dos Clãs, no sul de Nagrand, quer que você lhe traga 3 unidades de gás de elemental do ar."}},
[10110] = {"As fortificações da cidadela", {"Capture o Mirante, o Estádio e o Monte Partido, e então fale novamente com a Arauto de Batalha Olhonegro em Thrallmar."}},
[10111] = {"Traga-me aquele ovo!", {"Wazat, de Nagrand, quer que você use o Pula-tron 4000 para alcançar o ninho da árvore mais próxima e roubar o Ovo Misterioso."}},
[10112] = {"Um favor pessoal", {"Recupere 5 Mercadorias Roubadas de Lathrai. Entregue-as para Lathrai, o Mercador do Vento. Você pode encontrá-lo perto da Taberna Fim do Mundo, localizada no Bairro Inferior, em Shattrath."}},
[10113] = {"O safári Rosarães", {"Jonas \"Escopeta\" quer que você fale com Rosarães Guima no Safári Rosarães, a nordeste de Nagrand."}},
[10114] = {"O safári Rosarães", {"Ohlorn Anda Ligeiro quer que você fale com Umbro Anda Ligeiro, o \"Fio\", no Safári Rosarães, em Nagrand."}},
[10115] = {"A Anomalia Lamadaga", {"Mate 3 Assassinos Lamadaga e 15 Moralodos Lamadaga."}},
[10116] = {"Procura-se: Chefe Mummaki", {"Traga o Totem do Chefe Mummaki para Ikuti em Porto Orebor."}},
[10117] = {"Procura-se: Chefe Mummaki", {"Traga o Totem do Chefe Mummaki para o Caçador Sombrio Denjai."}},
[10118] = {"Mensagem para os Lamadaga", {"Mate 3 Assassinos Lamadaga e 15 Moralodos Lamadaga. Em seguida, fale novamente com o caçador sombrio Denjai, em Zabra'jin."}},
[10119] = {"Através do Portal Negro", {"Atravesse o Portal Negro e encontre o Comandante Durão. Ele espera sua chegada do outro lado - em Terralém."}},
[10120] = {"Chegada em Terralém", {"O Tenente-general Órion, no Portal Negro da Península Fogo do Inferno, quer que você leve um relatório para Vlagga Freipena."}},
[10121] = {"Extermine a Legião Ardente", {"Fale com o Sargento Estilhaça-crânio perto da Caravana de Suprimentos do lado de fora de Thrallmar, ao leste."}},
[10122] = {"Ao alcance da Cidadela", {"Procure pelo Soldado Imarion no acampamento mais ao norte na Muralha do Sul."}},
[10123] = {"Ravina de Vil Fagulha", {"O Sargento Estilhaça-crânio, na Caravana de Suprimentos a leste de Thrallmar, na Península Fogo do Inferno, quer que você mate 4 Diabretes Ardilantes, 6 Armipotentes Infernais e 1 Clamamedo."}},
[10124] = {"Comando Avançado: Posto dos Destroços", {"Apresente-se ao Comandante To'acha no Posto dos Destroços."}},
[10125] = {"Mission: Disrupt Communications", {"Forward Commander To'arch at Reaver's Fall in Hellfire Peninsula has asked that you use To'arch's Explosives and destroy Legion Antenna: Spite, Legion Antenna: Rage, Legion Antenna: Hate, Legion Antenna: Fear."}},
[10126] = {"As ordens do Chefe-de-guerra Necrogg", {"Vá para o sul ao longo do lado oeste da Muralha do Sul, e procure sinais do Soldado Imarion no próximo acampamento."}},
[10127] = {"Mission: Sever the Tie", {"Forward Commander To'arch at Reaver's Fall in Hellfire Peninsula wants you to kill 4 Fel Handlers, 8 Wrathguards, and Subjugator Shi'aziv."}},
[10128] = {"O resgate do soldado Imarion", {"Continue indo para o sul ao longo da Muralha do Sul e procure o local em que o soldado Imarion está sendo mantido prisioneiro pelo Chefe-de-guerra Necrogg."}},
[10129] = {"Missão: Portais Murketh e Shaadraz", {"Fale com o Comandante de Ala Brack, bombardeie o Portal Murketh e o Portal Shaadraz, e então fale novamente com o Comandante To'acha no Posto dos Destroços."}},
[10130] = {"O flanco ocidental", {"Mate 10 Guardas da Mão Despedaçada, 10 Berserkers da Mão Despedaçada e 5 Acólitos da Mão Despedaçada, e então apresente-se à Tenente Amadi na torre de guarda oeste da Fortaleza da Honra."}},
[10131] = {"Planning the Escape", {"Kill a Shattered Hand Grenadier and bring his Crude Explosives to Private Imarion at Gor'gaz Outpost."}},
[10132] = {"Ameaça colossal", {"Mate 5 Colossos Enfurecidos e fale novamente com Tola'thion, no Posto Cenariano, que fica na Península Fogo do Inferno."}},
[10133] = {"Mission: Kill the Messenger", {"Forward Commander To'arch at Reaver's Fall in Hellfire Peninsula has asked that you find and slay Arix'malidash and recover the Legion Missive."}},
[10134] = {"A crise do cristal carmesim", {"Leve o Estilhaço de Cristal Carmesim para Tola'thion, no Posto Cenariano, que fica na Península Fogo do Inferno."}},
[10135] = {"Mission: Be the Messenger", {"Take the Legion Missive to Nazgrel at Thrallmar in Hellfire Peninsula."}},
[10136] = {"As intenções do Cruel", {"Mate Arrázius, o Cruel, e fale novamente com Nazgrel em Thrallmar."}},
[10137] = {"Provocando o Chefe-de-guerra", {"Use os Explosivos Rudimentares para criar balbúrdia e atrair o Chefe-de-guerra Necrogg para fora da torre. Mate-o, pegue a Chave de Necrogg e use-a para abrir a jaula do Soldado Imarion. Após libertar o Soldado Imarion, apresente-se à Tenente Amadi."}},
[10138] = {"Quem mandou?", {"Mate 20 Brutos da Mão Despedaçada, 10 Bruxos da Mão Despedaçada e roube as Ordens do Instrutor Militar Zurok. Então, fale novamente com a Tenente Amadi na torre de guarda ocidental da Fortaleza da Honra."}},
[10139] = {"Executando o Comandante", {"Mate o Comandante-chefe Gorax e apresente-se à Tenente Amadi na torre de guarda ocidental da Fortaleza da Honra."}},
[10140] = {"Viagem à Fortaleza da Honra", {"Pegue um voo até a Fortaleza da Honra e entregue o Relatório de Durão para o Marechal Isildo"}},
[10141] = {"A Legião renascida", {"Fale com a Sargento Alana perto da Caravana de Suprimentos do lado de fora da Fortaleza da Honra, na direção leste."}},
[10142] = {"O Caminho da Angústia", {"A Sargento Alana, na Caravana de Suprimentos a leste da Fortaleza da Honra, na Península Fogo do Inferno, quer que você mate 4 Diabretes Ardilantes 6 Armipotentes Infernais e 1 Clamamedo."}},
[10143] = {"Posto da Expedição", {"Apresente-se ao Comandante Kingston, do Comando Avançado, no Posto da Expedição."}},
[10144] = {"Interrompa os reforços", {"Derrote inimigos perto do Portal Grimh e Kaalez para coletar Pedras Rúnicas Demoníacas.","","Use o detonador de Kingston dentro do Portal Grimh e do Portal Kaalez quando tiver pedras suficientes.","","Leve os explosivos que sobrarem para o Comandante Avançado Kingston."}},
[10145] = {"Mission: Sever the Tie UNUSED", {"Forward Commander Kingston at Expedition Point in Hellfire Peninsula wants you to kill 4 Fel Handlers, 8 Wrathguards, and Subjugator Yalqiz."}},
[10146] = {"Missão: Os Portais Murketh e Shaadraz", {"Fale com o Comandante de Ala Dabir'ee, bombardeie o Portal Murketh e o Portal Shaadraz, e então fale novamente com o Comandante Avançado Kingston no Posto da Expedição."}},
[10147] = {"Mission: Kill the Messenger", {"Forward Commander Kingston at Expedition Point in Hellfire Peninsula has asked that you find and slay Arix'malidash and recover the Legion Missive."}},
[10148] = {"Mission: Be the Messenger", {"Take the Legion Missive to Force Commander Danath Trollbane at Honor Hold in Hellfire Peninsula."}},
[10149] = {"Mission: End All, Be All", {"Force Commander Danath Trollbane at Honor Hold in Hellfire Peninsula has asked that you slay Arazzius the Cruel."}},
[10150] = {"Ao alcance da Cidadela", {"Procure pela Batedora Makha no acampamento mais ao norte na Muralha do Norte."}},
[10151] = {"As ordens do Chefe-de-guerra Necrogg", {"Vá para o sul seguindo pelo lado oeste da Muralha do Norte, e procure sinais da Batedora Makha no próximo acampamento."}},
[10152] = {"O flanco ocidental", {"Mate 10 Guardas da Mão Despedaçada, 10 Berserkers da Mão Despedaçada e 5 Acólitos da Mão Despedaçada, e então apresente-se ao Guarda Pétreo Ambelão, no Acampamento Quebra-espinha perto de Zeth'Gor."}},
[10153] = {"O resgate da batedora Makha", {"Siga para o sul ao longo do Caminho da Glória até chegar à Muralha do Sul, e procure o local em que a Batedora Makha está sendo mantida prisioneira pelo Chefe-de-guerra Necrogg."}},
[10154] = {"Planning the Escape", {"Kill a Shattered Hand Grenadier and bring his Crude Explosives to Scout Makha at Gor'gaz Outpost."}},
[10155] = {"Provocando o Chefe-de-guerra", {"Use os Explosivos Rudimentares para criar balbúrdia e atrair o Chefe-de-guerra Necrogg para fora da torre. Mate-o, pegue a chave de Necrogg e use-a para abrir a jaula da Batedora Makha. Quando a Batedora Makha estiver livre, fale novamente com o Guarda Pétreo Ambelão, no Acampamento Quebra-espinha, perto de Zeth'Gor."}},
[10156] = {"Quem mandou?", {"Mate 20 Brutos da Mão Despedaçada, 10 Bruxos da Mão Despedaçada e roube as Ordens do Instrutor Militar Zurok. Fale novamente com o Guarda Pétreo Ambelão, no Acampamento Quebra-espinha."}},
[10157] = {"Executando o Comandante", {"Mate o Comandante-chefe Gorax e fale novamente com o Caçador Sombrio Ty'jin, em Thrallmar."}},
[10158] = {"Suprimentos dos Olhos Sangrentos", {"Destrua 6 Pilhas de Suprimentos dos Olhos Sangrentos e apresente-se ao Guarda Pétreo Ambelão no Acampamento Quebra-espinha."}},
[10159] = {"Mantenha o Monte Presacúleo limpo!", {"Mate 8 Assoladores Presacúleos e 8 Cospeçonhas Presacúleos, e então fale novamente com Mauram Casco Robusto, no Posto Cenariano, que fica na Península Fogo do Inferno."}},
[10160] = {"Conheça seu inimigo", {"Fale com a Tenente Amadi na velha torre da guarda, logo a oeste da Fortaleza da Honra."}},
[10161] = {"Em caso de emergência...", {"Colete 30 peças de Escombros de Zepelim e leve-as para Gritonho Ouvessorte, o \"Berrante\", no Zepelim Caído."}},
[10162] = {"Missão: A Prateleira Abissal", {"O Comandante To'acha do Posto dos Destroços, na Península Fogo do Inferno, ordenou que você destrua 20 Peões Gan'args, 5 Feitores Mo'args e 5 Canhões Vis."}},
[10163] = {"Missão: A Prateleira Abissal", {"O Comandante de Ala Grifongar do Posto Despedaçado, na Península Fogo do Inferno, ordenou que você destrua 20 Peões Gan'args, 5 Feitores Mo'args e 5 Canhões Vis."}},
[10164] = {"Tudo vai dar pé", {"O Grão-pai Aldrimus, na entrada das Catacumbas Auchenai de Auchindoun, pede que você entre nas Catacumbas Auchenai e destrua o Exarca Maladaar."}},
[10165] = {"Minando a concorrência", {"O Príncipe do Nexus Haramad, em frente às Tumbas de Mana, quer que você mate o Príncipe do Nexus Shaffar e lhe traga as Bandagens de Shaffar."}},
[10166] = {"Memórias do Cascabranca", {"Larianna Fluminéola quer que você ponha o Pingente do Velho Cascabranca ao lado da pedra rúnica na Mata Queimada."}},
[10167] = {"Auchindoun...", {"Vá até as Catacumbas Auchenai, no Deserto de Ossos da Mata Terokkar, e mate o Exarca Maladaar para libertar o Espírito de D'ore."}},
[10168] = {"O que a alma vê", {"Encontre um dos Espelhos da Alma nas Catacumbas Auchenai e use-o para evocar um Espírito Enegrecido de Antepassados Orcs Antigos em Nagrand. Destrua 15 Espíritos Enegrecidos para que os antepassados possam descansar em paz.","","Quando completar a tarefa, retorne à Mãe Kashur, nas Terras Ancestrais, em Nagrand."}},
[10169] = {"Perdendo com elegância", {"O Magíster Falris, em Shattrath, quer que você leve a Ampola de Soro Sedativo até o Terraço da Luz e derrame o conteúdo da ampola na Panela do Regimento de Haronem."}},
[10170] = {"Retorno à Grande Mãe", {"Vá novamente à Garadar e fale com a Grande Mãe Geyah."}},
[10171] = {"O chefe inconsolável", {"A Grande Mãe Geyah, de Garadar, quer que você fale com Garrosh."}},
[10172] = {"Não há esperanças", {"Fale novamente com a Grande Mãe Geyah e relate-lhe o que Garrosh contou para você."}},
[10173] = {"O cajado do arquimago", {"Recupere o Cajado do Arquimago Vargoth de Ekkorash, a Inquisidora e traga-o a Ravandwyr, na Área 52. Para tirar Ekkorash de seu esconderijo, espalhe o pó conjurador no braseiro no centro das ruínas."}},
[10174] = {"A maldição da Torre Violeta", {"Use o Cajado do Arquimago Vargoth para contatá-lo.","","Se perder o cajado, fale com Ravandwyr, na Área 52, para substituí-lo."}},
[10175] = {"Thrall, Son of Durotan", {"Venture to Orgrimmar and deliver Geyah's message to Thrall."}},
[10176] = {"Ar'kelos, o guardião", {"Entre na Torre Violeta, no centro da Vila Kirin'Var e derrote Ar'kelos, em seguida fale com Arquimago Vargoth."}},
[10177] = {"Problemas em Auchindoun", {"Apresente-se ao Comandante de Campo Mahfuun na entrada do Labirinto Soturno em Auchindoun, na Mata Terokkar."}},
[10178] = {"Encontrar o espião To'gun", {"Encontre o Espião To'gun no Labirinto Soturno de Auchindoun."}},
[10179] = {"O zelador de Kirin'Var", {"Fale com o Zelador Dieworth, na Vila Kirin'Var, em Eternévoa."}},
[10180] = {"Isfar não desiste", {"Fale com Isfar, que se encontra nos Salões dos Sethekk em Auchindoun."}},
[10182] = {"Mago de Batalha Dathric", {"Encontre o alojamento do Mago de Batalha Dathric no Estreito do Teurgo e retorne-a ao cavalete de armas. Após, fale com o Zelador Dieworth, na Vila Kirin'Var."}},
[10183] = {"Para a Área 52", {"Entregue as Anotações do Eterologista ao Mestre-fogueteiro Fuselagem, na Área 52, em Eternévoa."}},
[10184] = {"Reminiscências malévolas", {"O Zelador Dieworth, na Vila Kirin'Var, quer que você derrote 20 Espíritos Cindidos na Praça da Cidade de Kirin'Var."}},
[10185] = {"Uma sina pior do que a morte", {"Mate 10 Matamagos e 10 Caçadores de Mana e fale novamente com o Zelador Dieworth, na vila Kirin'Var."}},
[10186] = {"Você foi convocado!", {"Colete 10 Cristais da Matriz de Eterlítio e leve-os ao Mestre-fogueteiro Fuselagem, na Área 52, em Eternévoa."}},
[10187] = {"Uma mensagem para o arquimago", {"Fale com o Arquimago Vargoth, no topo da Torre Violeta, em Eternévoa."}},
[10188] = {"O signo de Krasus", {"Recupere o signo de Krasus da Mesmerizadora Mariana, em Manaforja Coruu, a noroeste da Torre Violeta."}},
[10189] = {"Manaforja B'naar", {"O Mestre Espião Thalodien, na Área 52, quer que você vá às adjacências de Manaforja B'naar e consiga a Lista de Funcionários de B'naar do Capitão Arathyn.","","Cumprir missões para os Áugures fará com que sua reputação entre os Aldor diminua."}},
[10190] = {"Recarregar as baterias", {"Use o disparador de recarga de bateria nos caçadores dimensionais até chegar ao nível 10 de recarga da bateria. Em seguida, leve-o à Especialista em Robôs Alley, nas Ruínas Enkaat, em Eternévoa."}},
[10191] = {"Mass v5.0 está vivo!", {"Escolte Mass I. Mo v5.0 pelas Ruínas de Enkaat. Em seguida, fale com a Especialista em Robôs Alley do lado de fora das Ruínas de Enkaat, em Eternévoa."}},
[10192] = {"O compêndio de Krasus", {"Reclame os capítulos 1, 2 e 3 do Compêndio de Krasus na Praça da Cidade da Vila Kirin'Var."}},
[10193] = {"Cabeças valiosas", {"O Mestre Espião Thalodien, na Área 52, quer que você vá a Manaforja B'naar e mate 2 Mestres Dimensionais Solfúria, 6 Engenheiros Dimensionais Solfúria e 8 Geólogos Solfúria.","","Cumprir missões para os Áugures fará com que sua reputação entre os Aldor diminua."}},
[10194] = {"Voo furtivo", {"Fale com Veronia do lado de fora da Área 52 e voe para Manaforja Coruu.","","Cumprir missões para os Áugures fará com que sua reputação entre os Aldor diminua."}},
[10195] = {"Mercenário vê, mercenário faz", {"Traga 30 Penas de Arakkoa para Ros'eleth na Fortaleza Alleriana."}},
[10196] = {"Mais penas de arakkoa", {"Traga 30 Penas de Arakkoa para Malkaz, na Vila de Quebrapedra."}},
[10197] = {"Disfarce convincente", {"Obtenha o seguinte conjunto: Vestes de Arcanista Solfúria, um par de Luvas do Pesquisador Solfúria e um Medalhão do Guarda Solfúria e leve-os a Caledis Albafulgis, em Manaforja Coruu, em Eternévoa.","","Cumprir missões para os Áugures fará com que sua reputação entre os Aldor diminua."}},
[10198] = {"Conseguindo informações", {"Vista o Disfarce Solfúria, entre em Manaforja Coruu e escute a conversa entre o Comandante Albaforja e o Arcanista Ardonis.","","Ao cumprir missões para os Áugures, sua reputação entre os Aldor será reduzida."}},
[10199] = {"Um gás extra", {"Obtenha cinco Aguilhões de Arraia Etérea para o Botas, na Área 52, em Eternévoa."}},
[10200] = {"Apresente-se novamente a Thalodien", {"Fale com o Mestre Espião Thalodien, na Área 52, em Eternévoa."}},
[10201] = {"E agora, a hora da verdade", {"Ache Grek e peça para testar o lubrificante para armas. Após ele testar o óleo, informe os resultados a Rakoria."}},
[10202] = {"O desertor", {"Vá à Manaforja Ara e fale com o Magíster Theledorn.","","Cumprir missões para os Áugures fará com que sua reputação entre os Aldor diminua."}},
[10203] = {"Bugiganga fulminadora inestimável", {"Use o Fulminador Ultradesconsolidador para transportar o Cava-cava Hiper-rotacional, a Garra de Sucção Servopneumática, o Analisador de Terreno Multispectro, e o vagão cheio de explosivos de volta à Área 52. Em seguida, apresente-se ao Sapador Líder Isscabum na extremidade leste da fissura que corta a Área 52, em Eternévoa."}},
[10204] = {"Cristais gema de sangue", {"A Magistra Larynna quer que você vá a Manaforja B'naar e obtenha um Estilhaço de Gema de Sangue de um Magíster Solfúria. Use o estilhaço perto de Cristais Gema de Sangue maiores e, em seguida, leve-o para Larynna, na Área 52.","","Cumprir missões para os Áugures fará com que sua reputação entre os Aldor diminua."}},
[10205] = {"Saqueador Dimensional Nesaad", {"Mate o Saqueador Dimensional Nesaad e fale novamente com o Espreitador Etéreo Khay'ji, na Área 52, em Eternévoa."}},
[10206] = {"Pega o que é teu", {"Colete 10 Peças de Tecnologia Etérea e leve-as a Papai Macário, na Área 52, em Eternévoa."}},
[10207] = {"Forward Base: Reaver's Fall REUSE", {"Report to Forward Commander To'arch at Reaver's Fall."}},
[10208] = {"Interrompa os reforços", {"Derrote os inimigos perto dos Portais Xilus e Kruul para coletar 4 Pedras Rúnicas Demoníacas para cada portal.","","Use o Detonador de To'acha nos Portais Xilus e Kruul.","","Leve os detonadores que sobrarem de volta para To'acha no Posto dos Destroços na Península Fogo do Inferno."}},
[10209] = {"O prêmio do evocador Kanthin", {"Derrote o Evocador Kanthin na Manaforja Dúria, na ilha ao norte do Acampamento Solfúria, para recuperar a Pedra de Glacius."}},
[10210] = {"A'dal", {"Fale com A'dal, líder dos Sha'tar, no centro do Terraço da Luz."}},
[10211] = {"Cidade da Luz", {"Siga o servo de Hadggar e escute sua história. Fale novamente com Hadggar após completar essa tarefa."}},
[10212] = {"Hero of the Mag'har", {"Return to Greatmother Geyah at Garadar in Nagrand."}},
[10213] = {"Investigue a queda", {"Busque sobreviventes da queda."}},
[10214] = {"Balança mas não cai", {"Mate 15 Sapadores Gan'args e fale novamente com o Capataz Razelcraz."}},
[10216] = {"Segurança acima de tudo", {"O Artífice Morfílius quer que você mate 10 Saqueadores Etéreos da Cripta, 5 Feiticeiros Etéreos, 5 Espreitadores do Nexus e 5 Mesmerizadores Etéreos.","","Feito isso, encontre o Transportador Etéreo na câmara em frente aos aposentos de Shaffar e ative-o."}},
[10218] = {"O trabalho duro de outrem compensa", {"Acompanhe o Crio-engenheiro Sha'heen em segurança através das Tumbas de Mana para que ele colete o éter preso nos coletores de éter de Shaffar.","","Apresente-se ao Príncipe do Nexus Haramad diante das Tumbas de Mana, no acampamento do Consórcio, se tiver sucesso."}},
[10219] = {"Passeando com o cachorro", nil},
[10220] = {"Faça com que nos escutem", {"Mordiba quer que você derrote 12 Soldados Rasos Obstinados, 8 Cavaleiros Obstinados e 6 Feiticeiros Obstinados no Arsenal da Expedição, a oeste do Acampamento Quebra-espinha."}},
[10221] = {"Dr. Cabum!", {"Mate o Dr. Cabum e leve a boa notícia para o Líder Sapador Isscabum, na Área 52, em Eternévoa."}},
[10222] = {"A guarnição Solfúria", {"O Zelador Dieworth, na vila Kirin'Var, quer que você mate 10 Arqueiros Solfúria."}},
[10223] = {"Abaixo o Daellis", {"O Zelador Dieworth, na Vila Kirin'Var, quer que você mate o Mestre Daellis Albatacas."}},
[10224] = {"Essência para os motores", {"Colete 7 Essências de Aparição de Mana e leve-as ao Engenheiro-chefe Trep, na Área 52, em Eternévoa."}},
[10225] = {"Apresente-se à engenharia", {"Entregue os cristais da matriz de eterlítio ao Engenheiro-chefe Trep, na Área 52, em Eternévoa."}},
[10226] = {"Extração de poder elemental", {"Extraia 5 Poderes Elementais para o Engenheiro-chefe Trep, na Área 52, em Eternévoa."}},
[10227] = {"Eu vejo draeneis mortos", {"Fale com Ramdor, o Louco, em cima da pilha de ossos na entrada das Catacumbas Auchenai."}},
[10228] = {"Ezequiel", {"Encontre e fale com Ezequiel na cidade de Shattrath."}},
[10229] = {"Decifre o tomo", {"Leve o Tomo Misterioso para Althen, o Historiador, no Acampamento Quebra-espinha."}},
[10230] = {"A trompa de batalha", {"Pegue a trompa de batalha dos Obstinados com o Tenente-comandante Talvos e entregue-a para Althen, o Historiador, no Acampamento Quebra-espinha."}},
[10231] = {"Que livro? Não vejo livro nenhum", {"Ezequiel disse que o livro foi roubado. O melhor é encontrar os rufiões e ver se eles têm informações."}},
[10232] = {"Encrenca com a Legião", {"Mate 5 Forjadores da Ruína Mo'args e 15 Engenheiros Gan'args. Em seguida, fale com Papai Macário, na Área 52, em Eternévoa."}},
[10233] = {"Atear fogo no Acampamento Solfúria", {"Destrua 4 Balistas Solfúria e 4 barracas Solfúria usando uma Tocha Flamejante. Volte a falar com o Tenente-Feiticeiro Morran, na Vila Kirin'Var."}},
[10234] = {"Lixo infernal", {"Colete 10 Peças de Aníquilus e fale novamente com a Mamãe Macário, no Posto Terramédia, dentro do Eco-domo Terramédia, em Eternévoa."}},
[10235] = {"Sinistragarra desgarrado", {"Obtenha a Mão de Sinistragarra e leve-a a Mamãe Macário, no Posto Terramédia, dentro do Eco-domo Terramédia, em Eternévoa."}},
[10236] = {"Terralém fede!", {"Encontre 6 Peças de Retalhador e fale novamente com o Capataz Razelcraz, na mina a noroeste de Thrallmar."}},
[10237] = {"Avise a Área 52!", {"Entregue as más notícias ao Mestre-fogueteiro Fuselagem, na Área 52, em Eternévoa."}},
[10238] = {"Como servir goblins", {"Resgate Manni, Mô e Jakk do acampamento orc vil e fale novamente com o Capataz Razelcraz, na mina a noroeste de Thrallmar."}},
[10239] = {"Fonte potencial de energia", {"O Tenente-Feiticeiro Morran, na Vila Kirin'Var quer que você capture 10 Cubos de Isolamento de Energia nas operações dos elfos sangrentos, em Manaforja Coruu."}},
[10240] = {"Estabelecer o perímetro", {"Use o Dispositivo de Ativação Rúnica próximo da Runa Leste, da Runa Nordeste e da Runa Oeste. As runas encontram-se no perímetro dos distritos do Estreito do Teurgo e da praça da cidade da Vila Kirin'Var. Fale novamente com o Tenente-Feiticeiro Morran assim que completar a missão."}},
[10241] = {"Distrações em Manaforja B'naar", {"Mate 8 Magísteres Solfúria e 8 Guardas de Sangue Solfúria. Em seguida, fale novamente com o Exarca Orelis, na Área 52.","","Cumprir missões para os Aldor fará com que a sua reputação entre os Áugures diminua."}},
[10242] = {"Acampamento Quebra-espinha", {"A Boticária Zenóbia, no Posto dos Destroços, quer que você peça ao Comandante de Ala Brack uma mantícora para voar até o Acampamento Quebra-espinha e, uma vez lá, que você entregue a Amostra de Sangue de Olhos Sangrentos ao Boticário Alberto no Acampamento Quebra-espinha."}},
[10243] = {"Tecnologia naaru", {"Entre em Manaforja B'naar e encontre o Painel de Controle de B'naar.","","Cumprir missões para os Aldor fará com que sua reputação entre os Áugures diminua."}},
[10244] = {"R.T.F.R.C.M.", {"Take the Fel Reaver Construction Manual to Mama Wheeler at the Midrealm Post inside Eco-Dome Midrealm in the Netherstorm."}},
[10245] = {"Transcrição do painel de B'naar", {"Leve a Transcrição do painel de B'naar à Anacoreta Karja, na Área 52, em Eternévoa.","","Cumprir missões para os Aldor fará com que a sua reputação entre os Áugures diminua."}},
[10246] = {"Manaforja Coruu sob ataque", {"Viaje até Manaforja Coruu e mate 8 Arcanistas Solfúria e 5 Pesquisadores Solfúria. Fale novamente com o Exarca Orelis quando houver cumprido a tarefa.","","Cumprir missões para os Aldor fará com que a sua reputação entre os Áugures diminua."}},
[10247] = {"Doutor Vômisa, Ph.T.", {"Fale com o Doutor Vômisa, Ph.T., no Campo de Testes, em Eternévoa."}},
[10248] = {"Você, robô", {"Ative o Controle do Aniquilata X6000 e teste suas capacidades. Em seguida, faça um relatório para o Doutor Vômisa, Ph.T, no Campo de Testes, em Eternévoa. Cuide de todos os problemas que surgirem."}},
[10249] = {"De volta ao Chefe!", {"Apresente-se ao Mestre-fogueteiro Fuselagem, na Área 52, em Eternévoa, imediatamente!"}},
[10250] = {"Vingança sangrenta", {"Toque a Trompa de Batalha dos Obstinados perto do Estandarte da Aliança. Mate Urtrak e então fale novamente com Althen, o Historiador, no Acampamento Quebra-espinha."}},
[10251] = {"O plano magistral do amo?", {"Encontre Nitrin, o Erudito na Armaria Abandonada em Nagrand."}},
[10252] = {"Visão dos mortos", {"Nitrin, no Arsenal Abandonado em Nagrand, quer que você leve até ele 1 Olho de Gronn da Montanha, 1 Bico Perfeito de Roca-dos-ventos Maior e 1 Naco de Fenoceronte Idoso."}},
[10253] = {"Levixus o evocador de almas", {"Extermine Levixus e tire o Livro dos Mortos dele.","","Entregue o Livro dos Mortos a Ramdor, o Louco, na entrada das Catacumbas Auchenai, em Auchindoun.","","Você prometeu, lembra?"}},
[10254] = {"Comandante-chefe Danath", {"O Marechal Isildor quer que você se apresente ao Comandante-chefe Danath Matatroll na Fortaleza da Honra."}},
[10255] = {"Testando o antídoto", {"Use o Antídoto Cenariano em um Javiltusco Parrudo e observe os resultados. Assim que você obtiver os resultados do experimento, fale novamente com Tiá Juba Rubra, no Posto Cenariano, que fica na Península Fogo do Inferno."}},
[10256] = {"A senhora da chave encontrada", {"Localize Ápice e obtenha o Cristal Focal do Ápice. Retorne à Torre Violeta e use o Cristal Focal do Ápice próximo do orbe do Arquimago Vargoth para descobrir quem possui a chave para quebrar a maldição da torre."}},
[10257] = {"A captura da pedra-chave", {"Aventure-se na estufa da Bastilha da Tormenta e consiga a chave de pedra da Comandante Sarannis. Traga-a ao Arquimago Vargoth, na Torre Violeta."}},
[10258] = {"Honre os caídos", {"Apresente-se ao Comandante Hogarth no Arsenal da Expedição."}},
[10259] = {"Into the Breach", {"Mate 6 Guardas Vis Invasores e fale novamente com o Emissário Argênteo Bembrioso."}},
[10260] = {"Netherologist Coppernickels", {"Speak with Netherologist Coppernickels at the bridge leading into the Netherstorm from Blade's Edge."}},
[10261] = {"Procura-se: aniquilador servomecânico!", {"Obtenha um Aniquilador Servomecânico e leve-o a Papai Macário, na Área 52, em Eternévoa."}},
[10262] = {"Destroços etéreos", {"Colete 10 Insígnias Zaxxis e leve-as ao Espreitador Etéreo Khay'ji, na Área 52, em Eternévoa."}},
[10263] = {"Auxílio ao Consórcio", {"Fale com o Espreitador Etéreo Khay'ji, na Área 52, em Eternévoa."}},
[10264] = {"Auxílio ao Consórcio", {"Fale com o Espreitador Etéreo Khay'ji, na Área 52, em Eternévoa."}},
[10265] = {"Coleta de cristais para o Consórcio", {"Obtenha o artefato de cristal de Arklon e leve-o ao Espreitador Etéreo Khay'ji, na Área 52, em Eternévoa."}},
[10266] = {"Pedido de assistência", {"Procure-o e ofereça-lhe os seus serviços. Ele se encontra no Posto Terramédia, dentro do Eco-domo Terramédia, em Eternévoa."}},
[10267] = {"Retomada justa", {"Colete 10 Caixas de Equipamento de Sondagem e leve-os a Gahruj, no Posto Terramédia, dentro do Eco-domo Terramédia, em Eternévoa."}},
[10268] = {"Audiência com o príncipe", {"Entregue o Equipamento de Sondagem à imagem do Príncipe do Nexus Haramad, no Pináculo da Tempestade, em Eternévoa."}},
[10269] = {"Ponto de triangulação um", {"Use o Dispositivo de Triangulação para indicar a direção do primeiro ponto de triangulação. Assim que o encontrar, relate sua localização ao Mercador Hazzin, na Guarita do Protetorado, na ilha de Manaforja Ultris, em Eternévoa."}},
[10270] = {"Uma não-tão-modesta proposição", {"Consiga a bateria carregada de teleportador e traga-a à plataforma de teleporte etéreo no Pico Celestial, a leste de Manaforja Ultris. Ative a plataforma e dê o Recipiente de Energia do Teleporte à imagem do Mercador do Vento Marid para que ele o instale."}},
[10271] = {"Vamos aos negócios", {"Carregue o Coletador de Essências usando-o em Dracos Etéreos ou Dragões Etéreos no Pico Celestial. Em seguida, retorne à área da Plataforma de Teleporte Etéreo."}},
[10272] = {"Um início promissor", {"Colete 15 Ovos de Draconiano do Éter e, em seguida, fale com Tyri e Jorad no Pico Celestial."}},
[10273] = {"Distrações nocivas", {"Fale com o Mercador do Vento Marid e atraia-o para longe de seu entreposto. Acabe com ele e fale novamente com Tyri e Jorad, no Pico Celestial."}},
[10274] = {"Defendendo o Pico Celestial", {"Use o Desafio da Revoada Azul para provocar Verakan, na extremidade sul do Pico Celestial. Derrote-o e fale novamente com Tyri e Jorad, no Pico Celestial."}},
[10275] = {"Ponto de triangulação dois", {"Use o Dispositivo de Triangulação para indicar a direção do segundo ponto de triangulação. Assim que o encontrar, relate sua localização ao Mercador do Vento Tuluman, no Acampamento do Tuluman, logo depois da ponte que leva à ilha de Manaforja Ara, em Eternévoa."}},
[10276] = {"Triângulo completo", {"Recupere o Cristal Ata'mal e leve-o à Imagem do Príncipe do Nexus Haramad, no Pináculo da Tempestade, em Eternévoa."}},
[10277] = {"Cavernas do Tempo", {"Andormu, nas Cavernas do Tempo, lhe conferiu a tarefa de seguir a Guia do Tempo pela caverna."}},
[10278] = {"As fendas dimensionais", {"Use o Gerador de Fendas Dimensionais Instáveis embaixo dos Campos Dimensionais. Colete 3 amostras de Éter Dimensional de Emissários do Caos Instáveis e leve-as para Ogath, o Louco, no Acampamento Quebra-espinha."}},
[10279] = {"Para o covil do mestre", {"Fale com Andormu nas Cavernas do Tempo."}},
[10280] = {"Entrega especial para Shattrath", {"Entregue o Cristal de Ata'mal a A'dal, no Terraço da Luz, em Shattrath."}},
[10281] = {"Apresentações formais", {"Encontre Tyri na extremidade norte do Pico Celestial e fale com ela."}},
[10282] = {"Antiga Eira dos Montes", {"Andormu, nas Cavernas do Tempo, lhe conferiu a tarefa de ir até a Antiga Eira dos Montes e falar com Erozion."}},
[10283] = {"Distração de Taretha", {"Vá ao Forte do Desterro e prepare 5 cargas incendiárias nos barris encontrados dentro dos campos de prisioneiros, usando a Carga de Bombas Incendiárias que Erozion lhe deu.","","Fale com Thrall na prisão do porão do Forte do Desterro quando tiver incendiado os alojamentos."}},
[10284] = {"A fuga do Forte do Desterro", {"Quando estiver <pronto/pronta> para continuar, avise Thrall. Siga-o e ajude-o a libertar Taretha e a cumprir o destino dele.","","Fale com Erozion na Antiga Eira dos Montes caso tenha sucesso nesta missão."}},
[10285] = {"Reencontro com Andormu", {"Fale novamente com Andormu nas Cavernas do Tempo, que fica no deserto de Tanaris."}},
[10286] = {"O segredo de Arelion", {"A Magistra Carinda quer que você interrogue o Magíster Aledis, visto pela última vez indo da Vigília do Falcão para o Pântano Zíngaro."}},
[10287] = {"A amante revelada", {"Fale novamente com a Magistra Carinda, na Vigília do Falcão, que fica na Península Fogo do Inferno."}},
[10288] = {"Chegada em Terralém", {"O Comandante Durão, no Portal Negro da Península Fogo do Inferno, quer que você leve o Relatório de Durão para Emílio Martelo Feroz."}},
[10289] = {"Viagem a Thrallmar", {"Vá a Thrallmar usando a Mantícora de Vlagga Freipena. Leve o Relatório de Órion para o General Krakork."}},
[10290] = {"Em busca de faralita", {"Colete 4 peças de Faralita Bruta e fale novamente com Zuben Elgenubi, no Pináculo da Tempestade."}},
[10291] = {"Apresente-se a Nazgrel", {"O General Krakork, em Thrallmar, quer que você fale com Nazgrel."}},
[10292] = {"More Power!", {"Zuben Elgenubi wants you to capture 8 Legion Cannon and bring them back to the Stormspire."}},
[10293] = {"Eis o manancial", {"Zuben Elgenubi, no Pináculo da Tempestade, quer que você obtenha um Núcleo de Faralita."}},
[10294] = {"Escarpa do caos", {"Vá até a Escarpa do Caos e mate as criaturas que encontrar lá. Colete 40 Estilhaços de Alma na Escarpa do Caos e leve-os para Ogath, o Louco, no Acampamento Quebra-espinha."}},
[10295] = {"Do abismo", {"Mate o Barão do Caos Galaxis e colete o Estilhaço de Alma. Leve o estilhaço para Ogath, o Louco, no Acampamento Quebra-espinha."}},
[10296] = {"Lamaçal Negro", {"Viaje pelas Cavernas do Tempo até o Lamaçal Negro durante a abertura do Portal Negro e fale com Sa'at."}},
[10297] = {"A abertura do Portal Negro", {"Sa'at, dentro do Lamaçal Negro das Cavernas do Tempo, lhe conferiu a tarefa de defender Medivh enquanto ele abre o Portal Negro.","","Fale novamente com Sa'at caso tenha sucesso nesta missão."}},
[10298] = {"Herói da prole", {"Fale novamente com Andormu nas Cavernas do Tempo, que fica no deserto de Tanaris."}},
[10299] = {"Desativar Manaforja B'naar", {"Volte a Manaforja B'naar e obtenha o Cristal de Acesso de B'naar do Feitor Theredis. Use-o no Painel de Controle de B'naar para desligá-la e, em seguida, apresente-se novamente a Anacoreta Karja.","","Cumprir missões para os Aldor fará com que sua reputação entre os Áugures diminua."}},
[10300] = {"A restauração do cajado", {"Obtenha quatro Estilhaços de Cristal Impecáveis dos fantasmas das Ruínas Enkaat e fale novamente com Ravandwyr, na Área 52."}},
[10301] = {"O compêndio destrancado", {"Obtenha o Monóculo Heliotrópico da Enfeitiçadora Marathelle, no Acampamento Solfúria."}},
[10302] = {"Mutações voláteis", {"Mate 8 Mutações Voláteis e fale com a Botânica Taerix no Ponto de Impacto, que fica no Vale Ammen."}},
[10303] = {"Os elfos sangrentos", {"Mate 10 Batedores Elfos Sangrentos. Em seguida, fale com Tolaan na Serra das Sombras, que fica no Vale Ammen."}},
[10304] = {"O vindicante Aldar", {"Fale com o Vindicante Aldar no Ponto de Impacto, que fica no Vale Ammen."}},
[10305] = {"Abjurante Belmara", {"Encontre o alojamento da Abjurante Belmara no Estreito do Teurgo e retorne o livro à sua estante. Após, fale com o Zelador Dieworth, na Vila Kirin'Var."}},
[10306] = {"Conjurador Luminrath", {"Encontre o alojamento do Conjurador Luminrath no Estreito do Teurgo e retorne o manto à sua cômoda. Após, fale com o Zelador Dieworth, na Vila Kirin'Var."}},
[10307] = {"Cohlien Tecegelo", {"Encontre o alojamento de Cohlien Tecegelo no Estreito do Teurgo e devolva o chapéu ao Armarinho. Depois, fale com o Zelador Dieworth, na Vila Kirin'Var."}},
[10308] = {"Mais destroços etéreos", {"O Consórcio está muitíssimo interessado em exterminar todos os rebeldes Zaxxis nos Destroços, ao sul.","","<class>, se quiser cair mais ainda em nossas graças, desça lá e traga mais insígnias de afiliação."}},
[10309] = {"É um aníquilus, mas tem coração", {"Consiga o coração do Aníquilus e leve-o ao Tchunarubleize, nos Destroços, em Eternévoa."}},
[10310] = {"Sabotar o portal!", {"Escolte Drijya até o portal do Ponto de Invasão: Destruidor e mantenha-o a salvo, enquanto ele tenta sabotar o portal. Em seguida, fale com Gahruj, no Posto Terramédia, dentro do Eco-domo Terramédia, em Eternévoa."}},
[10311] = {"Drijya precisa de você", {"Fale com Drijya próximo do Ponto de Invasão: Destruidor, em Eternévoa."}},
[10312] = {"Os anais de Kirin'Var", {"Recupere os Anais de Kirin'Var, na prefeitura da Vila Kirin'Var, e retorne-os ao Zelador Dieworth."}},
[10313] = {"Medição de energias dimensionais", {"O Exarca Orelis, na Área 52, quer que você leve o Orbe Dimensional Harmonizado a Manaforja B'naar e que o use na extremidade dos quatro tubos.","","Cumprir missões para os Aldor fará com que sua reputação entre os Áugures diminua."}},
[10314] = {"Suspeita persistente", {"Junte 10 Restos Repugnantes dos espíritos corrompidos no pátio da capela da Vila Kirin'Var e leve-os ao Zelador Dieworth."}},
[10315] = {"Neutralize os etermantes", {"Mate 15 Etermantes Solfúria e fale novamente com o Espreitador do Nexus Oazul, no Acampamento do Tuluman, em Eternévoa."}},
[10316] = {"Em busca de evidências", {"Busque pistas no Pátio da Capela da Vila Kirin'Var para saber quem está por trás da manipulação dos espíritos."}},
[10317] = {"Dando um jeito no encarregado", {"Lide com o Encarregado Ocaso, no interior da Mina Trelleum, sob Manaforja Ara, em Eternévoa."}},
[10318] = {"Um mestre supremo a se lidar", {"Mate o Mestre Supremo Grindgarr. Em seguida, fale novamente com o Mercador do Vento Tuluman, no Acampamento do Tuluman, em Eternévoa."}},
[10319] = {"A captura do filactério", {"Consiga o Filactério de Naberius, no distrito do Pátio da Capela da Vila Kirin'Var, e traga-o ao Zelador Dieworth."}},
[10320] = {"Destrua Naberius!", {"O Zelador Dieworth, na Vila Kirin'Var, quer que você confronte e derrote Naberius, no Pátio da Capela da Vila."}},
[10321] = {"Desativar Manaforja Coruu", {"Viaje até Manaforja Coruu, a leste da Área 52, e obtenha o Cristal de Acesso de Coruu da Feitora Seylanna. Use-o no Painel de Controle de Coruu para desativá-la e fale novamente com a Anacoreta Karja.","","Cumprir missões para os Aldor fará com que sua reputação entre os Áugures diminua."}},
[10322] = {"Desativar Manaforja Dúria", {"A Anacoreta Karja quer que você vá a Manaforja Dúria e que obtenha o Cristal de Acesso de Dúria do Feitor Athanel. Use-o no Painel de Controle de Dúria para desativá-la.","","Cumprir missões para os Aldor fará com que sua reputação entre os Áugures diminua."}},
[10323] = {"Desativar Manaforja Ara", {"Viaje até Manaforja Ara e obtenha o Cristal de Acesso de Ara do Feitor Azarad. Use-o no Painel de Controle de Ara para desativá-la.","","Cumprir missões para os Aldor fará com que sua reputação entre os Áugures diminua."}},
[10324] = {"A grande caçada de pastolunas", {"Leve 6 Pelegos de Machos Pastoluna a Acteon no Entreposto Lazúli."}},
[10325] = {"Marca de Kil'Jaeden", {"Leve 10 Marcas de Kil'Jaeden para Adyen, o Guardião da Luz, em Shattrath.","","Completar missões para os Aldor reduzirá sua reputação com os Áugures."}},
[10326] = {"Mais marcas de Kil'jaeden", {"Trazes tu mais insígnias de nossos inimigos, <class>? A Legião Ardente ainda representa ameaça, e continuamos a persegui-los onde quer que os encontremos."}},
[10327] = {"Marca única de Kil'Jaeden", {"Aceitarei quaisquer marcas que ainda trouxeres contigo. Não te preocupes, tuas ações serão devidamente registradas."}},
[10328] = {"Instruções Solfúria", {"Vá a Manaforja Dúria e consiga as Instruções Militares dos Solfúria e as Instruções Arcanas dos Solfúria das unidades Solfúria posicionadas na manaforja. Fale novamente com o Exarca Orelis quando houver completado a tarefa.","","Cumprir missões para os Aldor fará com que sua reputação entre os Áugures diminua."}},
[10329] = {"Desativar Manaforja B'naar", {"Volte à Manaforja B'naar e obtenha o Cristal de Acesso de B'naar do Feitor Theredis. Use-o no Painel de Controle de B'naar para desativá-la e, em seguida, apresente-se ao Mestre Espião Thalodien.","","Cumprir missões para os Áugures fará com que sua reputação entre os Aldor diminua."}},
[10330] = {"Desativar Manaforja Coruu", {"Obtenha o Cristal de Acesso de Coruu da Feitora Seylanna. Use-o no Painel de Controle de Coruu para desativá-la e, em seguida, apresente-se a Caledis Albafulgis.","","Cumprir missões para os Áugures fará com que sua reputação entre os Aldor diminua."}},
[10331] = {"Ferramentas indispensáveis", {"Recupere o Martelo de Ferreiro em meio aos aprendizes de Kirin'Var e traga-o de volta ao Aprendiz Andrethan, na Vila Kirin'Var."}},
[10332] = {"Mestre Ferreiro Rhonsus", {"O Aprendiz Andrethan, na Vila Kirin'Var, quer que você mate o fantasma de Rhonsus, o mestre ferreiro da vila."}},
[10333] = {"Ajude Mamãe Macário", {"Fale com Mamãe Macário, no Posto Terramédia, dentro do Eco-domo Terramédia, em Eternévoa."}},
[10334] = {"Mais sinetas", {"Encontre Mocha nos campos próximos do Morro da Capela na Vila Kirin'Var, que fica em Eternévoa."}},
[10335] = {"Inspecionar as ruínas", {"Posicione os três Marcadores de Sondagem e fale novamente com Zefírio, no Pináculo da Tempestade, em Eternévoa."}},
[10336] = {"Os lacaios de Culuthas", {"Mate 10 Cães de Culuthas e 5 Olhos de Culuthas."}},
[10337] = {"Quando as vacas voltam para o lar", {"Escolte a Mocha sã e salva de volta a seu proprietário, Thadell."}},
[10338] = {"Desativar a Manaforja Dúria", {"Volte à Manaforja Dúria e obtenha o Cristal de Acesso de Dúria do Feitor Athanel. Use-o no Painel de Controle de Dúria para desativá-la e, em seguida, apresente-se ao Mestre Espião Thalodien.","","Cumprir missões para os Áugures fará com que sua reputação entre os Aldor diminua."}},
[10339] = {"O Etéreum", {"O Comandante Ameer quer que você mate 5 Assassinos Etéreum, 5 Tropas de Choque Etéreum, 2 Pesquisadores Etéreum e o Capitão Zovax e, após, quer que ative o rádio-sinalizador Zeta do Etéreum."}},
[10340] = {"Posto Despedaçado", {"O Comandante Avançado Kingston quer que você fale com o Comandante de Ala Dabir'ee, cavalgue um grifo até o Posto Despedaçado e então se apresente a Runatogo Martelo Feroz."}},
[10341] = {"Acabe com eles enquanto estão no chão", {"Mate 8 Conjuradores Solfúria, 6 Arqueiros Solfúria e 4 Centuriões Solfúria. Fale novamente com a Magistra Larynna, na Área 52, após concluir a tarefa.","","Cumprir missões para os Áugures fará com que sua reputação entre os Aldor diminua."}},
[10342] = {"Consiga o xisto dos xistocouro", {"Colete cinco Xistos de Xistocouro e leve-os ao Botas, na Área 52, em Eternévoa."}},
[10343] = {"Invasão infindável", {"Encontre, na área infestada de criaturas de mana da Vila Kirin'Var, um Fragmento de Bomba de Mana e traga-o ao Tenente-Feiticeiro Morran."}},
[10344] = {"Comandante de Ala Grifongar", {"Fale com o Comandante de Ala Grifongar no Posto Despedaçado."}},
[10345] = {"A carne é forte...", {"Use o incendiador do Protetorado em 12 cadáveres dessecados. Retorne o incendiador do Protetorado ao Agente Araxes, do lado de fora do poço de acesso Zeon, quando a tarefa for concluída."}},
[10346] = {"Retorno à Prateleira Abissal", {"A Grifoneira Urravento do Posto Despedaçado, na Península Fogo do Inferno, ordenou que você destrua 20 Peões Gan'args, 5 Feitores Mo'args e 5 Canhões Vis."}},
[10347] = {"Retorno à Prateleira Abissal", {"O Comandante de Ala Brack, no Posto dos Destroços, na Península Fogo do Inferno, ordenou que você destrua 20 Peões Gan'args, 5 Feitores Mo'args e 5 Canhões Vis."}},
[10348] = {"Novas oportunidades", {"Colete 15 Sinos de Marfim e fale novamente com Muhrdad, no Posto Terramédia."}},
[10349] = {"A Ataterra", {"Leve o Estilhaço de Cristal Carmesim para a Ataterra Galandria Umbrísia no Posto Cenariano, que fica na Península Fogo do Inferno."}},
[10350] = {"Behomat", {"Fale com Behomat no Setor Comercial, na Exodar."}},
[10351] = {"Remédios naturais", {"Use a Semente da Revitalização no Círculo da Ataterra para curar a terra em redor do cristal. Então, fale novamente com a Ataterra Galandria Umbrísia, no Posto Cenariano, que fica na Península Fogo do Inferno, e lhe informe o que descobrir."}},
