[10559] = {"Reverenciado em Thrallmar", {"Nazgrel, em Thrallmar, na Península Fogo do Inferno, quer que você se torne <reverenciado/reverenciada> em Thrallmar, combatendo os orcs vis dentro dos Salões Despedaçados da Cidadela Fogo do Inferno."}},
[10560] = {"Reverenciado pelos Sha'tar", {"A'dal, em Shattrath, quer que você se torne <reverenciado/reverenciada> com os Sha'tar, derrotando as forças de Kael'thas que estão na Bastilha da Tormenta."}},
[10561] = {"Reverenciado pelos Defensores do Tempo", {"Andormu, dentro das Cavernas do Tempo em Tanaris, quer que você se torne <Reverenciado/Reverenciada> pelos Defensores do Tempo, protegendo os eventos da Antiga Eira dos Montes e do Lamaçal Negro dentro das Cavernas do Tempo."}},
[10562] = {"Cercados!", {"Comandante de Ala Nuainn, do lado de fora da Fortaleza Martelo Feroz, quer que você mate 10 Atacantes Infernais."}},
[10563] = {"Contra a Cidadela da Legião", {"Rume para a Cidadela da Legião e encontre o Dispositivo de Comunicação da Legião. Enquanto estiver ao lado do dispositivo comunicador, use o disfarce e descubra os planos da Legião. Fale novamente com Comandante de Ala Nuainn na Fortaleza Martelo Feroz."}},
[10564] = {"Os infernais que se explodam!", {"Use a Bomba Vil em cima do glifo verde na Cidadela da Legião, e em seguida se apresente ao Comandante de Ala Nuainn na Fortaleza Martelo Feroz."}},
[10565] = {"As pedras de Vekh'nir", {"Colete e use um Cristal Vekh'nir no Círculo de Feitiço Vekh'nir e leve o Cristal Vekh'nir Carregado para Dertrok na Vila de Mok'nathal."}},
[10566] = {"Tentativa e erro", {"Teste cada varinha de Dertrok em um ogro escravo dos arakkoas. Cada varinha precisará de um Cristal Vekh'nir Infuso para ser usada."}},
