[10568] = {"Tabuletas de Baa'ri", {"A Anacoreta Ceyla no Altar de Sha'tar quer que você colete 12 Tabuletas Baa'ri no chão e com os Trabalhadores Grislíngua nas Ruínas de Baa'ri.","","Completar missões para os Aldor reduzirá sua reputação junto aos Áugures."}},
[10569] = {"As Ruínas de Sketh'lon", {"Consiga as Páginas 1, 2 e 3 do Diário do Comandante de Sketh'lon com os arakkoas do Conclave Negro nas Ruínas de Sketh'lon e as entregue ao Cavalga-grifo Kieran."}},
[10570] = {"Um cardólatra da pesada", {"Borak, Filho de Oronok, que está na ponte perto da Ponta do Eclipse, no Vale da Lua Negra, quer que você recupere a Missiva de Tempesfúria."}},
[10571] = {"Oronu, o Ancião", {"A Anacoreta Ceyla no Altar de Sha'tar quer que você obtenha as Ordens de Akama que estão com Oronu, o Ancião, nas Ruínas de Baa'ri.","","Completar missões para os Aldor reduzirá sua reputação junto aos Áugures."}},
[10572] = {"Bomba la bomba", {"Obtenha um Núcleo de Força de Aníquilus e uma Placa de Armadura de Aníquilus, e leve-os ao Comandante de Ala Nuainn, na Fortaleza Martelo Feroz."}},
[10573] = {"A Forja da Morte", {"Apresente-se a Ewan Asalouca, o Arrojado, na Torre da Forja da Morte."}},
[10574] = {"Os corruptores Grislíngua", {"Obtenha os quatro fragmentos de medalhão com Haalum, Eykenen, Lakaan e Uylaru e procure a Anacoreta Ceyla no Altar de Sha'tar, que fica no Vale da Lua Negra.","","Completar missões para os Aldor reduzirá sua reputação com os Áugures."}},
[10575] = {"A Jaula do Carcereiro", {"A Anacoreta Ceyla quer que você entre na Jaula do Carcereiro, ao sul das Ruínas de Baa'ri e interrogue Sanoru quanto à localização de Akama.","","Completar missões para os Aldor reduzirá sua reputação junto aos Áugures."}},
[10576] = {"O golpe Lua Negra", {"Borak, Filho de Oronok, que está na ponte perto da Ponta do Eclipse, no Vale da Lua Negra, quer que você recupere 6 peças de Armadura Eclipsion."}},
[10577] = {"O que Illidan quer, Illidan consegue...", {"Borak, Filho de Oronok, que está na ponte perto da Ponta do Eclipse, no Vale da Lua Negra, quer que você entregue a Mensagem de Illidan ao Grão-comandante Ruusk na Ponta do Eclipse."}},
[10578] = {"A Cifra da Danação: a missão de Borak", {"Borak, Filho de Oronok, que está na ponte perto da Ponta do Eclipse, no Vale da Lua Negra, quer que você recupere o Terceiro Fragmento da Cifra da Danação com Ruul, o Obscurecente.","","Nesta missão, será melhor ir em grupo."}},
[10579] = {"A Cifra da Danação: terceiro fragmento recuperado", {"Leve o Cofre de Borak para Oronok Coração-partido na Fazenda de Oronok, no Vale da Lua Negra."}},
[10580] = {"Cadê o gnomo que estava aqui?", {"Investigue a área sul da Serra dos Espigões nas Montanhas da Lâmina Afiada para encontrar algum indício do paradeiro de Tocha e dos outros gnomos."}},
[10581] = {"Siga as migalhas de pão", {"Siga a trilha de peças na ladeira para o Espinhaço Cortante. Fale com Tocha na Estação do Tocha, nas Montanhas da Lâmina Afiada."}},
[10582] = {"Lacaios do Concílio das Sombras", {"Ewan Asalouca, o Arrojado, na Torre da Forja da Morte, quer que você mate 10 Guardiões da Forja da Morte e 5 Evocadores da Forja da Morte."}},
[10583] = {"O destino de Flanis", {"Ewan Asalouca, o Arrojado, na Torre da Forja da Morte, quer que você vasculhe a Forja da Morte em busca de Flanis Celerasa e recupere a Mochila de Flanis."}},
[10584] = {"Recuperando os conversores", {"Use o Coletor de Magneto Protovoltaico para coletar 5 Eletromentais."}},
[10585] = {"A câmara de evocação", {"Obtenha um Deslocador Elemental com um Ferreiro da Forja da Morte ou um Engenhoqueiro da Forja da Morte. Use o dispositivo para interromper o ritual na câmara de evocação. Apresente-se a Ewan Asalouca, o Arrojado, na Torre da Forja da Morte, depois de completar a missão."}},
[10586] = {"Acabe com o Armipotente!", {"Derrote o Armipotente Razuun, recolha as Ordens de Razuun, e apresente-se ao Comandante de Ala Nuainn na Fortaleza Martelo Feroz, no Vale da Lua Negra."}},
