[10605] = {"O chamado de Silvério", {"Fale com Silvério Hidalgo na Cidade Baixa."}},
[10606] = {"A Arte da Manutenção de Aníquilus", {"Mate o Doutor Gutrick e obtenha a Chave do Doutor. Use a chave para abrir o Cofre do Doutor e pegar A Arte da Manutenção de Aníquilus, e entregue o livro a Pléxis no Ponto de Invasão: Cataclismo."}},
[10607] = {"Sussurros do deus Corvo", {"Mate os arakkoas Grishna para obter a Compreensão da Fala do Corvo por 2 minutos. Enquanto estiver sob os efeitos do feitiço, você poderá encontrar e receber a profecia dos quatro santuários."}},
[10608] = {"Claro como cristal", {"Mate 10 Esfola-pedras de Cristal."}},
[10609] = {"Quem nasceu primeiro? O ovo ou o draco?", {"Colete 3 Essências de Protodraco Etéreo, 3 Essências de Draco Jovem e 3 Essências de Draco Adulto."}},
