[10802] = {"Gorgrom, o Devorador de Dragão", {"Colete 3 Totens Medonhos dos ogros de Rocha'mok. Depois use a armadilha do Zibecrina no altar de Gorgrom para atrair e matar Gorgrom, o Devorador de Dragão.","","Quando ele morrer, coloque 3 Totens Medonhos perto do cadáver."}},
[10803] = {"Matança em Rocha'mok", {"Mate 5 Brutamontes Rocha'mok e 3 Xamãs Rocha'mok."}},
[10804] = {"Bondade", {"Mordenai, nos Campos da Asa Etérea, que fica no Vale da Lua Negra, quer que você alimente 8 Dracos Asa Etérea Adultos."}},
[10805] = {"Massacre no Covil de Gruul", {"O Barão Zibecrina pediu para você e seus amigos matarem 10 Impositores Giralança, 5 Magos de Batalha Giralança, 5 Assoladores Giralança e o líder do grupo, Fingrom. Depois de dar conta dessa façanha, fale novamente com o barão no Círculo Sangrento, que fica nas Montanhas da Lâmina Afiada."}},
[10806] = {"Confronto final", {"Use o Sinete do Zibecrina no Altar de Goc e depois mate Goc."}},
[10807] = {"Os Degradados Grislíngua", {"Varen, o Recuperador, quer que você vá até as Ruínas de Baa'ri e mate 3 Tratadores Grislíngua, 6 Guerreiros Grislíngua e 4 Xamãs Grislíngua.","","Completar missões para os Áugures reduzirá sua reputação junto aos Aldor."}},
[10808] = {"Frustrar o Conclave Negro", {"Mate os Ritualistas do Conclave Negro e use o Cajado de Parshah para encerrar o ritual de evocação do Conclave Negro. Então, fale novamente com Parshah nos Campos de Magma."}},
[10809] = {"Procura-se: Mestre de Worgs Esmaago", {"Mate o Mestre de Worgs Esmaago e leve sua cabeça para a Capitão Uivonegro no Acampamento Quebra-espinha."}},
[10810] = {"Máscara danificada", {"Entregue a Máscara Danificada a Anacletto Desastraz no Arvoredo Eterno, que fica nas Montanhas da Lâmina Afiada."}},
[10811] = {"Procure Neltarako", {"Procure Neltarako, patrono da Revoada Asa Etérea."}},
[10812] = {"Máscara misteriosa", {"Entregue a Máscara Misteriosa para o Lorde Selvagem Antelarion no Arvoredo Eterno, que fica nas Montanhas da Lâmina Afiada."}},
[10813] = {"Os olhos de Grillok", {"Use a Lasca de Zézzak para capturar um olho de Grillok, e se aproxime do caldeirão de Zézzak para extrai-lo. Depois, devolva-lhe a Lasca de Zézzak."}},
[10814] = {"A história de Neltarako", {"Fale com Neltarako e ouça a história dele."}},
[10815] = {"O diário de Val'zareq: presságios de guerra", {"Encontre Marcus Aurálion, Alto-general dos Sha'tar, na Vigília Carmesim."}},
[10816] = {"A reconquista do solo sagrado", {"O Vindicante Aluumen quer que você mate 8 Matadores Lua Negra, 8 Escolhidos Lua Negra e 4 Tecetrevas Lua Negra.","","Completar missões para os Aldor reduzirá sua reputação junto aos Áugures."}},
[10817] = {"A grande retaliação", {"Varen, o Recuperador, quer que você mate 8 Matadores Lua Negra, 8 Escolhidos Lua Negra e 4 Tecetrevas Lua Negra.","","Completar missões para os Áugures reduzirá sua reputação com os Aldor."}},
[10818] = {"Barão Zibecrina solicita a sua presença", {"Fale com Barão Zibecrina na torre do Círculo Sangrento, que fica nas Montanhas da Lâmina Afiada."}},
[10819] = {"Máscara de gás de devoto vil", {"Coloque a Máscara de Gás de Devoto Vil e use o Comunicador da Legião no Campo-forja: Fúria, nas Montanhas da Lâmina Afiada."}},
[10820] = {"Engana teu inimigo", {"Mate 4 Atendentes Forja da Perdição e 4 Engenheiros Forja da Perdição."}},
[10821] = {"Para o olho da rua!", {"Mate Malsinara."}},
[10822] = {"Sinete único dos Solfúria", {"Even the smallest contribution to our cause is noted, <name>. Our enemies are many, but we shall prevail!"}},
[10823] = {"Mais sinetes dos Solfúria", {"How fares the battle against Kael's forces?"}},
[10824] = {"Sinetes dos Solfúria", {"A Maga de Batalha Vyara, no Sacrário Estelar, quer que você lhe traga 10 Sinetes dos Solfúria.","","Completar missões para os Áugures reduzirá sua reputação com os Aldor."}},
[10825] = {"Os orbes da verdade", {"Entregue o orbe de Grishna ao Guardião das Árvores Chawn, no Arvoredo Eterno, que fica nas Montanhas da Lâmina Afiada."}},
[10826] = {"Marcas de Sargeras", {"Traga 10 Marcas de Sargeras para o Precursor Saronen no Vale da Lua Negra.","","Completar missões para os Aldor reduzirá sua reputação junto aos Áugures."}},
[10827] = {"Mais Marcas de Sargeras", {"Do you bear more of our enemies' insignias, <class>? The Burning Legion continues to be a threat and we continue to pursue them anywhere that we find them."}},
[10828] = {"Uma única Marca de Sargeras", {"I'll take any leftover marks you might have. Do not worry, your actions will be duly noted."}},
[10829] = {"Precisamos informar ao Arvotronco", {"Fale com Arvotronco, um anciente da tradição que vive na Mata do Corvo, nas Montanhas da Lâmina Afiada."}},
[10830] = {"Exorcista de árvores", {"Colete 5 Orbes de Grishnath e 5 Penas Hediondas e depois as combine para formar Penas de Exorcismo. Use essas penas para exorcizar e matar 5 Espíritos Koi-koi dos Barbafolhas da Mata do Corvo."}},
[10831] = {"Becoming a Mooncloth Tailor", {"Use the Square of Imbued Netherweave while standing in Cenarion Refuge's moonwell to create a Sample of Primal Mooncloth. Bring the completed sample back to Nasmara Moonsong in Shattrath's Lower City.","","*WARNING!* You can only select one tailoring specialization."}},
[10832] = {"Becoming a Spellfire Tailor", {"Bring a sample of Nether-wraith Essence to Gidge Spellweaver in Shattrath's Lower City.","","*WARNING!* You can only select one tailoring specialization."}},
[10833] = {"Becoming a Shadoweave Tailor", {"Use the Crystal of Deep Shadows near the Altar of Shadows to deepen your attunement to the altar, then return to Andrion Darkspinner in Shattrath's Lower City.","","*WARNING!* You can only select one tailoring specialization."}},
[10834] = {"Grillok \"Olho-negro\"", {"Mate Grillok \"Olho-negro\" e leve o tapa-olho de Grillok para Zézzak no Acampamento Quebra-espinha."}},
[10835] = {"Boticário Antonino", {"Leve os Achados de Alberto ao Boticário Antonino em Thrallmar, na Península Fogo do Inferno."}},
[10836] = {"Infiltrando-se na Fortaleza Presa do Dragão", {"Neltarako, voando alto sobre os Campos da Asa Etérea no Vale da Lua Negra, quer que você mate 15 Orcs Presa do Dragão.","","Qualquer orc da Presa do Dragão na Fortaleza Presa do Dragão servirá."}},
[10837] = {"Para a Plataforma da Asa Etérea!", {"Neltarako, voando alto sobre os Campos da Asa Etérea no Vale da Lua Negra, quer que você colete 12 Cristais Etervinha da Plataforma da Asa Etérea."}},
[10838] = {"O Áugure Demoníaco", {"Use o Áugure Demoníaco na Bacia Fogo do Inferno, espere que o aparelho entre em sintonia e então faça a leitura dos dados.","","Leve o Áugure Demoníaco e a leitura do Áugure Demoníaco para o Boticário Antonino, em Thrallmar."}},
[10839] = {"Véu Skith: Pedra Negra de Terokk", {"Use o Bastão da Purificação na Pedra Negra de Terokk, encontrada no Véu Skith. A seguir, fale novamente com Kirrik, o Desperto na Caravana dos Refugiados, que fica no Deserto de Ossos."}},
[10840] = {"A Tumba das Luzes", {"O Sumo Sacerdote Orglum quer que você viaje até a Tumba das Luzes e mate 10 Etermantes Etéreos e 10 Espoliadores Etéreos. A seguir, fale novamente com ele na Caravana dos Refugiados."}},
[10841] = {"The Vengeful Harbinger", {"[PH] Activate the thingy."}},
[10842] = {"O Precursor Vingativo", {"O Sumo Sacerdote Orglum quer que você viaje até a Tumba das Luzes e use a Relíquia de Tumba Draeneica perto do Antigo Altar Draeneico.","","Proteja o Guardião da Tumba e derrote o Precursor Vingativo. Quando o ritual terminar, fale com o Ascendente Draenei no Antigo Altar Draeneico."}},
[10843] = {"Desde os primórdios...", {"Mate Gnosh Brognat, líder dos ogros do Acampamento Giralança."}},
[10844] = {"Forge Camp: Anger", nil},
