[11419] = {"Carneiros de montaria da CervaFest", {"Mostre o carimbo de \"Cervejeiro Honorário\" para Driz Topalogo, que está na entrada da CervaFest, em Durotar."}},
[11425] = {"Teste de missão – Craig", {"Pestes, que ódio desses bichos."}},
[11431] = {"Capturar os Lebrervos Selvagens!", {"O \"anão\" Glodrak Caçatiros pediu para você capturar 5 Lebrervos Atordoados usando a Rede de Lebrervo. Ele disse que você precisa estar bêbado para vê-los."}},
[11439] = {"Treinamento de bombeiros", {"Jogue Baldes d'Água em 5 chamas e, a seguir, fale com a Matriarca Fantasiada."}},
[11440] = {"Treinamento de bombeiros", {"Jogue Baldes d'Água em 5 chamas e, a seguir, fale com a Matriarca Fantasiada."}},
