[11447] = {"Boas-vindas à CervaFest!", {"Fale com um Organizador da CervaFest para ganhar uma cerveja grátis."}},
[11449] = {"Treinamento contra o fogo", {"Jogue baldes d'água em 5 chamas, depois fale com a Matriarca Mascarada em Montalvo."}},
[11450] = {"Treinamento contra o fogo", {"Jogue baldes d'água em 5 chamas, depois fale com a Matriarca Mascarada na Praça Asa do Falcão."}},
[11451] = {"Poema de Alícia", {"Pediram para você entregar o Poema de Alícia para Caylee Dak na cidade de Shattrath, que fica em Terralém."}},
[11454] = {"Procure os sabotadores", {"Encontre Coren Cervatroz no Glutão Implacável, que fica dentro do Abismo Rocha Negra."}},
[11481] = {"Crise na Nascente do Sol", {"Adyen, o Guardião da Luz, no Terraço dos Aldor, que fica em Shattrath, pediu a você que viaje ao Platô da Nascente do Sol e fale com o Capitão Theris Aurolar."}},
[11482] = {"O dever chama", {"Dathris Golpessol, no Terraço dos Áugures, que fica em Shattrath, insistiu que você viaje a Azeroth de imediato e se apresente a Larethor na Nascente do Sol."}},
[11486] = {"A melhor das cervejas", {"Leve o Barril de Cerveja Ferro Negro para Ipfelkofer Barrildechope em Dun Morogh."}},
[11487] = {"A melhor das cervejas", {"Leve o Barril de Cerveja Ferro Negro para Torneiro Barrilantra em Durotar."}},
[11488] = {"Terraço dos Magísteres", {"O Exarca Larethor, na área do Bivaque Sol Partido, quer que você procure o Terraço dos Magísteres e encontre Tyrith, um espião sanguinélfico."}},
[11490] = {"O áugure do áugure", {"Tyrith quer que você use o orbe na sacada do Terraço dos Magísteres."}},
[11492] = {"Duro de matar", {"Kalecgos pediu a você que vença Kael'thas no Terraço dos Magísteres. Você deve pegar a cabeça de Kael e falar novamente com Larethor, no Bivaque Sol Partido."}},
[11496] = {"Proteções do sacrário", {"O Capitão Theris Aurolar, no Bivaque Sol Partido, quer que você obtenha 4 Vestígios de Mana de Malévolos Ignóbeis e Devoradores Ignóbeis e os use para energizar uma das Proteções de Cristal ao redor do Sacrário Beirassol a leste."}},
[11497] = {"Aprendendo a voar", {"Fale com Ilsa Cervabum na Fortaleza Martelo Feroz, que fica no Vale da Lua Negra."}},
[11498] = {"Aprendendo a voar", {"Fale com Olrokk na Aldeia Lua Negra, que fica no Vale da Lua Negra."}},
[11499] = {"PROCURA-SE: o anel-sinete do Príncipe Kael'thas", {"Zhareem, o Mercador do Vento, pediu para você pegar o Anel-sinete do Príncipe Kael'thas. Entregue-lhe o anel no Bairro Inferior de Shattrath para receber a recompensa.","","Esta missão só pode ser completada no Terraço dos Magísteres em modo heroico."}},
[11500] = {"PROCURAM-SE: irmãs da Angústia", {"O Espreitador Etéreo Mah'duun quer que você mate 4 Irmãs da Angústia. Fale novamente com ele no Bairro Inferior de Shattrath quando todas estiverem mortas para receber a recompensa."}},
[11502] = {"Em defesa de Halaa", {"Derrote 10 jogadores inimigos em Halaa e fale novamente com Lakoor em Telaar."}},
[11503] = {"Inimigos, novos e velhos", {"Derrote 10 jogadores inimigos em Halaa e fale novamente com Karrtog em Garadar."}},
[11505] = {"Espíritos de Auchindoun", {"Ajude a Aliança a proteger uma torre no Deserto de Ossos."}},
[11506] = {"Espíritos de Auchindoun", {"Ajude a Horda a proteger uma Torre Espiritual no Deserto de Ossos."}},
[11513] = {"Interceptação das células de mana", {"Obtenha 10 Células de Mana Contrabandeadas e entregue-as a Exarca Nasuun no Terraço da Luz, que fica em Shattrath."}},
[11514] = {"Manutenção do Portal para a Nascente do Sol", {"Obtenha 10 Células de Mana Contrabandeadas e entregue-as a Exarca Nasuun no Terraço da Luz, que fica em Shattrath."}},
[11515] = {"Sangue por sangue", {"A Magistra Seyla, no Trono de Kil'jaeden, quer que você mate 4 Sangrevis Esquálidos usando o Sifão Vil neles. Você precisará de Sangue Demoníaco dos Arautos da Ira próximos para encher o Sifão Vil."}},
[11516] = {"Bloqueio do portal", {"A Magistra Seyla, no Trono de Kil'jaeden, quer que você use as Brasas Crepitantes para evocar uma Tocha Viva. Mate Fagulhas Vis Incandescentes perto dela até que se torne uma Tocha Viva Instável. Leve a Tocha Viva Instável ao Portal da Legião e destrua-o."}},
[11517] = {"Apresente-se a Nasuun", {"O Mercador Portaruus pediu para você falar com o Exarca Nasuun no Terraço da Luz, que fica em Shattrath."}},
[11518] = {"Indicador do Portal Diário para a Nascente do Sol", nil},
[11520] = {"Descobrindo as suas raízes", {"Mar'nah, no Porto de Beirassol, quer que você viaje ao Morro Espinhoso, que fica na Terralém, colete 5 Raízes Espinhosas e torne a falar com ela."}},
[11521] = {"Redescobrindo as suas raízes", {"Mar'nah, no Porto de Beirassol, quer que você viaje ao Morro Espinhoso, que fica na Terralém, colete 5 Raízes Espinhosas e torne a falar com ela."}},
[11523] = {"Ative as proteções!", {"O Capitão Theris Aurolar, no Bivaque Sol Partido, quer que você obtenha 4 Vestígios de Mana de Malévolos Ignóbeis e Devoradores Ignóbeis e os use para energizar uma das Proteções de Cristal ao redor das estruturas no Beirassol."}},
[11524] = {"Fora de controle", {"A Vindicante Xayann, no Bivaque Sol Partido, quer que você insira os Núcleos Sintonizados de Cristal em 5 Sentinelas Erráticas derrotadas para transformá-las em unidades aliadas."}},
[11525] = {"Mais conversões", {"A Vindicante Xayann, no Bivaque Sol Partido, quer que você insira os Núcleos Sintonizados de Cristal em 5 Sentinelas Erráticas para transformá-las em unidades aliadas."}},
[11526] = {"A magistra desaparecida", {"Use o Pergaminho Capturado da Legião perto do portal na Praça da Aurora. Quando estiver do outro lado do portal, encontre a Magistra Seyla."}},
[11528] = {"Um presente de Véu de Inverno", nil},
[11531] = {"Trapizonga esquisita", {"Leve a Trapizonga Esquisita para K. Lee Smallfry em Telredor."}},
[11532] = {"Distração na Trilha da Morte", {"A Maga de Batalha Arynna quer que você fale com Ayren Quebranuvens quando estiver <preparado/preparada> para sobrevoar a Trilha da Morte. Quando estiver lá, use as Cargas Arcanas para matar 2 Suseranos do Abismo, 3 Feiticeiros Eredar e 12 Impositores da Fúria."}},
[11533] = {"Os ataques aéreos não podem parar", {"A Maga de Batalha Arynna quer que você fale com Ayren Quebranuvens quando estiver <preparado/preparada> para sobrevoar a Trilha da Morte. Quando estiver lá, use as Cargas Arcanas para matar 2 Suseranos do Abismo, 3 Feiticeiros Eredar e 12 Impositores da Fúria."}},
[11534] = {"Apresente-se a Nasuun", {"O Mercador Portaruus pediu para você falar com o Exarca Nasuun no Terraço da Luz, que fica em Shattrath."}},
[11535] = {"Preparamentos", {"Ferreira Hauthaa, no Arsenal de Beirassol, quer que você mate os Mirmidões Espinha Negra, ao leste, e use as chaves deles para roubar três pedaços de minério dos baús."}},
[11536] = {"Não pare agora...", {"Ferreira Hauthaa, no Arsenal de Beirassol, quer que você mate os Mirmidões Espinha Negra, ao leste, e use as chaves deles para roubar três pedaços de minério dos baús."}},
[11537] = {"A batalha não pode parar", {"O Emissário Inuuro quer que você mate 6 Demônios da Legião Ardente e o Emissário do Ódio na Praça da Aurora. Use o Estandarte Sol Partido para empalar o cadáver do Emissário do Ódio."}},
[11538] = {"Batalha pela Armaria de Beirassol", {"O Emissário Inuuro quer que você mate 6 Demônios da Legião Ardente e o Emissário do Ódio na Praça da Aurora ou na Armaria de Beirassol. Use o Estandarte Sol Partido para empalar o cadáver do Emissário do Ódio."}},
[11539] = {"A conquista do porto", {"O Magíster Ilastar, na Armaria de Beirassol, quer que você mate 6 Evocadores Aurolumes, 6 Cavaleiros Sangrentos Aurolumes e 3 Atiradores Peritos Aurolumes."}},
[11540] = {"Esmague os Aurolumes", {"O Magíster Ilastar, na Armaria de Beirassol, quer que você mate 6 Evocadores Aurolumes, 6 Cavaleiros Sangrentos Aurolumes e 3 Atiradores Peritos Aurolumes."}},
[11541] = {"Leve o caos à Costa Guelraverde", {"A Capitão Valindria quer que você liberte 10 Escravos Guelraverde. Fale novamente com ela a bordo do Orgulho de Luaprata no Porto de Beirassol após cumprir a tarefa."}},
[11542] = {"Intercepte os reforços", {"O Vindicante Kaalan, na Armaria de Beirassol, quer que você fale com Ayren Quebranuvens e sobrevoe a frota de reforço Aurolume. Use o Óleo Flamejante para atear fogo às velas dos navios durante o voo. Depois pouse e mate 6 Reservistas Aurolume."}},
[11543] = {"Manter o inimigo ao largo", {"O Vindicante Kaalan, na Armaria de Beirassol, quer que você fale com Ayren Quebranuvens e sobrevoe a frota de reforço Aurolume. Use o Óleo Flamejante para atear fogo às velas dos navios durante o voo. Depois pouse e mate 6 Reservistas Aurolumes."}},
[11544] = {"Armamentos de Ata'mal", {"A Ferreira Hauthaa quer que você produza 5 Metais de Ata'mal Purificados. Para isso, desmanche os Armamentos de Ata'mal na bigorna dela na Armaria de Beirassol, que fica na Ilha de Quel'Danas."}},
[11545] = {"Filantropia", {"A Anacoreta Ayuri, em Beirassol, que fica na Ilha da Nascente do Sol, quer que você doe 10 moedas de ouro."}},
[11546] = {"Aberto para negócios", {"Mar'nah, no Porto de Beirassol, quer que você colete 5 Frutas-sangue dos arbustos que se encontram em Quel'Danas."}},
[11547] = {"Decore as suas falas", {"O Astromante Darnarian, no Sacrário Beirassol, quer que você use o Cristal do Astromante no Cristal de Sangue, no portal da Praça da Aurora e no templo naga na Costa Guelraverde."}},
[11548] = {"Graças ao seu apoio de hoje e sempre", {"A Anacoreta Ayuri, em Beirassol, que fica na Ilha da Nascente do Sol, quer que você doe 10 moedas de ouro."}},
[11549] = {"Um benfeitor generoso", {"O Anacoreta Kairthos quer que você doe 1000 moedas de ouro para auxiliar os esforços da Anacoreta Ayuri. Você será <conhecido/conhecida> como <name> do Sol Partido se cumprir esta missão."}},
[11550] = {"O enganador", {"Apresente-se ao Arquimago Ne'thul na Ilha de Quel'Danas, que fica ao norte de Quel'thalas.","","Os mestres de voo em Altaforja e em Luaprata farão com que você chegue ao seu destino."}},
[11554] = {"Lealdade no fronte", {"Sua ajuda na ofensiva não passou despercebida, <name>. As forças coligadas dos Aldor e dos Áugures lhe são gratas."}},
[11555] = {"Honrado pelos aliados", {"Você continua a provar seu valor nesta batalha tão importante, <name>. Aceite isto como um símbolo da nossa eterna gratidão."}},
[11556] = {"Reverenciado no campo de batalha", {"Não teríamos conseguido chegar até aqui se não fosse pela sua inestimável ajuda, <name>. Mesmo em conjunto, o poder dos Aldor e dos Áugures não é suficiente para conter a Legião e Kael. Somos gratos por ter aliados do seu nível."}},
[11557] = {"O melhor em combate", {"Você é um dos nossos melhores combatentes, <name>. Seu nome ecoará entre as nossas legiões por muito tempo.","","Aceite este pequeno presente em nome das nossas tropas."}},
