[247] = {"Завершение охоты",nil, {"Шаманко, ты убил всех трех чудовищ охоты в Ясеневом лесу. Ты предоставил мне несомненные доказательства их гибели, так что сомнений в твоих силе и упорстве у меня нет. В твоих жилах течет кровь охотников, и да будет тебе хвала по делам!nnПрошу, прими эту безделицу в знак благодарности за твои деяния, совершенные тобой ныне в Ясеневом лесу во славу Орды. Да будут духи предков милостивы к тебе!"}},
[248] = {"Пронизывающий взор",nil, {"Поднимитесь на уцелевшую башню рядом с крепостью Каменной Стражи.nnОтыщите подходящее место и спрячьте там амулет Азоры."}},
[249] = {"Моргант",nil, {"Победите Морганта.nnПринесите его подвеску Тени Теокриту в башню Азоры в Элвиннском лесу."}},
[250] = {"Прямая и черная угроза",nil, {"Осмотрите область вокруг восточной части Дамбы и отыщите для главного инженера Плотины улику, указывающую на замыслы дворфов Черного Железа."}},
[251] = {"Перевод записки Аберкромби",nil, {"Отнесите письмо, адресованное Элло, Шире Фон-Инди."}},
[252] = {"Перевод для Элло",nil, {"Отнесите переведенное письмо лорду Элло Чернодреву."}},
[253] = {"Невеста Бальзамировщика",nil, {"Отыщите могилу Элизы. Извлеките из нее сердце Бальзамировщика и вернитесь к Элло Чернодреву."}},
[254] = {"Копание земли",nil, {"Вы копаете землю... и наконец находите остатки гроба.nnВы слышите скрежет когтей по крышке и лязганье гнилых зубов…nnРешитесь ли вы открыть гроб и достать сердце Бальзамировщика?"}},
[255] = {"Наемники",nil, {"Мировой судья Тупонос просит вас убить 4 огров из клана Мо'грош, 4 громил из клана Мо'грош и 4 головорезов из клана Мо'грош."}},
[256] = {"Разыскивается: Чок'сул",nil, {"Убейте Чок'сула и принесите его голову чиновнику Тупоносу в Телcамар."}},
[257] = {"Похвальба охотника",nil, {"Убейте 6 горных канюков и вернитесь к Дэрилу Юному в Приют странников. На выполнение задания отводится 15 минут."}},
[258] = {"Вызов охотнику",nil, {"Убейте 5 старых горных вепрей и вернитесь к Дэрилу Юному в Приют странников. На выполнение задания отводится 12 минут."}},
[261] = {"По пути Алого ордена",nil, {"Убейте 30 опустошителей-отрекшихся и вернитесь к брату Антону на Высоте Найджела."}},
[262] = {"Таинственный незнакомец",nil, {"Отнесите книгу, обнаруженную на ферме Свена, в Темнолесье и покажите ее горожанам."}},
[263] = {"Защита королевских земель",nil, {"Горный пехотинец Стенолом просит вас убить 10 шаманов из племени Камнедробов и 10 костегрызов из племени Камнедробов."}},
[264] = {"Покуда смерть не разлучит нас",nil, {"Положите подвеску Кларисы на могилу Юрива в Серебролесье."}},
[265] = {"Продолжение поисков незнакомца",nil, {"Поговорите с горожанами о таинственном незнакомце."}},
[266] = {"Расспросы в таверне",nil, {"Найдите в таверне улики, связанные с таинственным незнакомцем."}},
[267] = {"Троггская угроза",nil, {"Принесите 8 каменных зубов троггов капитану Ругельфассу."}},
[268] = {"Возвращение к Свену",nil, {"Отнесите Свену законченные записи Трясунчика."}},
[269] = {"В поисках мудрости",nil, {"Отдайте законченные записи Трясунчика епископу Фартингу в Штормграде."}},
[270] = {"Обреченный флот",nil, {"Отправляйтесь в Гавань Менетилов в Болотине и поговорите с дворфом по имени Глорин Стальной Лоб."}},
[271] = {"Месть Вайрины",nil, {"Убейте Старину Черношкура и принесите его голову Дэрилу Юному в Приюте странников."}},
[272] = {"Испытание Морского Льва",nil, {"Найдите половинку подвески ловкости водяного облика и половинку подвески выносливости водяного облика. Поговорите с жителями Лунной поляны, чтобы узнать, где могут находиться эти предметы.nnСложите из половинок подвеску морского льва. Для этого вам надо находиться рядом со святилищем Ремула.nnОтнесите собранную подвеску Дендриту Блеску Звезд в деревню Ночная Гавань, что на Лунной поляне."}},
[273] = {"Снабжение экспедиции",nil, {"Поговорите с Хальдаром."}},
[274] = {"Прямая и черная угроза",nil, {"Ашлан Камнехмыл просит вас сообщить главному инженеру о нейтрализующем коллоиде."}},
[275] = {"Нарывы на теле земли",nil, {"Убейте 8 трясинных ползунов и вернитесь к Ретилю Стражу Природы в Болотину."}},
[276] = {"Вытоптанные луга",nil, {"Убейте 15 гноллов из стаи Мохошкуров и 10 полукровок из стаи Мохошкуров. Вернитесь к Ретилю Стражу Природы в Болотину."}},
[277] = {"Запрет на огонь",nil, {"Принесите Ретилю Стражу Природы 9 необработанных кремней."}},
[278] = {"Прямая и черная угроза",nil, {"Принесите паучий яд, мо'грошский кристалл и слезу кроколиска главному инженеру Плотине."}},
[279] = {"Угроза из глубин",nil, {"Убейте 12 мурлоков из племени Синежабрых.nnПрикончите Жруна и принесите его голову Карлу Борану в Гавань Менетилов."}},
[280] = {"Прямая и черная угроза",nil, {"Проплывите по реке к основанию дамбы, отыщите бочку с порохом и влейте в нее нейтрализующую смесь, чтобы предотвратить взрыв."}},
[281] = {"Украденные товары",nil, {"Обыщите сундуки, бочки и ящики в поселениях мурлоков на побережье."}},
[282] = {"Наблюдения Сенира",nil, {"Поговорите с горным пехотинцем Талосом."}},
[283] = {"Прямая и черная угроза",nil, {"Кажется, нейтрализующая смесь сработала. Вернитесь к главному инженеру Плотине – принесите ему добрые вести."}},
[284] = {"Поиски продолжаются",nil, {"Найдите статуэтку Менетила."}},
[285] = {"Поиски в поселениях мурлоков",nil, {"Продолжите поиски статуэтки Менетила в поселении мурлоков."}},
[286] = {"Возвращение статуэтки",nil, {"Верните статуэтку Менетила Карлу Борану."}},
[287] = {"Форт Мерзлогривов",nil, {"Обследуйте форт Мерзлогривов и убейте 5 охотников за головами из племени Мерзлогривов по просьбе Сенира Белоборода из Караноса."}},
[288] = {"Третья флотилия",nil, {"Купите первому помощнику Фитсиммонсу кружку медовухи."}},
[289] = {"Проклятая команда",nil, {"Убейте 13 проклятых матросов, 5 проклятых стражей бухты и первого помощника Снеллига. Принесите табакерку Снеллига первому помощнику Фитсиммонсу в Гавань Менетилов."}},
[290] = {"Очищение",nil, {"Возьмите ключ от сейфа на \"Отважном\" у капитана Халиндора.nnОткройте сейф на \"Отважном\"."}},
[291] = {"Донесения",nil, {"Отнесите донесение Сенира сенатору Бэрину Краснокамню в Стальгорн."}},
[292] = {"Око Палета",nil, {"Отнесите проклятое Око Палета Глорину Стальному Лбу."}},
[293] = {"Проклятое Око",nil, {"Отнесите Око Палета архиепископу Бенедикту. Его можно найти в Соборе Света в Штормграде."}},
[294] = {"Месть Ормера",nil, {"Убейте 10 крапчатых ящеров и 10 крапчатых крикунов."}},
[295] = {"Месть Ормера",nil, {"Убейте 10 крапчатых смертехватов и 10 крапчатых острозубов. Вернитесь к Ормеру Сталеплету на раскоп Вельгара."}},
[296] = {"Месть Ормера",nil, {"Убейте Зарлозуба и принесите Ормеру Сталеплету один из когтей чудовища."}},
[297] = {"Сбор идолов",nil, {"Принесите Магмару Рудоколу 8 резных каменных идолов."}},
[298] = {"Отчет о ходе раскопок",nil, {"Отнесите отчет о ходе работ Джерну Шлеморогу в Телcамар."}},
[299] = {"Тайны прошлого",nil, {"Обыщите раскопки и найдите четыре недостающих фрагмента скрижали: Адос, Модр, Голм и Неру. Принесите их геологу Вельгару."}},
[301] = {"Отчет в Стальгорн",nil, {"Отнесите отчет Сталекрута о ходе работ геологу Грозовой Вершине."}},
[302] = {"Порох для Сталекрута",nil, {"Поговорите с Джерном Шлеморогом в Телcамаре."}},
[303] = {"Война с Черным Железом",nil, {"Убейте 10 дворфов, 5 проходчиков, 5 диверсантов и 5 подрывников из клана Черного Железа."}},
[304] = {"Мрачное поручение",nil, {"Убейте Балгараса Нечистого и принесите его ухо Длиннокосу Мрачному в Дун Модр."}},
[305] = {"Пропавшие ученые",nil, {"Таррел Камнепряд просит отыскать Меррин Камнепряд на раскопках."}},
[306] = {"Пропавшие ученые",nil, {"Отнесите Таррелу Камнепряду записку от Меррин."}},
[307] = {"Грязные лапы",nil, {"Принесите из рудника у Серебряного ручейка 4 ящика оборудования горному пехотинцу Грозовой Вершине."}},
[308] = {"Отвлечь Ярвена",nil, {"Ох! Сколько же тут выпивки, а у меня строгий приказ ничего не трогать. Мне бы хоть попробовать нашего громового эля... Я от него лучше соображаю, честное слово!"}},
[309] = {"Охрана груза",nil, {"Убедитесь, что Миран и его груз добрались до раскопа, и сообщите об этом геологу Сталекруту."}},
[310] = {"Злейшие соперники",nil, {"Замените один бочонок громового эля бочонком жгучего пива Ячменоваров в подвале \"Громоварки\" в Караносе."}},
[311] = {"Возвращение к Марлетте",nil, {"Принесите бочонок громового эля Марлетте Ячменовар в Поселок Пивоваров."}},
[312] = {"Ограбление тайника Тундры Макгранна",nil, {"Добудьте из пещеры Старого Ледоборода вяленое мясо из краденого ларя Тундры Макгранна."}},
[313] = {"Серая берлога",nil, {"Принесите 8 грив вендиго пилоту Камнекожцу."}},
[314] = {"Защита стада",nil, {"Убейте Вагаша и принесите его клык Рудре Янтарлен на Бараний выгон."}},
[315] = {"Всем портерам портер",nil, {"Принесите 6 порций звездолиста Регольду Ячменовару из Поселка Пивоваров."}},
[317] = {"Заготовка продовольствия",nil, {"Принесите 4 куска кабанины и 2 толстых медвежьих шкуры пилоту Брюхинсу в Поселок Сталежара."}},
[318] = {"\"Вечное сияние\"",nil, {"Возьмите бочонок \"Вечного сияния\" у Регольда Ячменовара в Поселке Пивоваров."}},
[319] = {"Услуга за \"Вечное сияние\"",nil, {"Убейте 6 медведей-ледолапов, 8 старых скальных вепрей и 8 снежных леопардов. Вернитесь к Регольду Ячменовару в Поселок Пивоваров."}},
[320] = {"Возвращение к Толстопузу",nil, {"Отдайте бочонок \"Вечного сияния\" пилоту Толстопузу в Поселке Сталежара."}},
[321] = {"Светлосталь",nil, {"Найдите груз с затонувшего корабля \"Орлик\" в Болотине."}},
[322] = {"Достойное оружие",nil, {"Отнесите сундук со слитками светлостали Гриманду Элмору в Штормград."}},
[323] = {"Доказательство силы",nil, {"Убейте 15 скелетов-налетчиков, 3 скелетов-лекарей и 3 скелетов-хранителей и вернитесь к Свену."}},
[324] = {"Утерянные слитки",nil, {"Соберите 5 слитков светлостали и вернитесь к Глорину Стальному Лбу в Болотину."}},
[325] = {"Вооружен и особо опасен",nil, {"Принесите Проклятье Морбента Свену в Сумеречный лес."}},
[328] = {"Спрятанный ключ",nil, {"Откройте сейф букмекера Ирода маленьким латунным ключом."}},
[329] = {"Шпион найден!",nil, {"Отнесите грязную записку лейтенанту Дорену в Лагерь повстанцев."}},
[330] = {"Расписание дежурств",nil, {"Поговорите с капралом Сетманом."}},
[331] = {"Доклад Дорену",nil, {"Поговорите с лейтенантом Дореном в Лагере повстанцев."}},
[332] = {"Реклама винного магазина",nil, {"Отправляйтесь на винокурню Галлины и принесите Сюзетте Галлине талон на вино, чтобы получить бутылку вина бесплатно."}},
[333] = {"Харлан нуждается в помощи",nil, {"Отправляйтесь в магазин одежды в районе каналов и принесите Реме Шнайдер заказ на ткани от Харлана Багли."}},
[334] = {"Посылка для Турмана",nil, {"Отправляйтесь в суконную лавку Ларсона в квартал магов Штормграда, и передайте Турману Шнайдеру его набор для шитья."}},
[335] = {"Благородный напиток",nil, {"Принесите Зардету Черному Когтю слезу Тиллоа из Темнолесья и мускусный корень из Болотины."}},
[336] = {"Благородный напиток",nil, {"Отнесите портер \"Черный коготь\" лорду Вишлоку в Зал просителей."}},
[337] = {"Старый учебник истории",nil, {"Вернуть книгу \"История Штормграда\" в библиотеку Штормграда."}},
[338] = {"Зеленые холмы Тернистой долины",nil, {"Соберите все недостающие страницы романа \"Зеленые холмы Тернистой долины\". Когда у вас на руках окажутся все четыре главы, верните их Барнилу."}},
[339] = {"Глава I",nil, {"Принесите 1, 4, 6 и 8 страницы книги \"Зеленые холмы Тернистой долины\" Барнилу Каменному Кубку, чтобы собрать всю первую главу."}},
[340] = {"Глава II",nil, {"Принесите 10, 11, 14 и 16 страницы книги \"Зеленые холмы Тернистой долины\" Барнилу Каменному Кубку, чтобы собрать всю вторую главу."}},
[341] = {"Глава III",nil, {"Принесите 18, 20, 21 и 24 страницы книги \"Зеленые холмы Тернистой долины\" Барнилу Каменному Горшку, чтобы собрать всю третью главу."}},
[342] = {"Глава IV",nil, {"Принесите 25, 26 и 27 страницы книги \"Зеленые холмы Тернистой долины\" Барнилу Каменному Кубку, чтобы собрать всю четвертую главу."}},
[343] = {"Кстати, о стойкости духа",nil, {"Отправляйтесь в Королевскую библиотеку Крепости Штормграда и поговорите с Милтоном Снопом. Он даст вам книгу по металлургии, которая интересует брата Кристофа."}},
[344] = {"Брат Пакстон",nil, {"Поговорите с братом Пакстоном из Аббатства Североземья."}},
[345] = {"Запас чернил",nil, {"Поговорите со штейгером Ослоу из Приозерья и расспросите его о ретбанской руде."}},
[346] = {"Возвращение к Кристофу",nil, {"Вернитесь к брату Кристофу на Соборную площадь. Отдайте ему книгу \"Как закалялось железо\"."}},
[347] = {"Ретбанская руда",nil, {"Добудьте 5 кусков ретбанской руды и принесите их брату Пакстону в библиотеку Североземского аббатства."}},
[348] = {"Лихорадка Тернистой долины",nil, {"Найдите знахаря Унбагва и попросите его вызвать Мокка Дикаря. Принесите сердце Мокка Фину Горемыку."}},
[349] = {"Лихорадка Тернистой долины",nil, {"temp text 02 - log"}},
[350] = {"Помощь старого друга",nil, {"Поговорите с Иллингом Триасом в Штормграде."}},
[351] = {"Найди КПХ-17/TN!",nil, {"Отнесите аварийный маяк к автономному роботу Карлса."}},
[353] = {"Посылка для Грозовой Вершины",nil, {"Доставьте посылку горному пехотинцу Грозовой Вершине в Лок Модан."}},
[354] = {"Смерти в семье",nil, {"Принесите останки Грегора, останки Ниссы и останки Турмана Колману Фартингу в Брилл."}},
[355] = {"Разговор с Севреном",nil, {"Поговорите с мировым судьей Севреном в Брилле."}},
[356] = {"Патруль в арьергарде",nil, {"Убейте 8 кровоточащих ужасов и 8 странствующих духов, а потом доложите о своих успехах Линнее в ее лагере."}},
[357] = {"Личность лича",nil, {"Принесите книгу лича Бетору Ледострелу в Подгород."}},
[358] = {"Грабители могил",nil, {"Убивайте грабителей могил и полукровок из стаи Гнилошкуров.nnПринесите мировому судье Севрену 8 образцов бальзамирующей лимфы."}},
[359] = {"Обязанности Отрекшихся",nil, {"Поговорите со стражем смерти Линнеей."}},
[360] = {"Возвращение к чиновнику",nil, {"Вернитесь к мировому судье Севрену в Брилл."}},
[361] = {"Недоставленное письмо",nil, {"Найдите Иветту Фартинг и вручите ей письмо от Турмана Агамонда."}},
[362] = {"Мельницы с привидениями",nil, {"Убейте Девлина Агамонда и принесите его останки Колману Фартингу в Брилл."}},
[363] = {"Резкое пробуждение",nil, {"Поговорите с жрецом Тьмы Сарвисом."}},
[364] = {"Безмозглые твари",nil, {"Жрец Тьмы Сарвис хочет, чтобы вы убили 8 безмозглых зомби и 8 презренных зомби."}},
[365] = {"Поля горя",nil, {"Украдите 10 тыкв с западной фермы, к северу от Могильника. Потом отнесите эти тыквы аптекарю Юхану в Брилл."}},
[366] = {"Книга Гюнтера",nil, {"Верните Гюнтеру книгу заклинаний. Его можно найти на острове, в приюте Гюнтера."}},
[367] = {"Новая чума",nil, {"Принесите аптекарю Юхану 5 склянок крови псов тьмы."}},
[368] = {"Новая чума",nil, {"Принесите аптекарю Юхану из Брилла 5 чешуек мурлоков из племени Злобного Плавника из Тирисфальских лесов."}},
[369] = {"Новая чума",nil, {"Принесите аптекарю Юхану из Брилла 4 образца яда злобного ночного кругопряда."}},
[370] = {"Война с Алым орденом",nil, {"Убейте капитана Перрина, 3 ревнителей и 3 миссионеров из Алого ордена по просьбе вершителя Зуганда."}},
[371] = {"Война с Алым орденом",nil, {"Убейте капитана Вакона и пятерых келейников из Алого ордена по заданию вершителя Зуганда."}},
[372] = {"Война с Алым орденом",nil, {"По просьбе палача Зуганда убейте капитана Мелраша и двух его телохранителей."}},
[373] = {"Неотправленное письмо",nil, {"Доставьте письмо городскому архитектору Баросу Алекстону в Штормград."}},
[374] = {"Доказательство верности",nil, {"Принесите 10 алых перстней стражу смерти Берджесу в Брилл."}},
[375] = {"Смертельный холод",nil, {"Принесите 5 шкурок сумеречницы и грубую нить Гретхен Дедмар в Брилл."}},
[376] = {"Проклятые",nil, {"Ученице Элрет нужно 6 лап падальщика и 6 крыльев сумеречницы."}},
[377] = {"Преступление и наказание",nil, {"Принесите советнику Миллстайпу руку Декстрена Варда."}},
[378] = {"Успокоить гневливого",nil, {"Принесите голову Кама Гневливого Мотли Каменщику в Дун Модр."}},
[379] = {"Утолить жажду",nil, {"Найдите 5 бурдюков с водой Скитальцев Пустыни главному инженеру Чепухастеру в Прибамбасск."}},
[380] = {"Паучья низина",nil, {"Палач Аррен хочет, чтобы вы убили 10 молодых ночных кругопрядов и 8 ночных кругопрядов."}},
[381] = {"Алый орден",nil, {"Принесите вершителю Аррену 12 алых поручей, сняв их с новообращенных и посвященных из Алого ордена."}},
[382] = {"Красный гонец",nil, {"Убейте гонца Мевена Коргала в лагере Алого ордена, а потом передайте перехваченные известия палачу Аррену."}},
[383] = {"Важные разведданные",nil, {"Отнесите бумаги Алого ордена вершителю Зуганду в Брилл."}},
[384] = {"Кабаньи ребрышки в пиве",nil, {"Принесите 6 ребер скального вепря и кружку \"Болтливого пива\" Рагнару Громовару в Караносе."}},
[385] = {"Охота на кроколисков",nil, {"Принесите Мареку Железносерду в Приют Странников 5 кусков мяса кроколиска и 6 шкур кроколиска."}},
[386] = {"Что происходит?",nil, {"Принесите голову Таргорра Ужасного стражнику Бертону в Приозерье."}},
[387] = {"Подавление бунта",nil, {"Тюремщик Телвотер просит вас убить в тюрьме 10 узников, 8 каторжников и 8 мятежников из Братства Справедливости."}},
[388] = {"Цвет крови",nil, {"Принесите Никове Раскол 10 красных шерстяных бандан."}},
[389] = {"Базиль Тредд",nil, {"Поговорите с надзирателем Телвотером в тюрьме."}},
[391] = {"Бунтовщики в тюрьме",nil, {"Убейте Базиля Тредда и принесите его голову Телвотеру в тюрьму Штормграда."}},
[392] = {"Таинственный посетитель",nil, {"Отнесите запечатанное описание посетителя Тредда Баросу Алекстону в Штормград."}},
[393] = {"Тень прошлого",nil, {"Отнесите запечатанное описание посетителя Тредда Матиасу Шоу в Штормград."}},
[394] = {"Голова чудовища",nil, {"Поговорите с Матиасом Шоу в Штормграде."}},
[395] = {"Гибель братства",nil, {"Поговорите с Баросом Алекстоном в Штормграде."}},
[396] = {"Аудиенция у короля",nil, {"Доставьте доклад Бароса Алекстона о Братстве Справедливости королю Вариану Ринну в крепость Штормграда."}},
[397] = {"За достойную службу",nil, {"Поговорите с Згги, слугой Зардета Черного Когтя, чтобы получить награду."}},
[398] = {"Разыскивается: Червеглаз",nil, {"Убейте Червеглаза и отнесите палачу Зуганду в Брилл его лапу."}},
[399] = {"Скромные дары прошлого",nil, {"Обыщите дом Бароса Алекстона в Западном Крае, найдите компас и вернитесь на Соборную площадь Штормграда."}},
[400] = {"Инструменты для Сталежара",nil, {"Доставьте инструменты Белдину Сталежару."}},
[401] = {"Ожидание перевода",nil, {"Подождите немного и снова поговорите с Широй Фон-Инди."}},
[402] = {"Шира занят",nil, {"Зайдите к Шире и выясните, занят ли он."}},
[403] = {"Охраняемый бочонок громопойла",nil, {"Этот бочонок эля охраняет Ярвен Громовар. Пока он в подвале, до бочек не добраться."}},
[404] = {"Грязная работа",nil, {"Принесите 7 гнилых когтей стражу смерти Диллинжеру в Брилл."}},
[405] = {"Блудный лич",nil, {"Отнесите приказы Севрена Бетору Ледострелу в Подгород."}},
[407] = {"Поля горя",nil, {"Отнесите отравленную тыкву пленному ревнителю из Алого ордена, которого держат в погребе таверны \"Петля висельника\"."}},
[408] = {"Семейный склеп",nil, {"Убейте 8 стенающих предков и 8 гниющих предков.nnУбейте капитана Даргола и принесите его череп мировому судье Севрену в Брилл."}},
[409] = {"Доказательство преданности",nil, {"Возьмите манящую свечу.nnВызовите Лилит Нефару и убейте ее.nnВернитесь на остров к Гюнтеру."}},
[410] = {"Спящая тень",nil, {"Старый, побитый, окровавленный стол."}},
[411] = {"Возвращение блудного лича",nil, {"Отнесите самоцвет Пустоты Бетору Ледострелу в Подгород."}},
[412] = {"Операция \"Перенаправление\"",nil, {"Принесите Раззлу Шустрецу в Караносе 8 зубцов рестабилизации и 8 гиромеханических шестерней."}},
[413] = {"Мерцающий портер",nil, {"Отнесите бочонок \"Мерцающего портера\" горному пехотинцу Ячменовару."}},
[414] = {"Портер для Кэдрелла",nil, {"Отнести бочонок \"Мерцающего портера\" горному пехотинцу Кэдреллу."}},
[415] = {"Новое пиво Регольда",nil, {"Поговорите с Регольдом Ячменоваром."}},
[416] = {"Ловля крыс",nil, {"Принесите 12 ушей кобольдов-туннельщиков горному пехотинцу Кэдреллу в Телcамар."}},
[417] = {"Месть пилота",nil, {"Убейте Паршивого Когтя.nnПринесите его коготь и журнал Хилдельва пилоту Большеногу."}},
[418] = {"Телcамарские кровяные колбаски",nil, {"Принесите Видре Жаркий Очаг в Телcамар 3 куска медвежатины, 3 связки кишок кабана и 3 порции паучьей лимфы."}},
[419] = {"Пропавший пилот",nil, {"Найдите пилота Хилдельва."}},
[420] = {"Наблюдения Сенира",nil, {"Доставьте донесение Грелина Сениру Белобороду в Караносе."}},
[421] = {"Проявить себя",nil, {"Далар Ткач Рассвета в Гробнице хочет, чтобы вы убили 5 белоголовов Лунной Ярости."}},
[422] = {"Безрассудство Аругала",nil, {"Раздобудьте книгу \"Зелье Аругала\" и принесите ее Далару Ткачу Рассвета в Гробницу."}},
[423] = {"Безрассудство Аругала",nil, {"Принесите Далару Ткачу Рассвета в Гробницу 6 наручей обжор Лунной Ярости и 3 наруча темных духов Лунной Ярости."}},
[424] = {"Безрассудство Аругала",nil, {"Убейте Мрачнона Белесого и принесите его голову Далару Ткачу Рассвета в Гробницу."}},
[425] = {"Ивар Нечистый",nil, {"Убейте Ивара Нечистого и принесите его голову Ран Йорик на делянку Ивара."}},
[426] = {"Нападение на мельницы",nil, {"Соберите 5 зазубренных ребер и 3 почерневших черепа для стража смерти Диллинджера в Брилле."}},
[427] = {"Война с Алым орденом",nil, {"Убейте 10 воинов из Алого ордена по просьбе вершителя Зуганда."}},
[428] = {"Пропавшие стражи смерти",nil, {"Найдите стражей смерти Квинна Йорика и Ран Йорик."}},
[429] = {"Звериные сердца",nil, {"Соберите 6 обесцвеченных сердец ворга и принесите их аптекарю Ренферрелу в Гробницу."}},
[430] = {"Назад к Квинну",nil, {"Отнесите зелье Квинна Квинну Йорику на делянку Ивара, к северу от Гробницы."}},
[431] = {"Манящие свечи",nil, {"Вы берете одну свечу и кладете ее в сумку."}},
[432] = {"Проклятые трогги!",nil, {"Убейте по просьбе штейгера Камнелоба 6 головоломов из племени Каменной Челюсти в карьере Гол'Болар."}},
[433] = {"Слуга народа",nil, {"Убейте 10 костегрызов из племени Каменной Челюсти по просьбе сенатора Мера Каменная Реликвия из карьера Гол'Болар."}},
[434] = {"Нападение!",nil, {"Спрячьтесь в саду и подслушайте разговор лорда Грегора Лесковара и Марзона Бесшумного Клинка. После этого убейте обоих и возвращайтесь к Иллингу Триасу в Штормград."}},
[435] = {"Сопровождая Эрланда",nil, {"Проводите Эрланда, защищая его от волков, к Ран Йорик."}},
[436] = {"Раскопки Сталекрута",nil, {"Обратитесь к исследователю Удару Топора."}},
[437] = {"Мертвое поле",nil, {"Убивайте гноллов из стаи Гнилошкуров на Мертвом поле. nnКогда появится банши Плеть Ночи, убейте ее. nnСущность банши принесите верховному палачу Хадрику в Гробницу."}},
[438] = {"Старая переправа",nil, {"Отправляйтесь к старой переправе. nnПоищите причину присутствия там сил Плети."}},
[439] = {"Необычная находка",nil, {"Принесите кольцо Делии верховному палачу Хадрику в Гробницу."}},
[440] = {"Кольцо с гравировкой",nil, {"Отнесите кольцо Делии мировому судье Севрену в Брилл."}},
[441] = {"Роли, который живет в Подгороде",nil, {"Отнесите Роли кольцо Делии."}},
[442] = {"Атака на остров Фенриса",nil, {"Принесите верховному палачу Хадрику в поселении Гробница голову Тула Когтя Ворона."}},
[443] = {"Лимфа Гнилошкуров",nil, {"Принесите 8 бутылок лимфы Гнилошкуров аптекарю Ренферрелу рядом с Гробницей."}},
[444] = {"Происхождение Гнилошкуров",nil, {"Отнесите образец лимфы Бетору Ледострелу в Подгород."}},
[445] = {"Доставка в Серебряный бор",nil, {"Отнесите находки аптекаря Юхана аптекарю Ренферрелу в Серебряном бору."}},
[446] = {"Тул Коготь Ворона",nil, {"Отнесите свиток Бетора аптекарю Ренферрелу в Серебряный бор, в селение Гробница."}},
[447] = {"Рецептура смерти",nil, {"Соберите 6 сердец медведя и 6 образцов Летящей крови и принесите их мастеру-аптекарю Фаранеллу в Подгород."}},
[448] = {"Доложиться Хадрику",nil, {"Доложите о себе верховному палачу Хадрику в поселении Гробница."}},
[449] = {"Отчет стражей смерти",nil, {"Отнесите отчет стражей смерти верховному палачу Хадрику в Гробницу."}},
[450] = {"Рецептура смерти",nil, {"Найдите в деревне Погребальных Костров записи Берарда и отнесите их в Гробницу аптекарю Ренферрелу."}},
[451] = {"Рецептура смерти",nil, {"Принесите 6 образцов мха озерного ползуна, 6 образцов мха озерного тихохода и твердую опухоль мастеру-аптекарю Фаранеллу в Подгород."}},
[452] = {"Засада в деревне",nil, {"Помогите стражу смерти Фарлии уничтожить совет деревни Погребальных Костров."}},
[453] = {"Поиски таинственного незнакомца",nil, {"Отыщите незнакомца. Его приметы:nnОн родом не из Темнолесья.nnОн – нервный тип.nnОн сбежал из Темнолесья и двинулся на запад."}},
[454] = {"После засады",nil, {"Поговорите с Мираном."}},
[455] = {"Альгазская битва",nil, {"Пройдите через Дун Альгаз.nnУбейте 8 разведчиков из клана Драконьей Пасти и 6 рубак из клана Драконьей Пасти.nnОб исполнении доложите Валстагу Сталезубу в Гавани Менетилов."}},
[456] = {"Природное равновесие",nil, {"Убейте 7 молодых ночных саблезубов и 4 молодых щетинистых вепрей и вернитесь к Опекуну Илталайну."}},
[457] = {"Природное равновесие",nil, {"Опекун Илталайн хочет, чтобы вы убили 7 облезлых ночных саблезубов и 7 щетинистых вепрей."}},
[458] = {"Защитница леса",nil, {"Найдите дриаду по имени Тариндрелла."}},
[459] = {"Защитница леса",nil, {"Соберите 8 порций мха Скверны и принесите их Тариндрелле."}},
[460] = {"В целости и сохранности",nil, {"Отнесите голову Аларика на его могилу."}},
[461] = {"Замаскированный тайник",nil, {"Отнесите Аларика к замаскированной нише."}},
[463] = {"Страж Природы",nil, {"Найдите Зеленого Стража."}},
[464] = {"Боевые знамена",nil, {"Принесите 8 боевых знамен клана Драконьей Пасти капитану Крепкому Кулаку."}},
[465] = {"Гамбит Нек'роша",nil, {"Уничтожьте катапульты Драконьей Пасти."}},
[466] = {"Поиски огневита",nil, {"Принесите 6 партий огневитовой руды пилоту Камнекожицу в Дун Морог."}},
[467] = {"Найдите Камнекожца",nil, {"Поговорите с пилотом Камнекожицем."}},
[468] = {"Отчитайтесь перед горным пехотинцем Рокхаром.",nil, {"Поговорите с горным пехотинцем Рокхаром."}},
[469] = {"Ежедневная доставка",nil, {"Отнесите стопку кроколисковых шкур кожевнику Джеймсу Халлорану в Гавань Менетилов."}},
[470] = {"Прожорливый слизнюк",nil, {"Отнимите сумку Сиды у неизвестного слизнюка возле гробницы Железноборода и верните ее хозяйке в Гавань Менетилов."}},
[471] = {"Тяжкая судьба помощника",nil, {"Принесите 6 шкур гигантских кроколисков Джеймсу Халлорану в Гавань Менетилов."}},
[472] = {"Падение Дун Модра",nil, {"Отыщите Грима Длинную Косу возле Дун Модра."}},
[473] = {"Доклад капитану Крепкому Кулаку",nil, {"Найдите капитана Крепкого Кулака и поговорите с ним."}},
[474] = {"Убить Нек'роша",nil, {"Убейте вождя Нек'роша.nnПринесите голову Нек'роша капитану Крепкому Кулаку."}},
[475] = {"Ветер тревоги",nil, {"Найдите Гароласа Талветрена в Деревне Звездного Ветра."}},
[476] = {"Порча Кривой Сосны",nil, {"Вернитесь к Атридасу Медвежьей Шкуре в Доланаар."}},
[477] = {"Пересечение границ",nil, {"Раздобудьте содержимое одного из сундуков даларанских волшебников. Деревня Погребальных Костров находится на юге."}},
[478] = {"Карты и руны",nil, {"Отнесите странную подвеску жрецу Тьмы Алистеру в Гробницу."}},
[479] = {"Расследование в Янтарной Мельнице",nil, {"Добудьте 8 подвесок Даларана и отнесите жрецу Тьмы Алистеру в Гробницу."}},
[480] = {"Ткач",nil, {"Убейте даларанского верховного мага, заберите его посох и отнесите жрецу Тьмы Алистеру в Гробницу."}},
[481] = {"Анализ Далара",nil, {"Отнесите подвеску рунной надписи Далару Ткачу Рассвета в Гробницу."}},
[482] = {"Планы Даларана",nil, {"Поговорите с жрецом Тьмы Алистером в Гробнице."}},
[483] = {"Реликвии Пробуждения",nil, {"Добудьте реликвии Пробуждения и принесите их Атридасу Медвежьей Шкуре в Доланаар."}},
[484] = {"Шкуры молодых кроколисков",nil, {"Принесите 4 шкуры молодых кроколисков Джеймсу Халлорану в Гавань Менетилов."}},
[485] = {"Найти КПХ-9/HL!",nil, {"Отнесите аварийный маяк к автономному роботу Карлса. Робот находится во Внутренних землях, в устье реки близ Осклизлой скалы."}},
[486] = {"Урсал Изверг",nil, {"Убейте Урсала Изверга и возвращайтесь к Атридасу Медвежьей Шкуре в Доланаар."}},
[487] = {"Дорога в Дарнасс",nil, {"Убейте 6 душителей из племени Кривой Сосны и найдите часового Амару Ночную Странницу возле Доланаара."}},
[488] = {"Просьба Зенна",nil, {"Принесите Зенну Грязному Копыту, стоящему недалеко от Доланаара, 3 клыка ночного саблезуба, 3 пера островной совы и 3 мотка паутины чащобного тенетника."}},
[489] = {"Искупление вины",nil, {"Соберите 3 скверношишки и отдайте их Зенну Грязному Копыту близ Доланаара."}},
[491] = {"Жезл для Бетора",nil, {"Отнесите Сплетенный жезл Бетору Ледострелу в Квартал Магов Подгорода."}},
[492] = {"Новая чума",nil, {"Отнесите особый напиток Юхана пленному горному пехотинцу."}},
[493] = {"Путешествие в предгорья Хилсбрада",nil, {"Отнесите находки аптекаря Ренферрела аптекарю Лидону в город Мельница Таррен в предгорьях Хилсбрада."}},
[494] = {"Время нанести удар",nil, {"Отправляйтесь в Мельницу Таррен и доставьте послание стража смерти Леш верховной вершительнице Дарталии."}},
[495] = {"Корона Воли",nil, {"Поговорите с Мелизарой в Мельнице Таррен в предгорьях Хилсбрада."}},
[496] = {"Эликсир Страдания",nil, {"Аптекарь Лидон из Мельницы Таррен попросил принести ему 10 языков серых медведей и немного лимфы паука-охотника."}},
[498] = {"Спасение",nil, {"Освободите Друлла и Тог'тара по поручению Круска из Мельницы Таррен."}},
[499] = {"Эликсир Страдания",nil, {"Дайте Ампи эликсир Страдания."}},
[500] = {"Награда Раздробленного Хребта",nil, {"Соберите 9 грязных бабок у огров из клана Раздробленного Хребта в Альтеракских горах. Принесите их маршалу Редпату в Южнобережье."}},
[501] = {"Эликсир боли",nil, {"Принесите 10 сосудов крови горного льва аптекарю Лидону из Мельницы Таррен."}},
[502] = {"Эликсир боли",nil, {"Напоите Стэнли эликсиром Боли."}},
[503] = {"Гол'дир",nil, {"Освободите Гол'дира и возвращайтесь к Круску в Мельницу Таррен."}},
[504] = {"Наемники клана Раздробленного Хребта",nil, {"Убейте 10 наемников из клана Раздробленного Хребта и возвращайтесь к маршалу Редпату в Южнобережье."}},
[505] = {"Убийцы из Синдиката",nil, {"Убейте 12 преступников и 8 воров Синдиката и возвращайтесь к чиновнику Генри Малебу в Южнобережье."}},
[506] = {"Наследие Блэкмура",nil, {"Отнесите собранные Гол'диром сведения Круску в Мельницу Таррен."}},
[507] = {"Лорд Алиден Перенольд",nil, {"Убейте лорда Алидена Перенольда и допросите его любовницу Элизу насчет ожерелья Тареты."}},
[508] = {"Подарок Тареты",nil, {"Верните ожерелье Тареты Круску в город Мельница Таррен."}},
[509] = {"Эликсир агонии",nil, {"Принесите 6 цветков грязного рыла аптекарю Лидону из Мельницы Таррен."}},
[510] = {"Зловещие планы",nil, {"Отнесите зловещие планы чиновнику Южнобережья Малебу."}},
[511] = {"Зашифрованное письмо",nil, {"Отнесите зашифрованное письмо хранителю мудрости Диббсу в Южнобережье."}},
[512] = {"Благородные смерти",nil, {"Принесите 7 альтеракских перстней-печаток чиновнику Малебу в Южнобережье."}},
[513] = {"Эликсир агонии",nil, {"Отнесите состав Грязного Рыла мастеру-аптекарю Фаранеллу в Подгород."}},
[514] = {"Письмо Грозовой Вершине",nil, {"Отнесите хитро зашифрованное письмо геологу Грозовой Вершине в Стальгорн."}},
[515] = {"Эликсир агонии",nil, {"Отнесите микстуру грязного рыла, крепкое зелье тролльей крови, 5 чешуек наг из клана Пронзающего Гребня и 5 глаз мурлоков из племени Рваного Плавника аптекарю Лидону в Мельницу Таррен."}},
[516] = {"Погибель Берена",nil, {"Найдите Погибель Берена, убейте 6 чернорабочих Когтя Ворона и 6 стражей Когтя Ворона, а затем вернитесь в Гробницу к жрецу Тьмы Алистеру."}},
[517] = {"Эликсир агонии",nil, {"Принесите бочонок \"Землекопского крепкого\" аптекарю Лидону из Мельницы Таррен."}},
[518] = {"Корона Воли",nil, {"Убейте 10 извергов из клана Раздробленного Хребта по поручению Мелизары из Мельницы Таррен."}},
[519] = {"Корона Воли",nil, {"Убейте Тарга, Навозера и Гломмуса и принесите их головы Мелизаре из Мельницы Таррен."}},
[520] = {"Корона Воли",nil, {"Убейте Муг'Тола и принесите его голову и корону воли Мелизаре в Мельницу Таррен."}},
[521] = {"Корона Воли",nil, {"Отнесите корону Воли Шарлиндре из Подгорода."}},
[522] = {"Контракт убийцы",nil, {"Отнесите контракт убийцы чиновнику Малебу в Южнобережье."}},
[523] = {"Смерть барона",nil, {"Принесите голову барона Вардуса чиновнику Малебу в Южнобережье."}},
[524] = {"Эликсир агонии",nil, {"Поставьте гниющий бочонок на ковер для плененных фермеров."}},
[525] = {"Все больше тайн",nil, {"Доставьте расшифрованное письмо чиновнику магистрата Южнобережья Малебу."}},
[526] = {"Слитки светлостали",nil, {"Найдите 5 слитков светлостали и вернитесь к Глорину Стальному Лбу в Болотину."}},
[527] = {"Битва за Хилсбрад",nil, {"Убейте 6 хилсбрадских батраков, 6 хилсбрадских фермеров, фермеров Рея и Гетца и возвращайтесь к Дарталии в Мельницу Таррен."}},
[528] = {"Битва за Хилсбрад",nil, {"Убейте 15 хилсбрадских крестьян и вернитесь к Дарталии в Мельницу Таррен."}},
[529] = {"Битва за Хилсбрад",nil, {"Убейте кузнеца Верринтана и 4 хилсбрадских учеников кузнеца.nnПринесите партию железа Дарталии в Мельницу Таррен."}},
[530] = {"Месть мужа",nil, {"Убейте Валдреда Морая.nnПринесите его руки Роли Андреану в Подгород."}},
[531] = {"Месть Вайрины",nil, {"Принесите голову Старины Черношкура Вайрине Быстрый Ветер в Приют странников."}},
[532] = {"Битва за Хилсбрад",nil, {"Убейте мирового судью Бернсайда, и 4 членов совета Хилсбрада. Уничтожьте хилсбрадское официальное объявление. Украдите регистрационную книгу Хилсбрада. Затем вернитесь к Дарталии в Мельницу Таррен."}},
[533] = {"Шпионская работа",nil, {"Добудьте сведения о местонахождении Гол'дира для Круска из Мельницы Таррен."}},
[535] = {"Валлик",nil, {"Принесите кружку эля из Южнобережья Вэллику в лагерь Синдиката."}},
[536] = {"На побережье",nil, {"Убейте 10 волноловов из племени Порванного Плавника и 10 оракулов из племени Порванного Плавника по поручению лейтенанта Фаррена Оринеля из Южнобережья."}},
[537] = {"Темный совет",nil, {"Убейте 4 темных магов Недремлющего Аргуса.nnПринесите голову Нагаза мировому судье Южнобережья Малебу."}},
[538] = {"Южнобережье",nil, {"Поговорите с Хранителем мудрости Диббсом в Южнобережье."}},
[539] = {"Битва за Хилсбрад",nil, {"Убейте штейгера Бондса и 10 хилсбрадских шахтеров и доложите о проделанной работе Дарталии в Мельнице Таррен."}},
[540] = {"Сохранение знаний",nil, {"Найдите 5 фолиантов и потертую кожаную книгу, называемую \"Рука Гри'лека\", и принесите их хранителю мудрости Диббсу в Южнобережье."}},
[541] = {"Битва за Хилсбрад",nil, {"Отправляйтесь в Дун Гарок и убейте 8 горных пехотинцев, 4 стрелков, 2 жрецов и капитана Железную Гору. После возвращайтесь к Дарталии в Мельницу Таррен."}},
[542] = {"Возвращение к Милтону",nil, {"Отнесите фолианты Альтерака Милтону Снопу в Штормград."}},
[543] = {"Тиара Перенольда",nil, {"Доставьте тиару Перенольда Ремингтону Риджвеллу в Штормград."}},
[544] = {"Взлом тюрьмы",nil, {"Найдите предателей и отберите у них артефакты, а потом возвращайтесь к волхву Сиянию Бездны в Мельницу Таррен."}},
[545] = {"Даларанские патрули",nil, {"Убейте 6 даларанских призывателей и 12 порабощенных элементалей. Затем возвращайтесь к волхву Сиянию Бездны в Мельницу Таррен."}},
[546] = {"Сувениры смерти",nil, {"Страж смерти Самса из Мельницы Таррен попросил принести 30 человеческих черепов из Хилсбрада."}},
[547] = {"Меч Гумберта",nil, {"Найдите меч стража смерти Гумберта в Дун Гароке и верните его владельцу в Мельнице Таррен."}},
[549] = {"Разыскиваются: сторонники Синдиката",nil, {"Убейте 10 разбойников и 10 сторожей из Синдиката и возвращайтесь за наградой к верховной вершительнице Дарталии в Мельницу Таррен."}},
[550] = {"Битва за Хилсбрад",nil, {"Отнесите запечатанное рекомендательное письмо Дарталии Вариматрасу в Подгород."}},
[551] = {"Заколдованный пергамент",nil, {"Отнесите заколдованный пергамент Хранителю мудрости Диббсу в Южнобережье."}},
[552] = {"Месть Гелькулара",nil, {"Отберите у йети жезл Гелькулара и отнесите его новобранцу Тайванду в Мельницу Таррен."}},
[553] = {"Месть Гелькулара",nil, {"Зарядите Гелькуларов жезл силой пламени Азеля, пламени Вераза и пламени Узеля.nnВонзите заряженный жезл в гробницу Гелькулара в Южнобережье."}},
[554] = {"Расшифровщик Грозовая Вершина",nil, {"Отнесите заколдованный пергамент геологу Грозовой Вершине в Стальгорн."}},
[555] = {"Нежный черепаховый суп",nil, {"Принесите 10 кусков черепашьего мяса и немного смягчающих пряностей шеф-повару Джессену в Южнобережье."}},
[556] = {"Каменные талисманы",nil, {"Отнесите 10 истертых каменных талисманов хранителю Бел'варилу из Мельницы Таррен."}},
[557] = {"Сковывающие браслеты",nil, {"Принесите 4 браслета земли хранителю Бел'варилу из Мельницы Таррен."}},
[558] = {"Автограф Джайны",nil, {"Отправляйтесь в Терамор и постарайтесь получить автограф Джайны Праудмур для своего подопечного."}},
[559] = {"Доказательство Фаррена",nil, {"Соберите 10 голов мурлоков на Западном побережье по просьбе лейтенанта Фаррена Оринеля из Южнобережья."}},
[560] = {"Доказательство Фаррена",nil, {"Отнесите мешок с головами мурлоков маршалу Редпату в Южнобережье."}},
[561] = {"Доказательство Фаррена",nil, {"Поговорите с лейтенантом Фарреном Оринелем из Южнобережья."}},
[562] = {"Ха, Штормград!",nil, {"Убейте 10 береговых охотников и 10 сирен из клана Пронзающего Гребня по просьбе лейтенанта Фаррена Оринеля из Южнобережья."}},
[563] = {"Новое назначение",nil, {"Отнесите донесение Фаррена майору Самуэльсону в Крепость Штормграда."}},
[564] = {"Серьезная угроза",nil, {"Убейте 8 горных львов и 10 крупных горных львов по поручению Даррена Малвея из Южнобережья."}},
[565] = {"Плащ из меха йети",nil, {"Принесите Бартоло Джинсетти из Южнобережья рулон шерсти, плащ жителя холмов, тонкую нить и 10 клоков меха йети."}},
[566] = {"Разыскивается: Барон Вардус",nil, {"Убейте барона Вардуса и принесите его голову верховной вершительнице Дарталии в Мельницу Таррен."}},
[567] = {"Вооружены и очень опасны!",nil, {"Верховная вершительница Дарталия из Мельницы Таррен назначила награду за убийство клерка Горация Белоконя, гражданина Уилкса, шахтера Хакетта и фермерши Калабы."}},
[568] = {"Защита Гром'гола",nil, {"Командир Агро'гош из лагеря Гром'гол поручил вам убить 15 ящеров-кнутохвостов."}},
[569] = {"Защита Гром'гола",nil, {"Командир Агро'гош из лагеря Гром'гол поручил вам убить 10 огров-громил и 5 огров-знахарей."}},
[570] = {"Чары Мок'тардина",nil, {"Предсказатель Мок'тардин из лагеря Гром'гол попросил вас принести ему 8 когтей тенебрюхих пантер и безупречный тигриный клык."}},
[571] = {"Чары Мок'тардина",nil, {"Принесите сухожилие старой гориллы предсказателю Мок'тардину в лагерь Гром'гола."}},
[572] = {"Чары Мок'тардина",nil, {"Принесите 10 перьев тропических ловцов предсказателю Мок'тардину в лагерь Гром'гол."}},
[573] = {"Чары Мок'тардина",nil, {"Предсказатель Мок'тардин из лагеря Гром'гол попросил вас принести ему воду из святого источника. Кроме того, он поручил вам убить 10 наг-исследователей."}},
[574] = {"Особые войска",nil, {"Убейте 10 диверсантов и 6 охотников за головами Курцена.nnДоложите об этом лейтенанту Дорену в Лагере повстанцев."}},
[575] = {"Спрос и предложение",nil, {"Принесите 2 шкуры большого речного кроколиска Дриззлику из Пиратской бухты."}},
[576] = {"Смотри в оба",nil, {"Диззи Одноглазый из Пиратской бухты попросил вас отобрать его глаз у пиратов Кровавого Паруса."}},
[577] = {"Добыча шкур",nil, {"Отнесите 5 шкур кроколисков-хрустогрызов Дриззлику в Пиратскую Бухту."}},
[578] = {"Камень приливов",nil, {"По поручению барона Ревилгаза из Пиратской бухты найдите остров, населенный духами Воды."}},
[579] = {"Библиотека Штормграда",nil, {"Король Андуин уделяет огромное внимание просвещению и всемерно заботится о том, чтобы с ценных книг вовремя делались списки, доступные горожанам. Все просто: принесите мне библиотечный свиток, и я выдам вам копию любой имеющейся книги."}},
[580] = {"Потерянный грог Алкача Виски",nil, {"Алкач Виски из Пиратской бухты попросил вас принести ему потерянные бутылки портвейна \"Пупелливербос\"."}},
[581] = {"Поиски Йеннику",nil, {"Принесите 9 клыков троллей из племени Кровавого Скальпа Нимбойе в лагерь Гром'гол."}},
[582] = {"Охота за головами",nil, {"Принесите Нимбойе из лагеря Гром'гол 20 сушеных голов."}},
[583] = {"Добро пожаловать в джунгли",nil, {"Поговорите с Хемингом Эрнестуэем-младшим."}},
[584] = {"Головы племени Кровавого Скальпа",nil, {"Принесите головы Ган'зулы и Неззилока и положите их в бурлящий котел в лагере Гром'гол."}},
[585] = {"Разговор с Неззилоком",nil, {"Принесите в лагерь Гром'гол трофей из Балиа'ма, трофей из Зиата'джаи и трофей из Зул'Мамве и положите их в бурлящий котел."}},
[586] = {"Разговор с Ган'зулахом",nil, {"Убейте 8 охотников, 6 охотников за головами и 4 берсерков из племени Дробителей Черепов.nnПринесите разбитые доспехи Ана'тека к бурлящему котлу в лагере Гром'гол."}},
[587] = {"Понюшка табака",nil, {"Диг из Пиратской бухты попросил принести ему 15 понюшек табаку."}},
[588] = {"Судьба Йеннику",nil, {"Поговорите с Кин'вилаем."}},
[589] = {"Поющие кристаллы",nil, {"Принесите 3 пульсирующих синих осколка Кин'вилаю в лагерь Гром'гол."}},
[590] = {"Выгодная сделка",nil, {"Одержите победу в схватке с Кальвином Монтеччи в Могильнике."}},
[591] = {"Око Разума",nil, {"Отнесите око разума Кинвилаю в лагерь Гром'гол."}},
[592] = {"Спасение Йеннику",nil, {"Отнесите Нимбойе наполненный самоцвет Души."}},
[593] = {"Наполнение самоцвета Души",nil, {"Йеннику стоит перед вами, и разум его витает далеко..."}},
[594] = {"Послание в бутылке",nil, {"Отыщите автора послания в бутылке."}},
[595] = {"Пираты Кровавого Паруса",nil, {"Первый помощник Кразз попросил вас осмотреть залив на севере от Пиратской бухты."}},
[596] = {"Ожерелье кровавых костей",nil, {"Принесите Кин'вилаю 25 ожерелий кровавых костей в лагерь Гром'гол."}},
[597] = {"Пираты Кровавого Паруса",nil, {"Отнесите находку первому помощнику Краззу в Пиратскую Бухту."}},
[598] = {"Ожерелье расколотых костей",nil, {"Принесите 25 ожерелий расколотых костей Кин'вилаю в лагерь Гром'гол."}},
[599] = {"Пираты Кровавого Паруса",nil, {"Сообщите о запланированном нападении Кровавого Паруса командиру флота Морскому Рогу."}},
[600] = {"Рудник Торговой Компании",nil, {"Принесите Шиззи Пуззыриксу из Пиратской бухты 10 синих Поющих кристаллов."}},
[601] = {"Элементали воды",nil, {"Принесите барону Ревилгазу в Пиратскую Бухту 6 браслетов элементалей воды."}},
[602] = {"Магический анализ",nil, {"Отнесите браслеты элементалей воды верховному магу Ансарему Руноплету в Даларан."}},
[603] = {"Ключ Ансарема",nil, {"Поговорите с Кателиной в Пиратской бухте."}},
[604] = {"Пираты Кровавого Паруса",nil, {"Командир флота Морской Рог попросил вас убить 10 висельников из шайки Кровавого Паруса и принести ему карты и приказы Кровавого Паруса."}},
[605] = {"Осколки Поющих кристаллов",nil, {"Принесите Шиззи Пуззыриксу 10 осколков Поющих кристаллов."}},
[606] = {"Напугать трусишку",nil, {"Отнесите 5 потрохов горилл из Мглистой долины Филиппу \"Трусишке\" в Пиратскую Бухту."}},
[607] = {"Возвращение к МакКинли",nil, {"Отнесите плату Трусишки \"Морскому волку\" Маккинли."}},
[608] = {"Пираты Кровавого Паруса",nil, {"Командир флота Морской Рог попросил вас убить капитана Штиллеца, капитана Кильвателя и командира флота Фираллона."}},
[609] = {"Должники вуду",nil, {"Принесите деревянную ногу Моури, золотую подзорную трубу Джон-Джона и огромное кольцо Чаки \"Морскому волку\" Маккинли в Пиратскую Бухту."}},
[610] = {"\"Красавчик\" Дункан",nil, {"Отберите меч Кателины у \"Красавчика\" Дункана."}},
[611] = {"Проклятье приливов",nil, {"Уничтожьте Алтарь Приливов при помощи меча Кателины, убейте Газбана и принесите Камень Приливов и меч Кателины барону Ревилгазу."}},
[613] = {"Вскрыть ногу Моури",nil, {"Принесите \"Морскому Волку\" Маккинли из Пиратской бухты ключ Моури."}},
[614] = {"Сундук капитана",nil, {"Верните Гекельберри Шмотту из Пиратской бухты его сундук."}},
