[743] = {"Опасности Ярости Ветра",nil, {"Принесите 8 когтей гарпий из стаи Ярости Ветра Руулу Орлиному Когтю в деревню Кровавого Копыта."}},
[744] = {"Подготовка к церемонии",nil, {"Добудьте 6 лазурных перьев и 6 бронзовых перьев, и принесите их Эйану Орлиному Когтю в Громовой утес."}},
[745] = {"Недобрые соседи",nil, {"Убейте 10 дубильщиков, 8 шкуродеров и 5 браконьеров из стаи Блеклогривов и возвращайтесь к Бейну Кровавому Копыту в деревню Кровавого Копыта."}},
[746] = {"Дворфийские делишки",nil, {"Соберите 5 кирок геолога.nnСломайте их в кузнице. nnОтнесите 5 сломанных инструментов Бейну Кровавому Копыту в деревню Кровавого Копыта."}},
[747] = {"Охота начинается",nil, {"Принесите 7 перьев равнинных долгоногов и 7 кусков мяса равнинных долгоногов Груллу Соколиному Ветру."}},
[748] = {"Отравленная вода",nil, {"Принесите 6 лап койотов и 4 когтя равнинного долгонога Муллу Громовому Рогу в деревню Кровавого Копыта."}},
[749] = {"Разграбленный караван",nil, {"Морин Ловец Облаков попросил вас изучить содержимое ящиков с припасами в разграбленном караване."}},
[750] = {"Охота продолжается",nil, {"Принесите 10 шкур горных пум Груллу Соколиному Ветру."}},
[751] = {"Разграбленный караван",nil, {"Отнесите бумаги Торговой компании Морину Ловцу Облаков, которого можно найти неподалеку от Деревни Кровавого Копыта."}},
[752] = {"Скромная просьба",nil, {"Поищите матушку вождя Соколиный Ветер у источника к юго-востоку от Лагеря Нараче."}},
[753] = {"Скромная просьба",nil, {"Наберите кувшин воды из колодца.nnОтнесите кувшин вождю Соколиному Ветру в Лагерь Нараче, который находится к северо-западу от источника."}},
[754] = {"Очищение колодца Заиндевевшего Копыта",nil, {"Очистите колодец Заиндевевшего Копыта с помощью тотема очищения и возвращайтесь к Муллу Громовому Рогу."}},
[755] = {"Обряды Матери-Земли",nil, {"Отправьтесь к провидцу Серой Пасти, который живет на юге от Лагеря Нараче."}},
[756] = {"Тотем Громового Рога",nil, {"Принесите 6 когтей охотников прерий и 6 когтей пум плоскогорья Муллу Громовому Рогу."}},
[757] = {"Обряд Силы",nil, {"Убивайте дыбогривов в Ежевичной лощине и принесите 12 их поясов вождю Соколиному Ветру в Лагерь Нараче."}},
[758] = {"Очищение колодца Громового Рога",nil, {"Очистите колодец Громового Рога на севере от деревни с помощью тотема очищения и возвращайтесь к Муллу Громовому Рогу."}},
[759] = {"Тотем Буйногривых",nil, {"Отнесите 8 зубов могучего волка прерий Муллу Громовому Рогу в Деревню Кровавого Копыта."}},
[760] = {"Очищение колодца Буйногривых",nil, {"Очистите колодец Буйногривых с помощью тотема очищения и возвращайтесь к Муллу Громовому Рогу."}},
[761] = {"Охота на перепелятников",nil, {"Принесите 8 трофейных перьев перепелятника Харкену Тотему Ветра в Деревне Кровавого Копыта."}},
[762] = {"Посланник зла",nil, {"Убейте посла Инфернуса в Крепости Ангор и принесите доказательство его смерти советнику Белграму в Стальгорн."}},
[763] = {"Обряды Матери-Земли",nil, {"Отнесите тотем Соколиного Ветра Бейну Кровавому Копыту в Деревню Кровавого Копыта. Следуйте по дороге из Лагеря Нараче."}},
[764] = {"Торговая компания",nil, {"Убейте 14 рабочих Торговой компании и 6 бригадиров Торговой компании по поручению Морина Ловца Облаков из Деревни Кровавого Копыта."}},
[765] = {"Бригадир Шумовик",nil, {"Убейте бригадира Шумовика и принесите его планшет Морину Ловцу Облаков в Деревню Кровавого Копыта."}},
[766] = {"Маззранач",nil, {"Принесите сердце койота, бедренную кость пумы плоскогорья, чешую долгонога и потроха перепелятника Мауру Заклинателю Дождя в деревню Кровавого Копыта."}},
[767] = {"Обряд прозрения",nil, {"Поговорите с Зарельманом Две Луны в Деревне Кровавого Копыта."}},
[768] = {"Добывание шкур",nil, {"Принесите 12 кусков тонкой кожи Верену Долгоногу в Громовой Утес."}},
[769] = {"Сумка из шкуры кодо",nil, {"Принесите 4 куска легкой кожи и 4 мотка грубой нити Верену Высокому Страннику в Громовой Утес."}},
[770] = {"Поцарапанный демоном плащ",nil, {"Отыщите кого-нибудь, кто слышал о Призрачном Вое."}},
[771] = {"Обряд прозрения",nil, {"Принесите 2 камня у источника и 2 янтарных семени Зарельману Две Луны в Деревню Кровавого Копыта."}},
[772] = {"Обряд прозрения",nil, {"Выпейте воду Провидцев перед костром племени в Деревне Кровавого Копыта и следуйте за видением."}},
[773] = {"Обряд мудрости",nil, {"Отправляйтесь в Красные Скалы на востоке от Громового Утеса и поговорите с духом предков."}},
[775] = {"Путешествие в Громовой Утес",nil, {"Поговорите с Кэрном Кровавым Копытом в Громовом Утесе."}},
[776] = {"Обряды Матери-Земли",nil, {"Кэрн Кровавый Рог из Громового Утеса попросил принести ему рог Арра'чеи."}},
[777] = {"Сложная задача",nil, {"Принесите шестеренку №5 Лотвилю Вериатусу."}},
[778] = {"Сложная задача",nil, {"Победите стража Фам'ретора и верните оковы стихий Лотвилю Вериатусу."}},
[779] = {"Печать Земли",nil, {"Перед вами печать Земли.В ней вы видите три потайные ниши для аметистового, опалового и алмазного рунных камней."}},
[780] = {"Боевые вепри",nil, {"Принесите 8 боков и 8 рыл боевых вепрей Груллу Соколиному Ветру."}},
[781] = {"Нападение на Лагерь Нараче",nil, {"Отнесите план нападения племени Дыбогривов вождю Соколиному Ветру в Лагерь Нараче."}},
[782] = {"Разорванные союзы",nil, {"Убейте главаря То'груна и принесите знак Земли Горну в Каргат."}},
[783] = {"Внутренняя угроза",nil, {"Поговорите с маршалом Макбрайдом."}},
[784] = {"Бей предателей!",nil, {"Убейте 10 моряков Кул-Тираса, 8 стражей бухты Кул-Тираса и лейтенанта Бенедикта и возвращайтесь к Га'Току на Колючий Холм."}},
[785] = {"Стратегический союз",nil, {"Разыщите Лара Ночного Клыка на западной окраине деревни Сен'джин."}},
[786] = {"Предотвращение агрессии племени Колкар",nil, {"Лар Ночной Клык из деревни Сен'джин хочет, чтобы вы уничтожили 3 плана нападения, которые хранятся в лагере кентавров, что в Утесе Колкара."}},
[787] = {"Новая Орда",nil, {"Зайти в Логово к Горнеку."}},
[788] = {"Кабаньи клыки",nil, {"Убейте 10 пятнистых вепрей и вернитесь в Логово к Горнеку."}},
[789] = {"Жала скорпидов",nil, {"Добудьте 10 хвостов рабочих скорпидов и принесите их в Логово Горнеку."}},
[790] = {"Саркот",nil, {"Убейте Саркота и принесите его коготь Хана'зуа."}},
[791] = {"Больше сумок – больше добычи!",nil, {"Принесите 8 лоскутов холста Фурлу Хмурому Лбу, стоящему на сторожевой башне рядом с Колючим Холмом."}},
[792] = {"Злобные фамильяры",nil, {"Убейте 12 злобных фамильяров и вернитесь к Зурите Востроглазой, которая ждет вас у Логова."}},
[793] = {"Разорванные союзы",nil, {"С помощью знака Земли активируйте алмазный, опаловый и аметистовый столпы и извлеките рунные камни. nnПоместите рунные камни на печать Земли и освободите драконов Бича Тьмы и Гематуса. nnУбейте драконов и принесите Горну наручники Чернохлыста, цепи Гематуса и Знак Земли."}},
[794] = {"Медальон клана Пылающего Клинка",nil, {"Принесите медальон клана Пылающего Клинка Зурите Востроглазой, стоящей у Логова."}},
[795] = {"Печать Земли",nil, {"Перед вами печать Земли. В ней вы видите три потайные ниши для аметистового, опалового и алмазного рунных камней."}},
[804] = {"Саркот",nil, {"Расскажите Горнеку о том, что Хана'зуа в беде."}},
[805] = {"Деревня Сен'джин",nil, {"Поговорите с мастером Гадрином в деревне Сен'джин."}},
[806] = {"Сгущаются черные тучи",nil, {"Принесите коготь Пшикса Оргнилу Душегрызу из Колючего Холма."}},
[808] = {"Череп Миншины",nil, {"Украдите череп Миншины из магического круга на островах Эха.nnПринесите его мастеру Гадрину в деревне Сен'джин."}},
[809] = {"Ак'Зелот",nil, {"Поговорите с Ак'Зелотом в Степях."}},
[812] = {"Нужда в исцелении",nil, {"Найдите в Оргриммаре Кор'гхана и добудьте сыворотку от яда ядохвоста. Принесите сыворотку Ринагу на северо-западной границе Даротара."}},
[813] = {"Поиски противоядия",nil, {"Принесите 4 ядовитых железы ядохвоста Кор'гхану в Оргриммаре."}},
[815] = {"Хорошенький омлет",nil, {"Принесите 3 яйца кнутохвостов повару Торке в Колючий Холм."}},
[816] = {"Пропавший, но не забытый",nil, {"Принесите известие о судьбе, постигшей Крона, Майше Тор'крен в усадьбу к северо-западу от Колючего Холма."}},
[817] = {"Полезная добыча",nil, {"Принесите 4 шкуры дуротарских тигров Вель'рину Клыку в деревне Сен'джин."}},
[818] = {"Растворитель",nil, {"Принесите 4 невредимых глаза макрур и 8 фиалов со слизью крабов мастеру Ворналу в деревне Сен'джин."}},
[819] = {"Пустой бочонок Чэня",nil, {"Отыщите кого-нибудь, кто знает о пустом бочонке Чэня."}},
[821] = {"Пустой бочонок Чэня",nil, {"Принесите 5 клыков равнинного льва, 5 почек долгонога и 1 рог рокочущей ящерицы пивовару Дрону в Кабестан."}},
[822] = {"Пустой бочонок Чэня",nil, {"Принесите 5 грозовых желез, 1 слюнную железу грозового змея и 1 печень кодо пивовару Дрону в Кабестан."}},
[823] = {"Явиться к Оргнилу",nil, {"Поговорите с Оргнилом Душегрызом на Колючем Холме."}},
[824] = {"Дже'неу из Служителей Земли",nil, {"Отнесите оскверненную водяную сферу Дже'неу Санкри в форт Зорам'гар в Ясеневом лесу."}},
[825] = {"Обломки кораблекрушения",nil, {"Га'Ток с Колючего Холма просит вас принести ему 3 комплекта инструментов гномов, найти которые можно в обломках кораблей у побережья."}},
[826] = {"Залазан",nil, {"Убейте Залазана, убейте 8 троллей-вудуистов и 8 проклятых троллей.nnПринесите голову Залазана Гадрину."}},
[827] = {"Скала Черепа",nil, {"Отберите у фанатиков из Скалы Черепа обжигающие ошейники.nnПринесите их в лагерь Маргозу."}},
[828] = {"Маргоз",nil, {"Поговорите с Маргозом."}},
[829] = {"Нииру Огненный Клинок",nil, {"Отнесите один обжигающий ошейник Нииру Огненному Клинку в Оргриммаре."}},
[830] = {"Приказы адмирала",nil, {"Отнесите приказы адмирала Праудмура Га'току на Колючий Холм."}},
[831] = {"Приказы адмирала",nil, {"Доставьте приказы адмирала Праудмура Вол'джину в зал приемов Тралла в Оргриммаре."}},
[832] = {"Пылающие тени",nil, {"Отнесите этот амулет Нииру Огненному Клинку."}},
[833] = {"Священное погребение",nil, {"Сказитель Тотем Дождя попросил вас убить 8 злоумышленников из племени Дыбогривов в Красных cкалах."}},
[834] = {"Ветра пустыни",nil, {"Добудьте 5 мешков припасов и верните их Резлаку близ Оргриммара."}},
[835] = {"Обеспечение безопасного сообщения",nil, {"Убейте 12 дикарок из стаи Пыльного Ветра и 8 грозовых ведьм из стаи Пыльного Ветра, обитающих возле Сухой лощины, по просьбе Резлака."}},
[836] = {"Спасти КПХ-9/HL!",nil, {"Сопроводите OOX-09/HL на побережье за Обзорными скалами, а потом доложитесь Карлсу Пропеллерзу в Пиратской бухте."}},
[837] = {"Вторжение",nil, {"Убейте 4 свинобразов из племени Иглогривых, 4 разведчиков из племени Иглогривых, 4 пылебегов из племени Иглогривых и 4 боевых стражей из племени Иглогривых по просьбе Га'Тока из Колючего Холма."}},
[838] = {"Некроситет",nil, {"Поговорите с аптекарем Дитерсом из Бастиона, что в Западных Чумных землях."}},
[840] = {"Новобранец Орды",nil, {"Ступайте из Колючего Холма в Степи по западной дороге. Перейдите через мост.nnОстановитесь у орочьей заставы за мостом.nnОтдайте Каргалу Боевому Шраму ваше письмо о найме."}},
[841] = {"Еще один источник энергии?",nil, {"Принесите 10 бурдюков с водой Скитальцев Пустыни главному инженеру Чепухастеру в Прибамбасск в обмен на еще один источник энергии."}},
[842] = {"Рекрутский набор Перекрестка",nil, {"Ступайте по западной дороге от форпоста Каргала.nnНа развилке поверните налево и ступайте на юг.nnНайдите Сергру Черный Шип у перекрестка дорог в Перекрестке."}},
[843] = {"Ответный удар Ганна",nil, {"Ганн Каменное Копье попросил вас убить 15 бейл'данских землекопов и 5 бейл'данских старшин.nnПринесите Ганну Каменному Копью записи Хазгорма."}},
[844] = {"Равнинные долгоноги",nil, {"Принесите 7 клювов долгоногов Сергре Черный Шип в Перекресток."}},
[845] = {"Жевры",nil, {"Убивайте жевр-бегунов, пока не наберете 4 пары копыт жевры для Сергры Черный Шип в Перекрестке."}},
[846] = {"Месть Ганна",nil, {"Ганн Каменное Копье попросил вас принести ему 6 сосудов с нитроглицерином, 6 пучков древесной сердцевины и 6 образцов натриевой селитры."}},
[847] = {"Коварство ящера",nil, {"Поговорите с Тор'Ганом."}},
[848] = {"Споры грибов",nil, {"Принесите аптекарю Хелбриму в Перекресток 4 порции спор грибов."}},
[849] = {"Месть Ганна",nil, {"Уничтожьте ветролет в Бейл Модане и возвращайтесь к Ганну Каменному Копью."}},
[850] = {"Главари племени Колкар",nil, {"Принесите Регтару Вратам Смерти, живущему на западе от Перекрестка, голову Барака."}},
[851] = {"Верог Дервиш",nil, {"Принесите Регтару Вратам Смерти, живущему на западе от Перекрестка, голову Верога."}},
[852] = {"Хэрзул Кровавая Отметина",nil, {"Принесите Регтару Вратам Смерти, живущему к западу от Перекрестка, голову Хэрзула."}},
[853] = {"Аптекарь Зама",nil, {"Отнесите топленые споры аптекарю Заме в Громовой Утес за отведенное время."}},
[854] = {"Путешествие в Перекресток",nil, {"Поговорите с Тхорком на Перекрестке в Степях."}},
[855] = {"Наручи кентавров",nil, {"Принесите Регтару Вратам Смерти, живущему к западу от Перекрестка, 15 наручей кентавров."}},
[857] = {"Слеза Лун",nil, {"Фриглай Изгнанник попросил вас вернуть ему слезу Лун."}},
[858] = {"Зажигание",nil, {"Добыв ключ зажигания, отнесите его Визлкранку."}},
[860] = {"Сергра Черный Шип",nil, {"Поговорите с Сергрой Черный Шип в Перекрестке."}},
[861] = {"Путь охотника",nil, {"Принесите 4 когтя хищника плоскогорья Мелору Каменному Копыту в Громовой Утес."}},
[862] = {"Похлебка из пещерной крысы",nil, {"Принесите 8 пещерных крыс Грубу на востоке от Перекрестка."}},
[863] = {"Бегство",nil, {"Охраняйте Зигзага и похищенный гоблинский резак по пути к Пароклапану в Кабестан."}},
[864] = {"Возвращение к аптекарю Зинг",nil, {"Отнесите запечатанный набор для полевых испытаний аптекарю Зинг в Подгород."}},
[865] = {"Рога ящера",nil, {"Соберите 5 невредимых рогов ящера с ящерочешуйчатых смертехватов и отнесите их Мебоку Миззриксу в Кабестан."}},
[866] = {"Образцы корней",nil, {"Принесите Мебоку Миззриксу из Кабестана 8 образцов корней."}},
[867] = {"Гарпии-налетчики",nil, {"Соберите 8 когтей Зачарованного Крыла.nnПринесите их Дарсоку Точному Ножу на Перекресток."}},
[868] = {"Охота за яйцами",nil, {"Принесите 12 яиц силитидов и вспарывающий коготь Коррану в Перекресток."}},
[869] = {"Ящеры-воры",nil, {"Принесите 12 голов ящеров Газрогу в Перекресток."}},
[870] = {"Забытые пруды",nil, {"Отыщите источник силы и доложите о нем Тонге Руническому Тотему."}},
[871] = {"Прекратить набеги",nil, {"Убейте 8 водоискателей, 8 терноплетов и 3 охотников племени Иглогривых и возвращайтесь к Тхорку с Перекрестка."}},
[872] = {"Конец бесчинствам",nil, {"Убейте 8 геомантов и 8 защитников из племени Иглогривых и Кринига Грознорыла и возвращайтесь к Тхорку из Перекрестка."}},
[873] = {"Иша Авак",nil, {"Принесите сердце Иши Авака Марен Небесной Провидице."}},
[874] = {"Марен Небесная Провидица",nil, {"Поговорите с Марен Небесной Провидицей."}},
[875] = {"Лейтенанты гарпий",nil, {"Соберите 6 колец лейтенантов гарпий с душегубок из стаи Зачарованного Крыла и возвращайся к Дарсоку Быстрому Ножу."}},
[876] = {"Серена Кровавое Перо",nil, {"Убейте Серену Кровавое Перо и принесите ее голову Дарсоку Точному Ножу на Перекресток."}},
[877] = {"Застывший оазис",nil, {"Внимательно осмотрите Застывший оазис и возвращайтесь к Тонге Рунному Тотему в Перекресток."}},
[878] = {"Воюющие племена",nil, {"Убейте 6 водоискателей, 12 терноплетов и 12 геомантов из племени Дыбогривов и возвращайтесь к Кривозубу в Лагерь Таурахо в Степях."}},
[879] = {"Предатель в наших рядах",nil, {"Убейте Нака, Куза и Лока Оркобоя и принесите их черепа Кривозубу в Лагерь Таурахо в Степях."}},
[880] = {"Измененные существа",nil, {"Принесите 8 измененных панцирей зубастых черепах Тонге Руническому Тотему в Перекресток."}},
[881] = {"Ичияки",nil, {"Принесите шкуру Ичияки Сергре Черный Шип на Перекресток."}},
[882] = {"Ишамухал",nil, {"Принесите клык Ишамухала Джорну Искателю Неба в Лагерь Таурахо."}},
[883] = {"Лакота'мани",nil, {"Принесите копыто Лакота'мани Джорну в Лагерь Таурахо."}},
[884] = {"Оватанка",nil, {"Принесите хвостовой зубец Оватанки Джорну Искателю Неба в Лагерь Таурахо."}},
[885] = {"Ваште Пауни",nil, {"Отнесите перо Ваште Пауни Джорну Заклинателю Небес в Лагерь Таурахо."}},
[886] = {"Оазисы Степей",nil, {"Поговорите на Перекрестке с Тонгой Руническим Тотемом."}},
[887] = {"Флибустьеры Южных морей",nil, {"Убейте 12 грабителей Южных морей и 6 канониров Южных морей по просьбе Газула из Кабестана."}},
[888] = {"Украденное добро",nil, {"Верните Газлоу из Кабестана посылку с сапогами и линзу от телескопа."}},
[889] = {"Дух ветра",nil, {"Принеси Кривозубу 10 кровавых осколков, и он одарит тебя большой скоростью. Ты сможешь перегнать жевр, и даже ветер тебе позавидует.nnПодействует это недолго, но облегчит странствия по Степям."}},
[890] = {"Пропавший груз",nil, {"Отнесите учетную книжку Газлоу управляющему пристанью Головокружилкинсу."}},
[891] = {"Пушки крепости Северной Стражи",nil, {"Капитан Тало'тас Сияющий поручил вам собрать 10 тераморских медалей и убить капитана Гордостанную, канонира Вессана и канонира Смайта."}},
[892] = {"Пропавший груз",nil, {"Верните Газулу из Кабестана его регистр."}},
[893] = {"Предпочитаемое оружие",nil, {"Принесите стилет Иглогривых, обгоревший жезл Иглогривых и боевой щит Иглогривых Таттернаку Стальному Горну в Лагерь Таурахо в Степях."}},
[894] = {"Самофланж",nil, {"Получите доступ к терминалу в исследовательском центре Торговой Компании."}},
[895] = {"Разыскивается: Барон Дольноберег",nil, {"Принесите Газулу в Кабестан голову барона Дольноберега."}},
[896] = {"Удача шахтера",nil, {"Заберите изумруд \"Кошачий глаз\" у одного из надзирателей или головорезов Торговой компании и отдайте его управляющему пристанью Головокружилкинсу в Кабестане."}},
[897] = {"Жнец",nil, {"Принесите голову Жнеца Джорну Заклинателю Небес."}},
[898] = {"Свобода!",nil, {"Проводите Гилтареса Огненный Цветок к капитану Сияющему в Кабестан."}},
[899] = {"Огонь ненависти",nil, {"Принесите Манкрику с Перекрестка 60 клыков иглосвинов из племени Дыбогривых."}},
[900] = {"Самофланж",nil, {"Закройте топливный распределительный клапан, перепускной клапан и главный клапан управления, а потом снова попытайтесь воспользоваться панелью управления."}},
[901] = {"Самофланж",nil, {"Возьмите у ремонтника Угрелова ключ от терминала и отоприте им панель управления."}},
[902] = {"Самофланж",nil, {"Отнесите самофланж Пароклапану в Кабестан."}},
[903] = {"Хищники Степей",nil, {"Принесите 7 когтей хищников саванны Сергре Черный Шип на Перекресток."}},
[905] = {"Разъяренные смертехваты",nil, {"Поохотьтесь на яркочешуйчатых ящеров и соберите с них перья. Отнесите перья в 3 гнезда когтедеров. Возвращайтесь к Сергре Черный Шип в Перекресток."}},
[906] = {"Предатель в наших рядах",nil, {"Отнесите череп Лока Тхорку на Перекресток."}},
[907] = {"Разъяренные рокочущие ящерицы",nil, {"Отнесите 3 порции крови рокочущей ящерицы Джорну Искателю Неба в Лагерь Таурахо."}},
[908] = {"Среди руин",nil, {"Принесите глубинный сердечник Дже'неу Санкри в форт Зорам'гар в Ясеневый лес."}},
[909] = {"Барон Акванис",nil, {"Принесите странную водяную сферу Дже'неу Санкри в форт Зорам'гар в Ясеневом лесу."}},
[910] = {"В порту",nil, {"Отведите своего подопечного в порт Кабестана в Степях."}},
[911] = {"Врата пограничья",nil, {"Отведите своего подопечного к Заставе Мор'шан в северной части Степей на границе с Ясеневым лесом."}},
[913] = {"Крик грозового змея",nil, {"Отыщите и убейте грозового змея. Принесите его крылья Джорну Заклинателю Небес в Лагерь Таурахо."}},
[914] = {"Повелители Змей",nil, {"Принесите камни Кобрана, Анакондры, Пифаса и Серпентиса Наре Буйногривой, на Громовой Утес."}},
[915] = {"Женщинам – цветы, детям -...",nil, {"Купите своему подопечному клубничное мороженое. Кажется, мальчик предпочитает мороженое Тигуле и Форора."}},
[916] = {"Яд чащобного паука",nil, {"Принесите 10 ядовитых желез чащобных тенетников Гилшалану Буйному Ветру, стоящему у Альдрассила."}},
[917] = {"Яйцо чащобного паука",nil, {"Принесите яйцо чащобного тенетника Гилшалану, стоящему у Альдрассила."}},
[918] = {"Саженцы древесника",nil, {"Принесите 8 саженцев древесников Деналану к озеру Аль'Амет."}},
[919] = {"Ростки древесника",nil, {"Принесите 12 ростков древесника Деналану к озеру Аль'Амет."}},
[920] = {"Зов Тенарона",nil, {"Поговорите с Тенароном Хваткой Бури. Он ждет вас на вершине Альдрассила в Тенистой долине."}},
[921] = {"Венец Земли",nil, {"Наполните хрустальный фиал и отнесите его Тенарону Хватке Бури, обитающему на вершине Альдрассила."}},
[922] = {"Релиан Зеленый Костер",nil, {"Принесите росток древесника Релиану Зеленому Костру в Дарнасс."}},
[923] = {"Опухоли",nil, {"Принесите 5 мшистых опухолей Релиану Зеленому Костру в Дарнасс."}},
[924] = {"Демоново семя",nil, {"Возьмите треснувший камень силы, возложите его на алтарь Огня до окончания его действия и возвращайтесь к Ак'Зелоту."}},
[925] = {"Отпечаток копыта Кэрна",nil, {"Отправляйтесь в Громовой Утес и постарайтесь получить отпечаток копыта Кэрна Кровавого Копыта для своего подопечного."}},
[926] = {"Треснувший камень силы",nil, {"Эти камни исполнены магической энергии, но в каждом из них есть умышленный изъян. Они не слишком стабильные, и если взять их со стола, они быстро поглотят сами себя."}},
[927] = {"Сердце, поросшее мхом",nil, {"Принесите поросшее мхом сердце Деналану к озеру Аль'Амет."}},
[928] = {"Венец Земли",nil, {"Отнесите частично наполненный сосуд Коритрасу Лунному Гневу в Доланаар."}},
[929] = {"Венец Земли",nil, {"Наполните нефритовый фиал и принесите его Коритрасу Лунному Гневу в Доланаар."}},
[930] = {"Светящийся плод",nil, {"Принесите светящийся плод Деналану к озеру Аль'Амет."}},
[931] = {"Мерцающий росток",nil, {"Принесите мерцающий росток Деналану к озеру Аль'Амет."}},
[932] = {"Извращенная ненависть",nil, {"Убейте лорда Меленаса и принесите его голову Таллонкаю Гибкому Корню в Доланаар."}},
[933] = {"Венец Земли",nil, {"Наполните турмалиновый фиал и вернитесь к Коритрасу Лунному Гневу в Доланаар."}},
[934] = {"Венец Земли",nil, {"Наполните аметистовый фиал и принесите его Коритрасу Лунному Гневу в Доланаар."}},
[935] = {"Венец Земли",nil, {"Отнесите наполненный сосуд верховному друиду Фэндралу Оленьему Шлему в Дарнасс."}},
[936] = {"Помощь верховному друиду Руническому Тотему",nil, {"Поговорите с верховным друидом Хамуулом Руническим Тотемом на Уступе Древних Громового Утеса."}},
[937] = {"Зачарованная поляна",nil, {"Соберите 6 поясов Кровавых перьев и принесите из часовому Арании Проблеску Солнца на Поляну Оракула."}},
[938] = {"Туманна",nil, {"Проводите Туманну к Стражу Арании Проблеску Солнца к лунному колодцу близ Древа-оракула."}},
[939] = {"Флейта Ксаварика",nil, {"Принесите флейту Ксаварика и 5 образцов уз Скверны нефритового огня Эридане Грусти Ветра в южную часть Оскверненного леса."}},
[940] = {"Тельдрассил",nil, {"Передайте отчет древа-оракула верховному друиду Фэндралу Оленьему Шлему в Дарнасс."}},
[941] = {"Сердце-семя",nil, {"Посадите запятнанное сердце в кадку Деналана."}},
[942] = {"Рассеянный геолог",nil, {"Отвезите таинственную окаменелость археологу Бутылопузу в Гавань Менетилов."}},
[943] = {"Рассеянный геолог",nil, {"Археолог Бутылопуз в Гавани Менетилов хочет, чтобы вы принесли ему камень Релу и окаменелость Бутылопуза."}},
[944] = {"Меч Властителя",nil, {"Соберите информацию, а затем используйте фиал виденья для создания гадальной чаши. С помощью чаши поговорите с Ону."}},
[945] = {"Спасение Тирелин",nil, {"Помогите Тирелин спастись, а потом сообщите Тересиль у святилища Эссины, что ее сестра в безопасности."}},
[947] = {"Грибы из пещеры",nil, {"Принесите 5 ножек струпника и 1 мертвянку Баритасу Лунному Сумраку в Аубердин."}},
[948] = {"Ону",nil, {"Поговорите с Ону в Роще Древних."}},
[949] = {"Лагерь Сумеречного Молота",nil, {"Найдите подсказку в лагере клана Сумеречного Молота, расположенном у Меча Властителя."}},
[950] = {"Возвращение к Ону",nil, {"Принесите Ону лист с безумными письменами."}},
[951] = {"Реликвии Матистры",nil, {"Принесите Ону в Рощу Древних 6 реликвий Матистры."}},
[952] = {"Роща Древних",nil, {"Доставьте письмо Фэндрала Ону в Рощу Древних на Темные берега к югу от Аубердина."}},
[953] = {"Падение Амет'Арана",nil, {"Изучите таблички, рассказывающие об Амет'Аране и его падении, а затем возвращайтесь к часовому Тише Лунный Клинок, находящейся на Темных берегах."}},
[954] = {"Башал'Аран",nil, {"Найдите источник странного синего свечения в руинах Башал'Арана."}},
[955] = {"Башал'Аран",nil, {"Соберите 8 серег греллей для Астериона в Башал'Аране."}},
[956] = {"Башал'Аран",nil, {"Добудьте древнюю печать из лунного камня и принесите ее Астериону в Башал'Аран."}},
[957] = {"Башал'Аран",nil, {"Уничтожьте древнюю печать из лунного камня у древнего огня в Амет'Аране, затем вернитесь к Астериону в Башал'Аран."}},
[958] = {"Инструменты высокорожденных",nil, {"Добудьте 7 реликвий высокорожденных для Тандриса Ветропряда в Аубердине."}},
[959] = {"Неприятности в порту",nil, {"Оператор крана Биггльфузз из Кабестана попросил вас отнять бутылку портвейна 99-летней выдержки у Безумного Магглиша, который прячется в Пещерах Стенаний."}},
[960] = {"Размышление Ону",nil, {"Я размышляю над твоим заданием, Цитромон. Размышляю над причинами присутствия здесь клана Сумеречного Молота и нагов.nnКогда будешь готов, используй фиал виденья для создания гадальной чаши. Затем свяжись со мной с ее помощью.nnЕсли ты потерял свой фиал виденья, то у меня есть еще."}},
[961] = {"Размышление Ону",nil, {"Обнаружение культистов клана Сумеречного Молота беспокоит меня. Я должен поразмыслить об их намерениях...nnКогда у тебя будет больше информации, поговори со мной посредством гадальной чаши. Если тебе для создания чаши нужен фиал виденья, то... у меня есть еще."}},
[962] = {"Змеецвет",nil, {"Аптекарша Зама из Громового Утеса просит вас принести ей 10 змеецветов."}},
[963] = {"Во имя вечной любви",nil, {"Освободите дух Анайи Вестницы Рассвета и принесите ее подвеску Кириллиану Белому Когтю в Аубердин."}},
[964] = {"Фрагменты скелетов",nil, {"Принесите 15 фрагментов скелетов аптекарю Дитерсу в Бастион в Западных Чумных землях."}},
[965] = {"Башня Алталакса",nil, {"Найдите Балтула Удар Тени возле башни Алталакса на Темном Берегу."}},
[966] = {"Башня Алталакса",nil, {"Соберите 4 свитка истрепанного пергамента для Балтула Удара Тени возле башни Алталакса."}},
[967] = {"Башня Алталакса",nil, {"Доставьте письмо Балтула Делгрену Очистителю в Ясеневый лес."}},
[968] = {"\"Подземные Силы\"",nil, {"Принесите книгу \"Подземные Силы\" Геррику Костохвату в Заброшенный грот в Стальгорне."}},
[969] = {"Да пребудет с тобой удача!",nil, {"Соберите 4 кристалла великанов Ледяного Молота для знахарки Мау'ари из Круговзора."}},
[970] = {"Башня Алталакса",nil, {"Добудьте светящийся самоцвет Души и вернитесь к Делгрену Очистителю на заставу Мейстры."}},
[971] = {"Знание в пучине",nil, {"Принесите манускрипт Лоргалиса Геррику Костохвату в Заброшенный грот в Стальгорне."}},
[972] = {"Сапта воды",nil, {"Возьми сапту воды и отыщи оскверненную заводь в Серебряном бору. Я возлагаю на тебя большие надежды, Ахахаха."}},
[973] = {"Башня Алталакса",nil, {"Принесите фолиант Илкруда Магтрулла Делгрену Очистителю на заставу Мейстры."}},
[974] = {"Найти источник",nil, {"Выясните, где находится точка самой высокой температуры на Вулкане Огненного Венца, по просьбе Кракла из Кратера Ун'Горо. nnДля измерения температуры щелкните по термометру правой кнопкой мыши. Проверяйте все жаркие точки, пока не будет обнаружена точка с самой высокой температурой."}},
[975] = {"Сокровище Мау'ари",nil, {"Еще раз поговорите со знахаркой Мау'ари через минуту, чтобы получить счастливый оберег."}},
[976] = {"Поставка для Аубердина",nil, {"Поговорите с Делгреном Очистителем на заставе Мейстры после того, как проводите Фиро через Ясеневый лес."}},
[977] = {"Йети где-то рядом…",nil, {"Добудьте 2 безупречных рога Йети для Уми Смехотреп из Круговзора."}},
[978] = {"Осененные луной дикие совухи",nil, {"Соберите в Зимних Ключах 10 светолунных перьев, принести их Эреласу Янтарному Небу в деревню Рут'теран."}},
[979] = {"В поисках Раншаллы",nil, {"Найдите Раншаллу в восточной части Зимних Ключей."}},
[980] = {"Новые источники",nil, {"Отправьтесь в Зимние Ключи и побеседуйте с Доновой Снегонорой."}},
[981] = {"Башня Алталакса",nil, {"Поговорите с Делгреном Очистителем на Заставе Мейстры."}},
[982] = {"Безбрежное море, глубокий океан",nil, {"Добудьте денежный ящик \"Серебряной Зари\" и денежный ящик \"Туманной Пелены\" для Горбольда Стальной Руки в Аубердине. Оба предмета можно найти в останках потерпевших крушение кораблей к северу от деревни."}},
[983] = {"Жужжалка 827",nil, {"Добудьте 6 ножек крабов и поместите их в Жужжалку 827."}},
[984] = {"Насколько велика угроза?",nil, {"Найдите оскверненный лагерь фурболгов на Темном Берегу и вернитесь к Терентису в Аубердин."}},
[985] = {"Насколько велика угроза?",nil, {"Убейте 8 следопытов и 5 заклинателей ветра из клана Чернолесья, а затем вернитесь к Терентису в Аубердин."}},
[986] = {"Пропавший хозяин",nil, {"Найдите 5 прекрасных шкур лунопарда и принесите их Терентису в Аубердин."}},
