[1101] = {"Хозяйка Лабиринтов",nil, {"Принесите медальон Чарлги Остробок Фалфиндеру Хранителю Путей в Таланааре."}},
[1102] = {"Отмщение грядет!",nil, {"Принесите сердце Чарлги Остробок Ольду Каменному Копью в Громовой Утес."}},
[1103] = {"Зов воды",nil, {"Принесите Тьеву Мордуну, который находится в Серебряном бору, сапту воды, если вам не удалось поговорить с низшим воплощением воды."}},
[1104] = {"Яд Соляных равнин",nil, {"Принесите 6 порций соленого яда скорпида Пшиксу Медноштифу на Мерцающую равнину."}},
[1105] = {"Крепкие панцири",nil, {"Принесите 9 крепких черепашьих панцирей Виззлу Медноштифу на Мерцающую равнину."}},
[1106] = {"Мартек Изгой",nil, {"Отнесите письмо Пшикса Мартеку Изгою в Бесплодные земли."}},
[1107] = {"Средство против трения",nil, {"Принесите 10 соленых хвостовых плавников Виззлу Медноштифту на Мерцающую равнину."}},
[1108] = {"Индарилий",nil, {"Принесите 10 пластин индарилия Мартеку Изгою в Бесплодные земли."}},
[1109] = {"Груды гуано",nil, {"Принесите 1 кучку гуано летучей мыши Лабиринтов мастеру-аптекарю Фаранеллу в Подгород."}},
[1110] = {"Обломки болидов",nil, {"Принестие 30 деталей болида Крейвелу Углебраду на Мерцающую равнину."}},
[1111] = {"Управляющий пристанью Головокружилкинc",nil, {"Отнесите заказ на детали Крейвела управляющему пристанью Головокружилкинcу в Кабестан."}},
[1112] = {"Детали для Крейвела",nil, {"Отнесите Крейвелу Углебраду его детали на Мерцающую равнину."}},
[1113] = {"Сердца Доблести",nil, {"Мастер-аптекарь Фаранелл из Подгорода просит принести ему 20 сердец Доблести."}},
[1114] = {"Заказ гномов",nil, {"Отнесите хрупкие детали машины Пшиксу Медноштифу на Мерцающую равнину."}},
[1115] = {"Сплетник",nil, {"Поговорите с Кразеком в Пиратской бухте."}},
[1116] = {"Сонная пыль в болоте",nil, {"Принесите 10 щепоток сонной пыли Кразеку в Пиратскую Бухту. Сонную пыль можно собрать с дракончиков на Болоте Печали."}},
[1117] = {"Сплетни для Крейвела",nil, {"Отнесите письмо с гоблинскими сплетнями Крейвелу Углебраду на Мерцающую равнину."}},
[1118] = {"Назад в Пиратскую Бухту",nil, {"Отнесите план Крейвела Шиззи Пуззыриксу в Пиратскую Бухту."}},
[1119] = {"Смесь Занзила и \"Дурацкое Крепкое\"",nil, {"Отнесите \"Дурацкое Крепкое\" Крейвелу на Мерцающую равнину."}},
[1120] = {"Напоить гномов",nil, {"Отнесите \"Дурацкое Крепкое\" начальнику гномского гаража."}},
[1121] = {"Напоить гоблинов",nil, {"Отнесите \"Дурацкое Крепкое\" начальнику гоблинского гаража."}},
[1122] = {"Возвращение к Пуззыриксу",nil, {"Отнесите отчет о \"Дурацком Крепком\" Шиззи Пуззыриксу в Пиратскую Бухту."}},
[1123] = {"Рабин Сатурна",nil, {"Поговорите с Рабином Сатурной в селении Ночная Гавань на Лунной поляне. Лунная поляна находится между Оскверненным лесом и Зимними Ключами, туда можно добраться по дороге, идущей от крепости Древобрюхов."}},
[1124] = {"Пустошь",nil, {"Поговорите с Лайо Зведным Всполохом близ кладбища Покой Доблестных в Силитусе и покажите ему письмо Рабина."}},
[1125] = {"Духи Южного Ветра",nil, {"Освободите в деревне Южного Ветра души 8 истерзанных друидов и 8 истерзанных часовых и вернитесь к Лайо Звездному Всполоху на кладбище Силитуса, Покой Доблестных."}},
[1126] = {"Улей в башне",nil, {"Осмотрите башню деревни Южного Ветра и отыщите способ разворошить улей. Принесите все необычное, что попадется при выполнении задания Лайо Звездному Всполоху на кладбище Силитуса, Покой Доблестных."}},
[1127] = {"\"Дурацкое Крепкое\"",nil, {"Принесите 12 порций смеси Занзила Шиззи Пуззыриксу в Пиратскую Бухту."}},
[1130] = {"Весть от Мелора",nil, {"Поговорите с Мелором Каменным Копытом в Громовом Утесе."}},
[1131] = {"Сталезуб",nil, {"Принесите ребро Сталезуба Мелору Каменному Копыту в Громовой Утес."}},
[1132] = {"Фиора Длинноушка",nil, {"Поговорите с Фиорой Длинноушкой в Аубердине."}},
[1133] = {"Путешествие в Астранаар",nil, {"Поговорите с Шиндрелл Огненный Всполох в Астранааре."}},
[1134] = {"Гордокрылы Каменного Когтя",nil, {"Принесите 12 ядовитых желез славнокрыла Шиндрелл Огненный Всполох в Астранаар."}},
[1135] = {"Яд из Скального гнездовья",nil, {"Принесите из Скального гнездовья 10 ядовитых желез для Фиоры Длинноушки в Терамор."}},
[1136] = {"Ледочрев",nil, {"Принесите гриву Ледочрева Мелору Каменному Копыту в Громовой Утес."}},
[1137] = {"Новости для Пшикса",nil, {"Поговорите с Пшиксом Медноштифтом на Мерцающей равнине."}},
[1138] = {"Дары моря",nil, {"Соберите 6 кусочков отличного крабьего мяса для Груббера Блумпа в Аубердине."}},
[1139] = {"Утерянные таблички Воли",nil, {"Найдите табличку Воли и принесите ее советнику Белграму в Стальгорн."}},
[1140] = {"Башня Алталакса",nil, {"Освободите заточенные души высокорожденных на Ночной поляне и Сатирнааре, затем вернитесь к Делгрену Очистителю на заставу Мейстры."}},
[1141] = {"Семья и рыболовная удочка",nil, {"Поймайте 6 окуней Темного Берега для Груббера Блумпа в Аубердине."}},
[1142] = {"Последнее желание",nil, {"Найдите подвеску Трешалы и верните ее Трешале Бурый Ручей в Дарнасс."}},
[1143] = {"Башня Алталакса",nil, {"Убейте Атрикуса Нарассина и принесите его голову Балтулу Удару Тени у башни Алталакса."}},
[1144] = {"Импортер Вилликс",nil, {"Сопроводите Вилликса из Лабиринтов Иглошкурых."}},
[1145] = {"Рой растет",nil, {"Отнесите запечатанную записку Коррана Белгрому Каменной Булаве в Оргриммар."}},
[1146] = {"Рой растет",nil, {"Доставьте запечатанное письмо Белгрома Моктару Крину в Тысячу Игл."}},
[1147] = {"Рой растет",nil, {"Убейте 5 силитидов-искателей, 5 силитидов-трутней и 5 силитидов-захватчиков, а потом вернитесь к Моктару Крину в Тысячу Игл."}},
[1148] = {"Части роя",nil, {"Добудьте 1 сердце, 5 шпор и 3 целых силитидских панциря для Коррана из Перекрестка."}},
[1149] = {"Испытание веры",nil, {"Если ваша вера сильна, прыгните с досок над Тысячью Игл."}},
[1150] = {"Испытание выносливости",nil, {"Принесите коготь Гренки Дорну Вольному Ловчему в Тысячу Игл."}},
[1151] = {"Испытание силы",nil, {"Принесите фрагменты Рок'Алима Дорну Вольному Ловчему в Тысячу Игл."}},
[1152] = {"Испытание знаний",nil, {"Найдите Брауга Тусклый Дух возле входа в Туннель Когтя в Когтистых горах."}},
[1153] = {"Образец новой руды",nil, {"Отберите неочищенный образец руды у землекопа или геодезиста из племени Камнерылов и принесите его Таттернаку Стальному Горну в Лагерь Таурахо в Степях."}},
[1154] = {"Испытание знаний",nil, {"Найдите книгу \"Наследие Аспектов\" и вернитесь к Браугу Тусклому Духу ко входу в Туннель Когтя."}},
[1159] = {"Испытание знаний",nil, {"Найдите Парквела Финталласа в Подгороде."}},
[1160] = {"Испытание знаний",nil, {"Найдите книгу \"Истоки угрозы нежити\" и отнесите ее Парквелу Финталласу в Подгород."}},
[1164] = {"Ограбить воров",nil, {"Принесите головы Кенаты, Фардела и Марселя Дабири Генви Кэллоу в Подгород."}},
[1166] = {"Заботы властителя Мок'Морокка",nil, {"Принесите Мок'Морокку его грог, табак и сейф на Пыльную трясину."}},
[1167] = {"Башня Алталакса",nil, {"Поговорите с Балтулом Ударом Тени возле башни Алталакса."}},
[1168] = {"Армия Черного дракона",nil, {"Тхарг из деревни Гиблотопь хочет, чтобы вы уничтожили 10 огнегривых разведчиков, 10 огнегривых пеплохвостов и 5 огнегривых чешуйчатых губителей."}},
[1169] = {"Корни угрозы",nil, {"Принесите Драз'Зилбу в деревню Гиблотопь 7 огненных языков и 7 пылающих сердец."}},
[1170] = {"Выводок Ониксии",nil, {"Поговорите с властителем Мок'Морокком в Деревне Гиблотопь."}},
[1171] = {"Выводок Ониксии",nil, {"Поговорите с Драз'Зилбом в Деревне Гиблотопь."}},
[1172] = {"Выводок Ониксии",nil, {"Драз'Зилб из Деревни Гиблотопь хочет, чтобы вы уничтожили 5 яиц Ониксии."}},
[1173] = {"Вызов властителю Мок'Морокку",nil, {"Победите Мок'Морокка и сообщите об этом Драз'Зилбу в Деревне Гиблотопь."}},
[1175] = {"Помехи на трассе",nil, {"Убейте 10 просоленных василисков, 10 просоленных хрусталисков и 6 просоленных взорней, после чего вернитесь к директору трассы Зерину на Мерцающую равнину."}},
[1176] = {"Уменьшение веса",nil, {"Добудьте 10 полых костей падальщика для Поззика с Мерцающей равнины."}},
[1177] = {"Моя голодный!",nil, {"Принесите дробильщику Грязноногу, живущему на севере Пыльной Трясины, 8 голов мурлоков из клана Болотного Плавника."}},
[1178] = {"Гоблинская поддержка",nil, {"Поговорите с Газлоу в Кабестане."}},
[1179] = {"Братья Латунный Болт",nil, {"Принесите ящик с защитными шлемами Виззлу Медноштифу на Мерцающую равнину."}},
[1180] = {"Гоблинская поддержка",nil, {"Поговорите с хозяином причала Лозгилем в Пиратской бухте."}},
[1181] = {"Гоблинская поддержка",nil, {"Поговорите с бароном Ревилгазом в Пиратской бухте."}},
[1182] = {"Гоблинская поддержка",nil, {"Принесите чертежи топливного регулятора барону Ревилгазу из Пиратской бухты."}},
[1183] = {"Гоблинская поддержка",nil, {"Отнесите чертежи топливного регулятора Поззику на Мерцающую равнину."}},
[1184] = {"Части роя",nil, {"Вернитесь к Белгрому Каменной Булаве в Оргриммар."}},
[1185] = {"А под хитином было...",nil, {"О, опять ты! В смысле, это ты – а ты, то есть, Томпсон! Да, это ты!nnСлушай-ка!nnТа штука, что ты нашел в Силитусе, оказалась книгой. Дневник, написанный на очень древнем диалекте того, что теперь называется дарнассийским. Это займет некоторое время, но я намерен как следует в нем разобраться. Мне показалось, что тебе следует знать, что твоя находка оказалась просто бесценной для Круга.nnМать-медведица в восхищении, и все такое!"}},
[1186] = {"Восемнадцатый пилот",nil, {"Поговорите с Раззериком на Мерцающей равнине."}},
[1187] = {"Настройки Раззерика",nil, {"Найдите сефориевый ускоритель для Раззерика на Мерцающей равнине."}},
[1188] = {"Безопасность превыше всего",nil, {"Отнесите сефориевый ускоритель Шриву в Прибамбасск."}},
[1189] = {"Безопасность превыше всего",nil, {"Верните модифицированный сефориевый ускоритель Раззерику на Мерцающую равнину."}},
[1190] = {"Только вперед",nil, {"Дождитесь, пока Замек устроит диверсию, украдите чертежи Риззла и отнестие их Поззику."}},
[1191] = {"Помеха Замека",nil, {"Поехали!"}},
[1192] = {"Индарилиевая руда",nil, {"Я хотел бы использовать индарилий в разных частях наших болидов, но сначала мне нужно создать эти части из большого количества этого материала.nnИндарилиевую руду можно найти в Ульдамане – это в Проклятых землях, на другом берегу моря в Каз Модане.nnГномы то и дело получают поставки индарилия, так что нам надо не отставать."}},
[1193] = {"Сломанная западня",nil, {"Перед вами – большая сломанная западня. Судя по ее внешнему виду, огры даже пытались ее чинить, но безуспешно. Рядом с западней лежит небрежно нацарапанная записка. Как ни странно, в записке подробно объясняется, как починить западню: для этого нужно ториевое устройство и немного масла льда. Когда западня работает, любой проходящий мимо огр может в нее попасть, и тогда его можно будет благополучно миновать.nnОчевидно, огры читать не умеют..."}},
[1194] = {"Чертежи Риззла",nil, {"Отнесите образец индарилиевой руды Поззику на Мерцающую равнину."}},
[1195] = {"Священное пламя",nil, {"Принесите наполненный гравированный фиал Зангену Каменному Копыту в Громовой Утес."}},
[1196] = {"Священное пламя",nil, {"Доставьте наполненный гравированный фиал Рау Скальному Бегуну на Заставу Вольного Ветра."}},
[1197] = {"Священное пламя",nil, {"Принестите раздвоенное копыто Рау Скальному Бегуну на Заставу Вольного Ветра."}},
[1198] = {"В поисках Талрида",nil, {"Найдите стража Талрида из ордена Серебряного Рассвета в Непроглядной Пучине."}},
[1199] = {"Наступление сумерек",nil, {"Принесите 10 подвесок сумерек стражу Менадосу из ордена Серебряного Рассвета в Дарнасс."}},
[1200] = {"Жестокость Черных Глубин",nil, {"Принесите голову повелителя сумрака Келриса Рассветному дозорному Селгорму в Дарнасс."}},
[1201] = {"Тераморские шпионы",nil, {"Убейте 9 тераморских шпионов, потом вернитесь к Назиру Кровавому Копью в деревню Гиблотопь."}},
[1202] = {"Доки Терамора",nil, {"Принесите документы капитана Назиру Кровавому Копью в Деревню Гиблотопь."}},
[1203] = {"Ярлу нужен клинок",nil, {"Принесите меч Костяной чешуи Ярлу \"Болотному Глазу\" в сторожку \"Болотный Огонек\"."}},
[1204] = {"Суп из илистой черепахи с жучками",nil, {"Принесите 8 раздвоенных языков илистых черепах Моргану Штерну в Терамор."}},
[1205] = {"Волокун",nil, {"Принесите зуб кроколиска Волокуна Мелору в Громовой Утес."}},
[1206] = {"Ярлу нужны глаза",nil, {"Принесите 20 целых глаз пауков Мглистой пещеры Ярлу \"Болотному Глазу\" в сторожку \"Болотный Огонек\"."}},
[1218] = {"Лягушачьи лапки",nil, {"Принесите 10 лапок болотных лягушек Ярлу \"Болотному Глазу\" в Пыльную трясину."}},
[1219] = {"Орочье донесение",nil, {"Доставьте донесение шпиона лейтенанту Адену в Терамор."}},
[1220] = {"Капитан Ваймс",nil, {"Принесите шпионское донесение капитану Гаррану Ваймсу."}},
[1221] = {"Корни Синелиста",nil, {"Возьмите ящик с дырками.nВозьмите палочку-погонялочку для шмыгуносов.nВозьмите справочник по шмыгуносам. nnВ Лабиринтах Иглошкурых выпустите шмыгуноса и воспользуйтесь стеком, чтобы он начал искать корни. nnПринесите 6 корней синелиста, стек и ящик с дырками Мебоку Миззриксу в Кабестан."}},
[1222] = {"Побег Вонючки",nil, {"Сопроводите \"Вонючку\" Игнаца, затем пообщайтесь с Морганом Штерном в Тераморе."}},
[1238] = {"Потерянный рапорт",nil, {"Отнесите донесение шпиона Назиру Кровавому Копью в Деревню Гиблотопь."}},
[1239] = {"Отрубленная Голова",nil, {"Принесите голову строптивого орка Назиру Кровавому Копью в Деревню Гиблотопь."}},
[1240] = {"Тролль-знахарь",nil, {"Принесите голову строптивого орка Кин'вилаю в лагерь Гром'гол в Тернистой долине."}},
[1241] = {"Пропавший дипломат",nil, {"Найдите Йоргена в Штормграде."}},
[1242] = {"Пропавший дипломат",nil, {"Доставьте запечатанную записку Йоргена Иллингу Триасу в Штормград."}},
[1243] = {"Пропавший дипломат",nil, {"Отнесите запечатанное письмо дозорному Бакусу в Сумеречный лес."}},
[1244] = {"Пропавший дипломат",nil, {"Найдите декларацию Братства Справедливости и верните ее дозорному Бакусу в Сумеречном лесу."}},
[1245] = {"Пропавший дипломат",nil, {"Отнесите декларацию Братства Справедливости Иллингу Триасу в Штормград."}},
[1246] = {"Пропавший дипломат",nil, {"Поговорите с Дашелем Каменным Кулаком в Штормграде."}},
[1247] = {"Пропавший дипломат",nil, {"Поговорите с Иллингом Триасом в Штормграде."}},
[1248] = {"Пропавший дипломат",nil, {"Поговорите с Михаилом в Болотине."}},
[1249] = {"Пропавший дипломат",nil, {"Перехватите Яна Проныру до его ухода и вернитесь к Михаилу."}},
[1250] = {"Пропавший дипломат",nil, {"Поговорите с Михаилом в Болотине."}},
