[1514] = {"Оскверненный ветроцвет",nil, {"Вы нашли чахлое, пораженное какой-то порчей растение, похожее на ветроцвет. Ягоды на нем тоже кажутся какими-то гнилыми и ядовитыми. Если вы хотите вернуть его к нормальному состоянию, то срочно необходимо им заняться."}},
[1515] = {"Догран в плену",nil, {"Отнесите зелье Кен'зиглы Дограну в Степях."}},
[1516] = {"Зов земли",nil, {"Принесите Канаге заклинателю земли 2 копыта ловцов Скверны."}},
[1517] = {"Зов земли",nil, {"Найдите Священный Камень и выпейте рядом с ним сапту Земли."}},
[1518] = {"Зов земли",nil, {"Отнесите необработанный кварц Канаге заклинателю земли в долину Испытаний."}},
[1519] = {"Зов земли",nil, {"Принесите 2 порции ритуальной мази провидице Воронье Перо в Лагерь Нараче."}},
[1520] = {"Зов земли",nil, {"Найдите Дольмен Кодо и выпейте сапту земли."}},
[1521] = {"Зов земли",nil, {"Отнесите грубый кварц провидице Воронье Перо в Лагерь Нараче."}},
