[1523] = {"Зов огня",nil, {"Найдите в Степях Кранала Фисса."}},
[1524] = {"Зов огня",nil, {"Отнесите факел Дремлющего Пламени Телфу Джуламу в Дуротаре."}},
[1525] = {"Зов огня",nil, {"Принесите 1 порцию огненной смолы и 1 мешочек с реагентами Телфу Джуламу в Дуротаре."}},
[1526] = {"Зов огня",nil, {"Сразитесь с низшим воплощением огня и поместите светящийся уголек в жаровню в святилище Неугасимого Пламени."}},
[1527] = {"Зов огня",nil, {"Принесите факел Неугасимого Пламени Краналу Фиссу в Степи."}},
[1528] = {"Зов воды",nil, {"Отыщите в Степях Ислен Заклинательницу Воды."}},
[1529] = {"Зов воды",nil, {"Отыщите в Степях Ислен Заклинательницу Воды."}},
[1530] = {"Зов воды",nil, {"Отыщите Брин в южной части Степей."}},
[1531] = {"Зов воздуха",nil, {"Найдите Пратту Заклинательницу Туч в Тысяче Игл."}},
[1532] = {"Зов воздуха",nil, {"Найдите Пратту Заклинательницу Туч в Тысяче Игл."}},
[1534] = {"Зов воды",nil, {"Наполните пустой синий бурдюк для воды в руинах Звездной Пыли и отнесите воду Брин из Степей."}},
[1535] = {"Зов воды",nil, {"Наполните пустой коричневый бурдюк для воды в источнике ниже хижины Брин и отнесите ей воду."}},
[1536] = {"Зов воды",nil, {"Наполните пустой красный бурдюк для воды в Мельнице Таррен и отнесите воду Брин в Степи."}},
[1558] = {"Каменная дамба",nil, {"Отведите сироту к Каменной дамбе у озера Лок Модан. Пусть постоит в центре дамбы и полюбуется огромным водопадом."}},
[1559] = {"Секрет изготовления световой бомбы",nil, {"Так ты у нас инженер, да? Ну что ж, за то, что нашел для меня жемчужины, вот тебе секрет изготовления световых бомб. nnТы с ними только уж поосторожней, как бы самому не ослепнуть!"}},
[1560] = {"Задание Тооги",nil, {"Сопроводите Тоогу к его жене, Торте."}},
[1578] = {"Помощь фронту",nil, {"Отнесите 6 медных топоров и 6 медных плетеных поясов Торвальду в южную сторожевую башню озера Лок Модан."}},
[1579] = {"Суперразъемы",nil, {"Принесите Бацбаху Кривошипу в Аубердин 8 суперразъемов."}},
[1580] = {"Электропеллеры",nil, {"Принесите 12 электропеллеров Бацбаху Кривошипу в Аубердин."}},
[1581] = {"Эликсиры для клана Режущего Листа",nil, {"Доставьте 6 эликсиров Львиной силы и 2 слабых эликсира защиты Сирел Остролист в Доланаар."}},
[1582] = {"Жилет лунного сияния",nil, {"Принесите 1 пару тисненых кожаных перчаток, 5 пар тисненых кожаных сапог и 5 тисненых кожаных плащей Лотериас в Дарнасс."}},
[1598] = {"Украденная книга",nil, {"Принесите книгу \"Силы Бездны\" Друзилле ла Салле."}},
[1599] = {"В начале славных дел",nil, {"Добудьте 3 оберега из перьев и отнесите их Аламару Гримму в Старую Наковальню."}},
[1618] = {"Экипирование Красногорья",nil, {"Отнесите 4 рунических медных пояса и 4 тяжелых медных кувалды Вернеру Осгуду в Красногорье."}},
[1638] = {"Обучение воина",nil, {"Поговорите с Гарри Берлгардом."}},
[1639] = {"Бартлби Пьянь",nil, {"Поговорите с Бартлби."}},
[1640] = {"Побить Бартлби",nil, {"Побейте Бартлби и снова поговорите с ним."}},
[1641] = {"Фолиант Божественности",nil, {"Здесь, в Штормграде, я – наставник паладинов. Я – тот, кто ведет их стезей добродетели к Истинному Свету, дабы они защищали Азерот от нашествия нежити и иных бедствий.nnИ для тебя пришло время сделать первый шаг на пути праведности. Если ты решишься принять этот путь, я дам тебе Фолиант Божественности. Изучая его, ты узнаешь кое-что о Свете и о том, чего от тебя ожидают. Постигнув мудрость, заключенную в этой книге, ты откроешь в себе новые, удивительные способности."}},
[1642] = {"Фолиант Божественности",nil, {"Поговорите с Даторианом Раллом в Штормграде."}},
[1643] = {"Фолиант Божественности",nil, {"Обыщите Торговый квартал и найдите Стефани Тернер."}},
[1644] = {"Фолиант Божественности",nil, {"Принесите 10 отрезов льна в Штормград и отдайте Стефани Тернер."}},
[1645] = {"Фолиант Божественности",nil, {"Приятно встретить тебя, Палсдубиной. Я направляю паладинов на путь высших добродетелей, когда они посвящают себя Свету и защите Азерота от Плети и прочих бед.nnПришла пора сделать первый шаг по пути истинных добродетелей. Если ты принимаешь этот путь, я дам тебе Фолиант Божественности. Изучая его, ты узнаешь кое-что о Свете и о том, что тебя ждет. Если ты это поймешь и окажешься способным учеником, то откроешь путь к великим возможностям."}},
[1646] = {"Фолиант Божественности",nil, {"Поговорите с Тизой Кузнебой в Стальгорне."}},
[1647] = {"Фолиант Божественности",nil, {"Найдите во внешнем круге Стальгорна Джона Тернера."}},
[1648] = {"Фолиант Божественности",nil, {"Принесите 10 отрезов льняной ткани Джону Тернеру в Стальгорн."}},
[1649] = {"Фолиант Доблести",nil, {"Поговорите с Даторианом Раллом в Штормграде."}},
[1650] = {"Фолиант Доблести",nil, {"Найдите Дафну Стилвелл в Западном Крае."}},
[1651] = {"Фолиант Доблести",nil, {"Защитите Дафну Стилвелл от нападения Братства Справедливости. Проследите, чтобы ни ее, ни ваша душа в случае смерти не покинули свои тела, иначе испытание не будет засчитано.nnПосле победы поговорите с Дафной Стилвелл."}},
[1652] = {"Фолиант Доблести",nil, {"Поговорите с Даторианом Раллом в Штормграде."}},
[1653] = {"Испытание доблести",nil, {"Поговорите с Джорданом Стилвеллом в Стальгорне."}},
[1654] = {"Испытание доблести",nil, {"Возьмите список Джордана, добудьте немного древесины белокаменного дуба, партию очищенной руды Бэйлора, кузнечный молот Джордана и самоцвет Кора и отдайте их Джордану Стилвеллу в Стальгорне."}},
[1655] = {"Партия руды Балора",nil, {"Доставьте партию руды для Джордана Балору Крепкоруку к озеру Лок Модан."}},
[1656] = {"Незаконченное задание",nil, {"Отнесите сверток мехов хозяину таверны Кауту в Деревню Кровавого Копыта."}},
[1657] = {"Чем пахнет в Южнобережье",nil, {"Бросьте три бомбы-вонючки Отрекшихся в центр Южнобережья в Хилсбраде и вернитесь к призывательнице тьмы Янке на фестиваль Плетеного человека в Тирисфальских лесах."}},
[1658] = {"Сорвать праздник Плетеного человека",nil, {"Отыщите место проведения фестиваля Плетеного человека в Тирисфальских лугах и возвращайтесь в Южнобережье к сержанту Хартману."}},
[1661] = {"Фолиант Благородства",nil, {"Поговорите с Даторианом Раллом в Штормграде."}},
[1665] = {"Кружка Бартлби",nil, {"Отнесите кружку Бартлби Берлгарду."}},
[1666] = {"Маршал Хаггард",nil, {"Поговорите с маршалом Хаггардом."}},
[1667] = {"Джек Мертвозуб",nil, {"Добыть знак маршала Хаггарда из лагеря Джека Мертвозуба и вернуться к маршалу Хаггарду."}},
[1678] = {"Веджрек",nil, {"Принесите голову Веджрека Марену Грозовой Вершине в Стальгорн."}},
[1679] = {"Марен Грозовая Вершина",nil, {"Поговорите с Мареном Грозовой Вершиной."}},
[1680] = {"Тормус Подземная Кузня",nil, {"Поговорите с Тормусом Подземной Кузней."}},
[1681] = {"Мастерская Сталекрута",nil, {"Принесите мрачную руду Тормусу Подземной Кузне в Стальгорн."}},
[1682] = {"Оружие из серой стали",nil, {"Вот тебе оружие, только что с наковальни. На него пошла добытая тобой мрачная руда!nnЧто же, Хрофтгор, выбирай!"}},
[1683] = {"Ворлус Подлое Копыто",nil, {"Принесите рог Ворлуса Эланарии из Дарнасса."}},
[1684] = {"Эланария",nil, {"Поговорите с Эланарией."}},
[1685] = {"Приглашение Гакина",nil, {"Обратитесь к Гакину Темнопряду в квартал магов Штормграда."}},
[1686] = {"Тень Элуры",nil, {"Принесите 8 партий элунитовой руды и медальон Элуры Эланарии из Дарнасса."}},
[1687] = {"Маяк с привидениями",nil, {"Отведите сироту к маяку на побережье Западного Края."}},
[1688] = {"Сурена Каледон",nil, {"Верните колье Кровавого камня Гакину Темной Вязи в Штормград."}},
[1689] = {"Связывание",nil, {"С помощью колье кровавого камня призовите и подчините демона Бездны, затем верните колье кровавого камня Гакину Темнопряду."}},
[1690] = {"Расправа над Скитальцами Пустыни",nil, {"Убейте 10 бандитов и 10 воров из шайки Скитальцев Пустыни на востоке от Прибамбасска и возвращайтесь к главному инженеру Чепухастеру."}},
[1691] = {"Еще одна расправа над Скитальцами Пустыни",nil, {"Убейте 10 темных магов, 8 разбойников и 6 убийц из шайки Скитальцев Пустыни по поручению главного инженера Чепухастера из Прибамбасска."}},
[1692] = {"Кузнец Матиль",nil, {"Отнесите сундук элунита кузнецу Матилю."}},
[1693] = {"Оружие из элунита",nil, {"Это оружие я выковал из принесенного тобой элунита. Можешь взять себе любое.nnБольшое спасибо. Работать с элунитом – не только удовольствие, но и великая честь."}},
[1698] = {"Йорус Ячменовар",nil, {"Поговорите с Йорусом Ячменоваром."}},
[1699] = {"Ретбанский вызов",nil, {"Войдите в пещеры Ретбана, доберитесь до первой развилки и вернитесь к Йорусу Ячменовару за отведенное время.nnВы не должны умирать и освобождать свой дух."}},
[1700] = {"Гриманд Элмор",nil, {"Отнесите записки Фьюрена Гриманду Элмору."}},
[1701] = {"Закаленный доспех",nil, {"Соберите все необходимые материалы для Фьюрена Длинноборода и отнесите их в Штормград."}},
[1702] = {"Щитник",nil, {"Отнесите бочонок жгучего пива Фьюрену Длиннобороду."}},
[1703] = {"Матиль",nil, {"Отнесите Матилю записки Фьюрена."}},
[1704] = {"Клацморт Гайкокрут",nil, {"Отнесите записки Фьюрена Клацморту Гайкокруту."}},
[1705] = {"Пылающая кровь",nil, {"Принесите 20 сосудов горящей крови и 1 горящий камень Гриманду Элмору из Штормграда."}},
[1706] = {"Доспехи Гриманда",nil, {"Глазам своим не верю! Технология Фьюрена оказалась удачной, и этот доспех способен выдержать серьезную нагрузку.nnДержи, Ишт. И огромное тебе спасибо. Если бы ты не принес мне все необходимые материалы, я бы так и не поверил в открытие Фьюрина."}},
[1707] = {"Награда за воду",nil, {"Принесите 5 бурдюков Скитальцев Пустыни оператору крана Тормозиллеру в Прибамбасск."}},
[1708] = {"Железный коралл",nil, {"Принесите 20 партий выжженного коралла Клацморту Гайкокруту в Стальгорн."}},
[1709] = {"Творение Клацморта",nil, {"Вот твои доспехи, Бронетазик. Идея Фьюрена отлично сработала. Идеально! Чудесно, если правду сказать!nnПо этой броне можно осадные орудия прокатить, и она сохранит форму!"}},
[1710] = {"Высохшая скорлупа",nil, {"Принесите 20 кусков высохшей на солнце скорлупы Матилю из Дарнасса."}},
[1711] = {"Доспехи Матиля",nil, nil},
[1712] = {"Циклониан",nil, {"Принесите Бат'ра Ветростражу все предметы из его списка."}},
[1713] = {"Призыв",nil, {"Отнести сердце Вихря Бат-ра Ветростражу."}},
[1714] = {"Субстанция Изгнанников",nil, {"Котел бурлит, и его пары так и манят..."}},
[1715] = {"Таверна \"Забитый ягненок\"",nil, {"Обратитесь к Гакину Темнопряду в квартал магов Штормграда."}},
[1716] = {"Пожирательница душ",nil, {"Поговорите с Такаром Провидцем в Степях."}},
[1717] = {"Приглашение Гакина",nil, {"Обратитесь к Гакину Темнопряду в квартал магов Штормграда."}},
[1718] = {"Островитянин",nil, {"Поговорите с Кланноком Маклеодом."}},
[1719] = {"Побоище",nil, {"Убейте Большого Билла и поговорите с Кланноком Маклеодом на Острове Битв."}},
[1738] = {"Сердцедрево",nil, {"Раздобудьте в Ясеневом лесу сердцедрево и принесите его Гакину Темнопряду в Квартал магов Штормграда."}},
[1739] = {"Связывание",nil, {"Призовите и подчините суккуба, используя сердцевину сердцедрева, затем верните сердцевину сердцедрева Гакину Темнопряду. Он будет ждать вас в \"Забитом ягненке\"."}},
[1740] = {"Шар Соран'рука",nil, {"Соберите 3 фрагмента Соран'рука и 1 большой фрагмент Соран'рука и принесите их Доану Кархану в Степи."}},
[1758] = {"Фолиант Заговора",nil, {"Поговорите с Кромом Крепкоруком в Стальгорне."}},
[1778] = {"Фолиант Божественности",nil, {"Поговорите с Тизой Кузнебой в Стальгорне."}},
[1779] = {"Фолиант Божественности",nil, {"Возьмите знак жизни и поговорите с Майридоном Кузнебоем в Стальгорне."}},
[1780] = {"Фолиант Божественности",nil, {"Поговорите с Даторианом Раллом в Штормграде."}},
[1781] = {"Фолиант Божественности",nil, {"Отнесите знак Жизни Газину Тенорму в Штормград."}},
[1782] = {"Доспехи Фьюрена",nil, {"Вот твои доспехи, Ишт. Они выдержат любой удар, не будь я Фьюрен Длиннобород!nnИ спасибо большое. Я многому научился, работая с принесенными тобой материалами. Думаю, в один прекрасный день я совершу новое открытие!nnА результаты этого я отправлю другим кузнецам Альянса.nnЯ скажу им, что ты мне очень помог, может быть, они тоже решат отблагодарить тебя."}},
[1783] = {"Фолиант Божественности",nil, {"Возьмите знак Жизни и воскресите Нарма Фолка в Дун Мороге."}},
[1784] = {"Фолиант Божественности",nil, {"Добудьте приказ клана Черного Железа у дворфов из клана Черного Железа возле Хельмова Ложа и вернитесь к Майридону Кузнебою в Стальгорн."}},
[1785] = {"Фолиант Божественности",nil, {"Поговорите с Тизой Кузнебой в Стальгорне."}},
[1786] = {"Фолиант Божественности",nil, {"Возьмите знак Жизни, пойдите в Элвиннский лес и воскресите Хенза Фолка."}},
[1787] = {"Фолиант Божественности",nil, {"Найдите волшебников из Братства Справедливости в Элвиннском лесу, заберите у них бумаги и отнесите их Газину Тенорму в Штормград."}},
[1788] = {"Фолиант Божественности",nil, {"Поговорите с Даторианом Раллом в Штормграде."}},
[1789] = {"Знак Жизни",nil, nil},
[1790] = {"Знак Жизни",nil, {"Поспеши, Болвар! Слишком много жизней зависит от твоего успеха или неудачи.nnЕсли ты не сумеешь помочь Газину и Хензу, возвращайся: я дам тебе новый знак Жизни.nnНе думай, что я помогаю тебе просто так, нет: тебе, как и всякому другому паладину, нужно доказать, что ты достоин своего звания. Постоянные неудачи – не лучшая рекомендация..."}},
[1791] = {"Ветростраж",nil, {"Поговорите с Бат'ра Ветростражем."}},
[1792] = {"Оружие Смерча",nil, {"У меня есть сердце Вихря, и я могу подобрать ему вместилище по твоему выбору.nnВозьми любое оружие, и знай, что его сила – сила Циклониана."}},
[1793] = {"Фолиант Доблести",nil, {"Теперь доказано, что ты – приверженец сил Света, Палинус, но времена наступают все более опасные, и поэтому важно знать, какая сила скрывается в твоем сердце. Когда демоны, драконы или какое другое зло ступят на нашу землю, тогда и подвергнется испытанию твой дух. Достанет ли у тебя сил?nnХрабрость в любой ситуации – такая же неотъемлемая часть сущности паладина, как Свет, доспех и оружие. Необходимость защищать слабых и не терять веры – вот еще два урока, которые ты должен усвоить, пока проходишь обучение в Соборе Света."}},
[1794] = {"Фолиант Доблести",nil, {"Что же, теперь нет сомнений, что благодать Света пребывает с тобой, Отхилюотпорю, но чем опаснее времена, тем важнее знать, какую силу скрывает плоть. Когда на твои земли ступят демоны, драконы или иное зло, твоя доблесть подвергнется испытанию. Достанет ли тебе сил?nnНе бывает паладина без отваги в любой ситуации, как не бывает паладина без Света, доспеха и оружия. Защищай слабых, не теряй веры – вот два урока, которые тебе следует усвоить во время обучения в Соборе Света."}},
[1795] = {"Связывание",nil, {"Призовите и одолейте охотника скверны при помощи фолианта Заговора и верните фолиант Страхаду Фарсану из Кабестана."}},
[1796] = {"Материалы для зачарованной золотой кроводежды",nil, {"Принесите одеяния Аркана Менаре Расщепительнице Бездны в Степи."}},
[1798] = {"На поиски Страхада",nil, {"Поговорите со Страхадом Фарсаном в Степях."}},
[1799] = {"Части шара Орахила",nil, {"Поговорите с послушниками Менары в башне над Кабестаном и изберите свой путь.nnПосле этого принесите Табете в Пыльную трясину сферу Подземного огня."}},
[1800] = {"Тронный зал Лордерона",nil, {"Отведите своего подопечного в старый тронный зал Лордерона, который находится как раз у входа в Подгород."}},
[1801] = {"Фолиант Заговора",nil, {"Поговорите с Джорахом Аннисоном в Подгороде."}},
[1802] = {"Фолиант Заговора",nil, {"Добудьте заплесневелый фолиант и потрепанный манускрипт для Крома Крепкорука из Стальгорна."}},
[1803] = {"Фолиант Заговора",nil, {"Отыщите заплесневелый фолиант и потрепанный манускрипт для Йораха Аннисона из Подгорода."}},
[1804] = {"Фолиант Заговора",nil, {"Принесите восстановленный фолиант и три жезла направления Страхаду Фарсану в Кабестан."}},
[1805] = {"Фолиант Заговора",nil, {"Принесите восстановленный фолиант и три жезла направления Страхаду Фарсану в Кабестан."}},
[1806] = {"Испытание доблести",nil, {"Подождите, пока Джордан Стилвелл не закончит изготовление оружия."}},
[1818] = {"Разговор с Диллинджером",nil, {"Поговорите со стражем смерти Диллинджером в Брилле."}},
[1819] = {"Улаг Колун",nil, {"Убейте Улага Колуна и поговорите со стражем смерти Диллинджером."}},
[1820] = {"Разговор с Колманом",nil, {"Поговорите с Колманом Фартингом."}},
[1821] = {"Наследие Агамондов",nil, {"Принесите Колману Фартингу фамильный топор Агамондов, фамильный меч Агамондов, фамильную палицу Агамондов, фамильный кинжал Агамондов."}},
[1822] = {"Фамильное оружие",nil, {"Я оставлю принесенное тобой оружие на память о понесенных потерях. Впрочем, ты можешь выбрать наиболее полюбившуюся тебе вещь и оставить ее себе.nnДа послужит она тебе лучше, чем прежним проклятым владельцами."}},
[1823] = {"Разговор с Ругой",nil, {"Поговорите с Ругой Тотемом Ярости."}},
[1824] = {"Испытание на Полях Великанов",nil, {"Принесите 5 дергающихся усиков Руге Тотему Ярости в Лагерь Таурахо."}},
[1825] = {"Разговор с Тун'гримом",nil, {"Поговорите с Тун'гримом Огневзором из Степей."}},
[1838] = {"Доспехи Жестокости",nil, {"Принесите Тун'гриму Огневзору 15 закопченных железных слитков, 10 мер толченого азурита, 10 железных слитков и сосуд флогистона."}},
[1839] = {"Ула'елек и рукавицы Жестокости",nil, {"Поговорите с Ула'елеком из Дуротара."}},
[1840] = {"Орм Каменное Копыто и шлем Жестокости",nil, {"Обратитесь к Орму Каменному Копыту из Громового Утеса."}},
[1841] = {"Велора Нители и Набедренники Жестокости",nil, {"Поговорите с Велорой Нители из Подгорода."}},
[1842] = {"Копыта сатиров",nil, {"Принесите 7 целых копыт сатиров Ула'елеку в Дуротар, в деревню Сен'джин."}},
[1843] = {"Рукавицы Жестокости",nil, {"Спасибо, Коммибро. Эти рукавицы – мое величайшее достижение. Возьми их. Ты окажешь мне честь, если пойдешь в них в бой."}},
[1844] = {"Рог химеры",nil, {"Принести Орму Каменному Копыту из Громового Утеса гальванизированный рог."}},
[1845] = {"Шлем Жестокости",nil, {"Возьми этот шлем.nnТебя привели ко мне великие свершения, и если Матери-земле будет угодно... тебе предстоит немало подвигов."}},
[1846] = {"Берцовые кости",nil, {"Принесите 8 крепких берцовых костей Драконьей Пасти Велоре Нители в Подгород."}},
[1847] = {"Набедренники Жестокости",nil, {"Вот твои набедренники, Большойзмей. Ну как, подходят? Или надо что-то менять? Мне хочется угодить заказчику! По крайней мере всегда хотелось.nnТеперь ремесло утратило прежнюю прелесть. Но все лучше, чем быть безмозглым рабом Плети!nnНаверное."}},
[1848] = {"Хауберк Жестокости",nil, {"Тебе несомненно пришлось пролить немало крови, чтобы справиться с моим заданием, Коммибро. Вот плод твоих трудов.nnНоси с удовольствием. Как-нибудь заходи, расскажешь, как он тебе пригодился в бою."}},
[1858] = {"Изувеченная Длань",nil, {"Украдите ключ Тазана, откройте сумку Тазана и отнесите содержимое сумки Терзоку в расселину Теней в Оргриммаре."}},
[1859] = {"Терзок",nil, {"Поговорите с Терзоком в расселине Теней в Оргриммаре."}},
[1860] = {"Поговорите с Джиннией",nil, {"Поговорите с Джиннией Пушкой в Штормграде."}},
[1861] = {"Зеркальное озеро",nil, {"Отнесите пробу воды из Зеркального озера Джиннии Пушке из Штормграда."}},
[1878] = {"Награда за воду",nil, {"Опять ты здесь? Если ты несешь еще пять бурдюков с водой Скитальцев Пустыни, то тебя ждет еще один именной полевой набор!"}},
[1879] = {"Поговорите с Бинк",nil, {"Поговорите с Бинк из Стальгорна."}},
[1880] = {"Маг-тастический гизмонитор",nil, {"Верните Бинк маг-тастический гизмонитор."}},
[1881] = {"Разговор с Анастасией",nil, {"Поговорите с Анастасией Хартвелл."}},
[1882] = {"Ферма Балнира",nil, {"Принесите львиный зев Бальнира Анастасии Хартвелл в Магический Квартал Подгорода."}},
[1883] = {"Разговор с Ун'Тувой",nil, {"Поговорите с Ун'Тувой."}},
[1884] = {"Груды джуджу",nil, {"Уничтожьте 4 груды джуджу и вернитесь к Ун'Туве из деревни Сен'джин."}},
[1885] = {"Меннит Каркад",nil, {"Поговорите с Меннитом Каркадом в Квартале Разбойников Подгорода."}},
[1886] = {"Стражи смерти",nil, {"Принесите рекомендательное письмо Астора Менниту Каркаду в Квартал Разбойников Подгорода."}},
[1898] = {"Стражи смерти",nil, {"Поговорите с Андроном Гантом в Подгороде."}},
[1899] = {"Стражи смерти",nil, {"Отнесите гроссбух Андрона Менниту Каркаду в Квартал Разбойников Подгорода."}},
[1918] = {"Оскверненная стихия",nil, {"Отнесите оскверненную водяную сферу Мастоку Райльшипу на заставу Расщепленного Дерева в Ясеневый лес."}},
[1919] = {"Задание Джиннии",nil, {"Поговорите с Джиннией Пушкой в Штормграде."}},
[1920] = {"Синий Отшельник",nil, {"Возьмите свиток заклятия Проявления и ящик с шкатулками-духоловками за спиной у Джиннии Пушки. Принесите 3 заполненных шкатулки-духоловки Джиннии в Башню магов."}},
[1921] = {"Сбор материалов",nil, {"Отнесите 10 кусков льна и 6 заряженных самоцветов Расселины Уинн Ларсон в Штормград."}},
[1938] = {"Трактат Ура по темной магии",nil, {"Принесите Трактат Ура по темной магии верховному колдуну Андромату из Штормграда."}},
[1939] = {"Верховный колдун Андромат",nil, {"Поговорите с верховным колдуном Андроматом."}},
[1940] = {"Безупречная паутина",nil, {"Принесите 8 кусков безупречной паутины Уинн Ларсон в Штормград."}},
[1941] = {"Одеяние из ткани маны",nil, {"Вот твое одеяние. Один из моих шедевров! Носи на здоровье, а если тебя спросят, кто шил... Посоветуй им обратиться в суконную лавку Ларсона!"}},
[1942] = {"Одеяние волшебного плетения",nil, {"Твое одеяние готово! Отличный шелк тебе достался... Ты уж прости, одеяние получилось довольно короткое, часть шелка я себе оставила.nnШучу, шучу. Оно тебе идеально подойдет, причем будет уместно смотреться как в бою, так и на торжественном обеде. Наслаждайся!"}},
[1943] = {"Разговор с Дейно",nil, {"Поговорите с Дейно."}},
[1944] = {"Воды Ксавиана",nil, {"Принесите пробу воды из Ксавиана Дейно в Оргриммар."}},
[1945] = {"Смеющиеся сестры",nil, {"Принесите 12 волос Смеющихся сестер Кил'хале с Перекрестка."}},
[1946] = {"Одеяние из кружева Пустоты",nil, {"Вот твое пустокружево. Оно прочное и удобное и хранит в себе магию волос Смеющейся сестрички.nnНаслаждайся. Если понравится, не забудь упомянуть об этом в разговоре с Дейно. Вот уж с кем бы не хотелось ссориться..."}},
[1947] = {"Путь в болото",nil, {"Поговорите с Табетой."}},
[1948] = {"Волшебные предметы",nil, {"Принесите 1 нефрит и шипастый жезл Молний Табете в Пыльную трясину."}},
[1949] = {"Великие тайны",nil, {"Поговорите с волхвом Тиртом на Мерцающей равнине."}},
[1950] = {"Волшебное слово",nil, {"Узнайте волшебные слова, отпирающие сейф Тирта и вернитесь к Тирту."}},
[1951] = {"Обряды силы",nil, {"Принесите книгу \"Обряды силы\" Табете в Пыльную трясину."}},
[1952] = {"Магический жезл",nil, {"Поговорите с Табетой по завершении ритуала."}},
[1953] = {"Назад в болото",nil, {"Поговорите с Табетой."}},
[1954] = {"Сфера Подземного огня",nil, {"Принесите сферу Подземного огня Табете из Пылевых топей."}},
[1955] = {"Поединок с демоном",nil, {"Убейте демона сферы, а потом поговорите с Табетой."}},
[1956] = {"Сила Ульдамана",nil, {"Добудьте обсидиановый источник силы и принесите его Табете в Пыльную трясину."}},
[1957] = {"Волнолов маны",nil, {"Убейте 12 волноловов маны за отведенное время."}},
[1958] = {"Небесная сила",nil, nil},
[1959] = {"Задание Анастасии",nil, {"Поговорите с Анастасией Хартвелл."}},
[1960] = {"Лавка алхимика",nil, {"Возьмите свиток заклятия Проявления и ящик с шкатулками-духоловками за спиной у Анастасии Хартвелл. Принесите 3 заполненных шкатулки-духоловки, ящик со шкатулками духоловками и заклинание Проявления Анастасии в Подгород."}},
[1961] = {"Сбор материалов",nil, {"Принесите 10 кусков льна и 6 даларанских самоцветов маны Джозефу Грегориану."}},
[1962] = {"Одеяние из чароткани",nil, {"Вот твое новое одеяние, Тузвколоде. Носи на здоровье."}},
[1963] = {"Изувеченная Длань",nil, {"Убейте Тазана и принесите его сумку Терзоку в расселину Теней в Оргриммаре."}},
[1978] = {"Стражи смерти",nil, {"Отнесите гроссбух Андрона Вариматрасу в Зал Темной Госпожи в Подгороде."}},
[1998] = {"Фенвик Татрос",nil, {"Убейте Фенвика Татроса и принесите его голову Менниту Каркаду в Квартал Разбойников Подгорода."}},
[1999] = {"Предметы торговли",nil, {"Отыщите отчет о состоянии Даларана и отдайте его Менниту Каркаду в Квартале Разбойников Подгорода."}},
[2000] = {"Рокар Тень Клинка",nil, {"Поговорите с Рокаром Тенью Клинка в Дуротаре."}},
[2018] = {"Испытание Рокара",nil, {"Убейте надзирателя Глибби и отдайте его свисток Рокару Тени Клинка в Дуротаре."}},
[2019] = {"Профессиональные инструменты",nil, {"Вернитесь к Терзоку в Расселину Теней в Оргриммаре."}},
[2038] = {"Пропавшее снаряжение Бинглза",nil, {"Найдите и верните Бинглзу гаечный ключ Бинглза, отвертку Бинглза, молот Бинглза и сорвиголовку Бинглза."}},
[2039] = {"Найти Бинглза",nil, {"Найдите Бинглза Покрышкинса в Лок Модане."}},
[2040] = {"Битва под землей",nil, {"Принесите Шони Молшунье в Штормград зубчатый спрек-механизм гномов из Мертвых копей."}},
[2041] = {"Разговор с Шони",nil, {"Поговорите с Шони Молшуньей в Штормграде."}},
[2078] = {"Месть Гиромачта",nil, {"Найдите первого помощника Гелхака, Бешенатора 4100, и отведите его к Гелхаку."}},
[2098] = {"Помощь Гиромачту",nil, {"Отыщите три фрагмента ключа и отнесите их Гелхаку Гиромачту."}},
[2118] = {"Зачумленные земли",nil, {"Поймайте бешеного колючешерстного медведя отведите его Тарнариуну.nnЕсли вам не удастся поймать зверя и вы потеряете ловушку, вернитесь к Тарнариуну Целителю Деревьев и попросите у него еще одну."}},
[2138] = {"Избавление от заразы",nil, {"Очистите лес от 20 бешеных колючешерстных медведей и возвращайтесь к Тарнариуну Исцелителю Деревьев на Темный Берег."}},
[2139] = {"Надежда Тарнариуна",nil, {"Найдите и убейте мать-медведицу."}},
[2158] = {"Отдых и покой",nil, {"Побеседуйте с Фарли, хозяином \"Гордости льва\"."}},
[2159] = {"Посылка в Доланаар",nil, {"Отнесите посылку хозяину таверны Кельдамиру в Доланааре."}},
[2160] = {"Припасы для Таннока",nil, {"Отнесите ящик с припасами хозяину таверны Танноку Молоту Стужи в Караносе."}},
[2161] = {"Ноша батрака",nil, {"Отнесите сумку Укора хозяину таверны Гроску в Колючий Холм."}},
[2178] = {"Спокойная жизнь долгоногов",nil, {"Принесите 5 кусков мяса долгонога Аландариан Песне Ночи в Аубердин."}},
[2198] = {"Порванное ожерелье",nil, {"Найдите создателя ожерелья, чтобы узнать, чего оно стоит."}},
[2199] = {"Мудрость за деньги",nil, {"Принесите пять серебряных слитков Талвашу де Кисселю в Стальгорн."}},
[2200] = {"Назад в Ульдаман",nil, {"Выясните, где именно в Ульдамане находится ожерелье Талваша. Убитый паладин, о котором он упоминал, был последним владельцем этого ожерелья."}},
[2201] = {"Время собирать камни",nil, {"Найдите рубин, сапфир, и топаз, которые спрятаны в разных местах Ульдамана. Когда найдете, свяжитесь с Талвашем де Кисселем, используя фиал Видения, который он предоставил.nnИз дневника вы узнали следующее:n*Рубин укрыт в забаррикадированной Кузне Теней.n*Топаз спрятан в урне в одной из областей, занятых троггами, недалеко от расположения дворфов Альянса.nn*Сапфир присвоил себе Гримлок, лидер троггов."}},
[2202] = {"В Ульдаман за реактивом",nil, {"Принести 12 грибов-малиновиков Джаркалу Замшелому Клыку в Каргат."}},
[2203] = {"Пробежка по Бесплодным землям-2",nil, {"Наполните пустые волшебные сосуды кровью опаленных драконов-стражей в Бесплодных землях и принесите их Джаркалу Замшелому Клыку в Каргат."}},
[2204] = {"Восстановление ожерелье",nil, {"Добудьте источник энергии из самого мощного волшебного создания, которое найдете в Ульдамане и доставьте его Талвашу де Кисселю в Стальгорн."}},
[2205] = {"Выход на ШРУ",nil, {"Доставьте пакет от Керина Сильвиуса мастеру Матиасу Шоу в казармы Штормграда."}},
[2206] = {"Держи-хватай!",nil, {"Найдите портового начальника из Братства Справедливости и добудьте расписание поставок для мастера Матиаса Шоу."}},
[2218] = {"Путь к спасению",nil, {"Поговорите с Хальфданом Чернобородом в Стальгорне."}},
[2238] = {"Простая уловка",nil, {"Найдите Онина Макмолота в Гномрегане."}},
[2239] = {"Донесение Онина",nil, {"Доставьте донесение Онина Хальфдану в Стальгорн."}},
[2240] = {"Потайной чертог",nil, {"Прочитайте журнал Балеога, исследуйте, потайной чертог и потом доложите об увиденном геологу Грозовой Вершине."}},
[2241] = {"Катись, яблочко...",nil, {"Отнесите розу Джаннока Сиарне в Дарнасс."}},
[2242] = {"Зов судьбы",nil, {"Найдите Сетира Древнего и принесите все, что сумеете найти, Сиарне."}},
[2258] = {"Пробежка по Бесплодным землям",nil, {"Принесите 5 вторых желудков канюка, 10 клыков скального койота и 5 осколков каменных элементалей Джаркалу Смешанному Лишайнику в Каргат."}},
[2259] = {"Эрион Шепот Тени",nil, {"Встретьтесь в Дарнассе с Эрионом Шепотом Тени."}},
[2260] = {"Директива Эриона",nil, {"Отправляйтесь в Штормград и посоветуйтесь с Рензиком Ножом."}},
[2278] = {"Платиновые диски",nil, {"Поговорите с каменным Стражем и запомните все, что он передаст вам. Когда он закончит свой пересказ событий древности, активируйте диски Норганнона."}},
[2279] = {"Платиновые диски",nil, {"Отнесите миниатюрную копию дисков Норганнона в Лигу исследователей в Стальгорне."}},
[2280] = {"Платиновые диски",nil, {"Отнесите миниатюрную копию дисков Норганнона кому-то из мудрецов с Громового Утеса."}},
[2281] = {"Встречи в Красногорье",nil, {"Отправляйтесь в Приозерье, что в Красногорье, и поговорите с Люцием."}},
[2282] = {"Лесопилка Альтера",nil, {"Откройте сундук Люция, достаньте руну воровства и вернитесь к Люцию в Приозерье."}},
[2283] = {"Пропавшее ожерелье",nil, {"Найдите на раскопках Ульдамана драгоценное ожерелье (возможно, поврежденное) и принесите его в Оргриммар Драну Дрофферсу."}},
[2284] = {"Пропавшее ожерелье, этап 2",nil, {"Разыщите ключ к местонахождению самоцветов в глубинах Ульдамана."}},
[2298] = {"Королевская облава",nil, {"Отправляйтесь в Штормград и найдите Рензика Ножа."}},
[2299] = {"К Хальфдану!",nil, {"Отправляйтесь в Стальгорн и поговорите с Хальфданом Чернобородом."}},
[2300] = {"ШРУ",nil, {"Отправляйтесь в Штормград и разыщите Рензика Ножа."}},
[2318] = {"Трудности перевода",nil, {"Найдите кого-нибудь, кто сможет перевести для вас дневник паладина. Ближайший к вам такой умелец, скорее всего, найдется в Каргате, форпосте в Бесплодных Землях."}},
[2338] = {"Трудности перевода",nil, {"Одолжите ожерелье Джаркалу. Взамен он переведет дневник."}},
[2339] = {"Найти самоцветы и источник энергии",nil, {"Добудьте все три самоцвета и источник энергии для ожерелья из Ульдамана и принесите их Джаркалу Замшелому Клыку в Каргат. Джаркал считает, что источник энергии можно найти у самой сильной твари в Ульдамане. nnИз дневника следует... n* Рубин спрятан на огражденной завалами территории Кузни Теней. n*Топаз спрятан в урне на территорях троггов под боком у дворфов Альянса.n*Сапфир забрал Гримлок, старейшина троггов."}},
[2340] = {"Принести самоцветы",nil, {"Доставьте ожерелье и драгоценности Драну Дрофферсу в Оргриммар."}},
[2341] = {"Пропавшее ожерелье, этап 3",nil, {"Навестите Джаркала Замшелого Клыка в Каргате."}},
[2342] = {"Возвращенные сокровища",nil, {"Принесите Патрику Гарретту в Подгород фамильное сокровище из сундука в Южном зале Ульдамана."}},
[2358] = {"Рога Незры",nil, {"Верните рога Незры Арантиру в Приозерье."}},
[2359] = {"Башня Клейвена",nil, {"Добудьте ключ от башни Братства Справедливости, проберитесь в башню и выясните, каково содержимое сундука из Сумеречного леса. Отнесите найденные вещи Матиасу Шоу в Штормград. Почитайте дневник Кирнен, чтобы больше узнать о башне."}},
[2360] = {"Матиас и Братство Справедливости",nil, {"Найдите посланницу Кеарнен в Западном Крае и поговорите с ней."}},
[2361] = {"Восстановление ожерелье",nil, {"Я с великой радостью вручаю тебе это ожерелье, могучий |3-6(Жрец). Ты оказал мне неоценимую услугу, и я благодарен тебе за это. Носи же его с гордостью и не забывай при этом все время упоминать о том, кто сделал его для тебя!nnДа, если еще добавишь, что на ожерелье нет проклятия, это будет великолепно."}},
[2378] = {"Поиски Изувеченной Длани",nil, {"Отправляйтесь в Оргриммар и поговорите с Шентулом в Расселине Теней."}},
[2379] = {"Зандо'зан",nil, {"Поговорите с Зандо'заном в расселине Теней."}},
[2380] = {"В Оргриммар!",nil, {"Поговорите с Шентулом в расселине Теней в Оргриммаре."}},
[2381] = {"Грабь награбленное",nil, {"Принесите Рениксу Презренному из Кабестана сокровище Южного моря. Не забудьте взять из гизмотронного аппарата Реникса ХОЧУ и отмычки. Они понадобятся для выполнения задания.nnЕсли на вас нападут необычайно враждебные попугаи, воспользуйтесь ХОЧУ!"}},
[2382] = {"Реникс из Кабестана",nil, {"Поговорите с Рениксом Презренным в Кабестане."}},
[2383] = {"Записка на пергаменте",nil, {"Прочтите пергамент и поговорите с Франгом в долине Испытаний."}},
[2398] = {"Потерянные дворфы",nil, {"Найдите Бейлога в Ульдамане."}},
[2399] = {"Росток папоротника",nil, {"Папоротник, который Деналан посадил у себя в саду, проклюнулся и вырос. Ростки так и просят, чтобы их скорее собрали..."}},
[2418] = {"Камни Силы",nil, {"Принесите 8 дентриевых камней Силы и 8 энелиевых камней Силы Ригглфаззу в Бесплодные земли."}},
[2438] = {"Изумрудный ловец снов",nil, {"Принесите изумрудного ловца снов Таллонкаю Гибкому Корню в Доланаар."}},
[2439] = {"Платиновые диски",nil, {"Отнесите наградной сертификат Дините Каменной Мантии в хранилище Стальгорна."}},
[2440] = {"Платиновые диски",nil, {"Отнесите наградной сертификат Бене Заиндевевшее Копыто в Громовой Утес."}},
[2458] = {"Надежное прикрытие",nil, {"Отыщите башню Торговой компании к северу от Нефтяного болота и вступите в контакт с тайным агентом Изувеченной Длани, надсмотрщиком Физзулом.nnВозле башни выстрелите из сигнального ружья ДВА раза, потом подойдите к надсмотрщику Физзулу и продемонстрируйте приветствие Изувеченной Длани (команда /честь).nnВсе нужно делать строго по порядку – Физзул убивает всех, кто кажется ему подозрительным."}},
[2459] = {"Фероситас Пожиратель Снов",nil, {"Таллонкай Гибкий Корень из Доланаара хочет, чтобы вы убили 7 мистиков из племени Кривой Сосны и нашли пропавший самоцвет."}},
[2460] = {"Приветствие Изувеченной Длани",nil, {"Повторите приветствие Раздробленной Руки вслед за Шентулом."}},
[2478] = {"Миссия: выполнима, но с трудом",nil, {"Украдите ключ от башни у штейгера Силиксиза, убейте двух мертвяков-мутантов Торговой компании, двух патрульных Торговой компании и двух смотрителей Торговой компании.nnЛиквидируйте старшего мастера Галливикса, взломайте замок на его ящике и украдите запас измененной смеси Занзила. Отнесите запас смеси и голову Галливикса Шентулу в Оргриммар и получите следующие указания."}},
[2479] = {"Помощь Хинотта",nil, {"Отправляйтесь в Мельницу Таррен в предгорьях Хилсбрада и доставьте образчик смеси Занзила Сержу Хинотту.nnСядьте на дирижабль, идущий до Подгорода, и летите вдоль дороги на юг, через Серебряный бор в сторону Хилсбрада. Следите за знаками."}},
[2480] = {"Помощь Хинотта",nil, {"Дождитесь, пока Серж Хинотт доделает лекарство."}},
[2498] = {"Возвращение к Деналану",nil, {"Релиан Зеленый Костер хочет, чтобы вы поговорили с Деналаном у озера Аль'Амет."}},
[2499] = {"Дубохмур",nil, {"Деналан с озера Аль'Амет хочет, чтобы вы сняли гигантскую опухоль с Дубохмура."}},
[2500] = {"Пробежка по Бесплодным землям",nil, {"Добудьте в Бесплодных землях реактивы для Гхака Целителя и отнесите их ему в Телcамар."}},
[2501] = {"Пробежка по Бесплодным землям-2",nil, {"Наполните пустые волшебные сосуды кровью опаленных драконов-стражей в Бесплодных землях и принесите их Гхаку Целителю в Телcамар."}},
[2518] = {"Слезы Луны",nil, {"Жрица А'моора из дарнасского храма Луны хочет, чтобы вы принесли ей серебряные паутинные железы леди Сатры."}},
[2519] = {"Храм Луны",nil, {"Сестра Аквинна хочет, чтобы вы поговорили со жрицей А'моорой из Храма Луны."}},
[2520] = {"Жертвоприношение Сатры",nil, {"Жрица А'моора просит вас положить серебряные паутинные железы леди Сатры у фонтана в храме и затем вернуться к ней."}},
[2521] = {"Служба для Кум'иша",nil, {"Если в своем путешествии по Выжженным землям вы найдете безупречную сферу из дренетиста, отнесите ее Кум'ише Коллекционеру. nnБудьте внимательны – этот камень – один из самых редких кристаллов во всем Азероте. Любое существо в этих землях может владеть безупречной сферой из дренетиста. nnВы будете вознаграждены за каждую безупречную сферу из дренетиста, которую доставите Кум-ише."}},
[2522] = {"Попытки Кум'иша",nil, {"Найдите безупречную сферу из дренетиста и верните ее Кум'ише Коллекционеру."}},
[2523] = {"Оскверненный песнецвет",nil, {"Вы нашли чахлое, пораженное какой-то порчей растение, похожее на песнецвет. Резкий, нездоровый запах исходит от него. Если вы хотите вернуть его к нормальному состоянию, то срочно необходимо им заняться."}},
[2541] = {"Спящий друид",nil, {"Принесите шаманский оберег вуду Обену Яростному Когтю в обитель Бен'этиль."}},
[2561] = {"Друид-медведь",nil, {"Обен Яростный Коготь просит, чтобы вы уничтожили его лишенное души тело, а затем воспользовались оберегом вуду."}},
[2581] = {"Челюсти гиены-хохотуна",nil, {"Принесите три челюсти гиены, два обожженных легких вепря и клешню скорпока магу крови Дразиалу."}},
[2582] = {"Ярость веков",nil, {"Принесите три челюсти гиены, два обожженных легких вепря и клешню скорпока магу крови Дразиалу."}},
[2583] = {"Жизненная сила вепря",nil, {"Принесите три обожженных легких вепря, две клешни скорпока и мозг василиска магу крови Дразиалу."}},
[2584] = {"Дух вепря",nil, {"Принесите три обожженных легких вепря, две клешни скорпока и мозг василиска магу крови Дразиалу."}},
[2585] = {"Решительный удар",nil, {"Принесите три клешни скорпока, две порции потрохов стервятника и обожженное легкое вепря магу крови Дразиалу."}},
[2586] = {"Скорпоковая соль",nil, {"Принесите три клешни скорпока, две порции потрохов стервятника и обожженное легкое вепря магу крови Дразиалу."}},
[2601] = {"Укус василиска",nil, {"Принесите десять мозгов василиска и потроха двух стервятников волшебнице крови Линоре."}},
[2602] = {"Непогрешимый разум",nil, {"Принесите десять мозгов василиска и потроха двух стервятников волшебнице крови Линоре."}},
[2603] = {"Стойкость стервятника",nil, {"Принесите потроха десяти стервятников и две челюсти гиены волшебнице крови Линоре."}},
[2604] = {"Духовное превосходство",nil, {"Принесите потроха десяти стервятников и две челюсти гиены волшебнице крови Линоре."}},
[2605] = {"Жаждущий гоблин",nil, {"Добудьте насыщенную влагой железу и принесите ее Синю Гогельмогелю в Прибамбасск."}},
[2606] = {"Хороший вкус",nil, {"Поговорите с Поливалкой в Прибамбасске по поручению Синя Гогельмогеля."}},
[2607] = {"Прикосновение Занзила",nil, {"Поговорите с Доком Миксиль-Пиксилем в подвале казарм."}},
[2608] = {"Прикосновение Занзила",nil, {"Лягте на землю (действие /lay) для осмотра Доком Миксиль-Пиксилем – он не сможет подобрать лекарство, пока не найдет причину болезни."}},
[2609] = {"Прикосновение Занзила",nil, {"Принесите Доку Миксиль-Пиксилю один букетик простых полевых цветов, одну освинцованную колбу, одну бронзовую трубку и одну катушку светлого зеленовато-желтого шелка. Чтотит, знаете ли, сам собой не проходит."}},
[2621] = {"Опозоренный",nil, {"Поговорите с командиром разведотряда Руагом в Каменоре."}},
[2622] = {"Пропавшие приказы",nil, {"Поговорите с Бенгором."}},
[2623] = {"Болотный говорун",nil, {"Заберите приказы вождя и верните их павшему герою Орды."}},
[2641] = {"Маленький секрет Поливалки",nil, {"Найдите фиалковый траган и вернитесь к Поливалке в Прибамбасск."}},
[2661] = {"Посылка для Синя",nil, {"Отнесите фиалковый порошок Синю Гогельмогелю в Прибамбасск."}},
[2662] = {"Эликсир Ноггенфоггера",nil, {"Синь Гогельмогель хочет поговорить с вами после завершения работы над эликсиром."}},
[2681] = {"Камни, что связывают нас",nil, {"Освободите девять слуг Разелиха, трех слуг Севины, трех слуг Аллистария и трех слуг Грола. Когда ваша задача будет выполнена, возвращайтесь к павшему герою. Вы должны оставаться поблизости от камней, иначе процесс не будет завершен."}},
[2701] = {"Герои древних времен",nil, {"Откройте шкатулку и возьмите награду."}},
[2702] = {"Герои древних времен",nil, {"Поговорите с капралом Тундом Треснувшим Копытом."}},
[2721] = {"Кирит",nil, {"Узнайте, что стало с лейтенантом Киритом."}},
[2741] = {"Супер Яйц-О-Матик",nil, {"Супер Яйц-О-Матик – последнее изобретение Кургль Поскакушки. Очень впечатляющее устройство.nnПриближаясь к панели управления, вы слышите, как машина яростно гудит."}},
[2742] = {"Рин'джи в ловушке",nil, {"Выведите Рин'джи из Сторожки Кель'Данил, а потом найдите то, что он спрятал в Отвесных скалах, на востоке."}},
[2743] = {"Покров Тьмы",nil, {"Вернитесь к павшему герою Орды со своими находками."}},
[2744] = {"Охотник на демонов",nil, {"Поговорите в Азшаре с Лорамусом Многоногом."}},
[2745] = {"Проникновение в крепость",nil, {"Поговорите с Тирионом в крепости Штормграда."}},
[2746] = {"Все по порядку",nil, {"Принесите Тириону в Штормград 3 отреза шелка и 2 свежих яблока Клары."}},
[2747] = {"Необычное яйцо",nil, {"Редко удается принести яйца из Фераласа в таком необыкновенном состоянии. Они очень редко встречаются..."}},
[2748] = {"Хорошее яйцо",nil, {"Из хороших яиц при правильных условиях вылупляются гиппогрифы, не пораженные заклинаниями огров клана Гордунни."}},
[2749] = {"Обычное яйцо",nil, {"Обычные яйца гиппогрифа вылупляются только в половине случаев. Мы будем тщательно ухаживать за ними, и при удаче кто-нибудь из них вылупится."}},
[2750] = {"Плохое яйцо",nil, {"Яйца вроде этого не спасти – из них никто не вылупится."}},
[2751] = {"Варварские доспехи",nil, {"Принесите 2 узорчатых бронзовых боевых браслета, 2 бронзовых больших меча и 2 острых когтя Орокку Омошу в Оргриммар."}},
[2752] = {"За железными наплечниками",nil, {"Принесите Орокку Омошу в Оргриммар 4 бронзовых боевых топора и 4 бронзовых боевых молота."}},
[2753] = {"Тяжелая поступь",nil, {"Принесите 4 зеленых железных шлема, 4 пары зеленых железных наручей и 2 пары зеленых железных поножей Орокку Омошу в Оргриммар."}},
[2754] = {"Рога безумия",nil, {"Принесите Орокку Омошу в Оргриммар 2 прочных железных кувалды, 2 пары посеребренных бронзовых сапог и 2 пары посеребренных бронзовых рукавиц."}},
[2755] = {"Радости Омоша",nil, {"Научитесь танцу Радости Омошей."}},
[2756] = {"Старые обычаи",nil, {"Принесите 4 стальные кирасы и 4 стальных латных шлема Атурку Наковальне в Оргриммар."}},
[2757] = {"Вперед, в Пиратскую Бухту!",nil, {"Поговорите с Макгеваном в Пиратской бухте."}},
[2758] = {"Происхождение кузнечного искусства",nil, {"Принесите 6 пар золотых чешуйчатых наручей Хэнку Молоту в Штормград."}},
[2759] = {"В поисках Гальвана",nil, {"Поговорите с Макгеваном в Пиратской бухте."}},
[2760] = {"Орден Мифрила",nil, {"Поговорите с Гальваном Древним в Тернистой долине."}},
[2761] = {"Плавим, не плавим, плавим, не плавим…",nil, {"Принесите сорок мифриловых слитков и сорок железных слитков Гальвану Древнему из Тернистой долины."}},
[2762] = {"Обманное серебро",nil, {"Принесите сорок мифриловых слитков и сорок слитков истинного серебра Гальвану Древнему из Тернистой долины."}},
[2763] = {"Могущество камней",nil, {"Принесите сорок мифриловых слитков и 4 цитрина Гальвану Древнему из Тернистой долины."}},
[2764] = {"Лучший ученик Гальвана",nil, {"Поговорите с Трентоном Молотом Света из Прибамбасска."}},
[2765] = {"Кузнец-умелец!",nil, {"Получите награду у Гальвана Древнего!"}},
[2766] = {"Найти ККX-22/FE!",nil, {"Отнесите аварийный маяк к автономному роботу Карлса, в пещеру рядом с Долиной Свирепого Утеса."}},
[2767] = {"Спасти ККX-22/FE!",nil, {"Проводите OOX-22/FE в порт на Забытом Берегу, затем сообщите об этом Карлсу Пропеллерзу в Пиратской бухте."}},
[2768] = {"Изыскательский жезл",nil, {"Принесите изыскательский жезл главному инженеру Чепухастеру в Прибамбасск."}},
[2769] = {"Братья Латунный Болт",nil, {"Поговорите с Виззлом Медноштифом на Мерцающей равнине."}},
[2770] = {"Газ'рилла",nil, {"Принесите искрящую чешую Газ'риллы Виззлу Медноштифу на Мерцающую равнину."}},
[2771] = {"Голова на плечах",nil, {"Принесите два мифриловых капюшона и изысканный мифриловый наплечник Трентону Молоту Света."}},
[2772] = {"Мир у твоих ног",nil, {"Принесите две пары тяжелых мифриловых сапог и изысканные мифриловые штаны Трентону Молоту Света."}},
[2773] = {"Мифриловый парень",nil, {"Принесите две тяжелых мифриловых кирасы и изысканные мифриловые перчатки Трентону Молоту Света."}},
[2781] = {"Разыскивается: Калиф Жало Скорпида",nil, {"Принесите голову Калифа Жало Скорпида главному инженеру Чепухастеру в Прибамбасск."}},
[2782] = {"Секрет Рин'джи",nil, {"Отнесите табличку Оран Змеиные Кольца в Квартал Магов Подгорода. Оран – известная ученая, изучающая троллей из племени Сухокожих, и она наверняка сумеет прочесть надписи на табличке."}},
[2783] = {"Мелкие раздоры",nil, {"Поговорите с павшим героем Орды на границе Выжженных земель и болот."}},
[2784] = {"Попавший в немилость",nil, {"Послушайте, как павший герой Орды рассказывает свою историю."}},
[2801] = {"Горестная повесть",nil, {"Послушайте, как павший герой Орды рассказывает свою историю."}},
