[2845] = {"Шай-бродяжница",nil, {"Достаньте из сундучка колокольчик Шай.nnПроводите Шай Листогон в лагерь Камнедробителя."}},
[2846] = {"Тиара глубин",nil, {"Принесите Тиару Глубин Табете в Пыльную Трясину."}},
[2847] = {"Доспехи из дикой кожи",nil, {"Принесите 10 кусков плотной кожи Пратту Макгруббену в Крепость Оперенной Луны."}},
[2848] = {"Наплечники из дикой кожи",nil, {"Принесите 6 накладок из толстой кожи и дикую лозу Пратту Макгруббену в крепость Оперенной Луны."}},
[2849] = {"Жилет из дикой кожи",nil, {"Принесите 2 черепашьих чешуйчатых кирасы, 2 пары черепашьих чешуйчатых перчаток и дикую лозу Пратту Макгруббену в крепость Оперенной Луны."}},
[2850] = {"Шлем из дикой кожи",nil, {"Принесите 2 ночных мундира, 2 ночных головных повязки и дикую лозу Пратту Макгруббену в крепость Оперенной Луны."}},
[2851] = {"Сапоги из дикой кожи",nil, {"Принесите 2 пары ночных штанов, 2 пары ночных сапог и 2 диких лозы Пратту Макгруббену в крепость Оперенной Луны."}},
[2852] = {"Поножи из дикой кожи",nil, {"Принесите два черепашьих чешуйчатых шлема, две пары черепашьих чешуйчатых наручей и две дикие лозы Пратту Макгруббену в крепость Оперенной Луны."}},
[2853] = {"Мастер школы Дикаря",nil, {"Отдайте письмо Пратта Телонису, главному кожевнику Дарнасса."}},
[2854] = {"Доспехи из дикой кожи",nil, {"Принесите 10 кусков плотной кожи Джангору Быстрому Страннику в Лагерь Мохаче."}},
[2855] = {"Наплечники из дикой кожи",nil, {"Принесите 6 накладок из толстой кожи и дикую лозу Джангору Быстрому Страннику в лагерь Мохаче."}},
[2856] = {"Жилет из дикой кожи",nil, {"Принесите 2 черепашьих чешуйчатых кирасы, 2 пары черепашьих чешуйчатых перчаток и дикую лозу Джангору Быстрому Страннику в лагерь Мохаче."}},
[2857] = {"Шлем из дикой кожи",nil, {"Принесите 2 ночных мундира, 2 ночных головных повязки и дикую лозу Джангору Быстрому Страннику в лагерь Мохаче."}},
[2858] = {"Сапоги из дикой кожи",nil, {"Принесите 2 пары ночных штанов, 2 пары ночных сапог и 2 диких лозы Джангору Быстрому Страннику в лагерь Мохаче."}},
[2859] = {"Поножи из дикой кожи",nil, {"Принесите два черепашьих чешуйчатых шлема, две пары черепашьих чешуйчатых наручей и две дикие лозы Джангору Быстрому Страннику в лагерь Мохаче."}},
[2860] = {"Мастер школы Дикаря",nil, {"Отдайте письмо Джангора Уне, главному кожевнику Громового Утеса."}},
[2861] = {"Миссия Табеты",nil, {"Поговорите с Табетой в Пылевых топях."}},
[2862] = {"Война с племенем Древолапов",nil, {"Принесите 10 грив гноллов-древолапов Хадокену Быстрому Страннику в Лагере Мохаче."}},
[2863] = {"Истребление вожаков",nil, {"Убейте в течение часа 5 вожаков стаи Древолапов и вернитесь к Хадокену Быстрому Страннику."}},
[2864] = {"Тран'рек",nil, {"Поговорите с Тран'реком в Прибамбасске."}},
[2865] = {"Панцири скарабеев",nil, {"Принести 5 целых панцирей скарабея Тран'реку в Прибамбасск."}},
[2866] = {"Руины Соларсаля",nil, {"Обследуйте руины Соларсаля на предмет присутствия наг."}},
[2867] = {"Возвращение в Крепость Оперенной Луны",nil, {"Вернитесь в Крепость Оперенной Луны к Шандрисе Оперенной Луне и доложите о находке."}},
[2869] = {"Борьба с Гребнем Ненависти",nil, {"Принесите 10 штук чешуи наг из клана Гребня Ненависти Латроникусу Лунному Копью в Крепость Оперенной Луны."}},
[2870] = {"Борьба с лордом Шалзару",nil, {"Убейте лорда Шалзару и принесите обнаруженную им таинственную реликвию Латроникусу Лунному Копью в Крепость Оперенной Луны."}},
[2871] = {"Доставка реликвии",nil, {"Доставьте таинственную реликвию Вестии Лунное Копье в Крепость Оперенной Луны."}},
[2872] = {"Долг Столи",nil, {"Поговорите со Столи из Порта Картеля."}},
[2873] = {"Посылка Столи",nil, {"Отыщите партию товара Столи и возвратите ее хозяину в Порт Картеля."}},
[2874] = {"Ром для Маккинли",nil, {"Отнесите бутылку Столи \"Морскому волку\" МакКинли в Пиратскую Бухту."}},
[2875] = {"Разыскивается: Андре Огнебородый",nil, {"Принесите голову Андре Огнебородого начальнику отдела безопасности Чепухастеру в Порт Картеля."}},
[2876] = {"Расписание кораблей",nil, {"Сообщите о корабельном расписании руководству Порта Картеля."}},
[2877] = {"Зачистка Осклизлой скалы",nil, {"Убейте 10 зеленых слякочей и 10 нефритовых слизнюков, после чего доложитесь Фраггару Громовой Мантии на Заоблачном Пике."}},
[2878] = {"Оскверненный песнецвет",nil, {"Вы нашли чахлое, пораженное какой-то порчей растение, похожее на песнецвет. Резкий, нездоровый запах исходит от него. Если вы хотите вернуть его к нормальному состоянию, то срочно необходимо им заняться."}},
[2879] = {"Посох равноденствия",nil, {"Зарядите посох Трояса и отыщите Монолит Равноденствия."}},
[2880] = {"Ожерелья троллей",nil, {"Принесите 5 тролльих ожерелий Фраггару Громовой Мантии на Заоблачный пик."}},
[2881] = {"Ожерелья троллей",nil, {"Вы уже выполнили мое задание, но если вы захотите принести мне еще тролльих ожерелий, то я с радостью приму их. Это поможет вам заслужить хорошее отношение Громового Молота – если вы нам помогаете, то и мы с радостью поможем вам!nnПриносите по пять ожерелий, Медведик."}},
[2882] = {"Золото Куэрго",nil, {"Найдите золото Куэрго!"}},
[2902] = {"Разведка планов Древолапов",nil, {"Найдите гноллские боевые планы в гноллских лагерях к югу от Лагеря Мохаче."}},
[2903] = {"Планы битвы",nil, {"Отнесите планы битвы Древолапов Хадокену Быстрому Страннику в Лагерь Мохаче."}},
[2904] = {"Катавасия",nil, {"Отведите Керноби к Часовому ходу. Затем отправляйтесь с донесением к Скути в Пиратскую Бухту."}},
[2922] = {"Промыть мозг Техботу",nil, {"Принесите ядро памяти Техбота мехмастеру Замыкальцу в Стальгорн."}},
[2923] = {"Мехмастер Замыкалец",nil, {"Поговорите с механомастером Большой Искрой."}},
[2924] = {"Базовый элемент",nil, {"Принесите 12 базовых элементов Клацморту Гайкокруту в Стальгорн."}},
[2925] = {"Базовые элементы Клацморта",nil, {"Поговорите с Клацмортом Гайкокрутом в Стальгорне."}},
[2926] = {"Новая формула",nil, {"Соберите радиоактивный осадок, оставляемый облученными захватчиками и облученными погромщиками в пустой свинцовый фиал для образцов. Принесите полный фиал Оззи Триггервольту в Каранос."}},
[2927] = {"На другой день",nil, {"Поговорите с Оззи Триггервольтом в Караносе."}},
[2928] = {"Сооружение автогиробуророек",nil, {"Принесите Шони в Штормград 24 горсти механических внутренностей роботов."}},
[2929] = {"Великое предательство",nil, {"Отправляйтесь в Гномреган и убейте Мекжинера Термоштепселя. Вернитесь к Главному Механику Меггакруту."}},
[2930] = {"Спасение данных",nil, {"Принесите призматическую перфокарту главному механику Чугунотрубзу в Стальгорн."}},
[2931] = {"Поручение Чугонотрубза",nil, {"Поговорите с главным механиком Чугунотрубзом в Стальгорне."}},
[2932] = {"Предупреждение",nil, {"Соберите черепа троллей из клана Сухокожих и насадите их на пику Нимбойи, после чего установите пику с тролльими черепами в центре тролльей деревни и возвращайтесь к Нимбойе в Тернистую долину."}},
[2933] = {"Бутыли с ядом",nil, {"Принесите бутылку яда аптекарю в Мельницу Таррен."}},
[2934] = {"Неповрежденная ядовитая железа",nil, {"Принесите неповрежденную ядовитую железу аптекарю Лидону в Мельницу Таррен."}},
[2935] = {"Разговор с мастером Гадрином",nil, {"Поговорите с мастером Гадрином в деревне Сен'джин."}},
[2936] = {"Паучья богиня",nil, {"Прочитайте надписи на табличке Теки, узнайте имя паучьей богини, которой поклоняются тролли из племени Сухокожих, а потом возвращайтесь к мастеру Гадрину."}},
[2937] = {"Призыв Шадры",nil, {"Убейте Шадру у алтаря Шадра'Алора и принесите ее яд аптекарю Лидону в Мельницу Таррен."}},
[2938] = {"Яд в Подгород",nil, {"Отнесите сверток мастеру-аптекарю Фаранеллу в Подгород."}},
[2939] = {"В поисках знаний",nil, {"Поговорите с Дарин Добрый Ветер в деревне Рут'теран."}},
[2940] = {"\"История Фераласа\"",nil, {"Попросите Дарин Добрый Ветер одолжить вам книгу."}},
[2941] = {"В обмен на книгу",nil, {"Отнесите письмо Кургль Поскакушке в Танарис."}},
[2942] = {"Завтрашний камень",nil, {"Верните искрящийся камень и посох Равноденствия Троясу Лунному Бризу в Крепость Оперенной Луны."}},
[2943] = {"Возвращение к Троясу",nil, {"Доставьте книгу Троясу Лунному Бризу в Крепость Оперенной Луны."}},
[2944] = {"Супер-хвататель ФХ",nil, {"Сделайте снимок Гаммериты с помощью супер-хватателя ФХ и вернитесь к Дарин Добрый Ветер в деревне Рут'теран."}},
[2945] = {"Кольцо, покрытое грязью",nil, {"Придумайте, как отчистить кольцо, покрытое грязью."}},
